__NUXT_JSONP__("/svet/mesto/smith-s", (function(a,b,c,d,e){return {data:[{place:[{id:259,id_state:d,name:"Smith’s",population:6363,area:6.03,altitude:3,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"smith-s",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a}],placeState:[{id:d,mpz:"BM",tld:".bm",name:"Bermudy",area:54,population:63918,slug:"bermudy",id_continent:1,number_cities:a,information_chatgpt:"\u003Cp\u003EBermudy je zámořské britské území, tvořené skupinou 181 korálových ostrovů v Atlantském oceánu, vzdálených asi 1050 km východně od amerického pobřeží. Největším městem na Bermudách je Hamilton, který je hlavním městem a hospodářským centrem ostrovů.\n\u003C\u002Fp\u003E\u003Cp\u003E\nBermudy jsou proslulé svými růžovými plážemi, tyrkysovými vodami, skvělým potápěním a bohatou flórou a faunou. Kromě turistiky je důležitým průmyslovým odvětvím i mezinárodní finanční služby a lodní doprava. Bermuda je také jedním z největších světových center pro obchod s pojištěním.\n\u003C\u002Fp\u003E\u003Cp\u003E\nBermudy byly objeveny Evropany v roce 1505 a kolonizovány Británií v roce 1612. Po mnoho let byly Bermudy významným centrem pro trestanecké osady a obchod s otroky. Po zrušení otroctví se Bermudy staly významným centrem pro rybolov, zemědělství a turistický průmysl.\n\u003C\u002Fp\u003E\u003Cp\u003E\nKultura na Bermudách je ovlivněna britskou a africkou kulturou. Místní lidé se vyznačují přátelským a pohostinným chováním a rádi se zapojují do místních tradic, jako jsou návštěvy kostela, konání parád a oslav a rybolov.\n\u003C\u002Fp\u003E\u003Cp\u003E\nBermuda je známá také pro své sporty, jako jsou kriket, fotbal, golf a plavání. Mezi nejvýznamnější sportovní akce patří Bermuda Gold Cup v jachtingu, který se koná každý rok v říjnu.\n\u003C\u002Fp\u003E\u003Cp\u003E\nBermuda je zámořské území Velké Británie s vlastním parlamentem a guvernérem. Místní ekonomika je založena na turistickém průmyslu, finančních službách, lodní dopravě, rybolovu a zemědělství. Ostrovy mají dobré vzdělávací systémy a zdravotní péči.\u003C\u002Fp\u003E",image_cover:a,image_hero:a,currency_code:"BMD",currency_name:"Bermudský dolar",money_prices:[{name:"Levný hotel",value:220,date_update:b},{name:"Normální hotel",value:525,date_update:b},{name:"Dražší hotel",value:1100,date_update:b},{name:"1km taxíkem",value:e,date_update:b},{name:"Běžná cena večeře",value:50,date_update:b},{name:"Káva",value:6,date_update:b},{name:"Kopeček zmrzliny",value:e,date_update:b},{name:"500ml vody",value:2,date_update:b},{name:"Pivo",value:9,date_update:b},{name:"Cigarety",value:14,date_update:b},{name:"Chleba",value:4,date_update:b},{name:"Coca Cola",value:2.5,date_update:b},{name:"Bic Mac",value:6.5,date_update:b}],phone_numbers_emergency:[{name:"Police",number:c},{name:"Ambulance",number:c},{name:"Fire",number:c}],phone_prefix:"+1-441",coordinates:[{latitude:"32.3078",longitude:"-64.7505"}],people_religion:a,people_nationality:a,people_education:a,visitors_entry:a}],placeContinent:a}],fetch:{},mutations:void 0}}(null,"03-2023","911",27,5)));