__NUXT_JSONP__("/svet/kontinent/antarktida", (function(a,b,c,d,e,f,g,h,i,j,k){return {data:[{place:[{id:c,slug:e,name:f,area:g,population:d,population_density:d,number_states:d,information_chatgpt:"Antarktida je nejjižnější kontinent na Zemi a je obklopena Jižním oceánem. Rozprostírá se na ploše asi 14,2 milionu km² a je pokryta převážně ledem, což ji činí jedním z nejchladnějších a nejextrémnějších míst na světě.\n\nAntarktida je domovem mnoha různých druhů zvířat, jako jsou tučňáci, albatrosi, tuleňi a velryby. Zároveň zde najdeme řadu výzkumných stanic, které se věnují výzkumu klimatu, ledovců, oceánografii a mnoha dalším vědním oblastem.\n\nAntarktida je také důležitá pro své zásoby přírodních zdrojů, jako jsou nerostné suroviny, ropa a zemní plyn, avšak její těžba a využívání jsou velmi omezené kvůli svému chráněnému statusu a odlehlé poloze.\n\nKromě toho Antarktida hraje důležitou roli v regulaci světového klimatu. Rozsáhlé ledovcové pláně a množství ledu na Antarktidě pomáhají udržovat stabilní hladinu oceánu a ovlivňují množství slunečního záření, které se dostává na Zemi.\n\nAntarktida je také chráněna mezinárodními dohodami, které zakazují těžbu a vojenskou činnost na území Antarktidy a regulují činnosti vědeckých stanic. Toto území je tedy považováno za společné dědictví celého lidstva.",image_cover:h,image_hero:h,coordinates:a}],placesStates:[{id:216,mpz:"TF",tld:".tf",name:"Francouzská jižní a antarktická území",area:7747,population:140,slug:"francouzska-jizni-a-antarkticka-uzemi",id_continent:c,number_cities:a,information_chatgpt:a,image_cover:a,image_hero:a,currency_code:"EUR",currency_name:"Euro",money_prices:a,phone_numbers_emergency:a,phone_prefix:"+262",coordinates:[[{latitude:i,longitude:j}],{latitude:i,longitude:j}],people_religion:a,people_nationality:a,people_education:a,visitors_entry:a},{id:34,mpz:"BV",tld:".bv",name:"Bouvetův ostrov",area:49,population:a,slug:"bouvetuv-ostrov",id_continent:c,number_cities:a,information_chatgpt:"\u003Cp\u003EBouvetův ostrov je malý a vzdálený ostrov v jižním Atlantiku. Je to neobydlené území Norska, které má rozlohu pouhých 49 km². Ostrov je zhruba kruhového tvaru a pokrytý ledovci. Je považován za jedno z nejizolovanějších míst na Zemi, přičemž nejbližší osídlené místo (Jihoafrická republika) je vzdáleno přes 1700 km.\n\u003C\u002Fp\u003E\u003Cp\u003E\nBouvetův ostrov byl objeven v roce 1739 francouzským navigátorem Jeanem-Baptistem Charles Bouvetem de Lozierem a je od té doby předmětem zájmu vědců a badatelů. Na ostrově nebyla nalezena žádná trvalá fauna nebo flóra. Předpokládá se však, že v okolí ostrova mohou být bohaté zásoby ryb a dalších mořských živočichů.\n\u003C\u002Fp\u003E\u003Cp\u003E\nVzhledem k tomu, že je Bouvetův ostrov neobydlený a vzdálený, nemá žádnou ekonomiku ani správu, kromě toho, že je spravován Norskou polární institucí. Ostrov je navštěvován pouze ojediněle vědci a expedicemi, kteří se zajímají o jeho unikátní geologické útvary.\u003C\u002Fp\u003E",image_cover:a,image_hero:a,currency_code:a,currency_name:"Norská koruna",money_prices:a,phone_numbers_emergency:a,phone_prefix:"+47",coordinates:[{latitude:"-54.4204",longitude:"3.3487"}],people_religion:a,people_nationality:a,people_education:a,visitors_entry:a},{id:9,mpz:"AQ",tld:".aq",name:f,area:g,population:a,slug:e,id_continent:c,number_cities:a,information_chatgpt:"\u003Cp\u003EAntarktida není státem, ale spíše územím pod mezinárodní správou, které je vyhrazeno pro vědecký výzkum a ochranu životního prostředí. Podle Antarktické smlouvy, podepsané v roce 1959, jsou nároky na území Antarktidy pozastaveny a na tomto území se nevztahují žádné národní zákony ani vládní pravomoci. Místo toho je Antarktida spravována Mezinárodním výborem pro ochranu Antarktidy (CCAMLR) a podléhá zvláštním pravidlům a regulacím, které mají zajistit její ochranu a udržitelnost pro budoucí generace.\u003C\u002Fp\u003E",image_cover:k,image_hero:k,currency_code:a,currency_name:a,money_prices:a,phone_numbers_emergency:a,phone_prefix:"+672",coordinates:[{latitude:"-82.8628",longitude:"135.0000"}],people_religion:a,people_nationality:a,people_education:a,visitors_entry:[{name:"Občanský průkaz",value:"Občanský průkaz není platným cestovním dokladem pro vstup do Antarktidy. Čeští občané musí mít platný cestovní pas.",date_update:b},{name:"Pas",value:"Pro vstup do Antarktidy potřebují čeští občané platný cestovní pas, který platí minimálně ještě 6 měsíců od data plánovaného odjezdu z Antarktidy.",date_update:b},{name:"Vízum",value:"Antarktida není běžně osídlena, takže většinou není potřeba žádného víza. Nicméně, pokud je plánováno vstupovat na území jiné země, která si vyžaduje vízum, může být potřeba získat vízum předem.",date_update:b},{name:"Očkování",value:"Neexistují žádná specifická očkování vyžadovaná pro vstup do Antarktidy. Nicméně, pokud plánujete návštěvu některých oblastí, jako jsou Falklandské ostrovy, může být doporučeno očkování proti žluté zimnici.",date_update:b},{name:"Cestovní pojištění",value:"Cestovní pojištění není povinné, ale je doporučeno kvůli vysokým nákladům na lékařskou péči a záchranné operace v případě potřeby.",date_update:b},{name:"Bezpečnostní situace",value:"V Antarktidě se obvykle neprovádí cestování za účelem turistiky a rekreačních aktivit. Pokud plánujete návštěvu Antarktidy, je důležité se řídit pokyny a doporučeními poskytovatele cesty a dodržovat veškerá pravidla a zákazy vztahující se k ochraně přírody a životního prostředí.",date_update:b}]}]}],fetch:{},mutations:void 0}}(null,"04-2023",7,0,"antarktida","Antarktida",14000000,"https:\u002F\u002Fimage.frytolnacestach.cz\u002Fstorage\u002Fworld\u002Fcontinents\u002Fantarktida.jpg","-49.2804","69.3486","https:\u002F\u002Fimage.frytolnacestach.cz\u002Fstorage\u002Fworld\u002Fstates\u002Fantarktida.jpg")));