__NUXT_JSONP__("/svet/stat/banglades", (function(a,b,c,d,e,f,g,h,i,j,k){return {data:[{place:[{id:c,mpz:"BD",tld:".bd",name:"Bangladéš",area:147570,population:164689383,slug:"banglades",id_continent:f,number_cities:a,information_chatgpt:"\u003Cp\u003EBangladéš je země nacházející se v jižní Asii. Hraničí na západě a na severu s Indií, na jihu s Barmou (Myanmar) a na východě je ohraničen zálivem Bengálským. Hlavním městem je Dháka a další významná města jsou například Chittagong, Khulna a Rajshahi.\n\u003C\u002Fp\u003E\u003Cp\u003E\nBangladéš má velmi rozvinutou zemědělskou ekonomiku, která zahrnuje pěstování rýže, čajovníku, juty a tabáku. Kromě toho je Bangladéš významným světovým vývozcem oděvů a textilních výrobků. Země má také velký potenciál pro rozvoj průmyslu a cestovního ruchu.\n\u003C\u002Fp\u003E\u003Cp\u003E\nBangladéš je domovem mnoha etnických skupin a náboženství, přičemž většina obyvatelstva jsou muslimové. Země má bohatou kulturu a tradice, včetně tradičního tance, hudby a literatury. Mezi nejznámější kulturní památky patří například mešita Baitul Mukarram, buddhistické kláštery v Paharpuru a historická pevnost Lalbagh Fort v Dháce.\n\u003C\u002Fp\u003E\u003Cp\u003E\nBangladéš má také řadu významných přírodních památek, jako jsou mangrovové lesy Sundarbans, které jsou domovem mnoha ohrožených druhů zvířat, nebo pohoří Chittagong Hill Tracts, které nabízí nádherné výhledy a turistické možnosti.\u003C\u002Fp\u003E",image_cover:g,image_hero:g,currency_code:"BDT",currency_name:"bangladéšská taka",money_prices:[{name:"Levný hotel",value:1100,date_update:b},{name:"Normální hotel",value:3500,date_update:b},{name:"Dražší hotel",value:9000,date_update:b},{name:"1km taxíkem",value:25,date_update:b},{name:"Běžná cena večeře",value:h,date_update:b},{name:"Káva",value:150,date_update:b},{name:"Kopeček zmrzliny",value:30,date_update:b},{name:"500ml vody",value:10,date_update:b},{name:"Pivo",value:h,date_update:b},{name:"Cigarety",value:160,date_update:b},{name:"Chleba",value:i,date_update:b},{name:"Coca Cola",value:i,date_update:b},{name:"Big Mac",value:300,date_update:b},{name:"Dálniční známka (nejkratší možná doba)",value:"nemá",date_update:""}],phone_numbers_emergency:[{name:"Policie",number:e},{name:"Hasiči",number:e},{name:"Zdravotnická záchranná služba",number:e},{name:"Národní ústav pro katastrofické situace a nouzové situace",number:"109"}],phone_prefix:"+880",coordinates:[{latitude:"23.6850",longitude:"90.3563"}],people_religion:[{name:"Islám",value:90.4},{name:"Hinduismus",value:8.5},{name:"Buddhismus",value:.6},{name:"Křesťanství",value:.4},{name:"Jiné náboženství",value:.1}],people_nationality:[{name:"Bengálci",value:98},{name:"Ostatní",value:2}],people_education:[{name:"Nevzdělaní",value:32.7},{name:"Základní vzdělání",value:46.4},{name:"Středoškolské vzdělání",value:15.6},{name:"Vysokoškolské vzdělání",value:5.3}],visitors_entry:[{name:"Občanský průkaz",value:"Občanský průkaz není platným dokladem pro vstup do země. Je nutné mít platný cestovní pas.",date_update:d},{name:"Pas",value:"Pro vstup do země je nutné mít platný cestovní pas, který platí minimálně 6 měsíců od data vstupu do země.",date_update:d},{name:"Vízum",value:"Pro vstup do země je pro občany České republiky vyžadováno vízum, které je nutné si zažádat předem v příslušné bangladéšské ambasádě nebo konzulátu.",date_update:d},{name:"Očkování",value:"Při vstupu do Bangladéše jsou vyžadována očkování proti některým chorobám, jako jsou například žlutá zimnice, hepatitida A a B, meningokoková meningitida atd. Je doporučeno se nechat očkovat v dostatečném předstihu před cestou.",date_update:d},{name:"Cestovní pojištění",value:"I když cestovní pojištění není povinné, je silně doporučeno si ho sjednat, protože náklady na lékařskou péči mohou být velmi vysoké. Je důležité si ověřit, zda pojištění pokrývá i náklady na lékařskou evakuaci v případě nutnosti.",date_update:d},{name:"Bezpečnostní situace",value:"Bezpečnostní situace v Bangladéši je v současné době nestabilní kvůli teroristickým útokům a násilnostem. Cestovatelé by měli být opatrní a vyvarovat se rizikových oblastí. Je důležité sledovat aktuální bezpečnostní doporučení a zůstat ostražitý.",date_update:d},{name:"Vstupní podmínky pro občany ČR",value:"Pro vstup do Bangladéše potřebují občané ČR vízum, které je nutné získat před cestou. Dále je nutné mít platný pas s minimální platností 6 měsíců a zpáteční letenku.",date_update:d}]}],placesCities:[{id:173,id_state:c,name:"Khulna",population:1944649,area:45.65,altitude:9,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"khulna",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:174,id_state:c,name:"Sylhet",population:693576,area:26.5,altitude:8,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"sylhet",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:175,id_state:c,name:"Mymensingh",population:507583,area:29.37,altitude:15,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"mymensingh",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:176,id_state:c,name:"Rajshahi",population:481422,area:96.69,altitude:21,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"rajshahi",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:177,id_state:c,name:"Narayanganj",population:301390,area:24,altitude:j,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"narayanganj",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:178,id_state:c,name:"Rangpur",population:285564,area:32.42,altitude:33,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"rangpur",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:179,id_state:c,name:"Barisal",population:202242,area:16.37,altitude:6,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"barisal",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:180,id_state:c,name:"Comilla",population:193958,area:11.47,altitude:11,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"comilla",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:171,id_state:c,name:"Dháka",population:20327352,area:306.38,altitude:j,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"dhaka",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:172,id_state:c,name:"Chittagong",population:5280832,area:168.07,altitude:5,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"chittagong",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a}],placeContinent:[{id:f,slug:"asie",name:"Asie",area:44580000,population:4630000000,population_density:104.03,number_states:50,information_chatgpt:"Asie je největším kontinentem na světě a nachází se na východ od Evropy, oddělena Uralským pohořím, a na sever od Afriky, oddělena Suezským průplavem a Rovníkovou Guineou. Asie zahrnuje mnoho různých zemí, včetně Ruska, Číny, Indie, Japonska, Jižní Koreje, Indonésie, Thajska, Saúdské Arábie, Izraele a mnoha dalších.\n\nAsie má rozmanitou krajinu, včetně hor, pouští, deštných pralesů, stepí a tundry. Mezi nejvýznamnější pohoří patří Himálaje, Karakoramské hory, Kavkaz, Altaj a Pamír.\n\nKromě toho je Asie domovem mnoha různých kultur, náboženství a jazyků. Například Indie je známá svým hinduistickým a buddhistickým dědictvím, zatímco Japonsko je známé svou samurajskou a gejšovskou tradicí. Na jihu Asie najdeme muslimské země jako Saúdská Arábie a Indonésie a na severovýchodě země jako Čína, Japonsko a Jižní Korea, které se rychle rozvíjejí v moderní technologické mocnosti.",image_cover:k,image_hero:k,coordinates:a}]}],fetch:{},mutations:void 0}}(null,"03-2023",19,"04-2023","999",3,"https:\u002F\u002Fimage.frytolnacestach.cz\u002Fstorage\u002Fworld\u002Fstates\u002Fbanglades.jpg",200,20,4,"https:\u002F\u002Fimage.frytolnacestach.cz\u002Fstorage\u002Fworld\u002Fcontinents\u002Fasie.jpg")));