__NUXT_JSONP__("/svet/mesto/praha", (function(a,b,c,d){return {data:[{place:[{id:528,id_state:b,name:"Praha",population:1324277,area:496.11,altitude:177,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"praha",information_chatgpt:"Praha je hlavní město České republiky a s více než 1,3 miliony obyvateli je největším městem v zemi. Nachází se uprostřed Evropy a je známá svým krásným historickým centrem, které je zapsáno na seznamu světového dědictví UNESCO. Mezi hlavní turistické atrakce patří Pražský hrad, Karlův most, Staroměstské náměstí a mnoho dalších památek, muzeí a galerií. Praha je také kulturním centrem a nabízí mnoho festivalů, koncertů a představení.",image_cover:c,image_hero:c,coordinates:a}],placeState:[{id:b,mpz:"CZ",tld:".cz",name:"Česko",area:78866,population:10708981,slug:"cesko",id_continent:4,number_cities:a,information_chatgpt:"\u003Cp\u003EČeská republika se nachází ve střední Evropě a má rozlohu přibližně 78 800 km². Česko je známé svou bohatou historií, krásnými památkami, zelenými lesy a malebnými městy.\u003C\u002Fp\u003E\n\u003Cp\u003EHlavním městem Česka je Praha, která je jednou z nejkrásnějších a nejnavštěvovanějších evropských metropolí. Mezi nejpopulárnější památky v Praze patří Karlův most, Staroměstské náměstí a Pražský hrad. Kromě Prahy má Česko mnoho dalších krásných měst, jako je například Český Krumlov, Plzeň, Kutná Hora a Olomouc. Tato města nabízejí mnoho kulturních a historických památek, ale také skvělou gastronomii a příležitosti pro sportovní aktivity.\u003C\u002Fp\u003E\n\u003Cp\u003EČesko je také známé svou přírodou a zelenými lesy. Mezi nejkrásnější přírodní scenérie patří Krkonoše, Šumava, Český ráj a Moravský kras. Tyto oblasti nabízejí mnoho možností pro turistiku, cyklistiku a další outdoorové aktivity.\u003C\u002Fp\u003E\n\u003Cp\u003EKromě kulturních a přírodních atrakcí Česko nabízí také vynikající gastronomii a pivní kulturu. České pivo je známé po celém světě a mezi nejpopulárnější patří Plzeňský Prazdroj, Budějovický Budvar a Staropramen. Česká kuchyně je rovněž vynikající, s mnoha tradičními pokrmy, jako jsou knedlíky, vepřové koleno a trdelník.\u003C\u002Fp\u003E\n\u003Cp\u003ECestování po Česku je snadné díky dobrému spojení vlaky a autobusy. Ceny jsou relativně levné a většina turistických atrakcí je snadno dostupná. Celkově je Česko skvělou destinací pro ty, kteří hledají kulturní, historické a přírodní zážitky, dobré jídlo a pití a přátelské lidi.\u003C\u002Fp\u003E",image_cover:a,image_hero:a,currency_code:"CZK",currency_name:"česká koruna",money_prices:a,phone_numbers_emergency:[{name:"Policie",number:"158"},{name:"Hasiči",number:"150"},{name:"Zdravotnická záchranná služba",number:"155"},{name:"Non-stop linka proti domácímu násilí",number:"116 006"},{name:"Záchranka horská služba",number:" +420 737 222 155"}],phone_prefix:"+420",coordinates:[{latitude:"49.8175",longitude:"15.4730"}],people_religion:[{name:"bez vyznání",value:"34.5%"},{name:"římskokatolické",value:"10.3%"},{name:"jiné křesťanské",value:"1.3%"},{name:"pravoslavné",value:"0.5%"},{name:"jiná náboženství",value:"0.9%"},{name:"neuvedeno",value:"52.5%"}],people_nationality:[{name:"Češi",value:"64.3%"},{name:"Moravané",value:"4.9%"},{name:"Slováci",value:"1.4%"},{name:"Polská menšina",value:"0.4%"},{name:"Německá menšina",value:"0.1%"},{name:"Vietnamci",value:"0.3%"},{name:"Rusové",value:d},{name:"Ukrajinci",value:d},{name:"Ostatní",value:"28.2%"}],people_education:[{name:"Základní vzdělání",value:"17.5%"},{name:"Střední vzdělání bez maturity",value:"36.7%"},{name:"Střední vzdělání s maturitou",value:"31.9%"},{name:"Vysokoškolské bakalářské studium",value:"6.1%"},{name:"Vysokoškolské magisterské a doktorské studium",value:"7.7%"}],visitors_entry:a}],placeContinent:a}],fetch:{},mutations:void 0}}(null,56,"https:\u002F\u002Fimage.frytolnacestach.cz\u002Fstorage\u002Fworld\u002Fcities\u002Fpraha.jpg","0.2%")));