__NUXT_JSONP__("/svet/mesto/zouerat", (function(a,b,c){return {data:[{place:[{id:636,id_state:b,name:"Zouérat",population:10244,area:40,altitude:242,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"zouerat",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a}],placeState:[{id:b,mpz:"EH",tld:".eh",name:"Západní Sahara",area:252120,population:597339,slug:"zapadni-sahara",id_continent:5,number_cities:a,information_chatgpt:"\u003Cp\u003EZápadní Sahara, také známá jako Západní Saharská demokratická republika, je neuznaný stát nacházející se na západním okraji Sahary. V současné době je Západní Sahara vleklým konfliktem mezi Marokem a polovojenským hnutím Fronta Polisario, které usiluje o nezávislost.\u003C\u002Fp\u003E\n\u003Cp\u003EToto území je velmi řídce osídlené a z velké části se skládá z pouště. Nicméně přestože toto území není příliš atraktivní pro turisty, nabízí návštěvníkům úžasné možnosti pro dobrodružné expedice, jako je například off-road jízda, písečné surfování nebo pouštní trekking.\u003C\u002Fp\u003E\n\u003Cp\u003EZápadní Sahara je také domovem několika kmenů, jako jsou například Tuaregové, kteří mají své vlastní zvyky a tradice, a turisté mohou navštívit tradiční trhy a osady, aby se dozvěděli více o jejich kultuře a způsobu života.\u003C\u002Fp\u003E\n\u003Cp\u003EPokud se chystáte na návštěvu Západní Sahary, je důležité si uvědomit, že území je v konfliktu a může být nebezpečné pro turisty. Většina cestovních kanceláří nedoporučuje návštěvu této oblasti kvůli bezpečnostním rizikům a neexistenci infrastruktury pro turisty.\u003C\u002Fp\u003E\n\u003Cp\u003EPřestože je Západní Sahara neuznaným státem, jeho území a kultura mají své jedinečné kouzlo a mohou být zajímavou destinací pro dobrodružné cestovatele, kteří jsou připraveni na extrémní podmínky a respektují místní zvyklosti a kulturu.\u003C\u002Fp\u003E\n\u003Cp\u003EJe důležité mít na paměti, že v této oblasti se vyskytují i nebezpečné teroristické skupiny, takže je důležité řídit se pokyny místních úřadů a cestovních kanceláří a sledovat aktuální bezpečnostní situaci před návštěvou.\u003C\u002Fp\u003E",image_cover:a,image_hero:a,currency_code:"MAD",currency_name:"saharský dinár",money_prices:a,phone_numbers_emergency:[{name:"Policejní záchranná služba",number:"150"},{name:"Nemocnice Západní Sahara",number:"+212 5288-93750"},{name:"Požární ochrana",number:"116"},{name:"Turistická policie",number:"+212 661-233212"}],phone_prefix:"+212",coordinates:[{latitude:"24.2155",longitude:"-12.8858"}],people_religion:[{name:"Islám",value:"99%"},{name:"Křesťanství",value:"1%"},{name:"Judaismus",value:c},{name:"Další náboženství",value:c}],people_nationality:[{name:"Berbeři",value:"40%"},{name:"Maurové",value:"30%"},{name:"Fulbové",value:"10%"},{name:"Toubouvé",value:"7%"},{name:"Senufové",value:"5%"},{name:"Další",value:"8%"}],people_education:[{name:"Nemají vzdělání",value:"63%"},{name:"Základní vzdělání",value:"24%"},{name:"Středoškolské vzdělání",value:"9%"},{name:"Vysokoškolské vzdělání",value:"4%"}],visitors_entry:a}],placeContinent:a}],fetch:{},mutations:void 0}}(null,66,"0%")));