__NUXT_JSONP__("/svet/stat/saudska-arabie", (function(a,b,c,d,e){return {data:[{place:[{id:b,mpz:"SA",tld:".sa",name:"Saúdská Arábie",area:2149690,population:35041038,slug:"saudska-arabie",id_continent:c,number_cities:a,information_chatgpt:"\u003Cp\u003ESaúdská Arábie je největší arabský stát nacházející se na Arabském poloostrově, s rozlohou 2 149 690 km² a s populací přibližně 35 milionů obyvatel. Tento stát je jedním z nejvýznamnějších producentů ropy na světě a má také důležitou strategickou polohu.\u003C\u002Fp\u003E\n\u003Cp\u003EV Saúdské Arábii se nachází mnoho turistických atrakcí, z nichž nejznámější je asi Mekka, posvátné město muslimů. Každý rok sem míří miliony poutníků, aby se zúčastnili hadždže, jedné z nejvýznamnějších náboženských událostí pro muslimy.\u003C\u002Fp\u003E\n\u003Cp\u003EKromě Mekky má Saúdská Arábie mnoho dalších zajímavých míst pro turisty, například historické památky, jako je Al-Diriyah, starobylé město z 18. století, nebo Madain Saleh, národní památník s nádhernými skalními útvary. Mezi další turistické atrakce patří také krásné pláže, jako například Jeddah Corniche, a rozmanité obchodní centra a trhy, jako například Souq Al Alawi v Džiddě.\u003C\u002Fp\u003E\n\u003Cp\u003EJe však třeba si uvědomit, že Saúdská Arábie je konzervativní země, která má přísná pravidla pro turisty, a tak se turisté musí řídit místními zákony a zvyklostmi. Například ženy musí nosit dlouhé a volné šaty a hlavu si zakrývat šálou, zatímco alkohol a jiné návykové látky jsou zakázány.\u003C\u002Fp\u003E\n\u003Cp\u003EZkrátka, Saúdská Arábie je země s bohatou historií a kulturním dědictvím, ale také s přísnými zákony a pravidly. Pokud se rozhodnete navštívit tuto zemi, je důležité být respektující a dodržovat místní zvyklosti a pravidla.\u003C\u002Fp\u003E",image_cover:a,image_hero:a,currency_code:"SAR",currency_name:"Saúdskoarabský rial",money_prices:a,phone_numbers_emergency:a,phone_prefix:"+966",coordinates:[{latitude:"23.8859",longitude:"45.0792"}],people_religion:a,people_nationality:a,people_education:a,visitors_entry:a}],placesCities:[{id:1891,id_state:b,name:"Rijád",population:8154059,area:1560,altitude:612,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"rijad",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:1893,id_state:b,name:"Mekka",population:1576804,area:850,altitude:277,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"mekka",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:1894,id_state:b,name:"Medína",population:1304730,area:589,altitude:624,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"medina",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:1895,id_state:b,name:"Ad-Dammám",population:1062391,area:d,altitude:33,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"ad-dammam",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:1896,id_state:b,name:"Ta'if",population:941358,area:350,altitude:1700,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"ta-if",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:1897,id_state:b,name:"Burajdah",population:695047,area:d,altitude:619,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"burajdah",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:1898,id_state:b,name:"Tabúk",population:571770,area:2354,altitude:834,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"tabuk",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:1899,id_state:b,name:"Chubar",population:558274,area:116.67,altitude:10,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"chubar",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:1900,id_state:b,name:"Abhá",population:510346,area:1300,altitude:2270,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"abha",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:1892,id_state:b,name:"Džidda",population:4027976,area:191.77,altitude:15,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"dzidda",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a}],placeContinent:[{id:c,slug:"asie",name:"Asie",area:44580000,population:4630000000,population_density:104.03,number_states:50,information_chatgpt:"Asie je největším kontinentem na světě a nachází se na východ od Evropy, oddělena Uralským pohořím, a na sever od Afriky, oddělena Suezským průplavem a Rovníkovou Guineou. Asie zahrnuje mnoho různých zemí, včetně Ruska, Číny, Indie, Japonska, Jižní Koreje, Indonésie, Thajska, Saúdské Arábie, Izraele a mnoha dalších.\n\nAsie má rozmanitou krajinu, včetně hor, pouští, deštných pralesů, stepí a tundry. Mezi nejvýznamnější pohoří patří Himálaje, Karakoramské hory, Kavkaz, Altaj a Pamír.\n\nKromě toho je Asie domovem mnoha různých kultur, náboženství a jazyků. Například Indie je známá svým hinduistickým a buddhistickým dědictvím, zatímco Japonsko je známé svou samurajskou a gejšovskou tradicí. Na jihu Asie najdeme muslimské země jako Saúdská Arábie a Indonésie a na severovýchodě země jako Čína, Japonsko a Jižní Korea, které se rychle rozvíjejí v moderní technologické mocnosti.",image_cover:e,image_hero:e,coordinates:a}]}],fetch:{},mutations:void 0}}(null,193,3,800,"https:\u002F\u002Fimage.frytolnacestach.cz\u002Fstorage\u002Fworld\u002Fcontinents\u002Fasie.jpg")));