__NUXT_JSONP__("/svet/stat/thajsko", (function(a,b,c,d,e,f,g,h,i){return {data:[{place:[{id:b,mpz:"TH",tld:".th",name:"Thajsko",area:513120,population:69799978,slug:"thajsko",id_continent:d,number_cities:a,information_chatgpt:"\u003Cp\u003EThajsko je krásná země v jihovýchodní Asii, která je známá svou bohatou kulturou, tradičním jídlem, překrásnými plážemi a turistickými atrakcemi. Nachází se zde mnoho historických památek, národních parků a chráněných oblastí s exotickými zvířaty, jako jsou sloni, opice, tygři a mnoho dalších.\u003C\u002Fp\u003E\n\u003Cp\u003EThajsko je také známé jako \"země úsměvů\" díky své přátelské a pohostinné atmosféře, kterou najdete v celé zemi. Turisté se sem vrací nejen kvůli nádherným plážím, ale také kvůli levnému a chutnému jídlu. Thajská kuchyně je známá po celém světě díky své různorodosti a chuťovým kombinacím. Turisté si zde mohou vychutnat tradiční jídla jako jsou Pad Thai, Tom Yum nebo Green Curry.\u003C\u002Fp\u003E\n\u003Cp\u003EBangkok, hlavní město Thajska, je pulzujícím městem, kde najdete mnoho zajímavých památek, jako jsou chrámy, paláce a trhy. Mezi nejznámější památky patří Velký palác, chrám Wat Phra Kaew a chrám Wat Pho s obřím zlatým Buddhou. Bangkok je také známý pro své noční trhy, kde můžete nakupovat oblečení, kožené výrobky, suvenýry a další zboží za velmi nízké ceny.\u003C\u002Fp\u003E\n\u003Cp\u003EMezi nejnavštěvovanější turistické destinace v Thajsku patří také ostrov Phuket, kde si můžete užít krásné pláže, vodní sporty, noční život a mnoho dalšího. Dalšími oblíbenými ostrovy jsou například Koh Samui, Koh Phi Phi a Koh Tao.\u003C\u002Fp\u003E\n\u003Cp\u003EThajsko má tropické klima, což znamená, že je zde vysoká vlhkost a vysoké teploty po celý rok. Nejlepší období pro návštěvu Thajska jsou období sucha od listopadu do února, kdy jsou teploty příjemnější. Většina turistů si ale vybírá návštěvu během letních prázdnin, kdy jsou pláže plné slunce hledajících turistů a noční život je v plném proudu.\u003C\u002Fp\u003E",image_cover:a,image_hero:a,currency_code:"THB",currency_name:"Thajský baht",money_prices:a,phone_numbers_emergency:a,phone_prefix:"+66",coordinates:[[{latitude:e,longitude:f}],{latitude:e,longitude:f}],people_religion:a,people_nationality:a,people_education:a,visitors_entry:a}],placesCities:[{id:2153,id_state:b,name:"Bangkok",population:10054000,area:1579,altitude:c,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"bangkok",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:2154,id_state:b,name:"Nonthaburi",population:288000,area:38.9,altitude:c,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"nonthaburi",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:2155,id_state:b,name:"Pak Kret",population:184900,area:44.8,altitude:c,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"pak-kret",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:2156,id_state:b,name:"Hat Yai",population:154400,area:45.75,altitude:105,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"hat-yai",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:2157,id_state:b,name:"Nakhon Ratchasima",population:148300,area:61.5,altitude:267,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"nakhon-ratchasima",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:2158,id_state:b,name:"Chiang Mai",population:131700,area:40.22,altitude:310,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"chiang-mai",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:2159,id_state:b,name:"Udon Thani",population:120400,area:47,altitude:182,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"udon-thani",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:2160,id_state:b,name:"Surat Thani",population:117000,area:g,altitude:h,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"surat-thani",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:2161,id_state:b,name:"Khon Kaen",population:114800,area:46.45,altitude:173,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"khon-kaen",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:2162,id_state:b,name:"Nakhon Si Thammarat",population:105900,area:g,altitude:h,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"nakhon-si-thammarat",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a}],placeContinent:[{id:d,slug:"asie",name:"Asie",area:44580000,population:4630000000,population_density:104.03,number_states:50,information_chatgpt:"Asie je největším kontinentem na světě a nachází se na východ od Evropy, oddělena Uralským pohořím, a na sever od Afriky, oddělena Suezským průplavem a Rovníkovou Guineou. Asie zahrnuje mnoho různých zemí, včetně Ruska, Číny, Indie, Japonska, Jižní Koreje, Indonésie, Thajska, Saúdské Arábie, Izraele a mnoha dalších.\n\nAsie má rozmanitou krajinu, včetně hor, pouští, deštných pralesů, stepí a tundry. Mezi nejvýznamnější pohoří patří Himálaje, Karakoramské hory, Kavkaz, Altaj a Pamír.\n\nKromě toho je Asie domovem mnoha různých kultur, náboženství a jazyků. Například Indie je známá svým hinduistickým a buddhistickým dědictvím, zatímco Japonsko je známé svou samurajskou a gejšovskou tradicí. Na jihu Asie najdeme muslimské země jako Saúdská Arábie a Indonésie a na severovýchodě země jako Čína, Japonsko a Jižní Korea, které se rychle rozvíjejí v moderní technologické mocnosti.",image_cover:i,image_hero:i,coordinates:a}]}],fetch:{},mutations:void 0}}(null,218,2,3,"15.8700","100.9925",68.97,4,"https:\u002F\u002Fimage.frytolnacestach.cz\u002Fstorage\u002Fworld\u002Fcontinents\u002Fasie.jpg")));