__NUXT_JSONP__("/svet/stat/turecko", (function(a,b,c,d,e,f){return {data:[{place:[{id:b,mpz:"TR",tld:".tr",name:"Turecko",area:783356,population:82319724,slug:"turecko",id_continent:c,number_cities:a,information_chatgpt:"\u003Cp\u003ETurecko je země, která se rozkládá na rozhraní Evropy a Asie. Svého času to byla významná kulturní a politická síla, která ovládala velkou část Balkánu, Blízkého východu a severní Afriky. Dnes je Turecko jednou z nejnavštěvovanějších turistických destinací na světě, která nabízí mnoho historických a přírodních památek, kulturních zážitků a gastronomických specialit.\u003C\u002Fp\u003E\n\u003Cp\u003EHlavním městem Turecka je Ankara, ale největším a nejznámějším městem je Istanbul, který se rozkládá na dvou kontinentech, Evropě a Asii. Istanbul je známý svou bohatou historií, architekturou, obchodními trhy a nočním životem. Největší turistickou atrakcí je Hagia Sofia, bývalá katedrála, mešita a nyní muzeum, která je považována za jedno z nejvýznamnějších děl byzantské architektury. Dalšími oblíbenými památkami jsou například Modrá mešita, Topkapi palác a Galata Tower.\u003C\u002Fp\u003E\n\u003Cp\u003EKromě Istanbulu má Turecko mnoho dalších zajímavých míst k návštěvě. Na jihozápadě země se nachází přírodní památka Pamukkale, která se skládá z bílých vápencových teras a je známá pro své léčivé termální prameny. Další populární turistickou destinací jsou pláže Antalye a Alanyi na pobřeží Středozemního moře, které nabízejí krásnou křišťálově čistou vodu a slunečné počasí po většinu roku.\u003C\u002Fp\u003E\n\u003Cp\u003ETurecko je také známé pro svou bohatou kuchyni, která je ovlivněna mnoha kulturami, které zde žily v minulosti. Mezi nejpopulárnější jídla patří kebab, döner, baklava, borek a mnoho dalších. Turecko je také známé svou kávou a čajem, které jsou tradičně servírovány po jídle nebo v průběhu společenských událostí.\u003C\u002Fp\u003E",image_cover:a,image_hero:a,currency_code:"TRY",currency_name:"Turecká lira",money_prices:a,phone_numbers_emergency:a,phone_prefix:"+90",coordinates:[[{latitude:d,longitude:e}],{latitude:d,longitude:e}],people_religion:a,people_nationality:a,people_education:a,visitors_entry:a}],placesCities:[{id:2223,id_state:b,name:"Istanbul",population:15029231,area:5342,altitude:31,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"istanbul",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:2224,id_state:b,name:"Ankara",population:5445026,area:2491,altitude:938,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"ankara",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:2225,id_state:b,name:"Izmir",population:4279670,area:7454,altitude:125,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"izmir",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:2226,id_state:b,name:"Bursa",population:1969948,area:10467,altitude:109,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"bursa",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:2227,id_state:b,name:"Adana",population:1980414,area:1478,altitude:23,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"adana",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:2228,id_state:b,name:"Gaziantep",population:1983459,area:7449,altitude:844,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"gaziantep",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:2229,id_state:b,name:"Konya",population:2322624,area:38504,altitude:1018,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"konya",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:2230,id_state:b,name:"Antalya",population:2433848,area:19700,altitude:39,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"antalya",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:2231,id_state:b,name:"Şanlıurfa",population:2146364,area:18928,altitude:464,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"sanliurfa",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a},{id:2232,id_state:b,name:"Mersin",population:1640348,area:15.35,altitude:0,id_continent:a,id_administrative_unit:a,id_districts:a,slug:"mersin",information_chatgpt:a,image_cover:a,image_hero:a,coordinates:a}],placeContinent:[{id:c,slug:"asie",name:"Asie",area:44580000,population:4630000000,population_density:104.03,number_states:50,information_chatgpt:"Asie je největším kontinentem na světě a nachází se na východ od Evropy, oddělena Uralským pohořím, a na sever od Afriky, oddělena Suezským průplavem a Rovníkovou Guineou. Asie zahrnuje mnoho různých zemí, včetně Ruska, Číny, Indie, Japonska, Jižní Koreje, Indonésie, Thajska, Saúdské Arábie, Izraele a mnoha dalších.\n\nAsie má rozmanitou krajinu, včetně hor, pouští, deštných pralesů, stepí a tundry. Mezi nejvýznamnější pohoří patří Himálaje, Karakoramské hory, Kavkaz, Altaj a Pamír.\n\nKromě toho je Asie domovem mnoha různých kultur, náboženství a jazyků. Například Indie je známá svým hinduistickým a buddhistickým dědictvím, zatímco Japonsko je známé svou samurajskou a gejšovskou tradicí. Na jihu Asie najdeme muslimské země jako Saúdská Arábie a Indonésie a na severovýchodě země jako Čína, Japonsko a Jižní Korea, které se rychle rozvíjejí v moderní technologické mocnosti.",image_cover:f,image_hero:f,coordinates:a}]}],fetch:{},mutations:void 0}}(null,225,3,"38.9637","35.2433","https:\u002F\u002Fimage.frytolnacestach.cz\u002Fstorage\u002Fworld\u002Fcontinents\u002Fasie.jpg")));