__NUXT_JSONP__("/svet/mesto/ciang-mai", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w,x,y,z,A,B,C,D,E,F,G){return {data:[{place:[{id:r,id_state:b,name:s,population:t,area:u,altitude:v,slug:w,information_chatgpt:x,coordinates:[{latitude:y,longitude:z}],biggest:f,type_place:g,id_image_cover:a,id_image_hero:a,affiliate:[{name:c,value:d}],alerts:a,zoom:[{google:h,booking:i}]}],placeState:[{id:b,tld:".th",name:"Thajsko",area:513120,population:69799978,slug:A,id_continent:l,information_chatgpt:"\u003Cp\u003EThajsko je krásná země v jihovýchodní Asii, která je známá svou bohatou kulturou, tradičním jídlem, překrásnými plážemi a turistickými atrakcemi. Nachází se zde mnoho historických památek, národních parků a chráněných oblastí s exotickými zvířaty, jako jsou sloni, opice, tygři a mnoho dalších.\u003C\u002Fp\u003E\n\u003Cp\u003EThajsko je také známé jako \"země úsměvů\" díky své přátelské a pohostinné atmosféře, kterou najdete v celé zemi. Turisté se sem vrací nejen kvůli nádherným plážím, ale také kvůli levnému a chutnému jídlu. Thajská kuchyně je známá po celém světě díky své různorodosti a chuťovým kombinacím. Turisté si zde mohou vychutnat tradiční jídla jako jsou Pad Thai, Tom Yum nebo Green Curry.\u003C\u002Fp\u003E\n\u003Cp\u003EBangkok, hlavní město Thajska, je pulzujícím městem, kde najdete mnoho zajímavých památek, jako jsou chrámy, paláce a trhy. Mezi nejznámější památky patří Velký palác, chrám Wat Phra Kaew a chrám Wat Pho s obřím zlatým Buddhou. Bangkok je také známý pro své noční trhy, kde můžete nakupovat oblečení, kožené výrobky, suvenýry a další zboží za velmi nízké ceny.\u003C\u002Fp\u003E\n\u003Cp\u003EMezi nejnavštěvovanější turistické destinace v Thajsku patří také ostrov Phuket, kde si můžete užít krásné pláže, vodní sporty, noční život a mnoho dalšího. Dalšími oblíbenými ostrovy jsou například Koh Samui, Koh Phi Phi a Koh Tao.\u003C\u002Fp\u003E\n\u003Cp\u003EThajsko má tropické klima, což znamená, že je zde vysoká vlhkost a vysoké teploty po celý rok. Nejlepší období pro návštěvu Thajska jsou období sucha od listopadu do února, kdy jsou teploty příjemnější. Většina turistů si ale vybírá návštěvu během letních prázdnin, kdy jsou pláže plné slunce hledajících turistů a noční život je v plném proudu.\u003C\u002Fp\u003E",currency_code:"THB",currency_name:"Thajský baht",money_prices:[{name:"Taxi (cena za km)",value:15,date_update:j},{name:"Hlavní jídlo",value:90,date_update:j},{name:"Chleba",value:a,date_update:B},{name:"Mléko",value:40,date_update:j},{name:"Voda (500ml)",value:13.5,date_update:j},{name:"Espresso",value:65,date_update:j},{name:"Zmrzlina (jeden kopeček)",value:30,date_update:j},{name:"Pivo",value:100,date_update:j},{name:"Krabička cigaret",value:C,date_update:j},{name:"Bic Mac",value:a,date_update:B},{name:"Coca Cola (500ml)",value:22.5,date_update:j}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:"112",date_update:e},{name:"Policie",value:"191",date_update:e},{name:"Hasiči",value:"199",date_update:e},{name:"Zdravotnická záchranná služba",value:"1669",date_update:e},{name:"Turistická policie",value:"1155",date_update:e}],phone_prefix:"+66",coordinates:[{latitude:"15.8700",longitude:"100.9925"}],people_religion:[{name:"Buddhismus",value:"94.5%",date_update:m},{name:"Muslimové",value:"4.3%",date_update:m},{name:"Křesťanství",value:"1.1%",date_update:m},{name:"Ostatní",value:D,date_update:m},{name:"Bez vyznání",value:D,date_update:m}],people_nationality:[{name:"Thajci",value:"75%",date_update:k},{name:"Číňané",value:"14%",date_update:k},{name:"Malajci",value:"3%",date_update:k},{name:"Khaové",value:n,date_update:k},{name:"Burmánci",value:n,date_update:k},{name:"Indové",value:n,date_update:k},{name:"Laové",value:n,date_update:k},{name:"Jinové",value:n,date_update:k},{name:"Další národnosti",value:"2%",date_update:k}],visitors_entry:[{name:"Občanský průkaz",value:"Pro vstup do Thajska není možné použít občanský průkaz. Je nutné mít platný cestovní pas.",date_update:e},{name:"Cestovní pas",value:"Pro vstup do Thajska je nutné mít platný cestovní pas.",date_update:e},{name:"Vízum",value:"Občané České republiky (a většina zemí EU) nepotřebují pro vstup do Thajska vízum, pokud pobývají v zemi do 30 dní. Pokud plánujete pobyt déle, je nutné si předem zajistit vízum.",date_update:e},{name:"Očkování",value:"Pro vstup do Thajska není vyžadováno žádné povinné očkování. Nicméně se doporučuje očkování proti hepatitidě A a B, břišnímu tyfu a tetanu. Při delším pobytu v zemi se doporučuje také očkování proti žluté zimnici a encefalitidě.",date_update:e},{name:"Cestovní pojištění",value:"Pro vstup do Thajska není povinné mít cestovní pojištění, nicméně se jedná o doporučenou záležitost. Některé zdravotní problémy mohou být velmi nákladné a pojištění vám může pomoci ušetřit značné peníze. Pokud cestujete s cennými věcmi, může být také užitečné mít pojištění na ztrátu nebo krádež.",date_update:e},{name:"Bezpečnostní situace",value:"Thajsko je v zásadě bezpečnou zemí, ale jako všude jinde na světě existují určitá rizika. Turisté jsou občas terčem zločinců, zejména v turistických oblastech. Dále mohou být problémy v oblastech s politickými neshodami nebo etnickými konflikty. Je dobré být obezřetný a sledovat aktuální zpravodajství.",date_update:e},{name:"Další informace",value:"Při návštěvě Thajska je důležité respektovat místní zvyklosti a tradice. Například není vhodné se oblékat příliš odhaleně, zejména v chrámech a na veřejnosti. Dále se nedoporučuje pít vodu z vodovodu, ale používat balenou vodu. V Thajsku je rozšířená korupce, takže je dobré být obezřetný při jednání s úřady. Doprava v zemi může být velmi chaotická a nebezpečná, proto je nutné být opatrný při používání silnic a dopravních prostředků.",date_update:e}],type_place:E,id_image_cover:p,id_image_hero:p,id_city_main:F,affiliate:[{name:c,value:d}],mpz:"TH",alerts:a,zoom:[{google:q,booking:5}],ids_neighboring_countries:a}],placeContinent:[{id:l,slug:"asie",name:"Asie",area:44580000,population:4630000000,population_density:104.03,number_states:50,information_chatgpt:"\u003Cp\u003EAsie se rozkládá na ploše téměř 44,6 milionu kilometrů čtverečních a žije zde více než 4,5 miliardy lidí, což představuje téměř 60 % celkového počtu obyvatel planety. Kontinent zahrnuje celkem 48 zemí, mezi největší patří Rusko, Čína, Indie, Japonsko a Indonésie.\u003C\u002Fp\u003E\n\u003Cp\u003EAsie je domovem mnoha významných světových kultur, jako jsou hinduismus, buddhismus, islám a taoismus. V každé zemi se můžeš setkat s odlišnými tradicemi, zvyky a jazyky. Mezi nejnavštěvovanější turistické destinace patří například Japonsko s jeho futuristickými městy, Thajsko s bohatou kulturou a krásnými plážemi, nebo Indonésie s neuvěřitelnou přírodou a množstvím ostrovů.\u003C\u002Fp\u003E\n\u003Cp\u003EAsie také nabízí mnoho kulturních a historických památek, jako je Velká čínská zeď, Taj Mahal v Indii, buddhistické chrámy v Thajsku a mnoho dalšího. Zajímavou zkušeností může být i návštěva tradičních trhů, kde se dají ochutnat místní speciality a nakoupit různé exotické produkty.\u003C\u002Fp\u003E\n\u003Cp\u003EAsie má však také své výzvy a problémy, jako jsou například růst městských oblastí, nedostatek vody a znečištění ovzduší. Mnoho zemí se snaží najít řešení těchto problémů a přizpůsobit se moderním trendům, aby mohly udržet krok s ostatními zeměmi světa.\u003C\u002Fp\u003E\n\u003Cp\u003EPokud tě láká cestování po Asii, měl by sis předem zajistit víza a informovat se o místních zvycích a tradicích. Rozhodně se ti však naskytne možnost prozkoumat jedinečnou kulturu, přírodu a historii tohoto fascinujícího kontinentu.\u003C\u002Fp\u003E\u003Cp\u003EAsie má také neuvěřitelně bohatou gastronomii, která se v jednotlivých zemích a regionech liší. Mezi nejznámější asijské pokrmy patří sushi, různé druhy nudlí, curry, dim sum, pho a další. Kromě toho se v Asii pěstuje mnoho exotických plodin, jako jsou duriany, mangostany, rambutany a další ovoce, které můžeš ochutnat pouze zde.\n\u003C\u002Fp\u003E\u003Cp\u003E\nAsie je také známá svými svátky a tradicemi, jako je například čínský Nový rok, Diwali v Indii, nebo Songkran v Thajsku. Tyto svátky jsou většinou spojeny s různými rituály a zvyky, které se v průběhu staletí vyvinuly a jsou dodnes respektovány.\n\u003C\u002Fp\u003E\u003Cp\u003E\nKromě toho má Asie mnoho přírodních krás, jako jsou Himálaje, pouště Gobi a Thar, nebo tropické pralesy v Indonésii a Malajsii. V Asii se také nachází mnoho národních parků a chráněných oblastí, kde žijí ohrožené druhy zvířat, jako jsou tygři, pandy, sloni a další.\n\u003C\u002Fp\u003E\u003Cp\u003E\nZkrátka, Asie je kontinent plný kontrastů a neuvěřitelných zážitků, které stojí za to objevovat. Budeš-li mít příležitost navštívit některou z asijských zemí, neváhej a vydaj se na dobrodružnou cestu plnou nových objevů a nezapomenutelných zážitků.\u003C\u002Fp\u003E",coordinates:[{latitude:47.751076,longitude:92.475286}],type_place:"continent",id_image_cover:l,id_image_hero:l,zoom:[{google:o,booking:l}]}],placesCities:[{id:F,id_state:b,name:"Bangkok",population:10054000,area:1579,altitude:o,slug:"bangkok",information_chatgpt:"\u003Cp\u003EBangkok, hlavní město Thajska, je fascinující kombinací tradiční kultury a moderního rozvoje. S více než 8 miliony obyvatel je jedním z největších a nejrušnějších měst na světě. Město je známé svými impozantními chrámy, živými trhy, bohatou historií a pulzujícím nočním životem.\u003C\u002Fp\u003E\n\u003Cp\u003EJedním z nejslavnějších symbolů Bangkoku je chrám Wat Arun, jehož špičaté věže se tyčí nad řekou Chaophraya. Dalším významným chrámem je Wat Phra Kaew, který uchovává nejposvátnější Buddhovu sochu v zemi. Návštěvníci mohou také obdivovat krásu Grand Palace, kdysi sídlo thajských králů.\u003C\u002Fp\u003E\n\u003Cp\u003EBangkok je také rájem pro milovníky jídla. Ulice jsou plné stánků a restaurací nabízejících tradiční thajskou kuchyni. Návštěvníci by neměli vynechat degustaci populárního pokrmu Pad Thai, pikantního Tom Yum Goong či lahodného Mango sticky rice.\u003C\u002Fp\u003E\n\u003Cp\u003EMěsto je také známé svými živými trhy. Na Chatuchak Weekend Market si lze prohlédnout nekonečné řady stánků nabízejících oblečení, doplňky, keramiku, umělecké předměty a mnoho dalšího. Tímto tržištěm je také možné se procházet a ochutnávat místní speciality.\u003C\u002Fp\u003E\n\u003Cp\u003EPokud jde o noční život, Bangkok má co nabídnout. Khlavním atrakcím patří ulice Khao San, která je oblíbenou destinací pro zahraniční cestovatele. Zdejší bary a kluby nabízejí hudbu, tance a zábavu po celou noc.\u003C\u002Fp\u003E\n\u003Cp\u003ENavštívit lze také ikonickou Lumpini Park, která slouží jako útočiště v srdci města. Zde si můžete odpočinout od ruchu ulic a procházet se v příjemném prostředí plném zeleně.\u003C\u002Fp\u003E\n\u003Cp\u003EBangkok nabízí také možnost pro nákupy. Obchodní domy jako Siam Paragon, CentralWorld nebo MBK Center nabízejí širokou škálu produktů od módy až po elektroniku.\u003C\u002Fp\u003E\n\u003Cp\u003EPro cestovatele, kteří hledají autentickou thajskou kulturu, historii a rozmanitost, je Bangkok nezbytnou zastávkou při návštěvě Thajska.\u003C\u002Fp\u003E",coordinates:[{latitude:13.7563,longitude:100.5018}],biggest:f,type_place:g,id_image_cover:a,id_image_hero:a,affiliate:[{name:c,value:d}],alerts:a,zoom:[{google:h,booking:i}]},{id:2156,id_state:b,name:"Hat Yai",population:154400,area:45.75,altitude:C,slug:"hat-yai",information_chatgpt:"\u003Cp\u003EHat Yai je město situované v provincii Songkhla, na jihu Thajska. Je to druhé největší město v zemi a významné obchodní centrum. Město je známé svou pulzující atmosférou, bohatou kulturou a různorodými atrakcemi. Je oblíbeným cílem jak pro místní obyvatele, tak i pro turisty.\u003C\u002Fp\u003E\n\u003Cp\u003EJedním z hlavních turistických míst v Hat Yai je Hat Yai Park, což je rozsáhlý park, kde si můžete užít krásné procházky a relaxaci v přírodě. V parku se nachází i chrám Kuan Yin, který je vyhledávaným cílem návštěvníků díky své architektuře a klidné atmosféře. Další zajímavostí je Hat Yai Municipal Park, který nabízí nádherné výhledy na město a okolní krajinu.\u003C\u002Fp\u003E\n\u003Cp\u003EJedním z nejpopulárnějších míst v Hat Yai je trh Kim Yong, který je známý svou pestrostí a bohatou nabídkou thajských specialit, čerstvého ovoce, zeleniny, koření a mnoha dalších produktů. Tento trh je také skvělým místem pro nakupování suvenýrů a tradičních thajských produktů.\u003C\u002Fp\u003E\n\u003Cp\u003EV Hat Yai najdete také mnoho chrámů a svatyní, které odrážejí bohatou buddhistickou kulturu Thajska. Wat Hat Yai Nai je jedním z největších a nejznámějších chrámů v městě. Tento majestátní chrám je domovem obrovské zlaté sochy Buddhovy a poskytuje úžasné výhledy na město.\u003C\u002Fp\u003E\n\u003Cp\u003EMilovníci přírody mohou navštívit Khao Nam Khang, což je přírodní rezervace s nádhernými vodopády a malebnými přírodními scenériemi. Pro ty, kteří hledají dobrodružství, je tu také možnost výletu na zalesněnou horu Khao Tang Kuan, která je oblíbená mezi horolezci.\u003C\u002Fp\u003E\n\u003Cp\u003EHat Yai nabízí také bohaté noční život s množstvím barů, klubů a restaurací. Nachází se zde také velké obchodní centrum CentralFestival Hat Yai, kde můžete nakupovat luxusní značky, sledovat filmy v kině nebo si dopřát dobré jídlo v restauracích.\u003C\u002Fp\u003E\n\u003Cp\u003ESvou polohou poblíž hranic s Malajsií je Hat Yai také významným dopravním uzlem, kudy procházejí mezinárodní vlaky a autobusy. Město je snadno dostupné jak z vnitrostátních letišť, tak i z blízkých měst.\u003C\u002Fp\u003E\n\u003Cp\u003EHat Yai je fascinujícím městem plným kultury, krásy a zábavy. Bez ohledu na to, jestli se zajímáte o památky, přírodu nebo místní kuchyni, toto město nabízí nespočet možností pro každého návštěvníka.\u003C\u002Fp\u003E",coordinates:[{latitude:7.0086,longitude:100.4767}],biggest:f,type_place:g,id_image_cover:a,id_image_hero:a,affiliate:[{name:c,value:d}],alerts:a,zoom:[{google:h,booking:i}]},{id:2161,id_state:b,name:"Khon Kaen",population:114800,area:46.45,altitude:173,slug:"khon-kaen",information_chatgpt:"\u003Cp\u003EKhon Kaen je město ležící ve stejnojmenné provincii v severovýchodním Thajsku. Je to významné obchodní a kulturní centrum regionu Isan. S více než 100 000 studenty je Khon Kaen také významným univerzitním městem.\u003C\u002Fp\u003E\n\u003Cp\u003EJednou z hlavních atrakcí Khon Kaenu je Wat Nong Wang, velký a nádherný buddhistický chrám. Tento chrám je významný svou architekturou a krásnými zahradami. Další zajímavou památkou je Phra Mahathat Kaen Nakhon, stožár vysoký 80 metrů, který je symbolem města.\u003C\u002Fp\u003E\n\u003Cp\u003EMístní trhy jsou také velmi oblíbené. Jedním z nich je Ton Tann Market, kde můžete ochutnat tradiční thajská jídla a zakoupit místní suvenýry. Další zajímavý trh je Klang Plaza, kde můžete nakupovat oblečení, elektroniku a další zboží.\u003C\u002Fp\u003E\n\u003Cp\u003EKhon Kaen nabízí i řadu přírodních krás. V okolí města se nachází Nong Prajak Park, malebný park se svěží zelení, jezerem a výhledem na Wat Nong Wang. Můžete si zde užít piknik nebo relaxovat na lavičkách. Dalším atraktivním místem je Phu Wiang National Park, který je domovem fosilních nalezišť dinosaurů.\u003C\u002Fp\u003E\n\u003Cp\u003EMěsto Khon Kaen je také známé svou gastronomií. Můžete ochutnat tradiční pokrmy jako som tam (zelený papájový salát), lap moo (uzené vepřové maso) a khao niao mamuang (mangová rýže). Restaurace a pouliční stánky nabízí bohatou škálu thajských specialit.\u003C\u002Fp\u003E\n\u003Cp\u003EKhon Kaen je živým a pulzujícím městem s bohatou kulturou a přírodními krásami. Nabízí mnoho zajímavých míst k návštěvě a je ideálním výchozím bodem pro objevování severovýchodního Thajska.\u003C\u002Fp\u003E",coordinates:[{latitude:16.4398,longitude:102.8283}],biggest:f,type_place:g,id_image_cover:a,id_image_hero:a,affiliate:[{name:c,value:d}],alerts:a,zoom:[{google:h,booking:i}]},{id:2157,id_state:b,name:"Nakhon Ratčasima",population:148300,area:61.5,altitude:267,slug:"nakhon-ratcasima",information_chatgpt:"\u003Cp\u003ENakhon Ratchasima, také známé jako Khorat, je živé a rozmanité město v Thajsku. Nachází se ve východní části země a je hlavním městem stejnojmenné provincie Nakhon Ratchasima. S více než 400 000 obyvateli je jedním z největších měst Thajska a důležitým obchodním a kulturním centrem v regionu.\u003C\u002Fp\u003E\n\u003Cp\u003EJednou z hlavních turistických atrakcí v Nakhon Ratchasima je Khao Yai National Park, který se nachází přibližně 60 kilometrů od města. Tento národní park je domovem bohaté fauny a flóry, včetně vzácných druhů ptáků a opic. Návštěvníci si zde mohou užít přírodní krásy, procházky po džungli, vodopády a výhledy z panoramatických míst.\u003C\u002Fp\u003E\n\u003Cp\u003EHistoricky významným místem je také Prasat Hin Phimai, starověký chrám z 11. století postavený v khmerském stylu. Tento monumentální chrám je obklopen nádhernými kamennými zídkami a sochami a je důležitým památkou khmerského umění v regionu.\u003C\u002Fp\u003E\n\u003Cp\u003EPro ty, kteří mají zájem o historii a kulturu, je zde také Muzeum provincie Nakhon Ratchasima, které nabízí přehled o místní historii a tradicích. Město má také živou a rušnou tržnici, kde můžete ochutnat místní kulinářské speciality a nakupovat tradiční thajské výrobky.\u003C\u002Fp\u003E\n\u003Cp\u003ENakhon Ratchasima je také známé pro své festivaly a události. Mezi nejvýznamnější patří Thajský mezinárodní festival filmu a videoher, který přitahuje filmaře a fanoušky ze všech koutů světa. Dalším významným festivalem je Phi Ta Khon, což je tradiční folklórní festival s pestrobarevnými maskami a kostýmy.\u003C\u002Fp\u003E\n\u003Cp\u003EMěsto nabízí širokou škálu ubytování, restaurací, kaváren a nočního života, které uspokojí potřeby turistů. Nakhon Ratchasima je dobře napojeno na dopravní síť, což usnadňuje cestování do dalších částí Thajska.\u003C\u002Fp\u003E\n\u003Cp\u003EVýlet do Nakhon Ratchasima je skvělý způsob, jak objevit místní kulturu, historii a přírodu. Město nabízí zážitky pro každého a je skvělou destinací pro ty, kteří chtějí poznat autentickou stránku Thajska mimo turisticky přeplněné oblasti.\u003C\u002Fp\u003E",coordinates:[{latitude:14.9707,longitude:102.1019}],biggest:f,type_place:g,id_image_cover:a,id_image_hero:a,affiliate:[{name:c,value:d}],alerts:a,zoom:[{google:h,booking:i}]},{id:2162,id_state:b,name:"Nakhon Si Thammarat",population:105900,area:G,altitude:q,slug:"nakhon-si-thammarat",information_chatgpt:"\u003Cp\u003ENakhon Si Thammarat je město nacházející se na jihu Thajska a je hlavním městem stejnojmenné provincie. Je to historicky bohaté město s bohatým kulturním dědictvím a krásnou přírodou.\u003C\u002Fp\u003E\n\u003Cp\u003EJednou z hlavních atrakcí v Nakhon Si Thammarat je Wat Phra Mahathat. Tento chrám je jedním z nejvýznamnějších buddhistických svatostánků v Thajsku a je známý svou zlatou pagodou, která se tyčí do výšky. Navštívení tohoto chrámu je skvělý způsob, jak poznat thajskou kulturu a architekturu.\u003C\u002Fp\u003E\n\u003Cp\u003EDalší zajímavou památkou je Nakhon Si Thammarat National Museum, kde si můžete prohlédnout mnoho artefaktů a exponátů, které dokumentují historii a kulturu regionu. Můžete zde nalézt staré sochy, keramiku, šperky a další historické předměty.\u003C\u002Fp\u003E\n\u003Cp\u003EPro milovníky přírody je ideální návštěva Khao Luang National Park. Tento národní park je domovem nádherných vodopádů, krásných horských scenérií a jedinečné flóry a fauny. Můžete se vydat na pěší túru a objevovat skryté jeskyně a malebné vodopády.\u003C\u002Fp\u003E\n\u003Cp\u003EV centru města se rozprostírá tradiční trh, známý jako Talad Nad. Na tomto trhu najdete širokou škálu čerstvých potravin, ovoce, zeleniny, ryb a dalších místních specialit. Je to skvělé místo, kde ochutnat thajskou kuchyni a zakoupit tradiční suvenýry.\u003C\u002Fp\u003E\n\u003Cp\u003ENakhon Si Thammarat je také domovem mnoha festivlů a oslav. Nejznámější z nich je Thajský nový rok, Songkran, během kterého město ožívá barevnými procesí a vodními bitvami. Dalšími významnými festivly jsou Chak Phra Festival, který slaví konci období dešťů, a Phimai Festival, který přináší tradiční hudbu, tance a divadlo.\u003C\u002Fp\u003E\n\u003Cp\u003ENakhon Si Thammarat je tedy fascinujícím městem, které nabízí bohaté historické a kulturní zážitky, nádhernou přírodu a vřelost místních obyvatel. Je to skvělé místo k návštěvě, pokud se chcete ponořit do thajského života a objevit méně známou část země.\u003C\u002Fp\u003E",coordinates:[{latitude:8.4306,longitude:99.9637}],biggest:f,type_place:g,id_image_cover:a,id_image_hero:a,affiliate:[{name:c,value:d}],alerts:a,zoom:[{google:h,booking:i}]},{id:2154,id_state:b,name:"Nonthaburi",population:288000,area:38.9,altitude:o,slug:"nonthaburi",information_chatgpt:"\u003Cp\u003ENonthaburi je druhé největší město v Thajsku a nachází se v blízkosti hlavního města Bangkok. S rozlohou přes 39 čtverečních kilometrů a populací přesahující milion obyvatel, Nonthaburi je důležitým ekonomickým a administrativním centrem v této oblasti.\u003C\u002Fp\u003E\n\u003Cp\u003EMěsto se pyšní bohatou historií, která sahá až do 16. století, kdy bylo založeno. Dnes je Nonthaburi moderním městem, které však stále uchovává své kulturní a historické dědictví. Turisté mohou navštívit mnoho zajímavých památek a atrakcí.\u003C\u002Fp\u003E\n\u003Cp\u003EJedním z nejvýznamnějších míst je Wat Chaloem Phra Kiat, chrám, který je považován za jedno z největších náboženských staveb v Asii. Jeho architektura je ohromující a interiér je bohatě zdobený sochami a freskami. Další důležitou atrakcí je Muzeum umění Nonthaburi, které nabízí návštěvníkům pohled na bohatou sbírku uměleckých děl od thajských i mezinárodních umělců.\u003C\u002Fp\u003E\n\u003Cp\u003ENonthaburi je také známé díky svým tradičním trhům. Jedním z největších a nejživějších je Pak Kret Floating Market, kde můžete ochutnat tradiční thajské pokrmy a zakoupit ručně vyrobené suvenýry a řemeslné výrobky. Kromě toho můžete také navštívit Chatuchak Weekend Market, který je jedním z největších trhů v Asii a nabízí široký výběr oblečení, šperků, keramiky a dalšího zboží.\u003C\u002Fp\u003E\n\u003Cp\u003ENonthaburi je také obklopeno krásnou přírodou. Můžete si užít procházky a pikniky v Bang Krachao, což je rozsáhlý zelený ostrov na řece Chao Phraya. Další přírodní atrakcí je Sirindhorn International Environmental Park, kde se můžete projít po naučných stezkách a obdivovat místní floru a faunu.\u003C\u002Fp\u003E\n\u003Cp\u003EV Nonthaburi najdete také moderní nákupní centra, restaurace s thajskou i mezinárodní kuchyní a pohodové kavárny. Město je dobře propojeno s Bangkokem a dalšími částmi Thajska, a proto je ideálním výchozím bodem pro objevování okolních oblastí.\u003C\u002Fp\u003E\n\u003Cp\u003ENonthaburi je zajímavým městem, které kombinuje moderní vybavení s kulturním dědictvím. Je ideálním místem pro ty, kteří chtějí poznat více než jen hlavní turistické atrakce Thajska a ponořit se do autentického thajského života.\u003C\u002Fp\u003E",coordinates:[{latitude:13.8622,longitude:100.5144}],biggest:f,type_place:g,id_image_cover:a,id_image_hero:a,affiliate:[{name:c,value:d}],alerts:a,zoom:[{google:h,booking:i}]},{id:2155,id_state:b,name:"Pak Kret",population:184900,area:44.8,altitude:o,slug:"pak-kret",information_chatgpt:"\u003Cp\u003EPak Kret je město nacházející se ve státě Thajsko. Leží na břehu řeky Chao Phraya a je součástí provincie Nonthaburi, která se nachází nedaleko od hlavního města Bangkok. Svého času bylo Pak Kret malou vesnicí, ale postupně se rozrostlo a nyní se jedná o jedno z největších předměstí Bangkoku.\u003C\u002Fp\u003E\n\u003Cp\u003EPak Kret je známé především pro své pláže a rekreační oblasti u řeky Chao Phraya. Lidé sem přicházejí relaxovat, opalovat se a koupat se ve vodě. Jedna z nejpopulárnějších pláží je pláž Wat Sanam Neua, která nabízí krásný výhled na řeku a okolní krajinu.\u003C\u002Fp\u003E\n\u003Cp\u003EMěsto je také domovem slavného trhu Wat Bua Kwan Floating Market. Tento trh je známý pro své plavící se prodejní lodě, které nabízejí čerstvé ovoce, zeleninu, mořské plody a další tradiční thajské pokrmy. Je to skvělé místo pro ochutnávání místní kuchyně a nakupování suvenýrů.\u003C\u002Fp\u003E\n\u003Cp\u003EMezi další zajímavá místa v Pak Kretu patří Wat Phai Lom, buddhistický chrám s krásnou architekturou a klidnou atmosférou. Další atrakcí je Muzeum vodních lodí, které ukazuje historii a vývoj lodní dopravy v této oblasti.\u003C\u002Fp\u003E\n\u003Cp\u003EPak Kret je také obklopen malebnou přírodou. Nedaleko se nachází Národní park Khao Yai, který je domovem mnoha druhů divokého zvířectva a poskytuje možnosti pro turistiku, pozorování ptáků a pikniky v přírodě.\u003C\u002Fp\u003E\n\u003Cp\u003EV blízkosti Pak Kretu se nachází také mnoho obchodních center a moderních restaurací, které nabízejí různé mezinárodní kuchyně. Místní trhy a noční trhy jsou také populární mezi obyvateli i návštěvníky, kde lze nakoupit čerstvé potraviny, oblečení a další produkty.\u003C\u002Fp\u003E\n\u003Cp\u003EPak Kret je fascinujícím místem, které kombinuje přírodu, kulturu a moderní vyžití. Je to ideální destinace pro ty, kteří hledají klidnější místo k pobytu, ale zároveň chtějí mít blízko k hlavnímu městu Thajska.\u003C\u002Fp\u003E",coordinates:[{latitude:13.9637,longitude:100.4986}],biggest:f,type_place:g,id_image_cover:a,id_image_hero:a,affiliate:[{name:c,value:d}],alerts:a,zoom:[{google:h,booking:i}]},{id:2160,id_state:b,name:"Surat Thani",population:117000,area:G,altitude:q,slug:"surat-thani",information_chatgpt:"\u003Cp\u003ESurat Thani je město nacházející se v jižním Thajsku. Je hlavním městem stejnojmenné provincie Surat Thani a leží na břehu řeky Tapi. Město má bohatou historii, která sahá až do 3. století. Díky své strategické poloze a přístavu se Surat Thani stal významným obchodním centrem.\u003C\u002Fp\u003E\n\u003Cp\u003EJedním z hlavních lákadel pro turisty v Surat Thani je Khao Sok Národní park. Tento národní park se nachází v pohoří Tenasserim Hills a nabízí úchvatné scenérie s bujnou tropickou vegetací, pralesy a jezery. Turisté si zde mohou užít pěší turistiku, rafting, kanoistiku a dokonce i přespání v plavbě na záchranném člunu. Dalším zajímavým místem je Mu Ko Ang Thong, což je skupina 42 ostrovů se skalnatými útesy a bílými plážemi. Ostrovy jsou ideální pro potápění, šnorchlování a prozkoumávání podmořského života.\u003C\u002Fp\u003E\n\u003Cp\u003EV Surat Thani se také nachází slavný buddhistický chrám Wat Phra Borommathat Chaiya, který pochází z 7. století. Tento chrám je významným náboženským místem a poskytuje návštěvníkům nádherný výhled na okolní krajinu.\u003C\u002Fp\u003E\n\u003Cp\u003EMěsto Surat Thani je také výchozím bodem pro cestu na oblíbený ostrov Koh Samui. Koh Samui je známý svými krásnými plážemi, palmami a luxusními letovisky. Cestovatelé si zde mohou užít slunění, potápění, šnorchlování a různé vodní sporty.\u003C\u002Fp\u003E\n\u003Cp\u003ESurat Thani je také proslulé svou místní kuchyní. Můžete si vychutnat tradiční thajská jídla, jako je Pad Thai, Tom Yum Goong a zelený kari. Na místních trzích najdete širokou škálu čerstvých plodů moře, tropického ovoce a dalších specialit.\u003C\u002Fp\u003E\n\u003Cp\u003EMěsto Surat Thani je skvělým místem pro objevování krás jižního Thajska. Nabízí kombinaci přírodního bohatství, kulturního dědictví a výletů na okolní ostrovy. Je to ideální destinace pro ty, kteří chtějí zažít autentickou thajskou kulturu a přírodu.\u003C\u002Fp\u003E",coordinates:[{latitude:9.1386,longitude:99.3217}],biggest:f,type_place:g,id_image_cover:a,id_image_hero:a,affiliate:[{name:c,value:d}],alerts:a,zoom:[{google:h,booking:i}]},{id:2159,id_state:b,name:"Udon Thani",population:120400,area:47,altitude:182,slug:"udon-thani",information_chatgpt:"\u003Cp\u003EUdon Thani je město nacházející se ve východní části Thajska, nedaleko hranic s Laoskem. S více než 400 000 obyvateli je to čtvrté největší město v zemi a hlavní město provincie Udon Thani. Jeho poloha ho činí významným dopravním uzlem a obchodním centrem této oblasti.\u003C\u002Fp\u003E\n\u003Cp\u003EMěsto má dlouhou historii a archeologické vykopávky v okolí odhalily artefakty, které pocházejí až z doby bronzové. V současné době je Udon Thani známé svým kulturním dědictvím a zároveň moderním rozvojem.\u003C\u002Fp\u003E\n\u003Cp\u003EJednou z hlavních atrakcí je chrám Wat Phothisomphon, který je považován za nejvýznamnější buddhistický chrám v městě. Jeho zlatá pagoda a nádherné sochy buddhů přitahují mnoho návštěvníků. Dalším zajímavým místem je Nong Prajak Park, rozlehlý park se svěží zelení, jezerem a fontánami. Je oblíbeným místem pro odpočinek, sportovní aktivity a procházky.\u003C\u002Fp\u003E\n\u003Cp\u003EUdon Thani je také známé jako centrum thajského východního potravinářského průmyslu. Místní trhy, jako například Night Market, jsou výborným místem k ochutnávání tradičních jídel a nákupu čerstvých surovin. Můžete si vychutnat lahodná jídla, jako je pad thai, som tam (zelený papája salát) nebo khao niao mamuang (mangová sladká rýže).\u003C\u002Fp\u003E\n\u003Cp\u003EV okolí Udon Thani se nachází také zajímavá místa k prozkoumání. Například Si That National Park, který nabízí úchvatné vodopády, jeskyně a přírodní krásy. Také se můžete vydat na výlet do vesnice Ban Chiang, která je známá pro své archeologické naleziště a starobylou keramiku.\u003C\u002Fp\u003E\n\u003Cp\u003EUdon Thani je fascinujícím městem, které kombinuje bohatou historii, kulturu a moderní rozvoj. Jeho přírodní krása, vynikající kuchyně a pohostinní obyvatelé dělají z tohoto místa ideální destinaci pro cestovatele, kteří chtějí prozkoumat méně známou část Thajska.\u003C\u002Fp\u003E",coordinates:[{latitude:17.404,longitude:102.7883}],biggest:f,type_place:g,id_image_cover:a,id_image_hero:a,affiliate:[{name:c,value:d}],alerts:a,zoom:[{google:h,booking:i}]},{id:r,id_state:b,name:s,population:t,area:u,altitude:v,slug:w,information_chatgpt:x,coordinates:[{latitude:y,longitude:z}],biggest:f,type_place:g,id_image_cover:a,id_image_hero:a,affiliate:[{name:c,value:d}],alerts:a,zoom:[{google:h,booking:i}]}],videos:[],posts:[],imagePlace:a,imageState:[{id:p,name:A,source:"\u002Fworld\u002Fstates\u002F",date_create:"2023-05-03T22:13:53.42428+00:00",type:E,author:"pixabay.com"}],imagesCities:a,imagesVideos:a,imagesPosts:a}],fetch:{},mutations:void 0}}(null,218,"booking",true,"05-2023","yes","city",10,11,"09-2021","12-2020",3,"01-2010","1%",2,374,4,2158,"Čiang Mai",131700,40.22,310,"ciang-mai","\u003Cp\u003EChiang Mai je krásné město nacházející se v severním Thajsku. Je to druhé největší město v zemi a nabízí návštěvníkům bohatou směs tradice, kultury a nádherné přírody.\u003C\u002Fp\u003E\n\u003Cp\u003EJednou z hlavních atrakcí v Chiang Mai je slavný chrám Wat Phra That Doi Suthep, který se nachází na kopci nad městem. Tento chrám je jedním z nejsvatějších thajských chrámů a nabízí úchvatný výhled na město a okolní krajinu.\u003C\u002Fp\u003E\n\u003Cp\u003EDalším populárním místem je historické centrum města, které je obklopeno starými hradbami. Zde najdete mnoho chrámů, jako například Wat Chedi Luang, Wat Phra Singh a Wat Chiang Man. Tyto chrámy jsou krásnými příklady tradiční thajské architektury a jsou místy klidu a meditace.\u003C\u002Fp\u003E\n\u003Cp\u003EChiang Mai je také známý svými tradičními trhy. Nejznámější z nich je Sunday Walking Street Market, který se koná každou neděli večer. Zde můžete procházet mezi stánky plnými tradičního thajského umění, řemesel a místních dobrot.\u003C\u002Fp\u003E\n\u003Cp\u003ENedaleko města se rozprostírá krásná příroda, která láká dobrodruhy i přírodní nadšence. Doi Inthanon, nejvyšší hora v Thajsku, nabízí nádherné výhledy, trekkingové trasy a exotickou flóru a faunu. Navštívte také vodopády Mae Sa a Buatong, kde se můžete osvěžit v jejich křišťálově čistých vodách.\u003C\u002Fp\u003E\n\u003Cp\u003EKromě toho, Chiang Mai je centrem thajského masážního průmyslu. Zde si můžete dopřát příjemnou relaxaci a uvolnění pomocí tradiční thajské masáže.\u003C\u002Fp\u003E\n\u003Cp\u003EChiang Mai je tedy fascinujícím místem, které kombinuje bohatou kulturu, nádhernou architekturu a překrásnou přírodu. Je to ideální místo pro ty, kteří chtějí objevovat tradiční thajskou kulturu a užívat si klidné a pohodové atmosféry.\u003C\u002Fp\u003E",18.7883,98.9853,"thajsko","",105,"0.1%","state",2153,68.97)));