__NUXT_JSONP__("/svet/mesto/orumije", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w,x,y,z){return {data:[{place:[{id:m,id_state:c,name:n,population:o,area:p,altitude:q,slug:r,information_chatgpt:s,coordinates:[{latitude:t,longitude:u}],biggest:g,type_place:h,id_image_cover:a,id_image_hero:a,affiliate:[{name:d,value:e}],alerts:a,zoom:[{google:i,booking:f}]}],placeState:[{id:c,tld:".ir",name:"Írán",area:1648195,population:83992949,slug:v,id_continent:j,information_chatgpt:"\u003Cp\u003EÍrán, oficiálně Islámská republika Írán, je stát v jihozápadní Asii, který sousedí s Irákem, Tureckem, Arménií, Ázerbájdžánem, Turkmenistánem, Afghánistánem a Pákistánem. Írán je významnou zemí Blízkého východu a má dlouhou historii, která sahá až do doby před 2500 lety.\u003C\u002Fp\u003E\n\u003Cp\u003EÍrán je známý pro svou kulturní a historickou dědictví, včetně starověkého města Persepole, města Isfahán a slavného náměstí Meidán-e Naqsh-e Jahan. Navštívíte-li Írán, určitě nevynechejte procházku bazary, které jsou centrem místního života. Dále můžete navštívit i slavný památník Fath-Abad Garden v Kášánu nebo chrám Persepolis, který se nachází v oblasti Fars.\u003C\u002Fp\u003E\n\u003Cp\u003EÍrán je také významným producentem ropy a zemního plynu. Tyto přírodní zdroje jsou pro Írán velmi důležité a mají velký vliv na jeho ekonomiku. Místní kuchyně se vyznačuje různorodostí a chutí. Nejznámějšími jídly jsou například kebab, pilaf nebo ghorme sabzi.\u003C\u002Fp\u003E\n\u003Cp\u003EÍrán má bohatou kulturu a tradice, ale zároveň je to země s politickými a společenskými výzvami. Někteří cestovatelé mohou mít obavy z bezpečnosti, ale většina turistů se setkává s vřelým přijetím ze strany místních obyvatel. Pro cestovatele platí některá omezení v oblasti oblečení a chování, ale při dodržení místních zvyklostí a kulturních rozdílů si mohou návštěvníci užít krásy této země.\u003C\u002Fp\u003E",currency_code:"IRR",currency_name:"rial",money_prices:[{name:"Taxi (cena za km)",value:a,date_update:b},{name:"Hlavní jídlo",value:a,date_update:b},{name:"Chleba",value:a,date_update:b},{name:"Mléko",value:a,date_update:b},{name:"Voda (500ml)",value:a,date_update:b},{name:"Espresso",value:a,date_update:b},{name:"Zmrzlina (jeden kopeček)",value:a,date_update:b},{name:"Pivo",value:a,date_update:b},{name:"Krabička cigaret",value:a,date_update:b},{name:"Bic Mac",value:a,date_update:b},{name:"Coca Cola (500ml)",value:a,date_update:b}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:"112",date_update:b},{name:"Policie",value:"110",date_update:b},{name:"Hasiči",value:"125",date_update:b},{name:"Zdravotnická záchranná služba",value:"115",date_update:b}],phone_prefix:"+98",coordinates:[{latitude:"32.4279",longitude:"53.6880"}],people_religion:[{name:"Islám",value:"99%"},{name:"Jiné náboženství",value:w}],people_nationality:[{name:"Peršané",value:"61%"},{name:"Ázerbájdžánci",value:"16%"},{name:"Kurdové",value:"10%"},{name:"Lureové",value:"6%"},{name:"Balúčové",value:k},{name:"Arabové",value:k},{name:"Turkmenové",value:k},{name:"Jiné národnosti",value:w}],visitors_entry:[{name:"Občanský průkaz",value:"Občanský průkaz nestačí jako cestovní doklad pro vstup do Íránu. Je nutné mít platný cestovní pas.",date_update:b},{name:"Cestovní pas",value:"Pro vstup do Íránu je nutné mít platný cestovní pas. Občanský průkaz nestačí.",date_update:b},{name:"Vízum",value:"Pro vstup do Íránu je obvykle nutné mít vízum. To se vztahuje i na občany evropské unie a šengenského prostoru. Víza lze získat na íránském konzulátu nebo velvyslanectví v zemi občana. Existují také možnosti získání víza při příletu na letišti v Íránu, ale tato možnost není vždy zaručena.",date_update:b},{name:"Očkování",value:"Pro vstup do Íránu není vyžadováno žádné specifické očkování. Nicméně, některá očkování jsou doporučena, zejména ty proti hepatitidě A a B, žloutence, obrně a žloutence typu A. Je také dobré mít očkování proti chřipce a tetanu. Před cestou se doporučuje konzultovat se svým lékařem nebo očkovacím centrem.",date_update:b},{name:"Cestovní pojištění",value:"Pro vstup do Íránu není vyžadováno mít cestovní ani zdravotní pojištění. Nicméně, je doporučeno mít platné cestovní pojištění, které pokrývá lékařské náklady a ztrátu zavazadel.",date_update:b},{name:"Bezpečnostní situace",value:"Írán je bezpečná země pro cestování. Nicméně, je důležité dodržovat základní bezpečnostní opatření, jako je sledování zpravodajství a varování ze strany vlády, vyhnout se politickým shromážděním a vzdát se návštěv míst, které jsou považovány za nebezpečné.",date_update:b},{name:"Další informace",value:"Při návštěvě Íránu je důležité mít na paměti místní zvyklosti a kulturu. Například je vhodné mít vždy při sobě šátek, kterým si můžete zakrýt hlavu, pokud navštívíte nějaké náboženské památky. Také je dobré mít na paměti, že konzumace alkoholu a drog je v Íránu zakázána a trestána zákonem. Pokud máte dotazy ohledně pobytu v zemi, můžete se obrátit na místní turistické informační kanceláře, které vám rády pomohou.",date_update:b}],type_place:x,id_image_cover:l,id_image_hero:l,id_city_main:y,affiliate:[{name:d,value:e}],mpz:"IR",alerts:a,zoom:[{google:z,booking:z}],ids_neighboring_countries:a}],placeContinent:[{id:j,slug:"asie",name:"Asie",area:44580000,population:4630000000,population_density:104.03,number_states:50,information_chatgpt:"\u003Cp\u003EAsie se rozkládá na ploše téměř 44,6 milionu kilometrů čtverečních a žije zde více než 4,5 miliardy lidí, což představuje téměř 60 % celkového počtu obyvatel planety. Kontinent zahrnuje celkem 48 zemí, mezi největší patří Rusko, Čína, Indie, Japonsko a Indonésie.\u003C\u002Fp\u003E\n\u003Cp\u003EAsie je domovem mnoha významných světových kultur, jako jsou hinduismus, buddhismus, islám a taoismus. V každé zemi se můžeš setkat s odlišnými tradicemi, zvyky a jazyky. Mezi nejnavštěvovanější turistické destinace patří například Japonsko s jeho futuristickými městy, Thajsko s bohatou kulturou a krásnými plážemi, nebo Indonésie s neuvěřitelnou přírodou a množstvím ostrovů.\u003C\u002Fp\u003E\n\u003Cp\u003EAsie také nabízí mnoho kulturních a historických památek, jako je Velká čínská zeď, Taj Mahal v Indii, buddhistické chrámy v Thajsku a mnoho dalšího. Zajímavou zkušeností může být i návštěva tradičních trhů, kde se dají ochutnat místní speciality a nakoupit různé exotické produkty.\u003C\u002Fp\u003E\n\u003Cp\u003EAsie má však také své výzvy a problémy, jako jsou například růst městských oblastí, nedostatek vody a znečištění ovzduší. Mnoho zemí se snaží najít řešení těchto problémů a přizpůsobit se moderním trendům, aby mohly udržet krok s ostatními zeměmi světa.\u003C\u002Fp\u003E\n\u003Cp\u003EPokud tě láká cestování po Asii, měl by sis předem zajistit víza a informovat se o místních zvycích a tradicích. Rozhodně se ti však naskytne možnost prozkoumat jedinečnou kulturu, přírodu a historii tohoto fascinujícího kontinentu.\u003C\u002Fp\u003E\u003Cp\u003EAsie má také neuvěřitelně bohatou gastronomii, která se v jednotlivých zemích a regionech liší. Mezi nejznámější asijské pokrmy patří sushi, různé druhy nudlí, curry, dim sum, pho a další. Kromě toho se v Asii pěstuje mnoho exotických plodin, jako jsou duriany, mangostany, rambutany a další ovoce, které můžeš ochutnat pouze zde.\n\u003C\u002Fp\u003E\u003Cp\u003E\nAsie je také známá svými svátky a tradicemi, jako je například čínský Nový rok, Diwali v Indii, nebo Songkran v Thajsku. Tyto svátky jsou většinou spojeny s různými rituály a zvyky, které se v průběhu staletí vyvinuly a jsou dodnes respektovány.\n\u003C\u002Fp\u003E\u003Cp\u003E\nKromě toho má Asie mnoho přírodních krás, jako jsou Himálaje, pouště Gobi a Thar, nebo tropické pralesy v Indonésii a Malajsii. V Asii se také nachází mnoho národních parků a chráněných oblastí, kde žijí ohrožené druhy zvířat, jako jsou tygři, pandy, sloni a další.\n\u003C\u002Fp\u003E\u003Cp\u003E\nZkrátka, Asie je kontinent plný kontrastů a neuvěřitelných zážitků, které stojí za to objevovat. Budeš-li mít příležitost navštívit některou z asijských zemí, neváhej a vydaj se na dobrodružnou cestu plnou nových objevů a nezapomenutelných zážitků.\u003C\u002Fp\u003E",coordinates:[{latitude:47.751076,longitude:92.475286}],type_place:"continent",id_image_cover:j,id_image_hero:j,zoom:[{google:2,booking:j}]}],placesCities:[{id:1055,id_state:c,name:"Ahváz",population:1193570,area:84,altitude:20,slug:"ahvaz",information_chatgpt:"\u003Cp\u003EAhváz je město ležící v jihozápadní části Íránu, nedaleko od hranic s Irákem. Je to hlavní město a největší město provincie Chúzistán. S více než 1,1 miliony obyvatel je Ahváz kulturním, ekonomickým a průmyslovým centrem regionu.\u003C\u002Fp\u003E\n\u003Cp\u003EMěsto je známé svým strategickým umístěním na břehu řeky Kárún, která je největší řekou Íránu. Díky svému poloze se Ahváz stal důležitým obchodním centrem a přístavem pro vodní dopravu. Místní ekonomika je převážně založena na ropě a petrochemickém průmyslu, což přitahuje mnoho podniků a investic.\u003C\u002Fp\u003E\n\u003Cp\u003EAhváz je bohaté na kulturní dědictví. V centru města se nachází historický bazar, který je místem plným barvitých obchodů nabízejících tradiční řemesla, oděvy, koření a další zboží. Mezi další zajímavé památky patří muzeum Chúzistánu, které představuje bohatou historii a kulturu regionu, a také zahrady Jangalí a park Jádžú, kde místní obyvatelé tráví svůj volný čas.\u003C\u002Fp\u003E\n\u003Cp\u003EKromě toho Ahváz nabízí i různé přírodní krásy. V blízkosti města se nachází přírodní rezervace Shadegan, která je domovem mnoha druhů vzácných ptáků a mokřadů. Rezervace je oblíbeným cílem pro ornitology a přírodovědce. Mnoho turistů také vyhledává výlety do okolních pouští, kde si mohou vychutnat písečné duny a zářící západy slunce.\u003C\u002Fp\u003E\n\u003Cp\u003EAhváz je také významným centrem vzdělání. V městě se nachází několik univerzit, které přitahují studenty z celého Íránu. Univerzita Jundišápur je jednou z nejstarších a nejvýznamnějších v zemi.\u003C\u002Fp\u003E\n\u003Cp\u003ENavštívit Ahváz je pro cestovatele skvělou příležitostí poznat jeho bohatou historii, ponořit se do místní kultury a objevit krásy okolní přírody. Město je plné života a nabízí pestrou škálu zážitků pro každého návštěvníka.\u003C\u002Fp\u003E",coordinates:[{latitude:31.3183,longitude:48.6693}],biggest:g,type_place:h,id_image_cover:a,id_image_hero:a,affiliate:[{name:d,value:e}],alerts:a,zoom:[{google:i,booking:f}]},{id:1050,id_state:c,name:"Isfahán",population:2051172,area:549,altitude:1570,slug:"isfahan",information_chatgpt:"\u003Cp\u003EIsfahán, známé také jako Esfahán, je historické město ležící v centrální části Íránu. Je to hlavní město stejnojmenné provincie a jedno z nejvýznamnějších kulturních a turistických center v zemi. Město má bohatou historii, která sahá až do dob starověké Persie.\u003C\u002Fp\u003E\n\u003Cp\u003EJedním z nejvýraznějších znaků Isfahánu je jeho architektura. Město je domovem mnoha historických památek, zahrnujících mešity, paláce a mosty. Nejznámější z nich je pravděpodobně Mešita Šejch Lotfolláh, která je považována za jednu z nejkrásnějších islámských staveb na světě. Další významnou památkou je Mešita Imám, obklopená rozlehlým náměstím Naqsh-e Jahan. Toto náměstí, které je také známé jako Imamovo náměstí, je jedno z největších na světě a bylo zapsáno na seznam Světového dědictví UNESCO.\u003C\u002Fp\u003E\n\u003Cp\u003EIsfahán je také proslulý svými trhy. Nejznámější z nich je Grand Bazaar, který je jedním z největších a nejstarších tržišť na Blízkém východě. Zde můžete objevit širokou škálu zboží, včetně místních řemeslných výrobků, koření, koberců a šperků. Trh je také skvělým místem k ochutnávání místní kuchyně.\u003C\u002Fp\u003E\n\u003Cp\u003EV Isfahánu se nachází také několik impozantních mostů, které jsou oblíbenými místy pro procházky a setkávání se s přáteli. Jedním z nejznámějších mostů je Most 33 oblouků, který se táhne přes řeku Zayandeh. Most je známý svou architekturou a je často zobrazován na pohlednicích a fotografických snímcích Isfahánu.\u003C\u002Fp\u003E\n\u003Cp\u003EIsfahán nabízí také mnoho zelených prostorů a parků, kde se místní obyvatelé i návštěvníci mohou odpočinout a relaxovat. Zahrady města, jako je Zahrada předních Šahů, jsou oázou klidu a krásy.\u003C\u002Fp\u003E\n\u003Cp\u003EIsfahán je město, které kombinuje historii, kulturu a architekturu. Jeho bohaté dědictví a nádherné památky činí z Isfahánu nezapomenutelné místo k návštěvě pro všechny, kteří se zajímají o perskou historii a kulturu.\u003C\u002Fp\u003E",coordinates:[{latitude:32.6546,longitude:51.6679}],biggest:g,type_place:h,id_image_cover:a,id_image_hero:a,affiliate:[{name:d,value:e}],alerts:a,zoom:[{google:i,booking:f}]},{id:1051,id_state:c,name:"Karadž",population:1592495,area:484,altitude:1367,slug:"karadz",information_chatgpt:"\u003Cp\u003EKaraj je město nacházející se v provincii Alborz v Íránu. Leží asi 20 kilometrů severozápadně od hlavního města Teheránu, což je hlavní město Íránské islámské republiky. S více než 1,6 miliony obyvatel je Karaj jedním z největších měst v zemi.\u003C\u002Fp\u003E\n\u003Cp\u003EHistorie města sahá až do pradávných dob, ačkoli se stalo významným městem teprve v průběhu 20. století. V minulosti sloužilo jako tranzitní stanice na cestě mezi Teheránem a městy na severozápadě Íránu. Díky své poloze poblíž hlavního města se v posledních desetiletích rychle rozrostlo a stalo se důležitým ekonomickým a průmyslovým centrem.\u003C\u002Fp\u003E\n\u003Cp\u003EJednou z hlavních atrakcí v Karaj je Karaj Dam, která se rozprostírá na řece Karaj a slouží jako zdroj pitné vody a elektrické energie pro město a okolní oblasti. Tato přehrada je také oblíbeným místem pro pikniky a rekreační aktivity, a nabízí nádherný výhled na okolní hory.\u003C\u002Fp\u003E\n\u003Cp\u003EV Karaji se nachází také řada parků a zahrad, které poskytují příjemná místa k odpočinku a relaxaci. Mezi nejznámější z nich patří Mellat Park, který je největším parkem ve městě. Park je vybaven různými zařízeními, jako jsou tenisové kurty, dětská hřiště a jízdní kola k zapůjčení. Je to skvělé místo, kde se místní obyvatelé scházejí, sportují a tráví volný čas.\u003C\u002Fp\u003E\n\u003Cp\u003EKaraj je také známý pro své trhy a nákupní centra, které nabízejí širokou škálu zboží. Místní trhy jsou živé a barevné, plné místních potravin, textilu, suvenýrů a dalších produktů. Nákupní centra zase nabízejí moderní značkové zboží a obchody s elektronikou.\u003C\u002Fp\u003E\n\u003Cp\u003EVzhledem ke své blízkosti k Teheránu je možné využít také přístup k historickým a kulturním památkám v této oblasti. Teherán je bohatý na muzea, mešity a paláce, které stojí za návštěvu.\u003C\u002Fp\u003E",coordinates:[{latitude:35.8355,longitude:51.0109}],biggest:g,type_place:h,id_image_cover:a,id_image_hero:a,affiliate:[{name:d,value:e}],alerts:a,zoom:[{google:i,booking:f}]},{id:1056,id_state:c,name:"Kermánšáh",population:862508,area:53,altitude:1350,slug:"kermansah",information_chatgpt:"\u003Cp\u003EKermánšáh je historické město ležící v západní části Íránu. Sídlí v stejnojmenné provincii Kermánšáh, která je bohatá na kulturní dědictví a památky. Město je obklopeno malebnými horami a je známé svou bohatou historií a archeologickými nalezišti.\u003C\u002Fp\u003E\n\u003Cp\u003EJednou z hlavních atrakcí v Kermánšáhu je Taq-e Bostan, která je starobylým komplexem s reliéfy vytesanými do skály. Tyto reliéfy pocházejí z období Sásánovské říše (3. až 7. století) a zobrazují krále, bohy a mytologické scény. Další významnou památkou je Tekyeh Moaven al-Molk, která je slavnou karavanserají z 19. století. Její architektura je ohromující a vnitřní prostory jsou zdobeny nádhernými freskami a mozaikami.\u003C\u002Fp\u003E\n\u003Cp\u003EV centru města se nachází také Kermánšáhský bazar, kde se můžete procházet mezi stánky plnými místních výrobků a suvenýrů. Bazar nabízí bohatou paletu zboží, jako jsou tradiční tkaniny, keramika, šperky a kožené výrobky. Je to skvělé místo pro nákupy a poznávání místní kultury.\u003C\u002Fp\u003E\n\u003Cp\u003EKromě historických památek nabízí Kermánšáh také nádhernou přírodu. Nedaleko města se nachází horstvo Zagros, které je rájem pro turisty a příznivce outdoorových aktivit. Můžete se vydat na pěší túru po horských stezkách, obdivovat vodopády a prozkoumat krásné kaňony. Oblast je také bohatá na archeologická naleziště, jako je Tepe Gijlar, kde byly objeveny starověké zbytky.\u003C\u002Fp\u003E\n\u003Cp\u003EPro milovníky kultury a historie je Kermánšáh skvělým místem k návštěvě. Město nabízí kombinaci bohatého kulturního dědictví a úchvatné přírody, která z něj činí atraktivní turistickou destinaci. Při návštěvě Kermánšáhu si jistě odnesete nezapomenutelné zážitky a poznání o bohaté historii této oblasti.\u003C\u002Fp\u003E",coordinates:[{latitude:34.3142,longitude:47.0649}],biggest:g,type_place:h,id_image_cover:a,id_image_hero:a,affiliate:[{name:d,value:e}],alerts:a,zoom:[{google:i,booking:f}]},{id:1049,id_state:c,name:"Mašhad",population:3103992,area:431,altitude:985,slug:"mashad",information_chatgpt:"\u003Cp\u003EMašhad je největším městem v Íránu a leží v severovýchodní části země. Je to jedno z nejposvátnějších míst islámu a oblíbené poutní místo pro muslimy. Město se nachází v provincii Chorásán-e Razaví a je známé především díky svému hlavnímu chrámu - Svatyni Imam Rezy.\u003C\u002Fp\u003E\n\u003Cp\u003ESvatyně Imam Rezy je největší poutní místo v Íránu a jedno z největších na světě. Je zde pohřben Imám Reza, osmý imám šíitského islámu. Každý rok sem přichází miliony poutníků z různých zemí, aby se poklonili hrobce a vykonali své náboženské povinnosti. Svatyně je bohatě zdobena a zahrnuje mnoho architektonických a historických památek.\u003C\u002Fp\u003E\n\u003Cp\u003EKromě svatyně můžete v Mashhadu navštívit také další zajímavá místa. Mešita Goharšad je jednou z nejstarších a nejkrásnějších mešit v městě. Je známá svými významnými uměleckými prvky, jako jsou mozaikové dlaždice a zdobené stropy.\u003C\u002Fp\u003E\n\u003Cp\u003EDalší zajímavostí je pohřebiště Tús, které se nachází nedaleko od Mashhadu. Tús je známé jako místo posledního odpočinku perského básníka Ferdousího, autora eposu Šáhnáma. Jeho hrob je oblíbenou turistickou atrakcí a místo, kde si lidé připomínají jeho literární odkaz.\u003C\u002Fp\u003E\n\u003Cp\u003EV Mashhadu najdete také moderní architekturu a obchodní centra. Příkladem je například obchodní dům Mega Mall, který nabízí široký výběr obchodů, restaurací a zábavních zařízení.\u003C\u002Fp\u003E\n\u003Cp\u003EMashhad je také městem s bohatou kulturou a tradičními hodnotami. Místní kuchyně je známá svými lahodnými jídly, jako je kebab, džudžeh kabáb (kuřecí špízy) a aš-e reshteh (tradiční perská polévka). Město také hostí různé kulturní festivaly a oslavy.\u003C\u002Fp\u003E\n\u003Cp\u003EMashhad je fascinujícím místem, které nabízí poutníkům a turistům bohatou směs historie, náboženského významu a moderních zážitků. Je to místo, které stojí za to navštívit, ať už jste zájemci o kulturu, historii nebo náboženství.\u003C\u002Fp\u003E",coordinates:[{latitude:36.2605,longitude:59.6168}],biggest:g,type_place:h,id_image_cover:a,id_image_hero:a,affiliate:[{name:d,value:e}],alerts:a,zoom:[{google:i,booking:f}]},{id:m,id_state:c,name:n,population:o,area:p,altitude:q,slug:r,information_chatgpt:s,coordinates:[{latitude:t,longitude:u}],biggest:g,type_place:h,id_image_cover:a,id_image_hero:a,affiliate:[{name:d,value:e}],alerts:a,zoom:[{google:i,booking:f}]},{id:1054,id_state:c,name:"Qom",population:1230103,area:f,altitude:928,slug:"qom",information_chatgpt:"\u003Cp\u003EQom je město ležící ve středním Íránu a nachází se přibližně 125 kilometrů jižně od Teheránu. Je jedním z nejvýznamnějších měst v zemi a považuje se za duchovní centrum íránského šíitského islámu. S více než 1,2 miliony obyvatel je Qom druhým největším městem Íránu po Teheránu.\u003C\u002Fp\u003E\n\u003Cp\u003EMěsto je známé svými vzdělávacími institucemi a univerzitami, které přitahují studenty ze všech částí Íránu. Hlavní institucí je Hássanská univerzita, která je jedním z nejvýznamnějších teologických center šíitského islámu. Tato univerzita je domovem pro mnoho náboženských učenců a studentů z celého světa.\u003C\u002Fp\u003E\n\u003Cp\u003EKromě své duchovní a vzdělávací role má Qom také bohatou historii. Město existuje již od předislámských dob a hrálo důležitou roli v islámských dějinách. Můžete navštívit několik historických památek, jako je mauzoleum Fátimy Ma'súmové, které je považováno za svaté místo šíitského islámu. Dalšími zajímavými místy jsou pevnost Alborz, bazilika Jamkaran a starobylá čtvrť Qom Bazaar.\u003C\u002Fp\u003E\n\u003Cp\u003EQom je také důležitým politickým centrem v Íránu. Město je sídlem nejvyššího duchovního vůdce íránského šíitského islámu, který má značný vliv na politické a náboženské záležitosti v zemi. To dává Qomu významnou roli v íránské politice a společnosti jako celek.\u003C\u002Fp\u003E\n\u003Cp\u003EVe městě najdete také mnoho tradičních restaurací, kde si můžete vychutnat íránskou kuchyni. Qom je známý pro své lahodné pokrmy, jako je dizi (tradiční jídlo s masem a fazolemi), ash-e reshte (polévka s nudlemi a fazolemi) a různé druhy kebabů.\u003C\u002Fp\u003E\n\u003Cp\u003EPři návštěvě Qomu je důležité respektovat místní zvyklosti a tradice. Ženy by měly nosit volné oblečení a zakrývat hlavu šátkem. Město je také známé svou konzervativní povahou, takže je důležité se chovat respektujícím způsobem a vyvarovat se kontroverzních témat.\u003C\u002Fp\u003E",coordinates:[{latitude:34.6401,longitude:50.8764}],biggest:g,type_place:h,id_image_cover:a,id_image_hero:a,affiliate:[{name:d,value:e}],alerts:a,zoom:[{google:i,booking:f}]},{id:1052,id_state:c,name:"Tabríz",population:1349922,area:317,altitude:1365,slug:"tabriz",information_chatgpt:"\u003Cp\u003ETabríz je historické a kulturní centrum v severozápadním Íránu. Leží na úpatí pohoří Sahand a je hlavním městem provincie Východní Ázerbájdžán. Město se pyšní bohatým dědictvím, které sahá až do starověku.\u003C\u002Fp\u003E\n\u003Cp\u003EJedním z nejvýznamnějších symbolů Tabrízu je Arg-e Tabríz, což je impozantní pevnost s historií sahající až do 14. století. V minulosti sloužila jako královská rezidence a vojenská pevnost. Dnes je to oblíbená turistická atrakce, kde můžete prozkoumat zbytky zdi a nádherné výhledy na město.\u003C\u002Fp\u003E\n\u003Cp\u003EMěsto je také domovem pro jednu z největších mešit v Íránu - Mešita Tabríz. Tato impozantní budova je obklopena zdobenými minarety a je známá svým architektonickým stylem. Uvnitř mešity najdete nádherné malby, mozaiky a vyřezávané dřevěné sloupy, které zdobí interiér.\u003C\u002Fp\u003E\n\u003Cp\u003EV Tabrízu se také nachází Bazar Tabríz, což je jedno z nejstarších a největších tržišť v Íránu. Je plný života, barev a vůní, a nabízí širokou škálu tradičního zboží, jako jsou koření, koberce, keramika a mnoho dalšího. Procházení bazarem je skvělý způsob, jak poznat místní kulturu a ochutnat místní kulinářské speciality.\u003C\u002Fp\u003E\n\u003Cp\u003EJinou zajímavou atrakcí je Čarandabský most, který spojuje oba břehy řeky Aji Čaj. Tento historický most byl postaven v 15. století a je známý svými oblouky a kamennými reliéfy. Je ideálním místem pro procházku a užívání si malebného výhledu na okolní krajinu.\u003C\u002Fp\u003E\n\u003Cp\u003ETabríz je rovněž známý svým uměleckým dědictvím. Město hostí jednu z nejprestižnějších uměleckých škol v Íránu, která se specializuje na tradiční perský koberec. Místní umělci jsou mistři ve výrobě ručně tkaných koberců s ohromujícími vzory a jemnými detaily.\u003C\u002Fp\u003E\n\u003Cp\u003EV současné době Tabríz kombinuje svou bohatou historii s moderním rozvojem. Město má dobře vyvinutou infrastrukturu, moderní obchodní centra a vynikající ubytovací možnosti pro turisty. Navštěvování Tabrízu je nezapomenutelným zážitkem, který vám umožní objevit krásy íránské kultury a tradice.\u003C\u002Fp\u003E",coordinates:[{latitude:38.0806,longitude:46.2919}],biggest:g,type_place:h,id_image_cover:a,id_image_hero:a,affiliate:[{name:d,value:e}],alerts:a,zoom:[{google:i,booking:f}]},{id:y,id_state:c,name:"Teherán",population:8154051,area:730,altitude:1200,slug:"teheran",information_chatgpt:"\u003Cp\u003ETeherán je hlavním městem Íránu a nachází se v severní části země. Je to jedno z nejlidnatějších měst na Blízkém východě s přibližně 9 miliony obyvatel. Jeho historie sahá až do starověku, ale město se výrazně rozrostlo v průběhu 20. století.\u003C\u002Fp\u003E\n\u003Cp\u003EJednou z hlavních turistických atrakcí v Teheránu je Golestan palác, který je součástí historického centra města. Tento komplex zahrnuje různé paláce, zahrady a muzea. Další zajímavé místo je Národní muzeum Íránu, kde lze nalézt rozsáhlou sbírku umění, archeologických nálezů a historických artefaktů.\u003C\u002Fp\u003E\n\u003Cp\u003EPokud máte zájem o nákupy, můžete navštívit Grand Bazaar, který je jedním z největších trhů na světě. Zde si můžete prohlédnout různé obchody nabízející šperky, koberce, koření, oděvy a mnoho dalšího. Dále je tu také moderní nákupní centrum Tehran Mega Mall, které nabízí mnoho známých značek a restaurací.\u003C\u002Fp\u003E\n\u003Cp\u003EJedním z symbolů města je Azadi Tower, ikonická věž postavená v roce 1971. Slouží jako památník a vyhlídková věž, ze které se naskýtá úchvatný výhled na město. Další zajímavou památkou je Milad Tower, která je s výškou přes 400 metrů nejvyšší budovou v Íránu.\u003C\u002Fp\u003E\n\u003Cp\u003ETeherán je také známý svými rozsáhlými parky a zahradami. Lze navštívit například Park Mellat, který nabízí pěkné procházky a různé sportovní aktivity. Kultivovaná zahrada Bagh-e Melli je dalším oblíbeným místem, kde můžete relaxovat a užívat si klidu.\u003C\u002Fp\u003E\n\u003Cp\u003EPokud se zajímáte o kulturu a historii, můžete navštívit divadelní představení v Divadle Vahdat nebo se zastavit v Sa'dabad Palace, bývalé rezidenci Íránských králů. Město také nabízí bohatou gastronomickou scénu, kde můžete ochutnat tradiční íránská jídla jako je kebab, džudžeh kabab nebo různé druhy pilafu.\u003C\u002Fp\u003E\n\u003Cp\u003ETeherán je fascinujícím městem, které kombinuje historii, kulturu a moderní život. Je to důležitá politická, ekonomická a kulturní metropole Íránu, která nabízí návštěvníkům širokou škálu zážitků.\u003C\u002Fp\u003E",coordinates:[{latitude:35.6892,longitude:51.389}],biggest:g,type_place:h,id_image_cover:a,id_image_hero:a,affiliate:[{name:d,value:e}],alerts:a,zoom:[{google:i,booking:f}]},{id:1053,id_state:c,name:"Šíráz",population:1327197,area:240,altitude:1486,slug:"siraz",information_chatgpt:"\u003Cp\u003EŠíráz je historické město ležící v jižní části Íránu, nedaleko pohoří Zagros. Je považováno za kulturní centrum a je jedním z nejstarších měst v zemi. Jeho historie sahá až do 6. tisíciletí př. n. l., kdy zde vzniklo osídlení.\u003C\u002Fp\u003E\n\u003Cp\u003EMěsto je známé svou bohatou historií a množstvím památek. Jednou z nejvýznamnějších atrakcí je Národní muzeum Šírázu, které hostí rozsáhlou sbírku uměleckých děl a historických artefaktů. Mezi další významné památky patří Persepolis, bývalé hlavní město starověké Persie, které se nachází nedaleko Šírázu. Persepolis je zapsáno na seznamu světového dědictví UNESCO a je považováno za jedno z nejvýznamnějších archeologických nalezišť v Íránu.\u003C\u002Fp\u003E\n\u003Cp\u003EŠíráz je také známý svými zahradami. Například Eramská zahrada, která je jednou z nejkrásnějších perských zahrad, nabízí návštěvníkům malebné procházky mezi květinami, fontánami a historickými budovami. Další zajímavou zahradou je Zahrada Dolat-Abad, která je proslulá svým vodním mlýnem a velkým cypřišem, který je starý přes 450 let.\u003C\u002Fp\u003E\n\u003Cp\u003EKromě historie a přírodních krás město nabízí i bohatý kulturní život. Je známé svými básníky a spisovateli, jako například Háfiz nebo Sa'dí. Šíráz je rovněž významným vzdělávacím centrem s mnoha univerzitami a školami.\u003C\u002Fp\u003E\n\u003Cp\u003ECo se týče kulinářských specialit, město je známé pro svou bohatou perskou kuchyni. Mezi nejznámější pokrmy patří kebab, adas polo (rýže s červenou čočkou), džudžeh kebab (kachní maso na rožni) a faloodeh (dezert z rýžových nudlí se sirupem).\u003C\u002Fp\u003E\n\u003Cp\u003EŠíráz je fascinující destinací pro cestovatele, kteří si přejí poznat historii, kulturu a přírodní krásy Íránu. S jeho bohatou historií a atrakcemi se stává jedním z nejoblíbenějších míst k návštěvě v zemi.\u003C\u002Fp\u003E",coordinates:[{latitude:29.5918,longitude:52.5837}],biggest:g,type_place:h,id_image_cover:a,id_image_hero:a,affiliate:[{name:d,value:e}],alerts:a,zoom:[{google:i,booking:f}]}],videos:[],posts:[],imagePlace:a,imageState:[{id:l,name:v,source:"\u002Fworld\u002Fstates\u002F",date_create:"2023-05-04T07:17:51.463854+00:00",type:x,author:"pixabay.com"}],imagesCities:a,imagesVideos:a,imagesPosts:a}],fetch:{},mutations:void 0}}(null,"05-2023",108,"booking",true,11,"yes","city",10,3,"2%",393,1057,"Orumíje",736224,75,1320,"orumije","\u003Cp\u003EOrumíje je město ležící v severozápadní části Íránu. Je hlavním městem provincie Západní Ázerbájdžán. Město je známé svou bohatou historií a kulturním dědictvím. Orumíje se nachází v pohoří Zagros, které poskytuje nádhernou kulisu pro tuto oblast.\u003C\u002Fp\u003E\n\u003Cp\u003EJednou z hlavních atrakcí v Orumíje je jezero Urmia, které je jedním z největších slaných jezer na světě. Jezero je obklopeno nádhernou krajinou a nabízí návštěvníkům možnost se koupat nebo relaxovat na pláži. Kromě jezera stojí za návštěvu také Orumíjská pevnost, která se nachází na vrcholu kopce a poskytuje úchvatný výhled na město a okolní krajinu.\u003C\u002Fp\u003E\n\u003Cp\u003EOrumíje je také známé svými historickými památkami. Mezi nejznámější patří archeologické naleziště Arg-e Orumíje, které je staré přes 2500 let. Nachází se na vrcholu kopce a nabízí návštěvníkům možnost prozkoumat starověké zdi, brány a paláce. Další zajímavou památkou je modrá mešita Kabud, která je známá svými krásně zdobenými modrými kachlíky.\u003C\u002Fp\u003E\n\u003Cp\u003EKulturní scéna v Orumíje je živá a plná aktivit. Město je domovem různých trhů a bazárů, kde můžete objevovat místní řemeslné výrobky a ochutnat tradiční kuchyni. Lidé zde jsou známí svou pohostinností a přátelským přístupem k návštěvníkům.\u003C\u002Fp\u003E\n\u003Cp\u003EOrumíje je fascinující destinací pro každého, kdo má zájem o historii, kulturu a přírodu. S kombinací svých historických památek, jezera Urmia a krásného okolního prostředí nabízí mnoho možností pro dobrodružství a objevování.\u003C\u002Fp\u003E",37.5527,45.0761,"iran","1%","state",1048,5)));