__NUXT_JSONP__("/jidlo", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w,x,y,z){return {data:[{foods:[{id:2,slug:q,created_at:"2023-05-24T15:39:09.974074+00:00",name:"Bramboráky",description:"Bramboráky jsou tradiční český pokrm, který se připravuje z jemně strouhaných brambor, cibule, mouky, vajec a koření. Směs se tvaruje do placek a smaží se na pánvi dozlatova. Bramboráky se často podávají s kyselým zelím nebo se zakysanou smetanou.",ingredients:a," recipe":a,id_image_cover:g,id_image_hero:g,ids_states:[{id:e}]},{id:7,slug:r,created_at:"2023-05-24T15:43:25.587266+00:00",name:"Couscous",description:"Couscous je další tradiční marocké jídlo, které se stalo oblíbeným po celém světě. Jedná se o semolinovou strouhu, která se podává s omáčkou z masového vývaru, zeleniny a často i s kousky masa (jako jsou kousky kuřete, hovězího nebo jehněčího).",ingredients:a," recipe":a,id_image_cover:h,id_image_hero:h,ids_states:[{id:f}]},{id:4,slug:s,created_at:"2023-05-24T15:39:58.822847+00:00",name:"Guláš",description:"Guláš je tradiční české jídlo, které pochází z maďarské kuchyně. Jedná se o husté a aromatické masové jídlo, obvykle z hovězího masa, které se dusí v kořeněném omáčce s cibulí, paprikou a dalšími přísadami. Guláš se často podává s houskovým knedlíkem nebo bramborovými knedlíky.",ingredients:a," recipe":a,id_image_cover:i,id_image_hero:i,ids_states:[{id:e}]},{id:8,slug:t,created_at:"2023-05-24T15:44:06.603262+00:00",name:"Harira",description:"Harira je tradiční marocká polévka, která se často konzumuje během měsíce ramadánu. Je vyrobená z cizrny, červeného nebo hnědého loupavého hrachu, rajčat, cibule, koření a bylinek. Může také obsahovat hovězí nebo kuřecí maso.",ingredients:a," recipe":a,id_image_cover:j,id_image_hero:j,ids_states:[{id:f}]},{id:10,slug:u,created_at:"2023-05-24T15:44:46.502081+00:00",name:"Mechoui",description:"Mechoui je tradiční marocké jídlo, které se skládá z celé pečené jehněčího masa. Jehněčí se tradičně peče na otevřeném ohni nebo v peci a dochází k němu pomalému vaření, aby se maso stalo měkké a šťavnaté. Mechoui se často podává s chlebem a různými omáčkami.",ingredients:a," recipe":a,id_image_cover:k,id_image_hero:k,ids_states:[{id:f}]},{id:9,slug:v,created_at:"2023-05-24T15:44:25.929805+00:00",name:"Pastilla",description:"Pastilla (také známá jako bastilla nebo b'stilla) je tradiční slané jídlo, které kombinuje sladké a slané chutě. Je to druh listového těsta plněného nasekaným masem (nejčastěji holoubě, ale také se používá kuřecí maso), mandlovými plátky, vajíčky a kořením. Pastilla je často posypána cukrem a skořicí.",ingredients:a," recipe":a,id_image_cover:l,id_image_hero:l,ids_states:[{id:f}]},{id:5,slug:w,created_at:"2023-05-24T15:40:24.497574+00:00",name:"Smažený sýr",description:"Smažený sýr je oblíbeným tradičním českým jídlem. Jedná se o kousek sýra, obvykle eidamu nebo hermelínu, který je obalen v těstíčku a smažený dozlatova. Smažený sýr se často podává s bramborovým salátem, hranolkami nebo chlebem a tatarskou omáčkou.",ingredients:a," recipe":a,id_image_cover:m,id_image_hero:m,ids_states:[{id:e}]},{id:1,slug:x,created_at:"2023-05-24T15:37:20.356714+00:00",name:"Svíčková na smetaně",description:"Jedná se o jedno z nejoblíbenějších českých jídel. Toto jídlo zahrnuje vařený hovězí nebo vepřový rošt, který je podáván s omáčkou vyrobenou z koření, zeleniny, smetany a citronové šťávy. Obvykle se podává s knedlíkem, brusinkami a šlehačkou.",ingredients:a," recipe":a,id_image_cover:n,id_image_hero:n,ids_states:[{id:e}]},{id:6,slug:y,created_at:"2023-05-24T15:43:03.173585+00:00",name:"Tajine",description:"Tajine je jedním z nejznámějších marockých jídel. Jedná se o druh keramického hrnce, ve kterém se připravuje mnoho různých pokrmů. Nejčastěji se v tajine vaří maso (například kuřecí, jehněčí nebo ryby) spolu s různými druhy zeleniny, kořením a sušenými plody.",ingredients:a," recipe":a,id_image_cover:o,id_image_hero:o,ids_states:[{id:f}]},{id:3,slug:z,created_at:"2023-05-24T15:39:37.187283+00:00",name:"Vepřo-knedlo-zelo",description:"Toto jídlo je jedním z nejznámějších tradičních českých jídel. Skládá se z pečeného vepřového masa, houskových knedlíků a dušeného zelí. Tato kombinace je velmi oblíbená v české kuchyni.",ingredients:a," recipe":a,id_image_cover:p,id_image_hero:p,ids_states:[{id:e}]}],images:[{id:k,name:u,source:b,date_create:"2023-05-26T08:51:58.647774+00:00",type:c,author:"https:\u002F\u002Fcommons.wikimedia.org\u002Fwiki\u002FFile:Mechoui-01.jpg"},{id:l,name:v,source:b,date_create:"2023-05-26T08:51:46.684487+00:00",type:c,author:d},{id:j,name:t,source:b,date_create:"2023-05-26T08:51:37.838395+00:00",type:c,author:"https:\u002F\u002Fcommons.wikimedia.org\u002Fwiki\u002FFile:Harira.png"},{id:h,name:r,source:b,date_create:"2023-05-26T08:51:29.954349+00:00",type:c,author:d},{id:o,name:y,source:b,date_create:"2023-05-26T08:51:21.955227+00:00",type:c,author:d},{id:m,name:w,source:b,date_create:"2023-05-26T08:51:14.129675+00:00",type:c,author:d},{id:i,name:s,source:b,date_create:"2023-05-26T08:49:17.768859+00:00",type:c,author:d},{id:p,name:z,source:b,date_create:"2023-05-26T08:48:57.330794+00:00",type:c,author:d},{id:g,name:q,source:b,date_create:"2023-05-26T08:48:43.538628+00:00",type:c,author:d},{id:n,name:x,source:b,date_create:"2023-05-26T08:48:29.008463+00:00",type:c,author:"https:\u002F\u002Fwww.ilustracnifotky.cz\u002Fjidlo--14382084211090--1090"}]}],fetch:{},mutations:void 0}}(null,"\u002Ffoods\u002F","food","pixabay.com",56,137,494,499,496,500,502,501,497,493,498,495,"bramboraky","couscous","gulas","harira","mechoui","pastilla","smazeny-syr","svickova-na-smetane","tajine","vepro-knedlo-zelo")));