__NUXT_JSONP__("/svet/mesto/altai", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t){return {data:[{place:[{id:h,id_state:i,name:j,population:27320,area:59.62,altitude:782,slug:k,information_chatgpt:a,coordinates:[{latitude:46.3761,longitude:96.2583}],biggest:"yes",type_place:b,id_image_cover:a,id_image_hero:a,affiliate:[{name:l,value:m}],alerts:a,zoom:[{google:10,booking:11}]}],placeState:[{id:i,tld:".mn",name:"Mongolsko",area:1564110,population:3325282,slug:n,id_continent:e,information_chatgpt:"\u003Cp\u003EMongolsko je stát v Asii, který je známý svými nekonečnými stepmi, divokými zvířaty, horskými vrcholky a bohatou historií nomádských kmenů. Nachází se mezi Ruskem a Čínou, a jeho rozloha činí přibližně 1,5 milionu kilometrů čtverečních. Hlavním městem je Ulaanbaatar, kde žije více než polovina obyvatel země.\u003C\u002Fp\u003E\n\u003Cp\u003EMongolsko je domovem jednoho z největších koňských kmenů na světě a jeho historie sahá až do dob vlády Džingischána, zakladatele Mongolské říše. Turisté mohou navštívit chrámy a kláštery, jako například klášter Erdene Zuu nebo Khamar Dambii, které jsou důležitými zastávkami na historické trase Sedačkové říše. Kromě toho mohou turisté sledovat tradiční nomádský život, včetně koňských závodů a obřadů.\u003C\u002Fp\u003E\n\u003Cp\u003EPro milovníky přírody a dobrodružství je Mongolsko ideální destinací. Země má mnoho chráněných oblastí, jako například národní park Gorkhi-Terelj, Altaj Tavan Bogd National Park a Gobi Gurvansaikhan National Park, které nabízejí úchvatné scenérie a zvířata, jako jsou kamzíci, levharti, vlci a dokonce i kamelí velbloudi.\u003C\u002Fp\u003E\n\u003Cp\u003ECestování po Mongolsku může být náročné, zejména mimo větší města. Nejlepší způsob, jak se dostat do vzdálených oblastí, je s pomocí odborného průvodce nebo zkušeného řidiče. Země má také mnoho festivalů a oslav, které jsou pro turisty atraktivní a nabízejí možnost poznat místní kulturu a tradice.\u003C\u002Fp\u003E\n\u003Cp\u003EMongolsko je země s bohatou historií, kulturou a přírodou, která je pro turisty nevšední a exotickou destinací. Je to země, která může poskytnout nezapomenutelné zážitky a dobrodružství, a proto je ideálním cílem pro ty, kteří hledají něco mimořádného.\u003C\u002Fp\u003E",currency_code:"MNT",currency_name:"mongolský tugrik",money_prices:[{name:"Taxi (cena za km)",value:800,date_update:c},{name:"Hlavní jídlo",value:o,date_update:c},{name:"Chleba",value:p,date_update:c},{name:"Mléko",value:1200,date_update:c},{name:"Voda (500ml)",value:500,date_update:c},{name:"Espresso",value:2500,date_update:c},{name:"Zmrzlina (jeden kopeček)",value:p,date_update:c},{name:"Pivo",value:3000,date_update:c},{name:"Krabička cigaret",value:7000,date_update:c},{name:"Big Mac",value:o,date_update:c},{name:"Coca Cola (500ml)",value:1500,date_update:c}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:"112",date_update:d},{name:"Policie",value:"102",date_update:d},{name:"Hasiči",value:"101",date_update:d},{name:"Zdravotnická záchranná služba",value:"103",date_update:d}],phone_prefix:"+976",coordinates:[{latitude:"46.8625",longitude:"103.8467"}],people_religion:[{name:"Buddhismus",value:"50%"},{name:"Šamanismus",value:"40%"},{name:"Lamaismus",value:"5%"},{name:"Křesťanství",value:f},{name:"Islám",value:q},{name:"Judaismus",value:r},{name:"Jiná náboženství",value:r},{name:"Bez vyznání",value:f}],people_nationality:[{name:"Chalchové",value:"81%"},{name:"Oiratové",value:"4%"},{name:"Bajadové",value:"3%"},{name:"Durvudové",value:f},{name:"Torguudové",value:f},{name:"Uzemčinové",value:q},{name:"Jiné národnosti",value:"7%"}],visitors_entry:[{name:"Občanský průkaz",value:"Pro vstup do Mongolska není možné použít pouze občanský průkaz. Je nutné mít platný cestovní pas.",date_update:d},{name:"Cestovní pas",value:"Pro vstup do Mongolska je nutné mít platný cestovní pas.",date_update:d},{name:"Vízum",value:"Pro vstup do Mongolska je nutné mít vízum. Pro občany České republiky platí vízová povinnost a je třeba si vízum zajistit předem na konzulátu Mongolska. Víza se vydávají většinou na dobu 30 nebo 90 dnů a jsou platná 90 dnů od data vydání.",date_update:d},{name:"Očkování",value:"Pro vstup do Mongolska nejsou povinné žádné očkování. Nicméně je doporučeno být očkován proti žluté zimnici, hepatitidě A a B a tetanu. Dále se doporučuje očkování proti klíšťové encefalitidě v závislosti na plánovaných aktivitách a oblastech pobytu v Mongolsku.",date_update:d},{name:"Cestovní pojištění",value:"Při cestování do Mongolska se doporučuje mít sjednané cestovní pojištění, včetně zdravotního pojištění. Nicméně vstup do země není vázán na toto pojištění a jeho předložení není požadováno při překročení hranic.",date_update:d},{name:"Bezpečnostní situace",value:"V Mongolsku dochází k příležitostné kriminalitě a teroristickým útokům. Před cestou do Mongolska se doporučuje pečlivě zvážit bezpečnostní rizika a sledovat aktuální bezpečnostní situaci na místě. Při cestování se doporučuje být obezřetný a přijímat opatření pro minimalizaci rizik.",date_update:d},{name:"Další informace",value:"Při cestování do Mongolska je třeba mít na paměti, že se jedná o zemi s rozsáhlými a odlehlými oblastmi. Při cestování do těchto oblastí je třeba být dobře připraven a vybaven na případné mimořádné situace. Dále je třeba dodržovat zvyklosti a kulturu místního obyvatelstva. Například nežádat o jídlo nebo nápoje pravou rukou a neukazovat prstem na lidi, budovy nebo předměty.",date_update:d}],type_place:s,id_image_cover:g,id_image_hero:g,id_city_main:t,affiliate:[{name:l,value:m}],mpz:"MN",alerts:a,zoom:[{google:4,booking:5}],ids_neighboring_countries:a}],placeContinent:[{id:e,slug:"asie",name:"Asie",area:44580000,population:4630000000,population_density:104.03,number_states:50,information_chatgpt:"\u003Cp\u003EAsie se rozkládá na ploše téměř 44,6 milionu kilometrů čtverečních a žije zde více než 4,5 miliardy lidí, což představuje téměř 60 % celkového počtu obyvatel planety. Kontinent zahrnuje celkem 48 zemí, mezi největší patří Rusko, Čína, Indie, Japonsko a Indonésie.\u003C\u002Fp\u003E\n\u003Cp\u003EAsie je domovem mnoha významných světových kultur, jako jsou hinduismus, buddhismus, islám a taoismus. V každé zemi se můžeš setkat s odlišnými tradicemi, zvyky a jazyky. Mezi nejnavštěvovanější turistické destinace patří například Japonsko s jeho futuristickými městy, Thajsko s bohatou kulturou a krásnými plážemi, nebo Indonésie s neuvěřitelnou přírodou a množstvím ostrovů.\u003C\u002Fp\u003E\n\u003Cp\u003EAsie také nabízí mnoho kulturních a historických památek, jako je Velká čínská zeď, Taj Mahal v Indii, buddhistické chrámy v Thajsku a mnoho dalšího. Zajímavou zkušeností může být i návštěva tradičních trhů, kde se dají ochutnat místní speciality a nakoupit různé exotické produkty.\u003C\u002Fp\u003E\n\u003Cp\u003EAsie má však také své výzvy a problémy, jako jsou například růst městských oblastí, nedostatek vody a znečištění ovzduší. Mnoho zemí se snaží najít řešení těchto problémů a přizpůsobit se moderním trendům, aby mohly udržet krok s ostatními zeměmi světa.\u003C\u002Fp\u003E\n\u003Cp\u003EPokud tě láká cestování po Asii, měl by sis předem zajistit víza a informovat se o místních zvycích a tradicích. Rozhodně se ti však naskytne možnost prozkoumat jedinečnou kulturu, přírodu a historii tohoto fascinujícího kontinentu.\u003C\u002Fp\u003E\u003Cp\u003EAsie má také neuvěřitelně bohatou gastronomii, která se v jednotlivých zemích a regionech liší. Mezi nejznámější asijské pokrmy patří sushi, různé druhy nudlí, curry, dim sum, pho a další. Kromě toho se v Asii pěstuje mnoho exotických plodin, jako jsou duriany, mangostany, rambutany a další ovoce, které můžeš ochutnat pouze zde.\n\u003C\u002Fp\u003E\u003Cp\u003E\nAsie je také známá svými svátky a tradicemi, jako je například čínský Nový rok, Diwali v Indii, nebo Songkran v Thajsku. Tyto svátky jsou většinou spojeny s různými rituály a zvyky, které se v průběhu staletí vyvinuly a jsou dodnes respektovány.\n\u003C\u002Fp\u003E\u003Cp\u003E\nKromě toho má Asie mnoho přírodních krás, jako jsou Himálaje, pouště Gobi a Thar, nebo tropické pralesy v Indonésii a Malajsii. V Asii se také nachází mnoho národních parků a chráněných oblastí, kde žijí ohrožené druhy zvířat, jako jsou tygři, pandy, sloni a další.\n\u003C\u002Fp\u003E\u003Cp\u003E\nZkrátka, Asie je kontinent plný kontrastů a neuvěřitelných zážitků, které stojí za to objevovat. Budeš-li mít příležitost navštívit některou z asijských zemí, neváhej a vydaj se na dobrodružnou cestu plnou nových objevů a nezapomenutelných zážitků.\u003C\u002Fp\u003E",coordinates:[{latitude:47.751076,longitude:92.475286}],type_place:"continent",id_image_cover:e,id_image_hero:e,zoom:[{google:2,booking:e}]}],placesCities:[{id:h,id_image_cover:a,slug:k,type_place:b,name:j},{id:1447,id_image_cover:a,slug:"bulgan",type_place:b,name:"Bulgan"},{id:1441,id_image_cover:a,slug:"choibalsan",type_place:b,name:"Choibalsan"},{id:1440,id_image_cover:a,slug:"darkhan",type_place:b,name:"Darkhan"},{id:1439,id_image_cover:a,slug:"erdenet",type_place:b,name:"Erdenet"},{id:1442,id_image_cover:a,slug:"khovd",type_place:b,name:"Khovd"},{id:1445,id_image_cover:a,slug:"suhbaatar",type_place:b,name:"Sühbaatar"},{id:t,id_image_cover:a,slug:"ulaanbaatar",type_place:b,name:"Ulaanbaatar"},{id:1446,id_image_cover:a,slug:"zuunkharaa",type_place:b,name:"Züünkharaa"},{id:1443,id_image_cover:a,slug:"olgii",type_place:b,name:"Ölgii"}],videos:[],posts:[],imagePlace:a,imageState:[{id:g,name:n,source:"\u002Fworld\u002Fstates\u002F",date_create:"2023-05-03T20:14:00.749527+00:00",type:s,author:"pixabay.com"}],imagesCities:a,imagesVideos:a,imagesPosts:a}],fetch:{"OrganismsoPlaceTeaserSpotsComponent:0":{spots:[],images:a},"OrganismsoSidebarListComponent:0":{events:[],images:a}},mutations:void 0}}(null,"city","09-2021","05-2023",3,"2%",320,1444,147,"Altai","altai","booking",true,"mongolsko",10000,1000,"1%","\u003C1%","state",1438)));