__NUXT_JSONP__("/svet/mesto/hlatikulu", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u){return {data:[{place:[{id:j,id_state:k,name:l,population:3022,area:2.27,altitude:801,slug:m,information_chatgpt:a,coordinates:[{latitude:-26.9833,longitude:31.4167}],biggest:"yes",type_place:b,id_image_cover:a,id_image_hero:a,affiliate:[{name:n,value:o}],alerts:a,zoom:[{google:p,booking:11}]}],placeState:[{id:k,tld:".sz",name:"Svazijsko",area:17364,population:1160164,slug:q,id_continent:f,information_chatgpt:"\u003Cp\u003ESvazijsko je menší země v jižní Africe, která se nachází mezi Jihoafrickou republikou a Mosambikem. Přestože se jedná o jednu z nejmenších afrických zemí, má mnoho zajímavých turistických atrakcí a kulturních zážitků.\u003C\u002Fp\u003E\n\u003Cp\u003EJedním z hlavních lákadel Svazijska je jeho přírodní krása. Země má mnoho národních parků a rezervací, které nabízejí možnost sledovat divoká zvířata jako jsou sloni, lvi, nosorožci a žirafy. Největším národním parkem v zemi je Mlilwane Wildlife Sanctuary, který nabízí krásné scenérie a mnoho turistických aktivit jako jsou horské túry, cyklistika a jízda na koni.\u003C\u002Fp\u003E\n\u003Cp\u003EPro ty, kteří se zajímají o kulturu, je v Svazijsku mnoho příležitostí prozkoumat tradiční svazijské vesnice a získat přehled o místních obyčejích a zvycích. Mezi nejvýznamnější kulturní akce patří Umhlanga, tradiční festival, který se koná v srpnu a je spojený s obřadem, při kterém mladé dívky přinášejí králi trávu.\u003C\u002Fp\u003E\n\u003Cp\u003EV posledních letech se Svazijsko také stalo stále populárnějším cílem pro adrenalinové aktivity jako je bungee jumping, rafting a zip-lining. Tyto aktivity jsou obzvláště populární v oblasti Ezulwini Valley, která je také známá pro své luxusní hotely a restaurace.\u003C\u002Fp\u003E\n\u003Cp\u003EPřestože Svazijsko je relativně bezpečnou zemí pro turisty, je důležité být obezřetný a informovaný o aktuální situaci. Před cestou je třeba zkontrolovat bezpečnostní doporučení a případně se poradit s místními odborníky.\u003C\u002Fp\u003E",currency_code:"SZL",currency_name:"Swazi lilangeni",money_prices:[{name:"Taxi (cena za km)",value:p,date_update:c},{name:"Hlavní jídlo",value:95,date_update:c},{name:"Chleba",value:6.5,date_update:c},{name:"Mléko",value:12.5,date_update:c},{name:"Voda (500ml)",value:r,date_update:c},{name:"Espresso",value:13.5,date_update:c},{name:"Zmrzlina (jeden kopeček)",value:r,date_update:c},{name:"Pivo",value:17.5,date_update:c},{name:"Krabička cigaret",value:29,date_update:c},{name:"Bic Mac",value:a,date_update:c},{name:"Coca Cola (500ml)",value:7,date_update:c}],phone_numbers_emergency:[{name:"Tísňová linka",value:"112",date_update:d},{name:"Policie",value:"158",date_update:d},{name:"Hasiči",value:"150",date_update:d},{name:"Zdravotnická záchranná služba",value:"155",date_update:d}],phone_prefix:"+268",coordinates:[{latitude:"-26.5225",longitude:"31.4659"}],people_religion:[{name:"Křesťanství",value:"90%",date_update:d},{name:"Islám",value:g,date_update:d},{name:"Jiná náboženství",value:g,date_update:d},{name:"Bez vyznání",value:"6%",date_update:d}],people_nationality:[{name:"Swazi",value:"95%",date_update:h},{name:"Zulu",value:"3%",date_update:h},{name:"Jiná",value:g,date_update:h}],visitors_entry:[{name:"Občanský průkaz",value:"Pro vstup do Svazijska je potřeba cestovního pasu, občanský průkaz není platným dokladem.",date_update:e},{name:"Cestovní pas",value:"Pro vstup do Svazijska je potřeba platný cestovní pas.",date_update:e},{name:"Vízum",value:"Pro vstup do Svazijska potřebují občané České republiky vízum. Podrobnosti o postupu získání víza jsou k dispozici na stránkách velvyslanectví Svazijska.",date_update:e},{name:"Očkování",value:"Pro vstup do Svazijska není vyžadováno žádné očkování. Přestože vakcinace není povinná, doporučuje se zkontrolovat očkování proti běžným chorobám před cestou.",date_update:e},{name:"Cestovní pojištění",value:"Pro vstup do Svazijska není nutné mít cestovní ani zdravotní pojištění. Nicméně, doporučuje se sjednat si pojištění před cestou, protože náklady na lékařskou péči v zahraničí mohou být vysoké.",date_update:e},{name:"Bezpečností situace",value:"Vzhledem k politické situaci v zemi a vysoké úrovni kriminality by měli cestovatelé být obezřetní a vyhnout se nebezpečným oblastem. Doporučuje se informovat o aktuální situaci před cestou a sledovat zpravodajské kanály.",date_update:e},{name:"Další informace",value:"V Svazijsku platí levý provoz, místní měna je lilangeni. Je vhodné mít při sobě hotovost v místní měně nebo kreditní kartu. Vzhledem k podnebí v zemi, je vhodné vzít si na cestu vhodné oblečení a případně i repelent proti hmyzu.",date_update:e}],type_place:s,id_image_cover:i,id_image_hero:i,id_city_main:t,affiliate:[{name:n,value:o}],mpz:"SZ",alerts:a,zoom:[{google:u,booking:u}],ids_neighboring_countries:a}],placeContinent:[{id:f,slug:"afrika",name:"Afrika",area:30370000,population:1340000000,population_density:44.12,number_states:54,information_chatgpt:"\u003Cp\u003EAfrika je největší kontinent na Zemi a rozkládá se na ploše přesahující 30 milionů kilometrů čtverečních. Nachází se zde více než 50 zemí a žije zde více než 1,3 miliardy lidí. \u003C\u002Fp\u003E\n\u003Cp\u003EJižní Afrika, Egypt, Maroko a Keňa patří mezi nejoblíbenější turistické cíle v Africe. V Jihoafrické republice se můžete podívat na slavné Krugerův národní park a sledovat zde život zvířat v jejich přirozeném prostředí. Egypt je zase proslulý svými pyramidami, které se nacházejí v Gíze. V Maroku můžete navštívit tradiční trhy, zatímco v Keni se můžete setkat s Masajským kmenem a podívat se na úžasnou přírodu.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má bohatou historii a kulturu. První lidé, kteří se usadili v Africe, byli loveckým a sběračským národem. Poté se v této oblasti objevily první civilizace, jako je Kúšská říše, Egyptská říše nebo Království Aksum. Afrika byla také kolonizována evropskými mocnostmi a toto období mělo značný dopad na dnešní politickou situaci na kontinentu.\u003C\u002Fp\u003E\n\u003Cp\u003EJe důležité zmínit, že Afrika má velké množství jazyků a kultur. Zahrnuje mnoho různých národů a etnických skupin, a to má vliv na různorodost obyvatelstva a kultury. Nejrozšířenějším jazykem je arabština, která se mluví především v severní Africe a v některých částech západní Afriky. Dalšími důležitými jazyky jsou svahilština, francouzština, portugalština, angličtina a mnoho dalších.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má také nádhernou přírodu. Nachází se zde nejvyšší hora světa, Kilimandžáro, která se nachází na území Tanzanie a Keňy. Kromě toho se v Africe nachází také největší pouště na světě, Sahara, a největší prales, Kongský prales. Na tomto kontinentě můžete vidět také mnoho zvířat, jako jsou sloni, lvi, gepardi, zebry a mnoho dalších druhů. V Africe se také nachází několik nejvýznamnějších řek světa, jako je Nil, Kongo, Niger a Zambezi.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je bohužel také známá pro své problémy. Mnoho zemí se potýká s chudobou, nedostatkem potravin a vody, nemocemi a válečnými konflikty. Mnoho afrických zemí závisí na zemědělství a rybolovu, což však často není dostatečně výnosné, aby zabezpečilo potřeby obyvatelstva. Kromě toho jsou některé země v Africe trvale postižené extrémními přírodními katastrofami, jako jsou sucha, povodně a bouře.\u003C\u002Fp\u003E\n\u003Cp\u003ENa druhou stranu se v Africe také nachází mnoho lidí, kteří se snaží změnit situaci a zlepšit životy svých spoluobčanů. Vznikají neziskové organizace, které se snaží pomáhat při řešení těchto problémů a zlepšovat kvalitu života v mnoha afrických zemích. Zároveň se v posledních letech objevují nové možnosti pro ekonomický růst a rozvoj, jako jsou technologické start-upy a zahraniční investice.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika je fascinující kontinent, který nabízí mnoho zážitků a zároveň v sobě ukrývá mnoho výzev. Pokud se rozhodnete navštívit Afriku, můžete se těšit na krásnou přírodu, bohatou kulturu a nezapomenutelné zážitky. Je však také důležité být obezřetný a připravený na místní podmínky, protože některé oblasti mohou být nebezpečné kvůli konfliktům nebo zločinnosti.\u003C\u002Fp\u003E",coordinates:[{latitude:1.650801,longitude:17.371642}],type_place:"continent",id_image_cover:f,id_image_hero:f,zoom:[{google:2,booking:3}]}],placesCities:[{id:j,id_image_cover:a,slug:m,type_place:b,name:l},{id:2105,id_image_cover:a,slug:"lobamba",type_place:b,name:"Lobamba"},{id:2107,id_image_cover:a,slug:"malkerns",type_place:b,name:"Malkerns"},{id:2104,id_image_cover:a,slug:"manzini",type_place:b,name:"Manzini"},{id:t,id_image_cover:a,slug:"mbabane",type_place:b,name:"Mbabane"},{id:2109,id_image_cover:a,slug:"mhlambanyatsi",type_place:b,name:"Mhlambanyatsi"},{id:2108,id_image_cover:a,slug:"mhlume",type_place:b,name:"Mhlume"},{id:2112,id_image_cover:a,slug:"nsoko",type_place:b,name:"Nsoko"},{id:2110,id_image_cover:a,slug:"piggs-peak",type_place:b,name:"Piggs Peak"},{id:2106,id_image_cover:a,slug:"siteki",type_place:b,name:"Siteki"}],videos:[],posts:[],imagePlace:a,imageState:[{id:i,name:q,source:"\u002Fworld\u002Fstates\u002F",date_create:"2023-05-03T20:14:00.749527+00:00",type:s,author:"pixabay.com"}],imagesCities:a,imagesVideos:a,imagesPosts:a}],fetch:{"OrganismsoPlaceTeaserSpotsComponent:0":{spots:[],images:a},"OrganismsoSidebarListComponent:0":{events:[],images:a}},mutations:void 0}}(null,"city","04-2023","01-2022","05-2023",5,"2%","12-2020",370,2111,213,"Hlatikulu","hlatikulu","booking",true,10,"svazijsko",6,"state",2103,8)));