__NUXT_JSONP__("/svet/stat/turkmenistan", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p){return {data:[{place:[{id:i,tld:".tm",name:"Turkmenistán",area:491210,population:6031200,slug:j,id_continent:e,information_chatgpt:"\u003Cp\u003ETurkmenistán je stát nacházející se v Střední Asii. Má velmi bohatou historii, která se datuje až do antického období. V moderní době se Turkmenistán proslavil zejména jako producent zemního plynu, což mu zajistilo významné příjmy.\u003C\u002Fp\u003E\n\u003Cp\u003ETurkmenistán je domovem mnoha překrásných turistických atrakcí. Nejznámější z nich je zřejmě Darvaza, také známá jako \"Vrata pekla\". Jedná se o ohnivou jámu, která hoří již několik desetiletí a je jedním z nejzajímavějších přírodních záznamů na světě. Mezi další turistické atrakce v Turkmenistánu patří například národní park Kugitang, pohoří Kopet Dag a historická oáza Merv, která byla zařazena na seznam Světového dědictví UNESCO.\u003C\u002Fp\u003E\n\u003Cp\u003EBohužel, Turkmenistán se často kritizuje za své nedemokratické vládnutí a porušování lidských práv. Cestování do Turkmenistánu může být poměrně obtížné, neboť tamní úřady zavedly řadu restrikcí a omezení. Například turisté musí mít předem schválenou vízovou žádost a jsou velmi omezeni v pohybu po zemi.\u003C\u002Fp\u003E\n\u003Cp\u003ENavzdory těmto obtížím může být cestování do Turkmenistánu velmi zajímavým zážitkem pro cestovatele, kteří hledají nové a exotické zážitky. Místní kultura je fascinující a gastroturistika může být velmi příjemným zážitkem díky množství tradičních pokrmů a nápojů. \u003C\u002Fp\u003E",currency_code:"TMT",currency_name:"Turkmenský manat",money_prices:[{name:"Taxi (cena za km)",value:a,date_update:b},{name:"Hlavní jídlo",value:a,date_update:b},{name:"Chleba",value:a,date_update:b},{name:"Mléko",value:a,date_update:b},{name:"Voda (500ml)",value:a,date_update:b},{name:"Espresso",value:a,date_update:b},{name:"Zmrzlina (jeden kopeček)",value:a,date_update:b},{name:"Pivo",value:a,date_update:b},{name:"Krabička cigaret",value:a,date_update:b},{name:"Big Mac",value:a,date_update:b},{name:"Coca Cola (500ml)",value:a,date_update:b}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:"112",date_update:b},{name:"Policie",value:"02",date_update:b},{name:"Hasiči",value:k,date_update:b},{name:"Zdravotnická záchranná služba",value:k,date_update:b}],phone_prefix:"+993",coordinates:[{latitude:"38.9697",longitude:"59.5563"}],people_religion:[{name:"Muslimové",value:"89%",date_update:f},{name:"Nepraktikující muslimové",value:"8%",date_update:f},{name:"Křesťané",value:"3%",date_update:f},{name:"Ostatní",value:"0%",date_update:f}],people_nationality:[{name:"Turkmené",value:"72.0%",date_update:c},{name:"Uzbeci",value:"9.2%",date_update:c},{name:"Ruští občané",value:"6.7%",date_update:c},{name:"Ázerbájdžánci",value:"2.7%",date_update:c},{name:"Arméni",value:"2.2%",date_update:c},{name:"Turečtí občané",value:"1.8%",date_update:c},{name:"Kazaši",value:"1.2%",date_update:c},{name:"Tatarské občanství",value:"1.1%",date_update:c},{name:"Ukrajinci",value:"0.5%",date_update:c},{name:"Íránci",value:"0.3%",date_update:c},{name:"Jiné národnosti",value:"2.3%",date_update:c}],visitors_entry:[{name:"Občanský průkaz",value:"Pro vstup do Turkmenistánu je potřeba mít platný cestovní doklad s fotografií, tedy cestovní pas. Občanský průkaz není pro vstup do země postačující.",date_update:b},{name:"Cestovní pas",value:"Pro vstup do Turkmenistánu je potřeba mít platný cestovní pas s minimální platností 6 měsíců ode dne vstupu do země.",date_update:b},{name:"Vízum",value:"Pro vstup do Turkmenistánu je potřeba mít vízum, které je nutné si předem zařídit na ambasádě Turkmenistánu v Praze nebo jiné země, která má s Turkmenistánem diplomatické styky. Pro občany ČR a dalších zemí EU platí vízová povinnost.",date_update:b},{name:"Očkování",value:"Pro vstup do Turkmenistánu není vyžadováno žádné specifické očkování. Nicméně, jako v každé zemi, jsou doporučována základní očkování, jako například proti hepatitidě A a B, žloutence, tetanu a dále dle doporučení odborníků. Před cestou je vhodné konzultovat očkování s odborníkem.",date_update:b},{name:"Cestovní pojištění",value:"Cestovní pojištění není vyžadováno, ale doporučuje se si jej sjednat kvůli rizikům spojeným s cestováním do zahraničí.",date_update:b},{name:"Bezpečnostní situace",value:"Turkmenistán je uzavřená země s autoritářským režimem, kde jsou porušována lidská práva a demokratické svobody. Zahraniční turisté jsou pod přísným dohledem a jejich pohyb je často omezen. Je nutné být obezřetný a informovat se o aktuální situaci před cestou do země.",date_update:b},{name:"Další informace",value:"V Turkmenistánu je zakázáno fotografovat vládní budovy, památky, mosty a další strategické objekty. Je také zakázáno se fotografovat s uniformovanými lidmi, což zahrnuje i policisty a vojáky. Zahraniční turisté jsou často sledováni a jejich pohyb je omezen. Je důležité mít s sebou dostatek hotovosti, protože v zemi není běžné používání platebních karet. Dále je nutné respektovat místní zvyklosti a náboženské zvyky.",date_update:b}],type_place:l,id_image_cover:g,id_image_hero:g,id_city_main:h,affiliate:[{name:m,value:n}],mpz:"TM",alerts:a,zoom:[{google:5,booking:6}],ids_neighboring_countries:a}],placesStatesNeighboring:a,placeContinent:[{id:e,slug:"asie",name:"Asie",area:44580000,population:4630000000,population_density:104.03,number_states:50,information_chatgpt:"\u003Cp\u003EAsie se rozkládá na ploše téměř 44,6 milionu kilometrů čtverečních a žije zde více než 4,5 miliardy lidí, což představuje téměř 60 % celkového počtu obyvatel planety. Kontinent zahrnuje celkem 48 zemí, mezi největší patří Rusko, Čína, Indie, Japonsko a Indonésie.\u003C\u002Fp\u003E\n\u003Cp\u003EAsie je domovem mnoha významných světových kultur, jako jsou hinduismus, buddhismus, islám a taoismus. V každé zemi se můžeš setkat s odlišnými tradicemi, zvyky a jazyky. Mezi nejnavštěvovanější turistické destinace patří například Japonsko s jeho futuristickými městy, Thajsko s bohatou kulturou a krásnými plážemi, nebo Indonésie s neuvěřitelnou přírodou a množstvím ostrovů.\u003C\u002Fp\u003E\n\u003Cp\u003EAsie také nabízí mnoho kulturních a historických památek, jako je Velká čínská zeď, Taj Mahal v Indii, buddhistické chrámy v Thajsku a mnoho dalšího. Zajímavou zkušeností může být i návštěva tradičních trhů, kde se dají ochutnat místní speciality a nakoupit různé exotické produkty.\u003C\u002Fp\u003E\n\u003Cp\u003EAsie má však také své výzvy a problémy, jako jsou například růst městských oblastí, nedostatek vody a znečištění ovzduší. Mnoho zemí se snaží najít řešení těchto problémů a přizpůsobit se moderním trendům, aby mohly udržet krok s ostatními zeměmi světa.\u003C\u002Fp\u003E\n\u003Cp\u003EPokud tě láká cestování po Asii, měl by sis předem zajistit víza a informovat se o místních zvycích a tradicích. Rozhodně se ti však naskytne možnost prozkoumat jedinečnou kulturu, přírodu a historii tohoto fascinujícího kontinentu.\u003C\u002Fp\u003E\u003Cp\u003EAsie má také neuvěřitelně bohatou gastronomii, která se v jednotlivých zemích a regionech liší. Mezi nejznámější asijské pokrmy patří sushi, různé druhy nudlí, curry, dim sum, pho a další. Kromě toho se v Asii pěstuje mnoho exotických plodin, jako jsou duriany, mangostany, rambutany a další ovoce, které můžeš ochutnat pouze zde.\n\u003C\u002Fp\u003E\u003Cp\u003E\nAsie je také známá svými svátky a tradicemi, jako je například čínský Nový rok, Diwali v Indii, nebo Songkran v Thajsku. Tyto svátky jsou většinou spojeny s různými rituály a zvyky, které se v průběhu staletí vyvinuly a jsou dodnes respektovány.\n\u003C\u002Fp\u003E\u003Cp\u003E\nKromě toho má Asie mnoho přírodních krás, jako jsou Himálaje, pouště Gobi a Thar, nebo tropické pralesy v Indonésii a Malajsii. V Asii se také nachází mnoho národních parků a chráněných oblastí, kde žijí ohrožené druhy zvířat, jako jsou tygři, pandy, sloni a další.\n\u003C\u002Fp\u003E\u003Cp\u003E\nZkrátka, Asie je kontinent plný kontrastů a neuvěřitelných zážitků, které stojí za to objevovat. Budeš-li mít příležitost navštívit některou z asijských zemí, neváhej a vydaj se na dobrodružnou cestu plnou nových objevů a nezapomenutelných zážitků.\u003C\u002Fp\u003E",coordinates:[{latitude:47.751076,longitude:92.475286}],type_place:"continent",id_image_cover:e,id_image_hero:e,zoom:[{google:2,booking:e}]}],placesCities:[{id:2201,id_image_cover:a,slug:"abadan",type_place:d,name:"Abadan"},{id:h,id_image_cover:a,slug:o,type_place:d,name:p},{id:2197,id_image_cover:a,slug:"balkanabat",type_place:d,name:"Balkanabat"},{id:2198,id_image_cover:a,slug:"bayramaly",type_place:d,name:"Bayramaly"},{id:2194,id_image_cover:a,slug:"dasoguz",type_place:d,name:"Daşoguz"},{id:2202,id_image_cover:a,slug:"koneurgench",type_place:d,name:"Köneürgench"},{id:2195,id_image_cover:a,slug:"mary",type_place:d,name:"Mary"},{id:2200,id_image_cover:a,slug:"tejen",type_place:d,name:"Tejen"},{id:2196,id_image_cover:a,slug:"turkmenabat",type_place:d,name:"Türkmenabat"},{id:2199,id_image_cover:a,slug:"turkmenbasy",type_place:d,name:"Türkmenbaşy"}],placeCityMain:[{id:h,id_state:i,name:p,population:1151720,area:470.53,altitude:220,slug:o,information_chatgpt:a,coordinates:[{latitude:37.9604,longitude:58.3263}],biggest:"yes",type_place:d,id_image_cover:a,id_image_hero:a,affiliate:[{name:m,value:n}],alerts:a,zoom:[{google:10,booking:11}]}],brands:[],foods:[],fauna:[],flora:[],videos:[],posts:[],imagePlace:[{id:g,name:j,source:"\u002Fworld\u002Fstates\u002F",date_create:"2023-05-03T22:13:53.42428+00:00",type:l,author:"pixabay.com"}],imagesStatesNeighboring:a,imagesCities:a,imageCityMain:a,imagesBrands:a,imagesFoods:a,imagesFauna:a,imagesFlora:a,imagesVideos:a,imagesPosts:a}],fetch:{"OrganismsoSidebarListComponent:0":{events:[],images:a}},mutations:void 0}}(null,"05-2023","12-1995","city",3,"12-2009",378,2193,222,"turkmenistan","03","state","booking",true,"asgabat","Aşgabat")));