__NUXT_JSONP__("/svet/mesto/gaya", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s){return {data:[{place:[{id:j,id_state:k,name:l,population:42616,area:76,altitude:240,slug:m,information_chatgpt:a,coordinates:[{latitude:11.8889,longitude:3.4462}],biggest:b,type_place:c,id_image_cover:a,id_image_hero:a,affiliate:[{name:n,value:o}],alerts:a,zoom:[{google:10,booking:11}]}],imagePlace:a,placeState:[{id:k,tld:".ne",name:"Niger",area:1186408,population:24206636,slug:p,id_continent:f,information_chatgpt:"\u003Cp\u003ENiger je vnitrozemský stát v západní Africe s rozlohou přes 1,2 milionu km². S populací přibližně 24 milionů obyvatel je Niger jedním z nejchudších států světa. Niger hraničí s Alžírskem na severu, Libyí na severovýchodě, Nigérií a Beninem na jihu, Burkina Faso a Mali na západě.\u003C\u002Fp\u003E\n\u003Cp\u003ENiger se skládá především z pouště Sahara a Sahelu, ale také má horské oblasti v jihozápadní části země. Niger je domovem mnoha zvířat, včetně lvů, slonů, gepardů, antilop a dalších druhů savců, ale mnoho z nich je ohroženo vyhynutím. Turisté mohou navštívit národní parky a rezervace, jako je například Národní park W Niger, aby si prohlédli místní přírodu a zvířata.\u003C\u002Fp\u003E\n\u003Cp\u003ENiger má bohatou historii, která sahá až do doby starověkého Ghany a Songhajské říše. Dnes se v Nigeru můžete setkat s mnoha různými etnickými skupinami, z nichž největší jsou Hausa, Zarma a Tuaregové. Hlavním jazykem je francouzština, ale většina obyvatel hovoří také národními jazyky, jako jsou hausa, zarma a tamashek.\u003C\u002Fp\u003E\n\u003Cp\u003EEkonomika Nigeru je založena na těžbě uranu, zemního plynu, železné rudy a manganu, ale většina obyvatel žije zemědělstvím a pastevectvím. Niger se potýká s mnoha socioekonomickými problémy, jako je chudoba, nedostatek vody, hladomor a nízká úroveň vzdělání a zdravotnictví.\u003C\u002Fp\u003E\n\u003Cp\u003ENavzdory svým problémům je Niger pro turisty, kteří hledají autentický zážitek z africké kultury a přírody, skvělou volbou. Niger nabízí mnoho možností pro dobrodružné cestování, jako jsou výpravy do Sahary nebo setkání s místními lidmi, kteří vám rádi představí jejich tradice a kulturu.\u003C\u002Fp\u003E",currency_code:"XOF",currency_name:"zlatý",money_prices:[{name:"Taxi (cena za km)",value:350,date_update:e},{name:"Hlavní jídlo",value:1500,date_update:e},{name:"Chleba",value:200,date_update:e},{name:"Mléko",value:a,date_update:g},{name:"Voda (500ml)",value:100,date_update:e},{name:"Espresso",value:a,date_update:g},{name:"Zmrzlina (jeden kopeček)",value:a,date_update:g},{name:"Pivo",value:500,date_update:e},{name:"Krabička cigaret",value:700,date_update:e},{name:"Big Mac",value:a,date_update:g},{name:"Coca Cola (500ml)",value:300,date_update:e}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:"112",date_update:d},{name:"Policie",value:"17",date_update:d},{name:"Hasiči",value:"18",date_update:d},{name:"Zdravotnická záchranná služba",value:"15",date_update:d}],phone_prefix:"+227",coordinates:[{latitude:"17.60779",longitude:"8.08167"}],people_religion:[{name:"Islám",value:"99%"},{name:"Tradiční náboženství",value:"1%"},{name:"Křesťanství",value:h},{name:"Baha'i",value:h},{name:"Ostatní",value:h}],people_nationality:[{name:"Hausové",value:"55%"},{name:"Zarma",value:"20%"},{name:"Tuaregové",value:q},{name:"Fulbové",value:q},{name:"Další",value:"5%"}],visitors_entry:[{name:"Občanský průkaz",value:"Pro vstup do Nigeru je potřeba cestovní pas, občanský průkaz nestačí.",date_update:d},{name:"Cestovní pas",value:"Pro vstup do Nigeru je nutné mít platný cestovní pas.",date_update:d},{name:"Vízum",value:"Pro vstup do Nigeru je nutné mít vízum. Pro občany ČR (EU) je možné získat vízum na nigerijském velvyslanectví v Praze nebo na hraničním přechodu v Nigeru. Podrobné informace je možné získat na nigerijském velvyslanectví.",date_update:d},{name:"Očkování",value:"Při vstupu do Nigeru není povinné očkování. Avšak je doporučeno očkování proti žluté zimnici a při delším pobytu v zemi také proti hepatitidě A a B, tyfu a meningokoku.",date_update:d},{name:"Cestovní pojištění",value:"Pro vstup do Nigeru není povinné mít cestovní nebo zdravotní pojištění, ale doporučuje se mít ho uzavřené kvůli případné lékařské péči v zemi.",date_update:d},{name:"Bezpečnostní situace",value:"V Nigeru existuje vysoké riziko teroristických útoků a únosců. Obzvláště v oblastech na severu a východě země jsou útoky časté. Cestování do těchto oblastí je velmi nebezpečné. Před cestou do Nigeru je nutné získat aktuální informace o bezpečnostní situaci v zemi a v případě potřeby se poradit s místními orgány.",date_update:d},{name:"Další informace",value:"Při cestování do Nigeru je důležité respektovat místní zvyklosti a tradice. Obzvláště v muslimských oblastech je třeba se oblékat skromně a nekonzumovat alkohol. V Nigeru je také třeba dávat pozor na vodu a používat pouze balenou vodu. V některých oblastech se vyskytují malárie a další nemoci přenášené hmyzem, je tedy nutné dodržovat preventivní opatření.",date_update:d}],type_place:r,id_image_cover:i,id_image_hero:i,id_city_main:s,affiliate:[{name:n,value:o}],mpz:"NE",alerts:a,zoom:[{google:f,booking:6}],ids_neighboring_countries:a}],imageState:[{id:i,name:p,source:"\u002Fworld\u002Fstates\u002F",date_create:"2023-05-03T20:14:00.749527+00:00",type:r,author:"pixabay.com"}],placeContinent:[{id:f,slug:"afrika",name:"Afrika",area:30370000,population:1340000000,population_density:44.12,number_states:54,information_chatgpt:"\u003Cp\u003EAfrika je největší kontinent na Zemi a rozkládá se na ploše přesahující 30 milionů kilometrů čtverečních. Nachází se zde více než 50 zemí a žije zde více než 1,3 miliardy lidí. \u003C\u002Fp\u003E\n\u003Cp\u003EJižní Afrika, Egypt, Maroko a Keňa patří mezi nejoblíbenější turistické cíle v Africe. V Jihoafrické republice se můžete podívat na slavné Krugerův národní park a sledovat zde život zvířat v jejich přirozeném prostředí. Egypt je zase proslulý svými pyramidami, které se nacházejí v Gíze. V Maroku můžete navštívit tradiční trhy, zatímco v Keni se můžete setkat s Masajským kmenem a podívat se na úžasnou přírodu.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má bohatou historii a kulturu. První lidé, kteří se usadili v Africe, byli loveckým a sběračským národem. Poté se v této oblasti objevily první civilizace, jako je Kúšská říše, Egyptská říše nebo Království Aksum. Afrika byla také kolonizována evropskými mocnostmi a toto období mělo značný dopad na dnešní politickou situaci na kontinentu.\u003C\u002Fp\u003E\n\u003Cp\u003EJe důležité zmínit, že Afrika má velké množství jazyků a kultur. Zahrnuje mnoho různých národů a etnických skupin, a to má vliv na různorodost obyvatelstva a kultury. Nejrozšířenějším jazykem je arabština, která se mluví především v severní Africe a v některých částech západní Afriky. Dalšími důležitými jazyky jsou svahilština, francouzština, portugalština, angličtina a mnoho dalších.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má také nádhernou přírodu. Nachází se zde nejvyšší hora světa, Kilimandžáro, která se nachází na území Tanzanie a Keňy. Kromě toho se v Africe nachází také největší pouště na světě, Sahara, a největší prales, Kongský prales. Na tomto kontinentě můžete vidět také mnoho zvířat, jako jsou sloni, lvi, gepardi, zebry a mnoho dalších druhů. V Africe se také nachází několik nejvýznamnějších řek světa, jako je Nil, Kongo, Niger a Zambezi.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je bohužel také známá pro své problémy. Mnoho zemí se potýká s chudobou, nedostatkem potravin a vody, nemocemi a válečnými konflikty. Mnoho afrických zemí závisí na zemědělství a rybolovu, což však často není dostatečně výnosné, aby zabezpečilo potřeby obyvatelstva. Kromě toho jsou některé země v Africe trvale postižené extrémními přírodními katastrofami, jako jsou sucha, povodně a bouře.\u003C\u002Fp\u003E\n\u003Cp\u003ENa druhou stranu se v Africe také nachází mnoho lidí, kteří se snaží změnit situaci a zlepšit životy svých spoluobčanů. Vznikají neziskové organizace, které se snaží pomáhat při řešení těchto problémů a zlepšovat kvalitu života v mnoha afrických zemích. Zároveň se v posledních letech objevují nové možnosti pro ekonomický růst a rozvoj, jako jsou technologické start-upy a zahraniční investice.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika je fascinující kontinent, který nabízí mnoho zážitků a zároveň v sobě ukrývá mnoho výzev. Pokud se rozhodnete navštívit Afriku, můžete se těšit na krásnou přírodu, bohatou kulturu a nezapomenutelné zážitky. Je však také důležité být obezřetný a připravený na místní podmínky, protože některé oblasti mohou být nebezpečné kvůli konfliktům nebo zločinnosti.\u003C\u002Fp\u003E",coordinates:[{latitude:1.650801,longitude:17.371642}],type_place:"continent",id_image_cover:f,id_image_hero:f,zoom:[{google:2,booking:3}]}],placesCities:[{id:1591,id_image_cover:a,slug:"agadez",type_place:c,name:"Agadez",biggest:b},{id:1592,id_image_cover:a,slug:"alaghsas",type_place:c,name:"Alaghsas",biggest:b},{id:1595,id_image_cover:a,slug:"birnin-konni",type_place:c,name:"Birnin Konni",biggest:b},{id:1594,id_image_cover:a,slug:"dosso",type_place:c,name:"Dosso",biggest:b},{id:j,id_image_cover:a,slug:m,type_place:c,name:l,biggest:b},{id:1590,id_image_cover:a,slug:"maradi",type_place:c,name:"Maradi",biggest:b},{id:s,id_image_cover:a,slug:"niamey",type_place:c,name:"Niamey",biggest:b},{id:1593,id_image_cover:a,slug:"tahoua",type_place:c,name:"Tahoua",biggest:b},{id:1597,id_image_cover:a,slug:"tessaoua",type_place:c,name:"Tessaoua",biggest:b},{id:1589,id_image_cover:a,slug:"zinder",type_place:c,name:"Zinder",biggest:b}],imagesCities:a,videos:[],imagesVideos:a,posts:[],imagesPosts:a}],fetch:{"OrganismsoPlaceTeaserSpotsComponent:0":{spots:[],images:a},"OrganismsoSidebarListComponent:0":{events:[],images:a}},mutations:void 0}}(null,"yes","city","05-2023","01-2021",5,"","zanedbatelné",326,1596,162,"Gaya","gaya","booking",true,"niger","10%","state",1588)));