__NUXT_JSONP__("/svet/mesto/telavi", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v){return {data:[{place:[{id:j,id_state:k,name:l,population:38700,area:27,altitude:500,slug:m,information_chatgpt:a,coordinates:[{latitude:41.9193,longitude:45.4737}],biggest:b,type_place:c,id_image_cover:a,id_image_hero:a,affiliate:[{name:n,value:o}],alerts:a,zoom:[{google:10,booking:11}],parking:a}],imagePlace:a,placeState:[{id:k,tld:".ge",name:"Gruzie",area:69700,population:3989167,slug:p,id_continent:e,information_chatgpt:"\u003Cp\u003EGruzie je země ležící v Kavkaze, mezi Evropou a Asií. Je známá svou bohatou historií, kulturou a malebnou přírodou. Hlavním městem je Tbilisi, které nabízí zajímavou kombinaci moderní architektury a starobylých památek.\u003C\u002Fp\u003E\n\u003Cp\u003EJednou z hlavních atrakcí Gruzie je její horská krajina. Nachází se zde Kavkazské hory, které nabízejí nádherné panoramatické výhledy, malebné horské vesničky a možnosti pro pěší turistiku a horskou turistiku.\u003C\u002Fp\u003E\n\u003Cp\u003EGruzínská kuchyně je také jedinečná. Mezi nejznámější pokrmy patří khinkali, což jsou tradiční kavkazské knedlíky plněné masem nebo zeleninou, a khachapuri, což je plněný sýrový chléb. Gruzínské víno je také slavné po celém světě, a tak návštěva místních vinných sklepů je must-do pro milovníky vína.\u003C\u002Fp\u003E\n\u003Cp\u003EHistoricky je Gruzie známá pro své starobylé památky, jako je například město Mtscheta, které je zapsáno na seznamu světového dědictví UNESCO. Další zajímavou památkou je pevnost Narikala v Tbilisi, která nabízí nádherný výhled na město.\u003C\u002Fp\u003E\n\u003Cp\u003EGruzínci jsou známí svou pohostinností a přátelským přístupem k návštěvníkům. Tradiční gruzínská hudba, tance a festivaly jsou také důležitou součástí kultury tohoto státu.\u003C\u002Fp\u003E\n\u003Cp\u003EV posledních letech se Gruzie stává stále populárnějším cílem pro turisty, kteří hledají exotické a neotřelé destinace. Země nabízí jedinečnou kombinaci přírodních krás, kulturního bohatství a pohostinnosti místních obyvatel.\u003C\u002Fp\u003E",currency_code:"GEL",currency_name:"gruzínské lari",money_prices:[{name:"Taxi (cena za km)",value:f,date_update:q},{name:"Hlavní jídlo",value:20,date_update:"01-2022"},{name:"Chleba",value:h,date_update:r},{name:"Mléko",value:f,date_update:"02-2022"},{name:"Voda (500ml)",value:.5,date_update:"10-2021"},{name:"Espresso",value:f,date_update:q},{name:"Zmrzlina (jeden kopeček)",value:h,date_update:"08-2021"},{name:"Pivo",value:e,date_update:"03-2022"},{name:"Krabička cigaret",value:5,date_update:s},{name:"Bic Mac",value:a,date_update:s},{name:"Coca Cola (500ml)",value:h,date_update:r}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:g,date_update:d},{name:"Policie",value:g,date_update:d},{name:"Hasiči",value:g,date_update:d},{name:"Zdravotnická záchranná služba",value:g,date_update:d}],phone_prefix:"+995",coordinates:[{latitude:"42.3154",longitude:"43.3569"}],people_religion:[{name:"Gruzínská pravoslavná církev",value:"83.4%"},{name:"Islám",value:"10.7%"},{name:"Arménská apoštolská církev",value:"2.9%"},{name:"Křesťanské nekonfesní",value:"0.5%"},{name:"Judaismus",value:"0.1%"},{name:t,value:"2.4%"}],people_nationality:[{name:"Gruzíni",value:"87.6%"},{name:"Ázerbájdžánci",value:"6.3%"},{name:"Arméni",value:"4.5%"},{name:t,value:"1.6%"}],visitors_entry:[{name:"Občanský průkaz",value:"Pro vstup do Gruzie nestačí pouze občanský průkaz, je třeba mít platný cestovní pas.",date_update:d},{name:"Cestovní pas",value:"Pro vstup do Gruzie je potřeba mít platný cestovní pas.",date_update:d},{name:"Vízum",value:"Pro občany České republiky není pro vstup do Gruzie většinou potřeba vízum, pokud chtějí zůstat v zemi déle než 365 dnů, musí požádat o vízum. Podrobnosti o vízové politice je nutné ověřit přímo u gruzínského velvyslanectví.",date_update:d},{name:"Očkování",value:"Pro vstup do Gruzie není povinné žádné očkování, ale doporučuje se očkování proti žloutence A a B, hepatitidě typu A a B, tetanu a dávivému kašli.",date_update:d},{name:"Cestovní pojištění",value:"Pro vstup do Gruzie není povinné mít cestovní pojištění, ale doporučuje se, aby turisté měli sjednané zdravotní pojištění, které pokryje všechny náklady související se zdravotní péčí.",date_update:d},{name:"Bezpečností situace",value:"Gruzie je v současné době bezpečnou zemí pro turisty, ale v blízkosti hranic s Ruskem a na separatistických územích v Abcházii a Jižní Osetii se mohou vyskytnout bezpečnostní problémy. Cestovatelé by měli být opatrní a vyhýbat se těmto oblastem.",date_update:d},{name:"Další informace",value:"Při návštěvě kostelů a klášterů je nutné dodržovat zásady slušného oblékání. Vyzývá se k respektování místních zvyklostí a tradic. Turisté by měli být obezřetní při používání veřejné dopravy, zejména v nočních hodinách. V Gruzii se používá gruzínština a ruština je také rozšířená, ale mnoho Gruzínů hovoří také anglicky.",date_update:d}],type_place:u,id_image_cover:i,id_image_hero:i,id_city_main:v,affiliate:[{name:n,value:o}],mpz:"GE",alerts:a,zoom:[{google:6,booking:7}],ids_neighboring_countries:a,organization:a,apps:a,links:a}],imageState:[{id:i,name:p,source:"\u002Fworld\u002Fstates\u002F",date_create:"2023-05-02T18:49:47.851894+00:00",type:u,author:"pixabay.com"}],placeContinent:[{id:e,slug:"asie",name:"Asie",area:44580000,population:4630000000,population_density:104.03,number_states:50,information_chatgpt:"\u003Cp\u003EAsie se rozkládá na ploše téměř 44,6 milionu kilometrů čtverečních a žije zde více než 4,5 miliardy lidí, což představuje téměř 60 % celkového počtu obyvatel planety. Kontinent zahrnuje celkem 48 zemí, mezi největší patří Rusko, Čína, Indie, Japonsko a Indonésie.\u003C\u002Fp\u003E\n\u003Cp\u003EAsie je domovem mnoha významných světových kultur, jako jsou hinduismus, buddhismus, islám a taoismus. V každé zemi se můžeš setkat s odlišnými tradicemi, zvyky a jazyky. Mezi nejnavštěvovanější turistické destinace patří například Japonsko s jeho futuristickými městy, Thajsko s bohatou kulturou a krásnými plážemi, nebo Indonésie s neuvěřitelnou přírodou a množstvím ostrovů.\u003C\u002Fp\u003E\n\u003Cp\u003EAsie také nabízí mnoho kulturních a historických památek, jako je Velká čínská zeď, Taj Mahal v Indii, buddhistické chrámy v Thajsku a mnoho dalšího. Zajímavou zkušeností může být i návštěva tradičních trhů, kde se dají ochutnat místní speciality a nakoupit různé exotické produkty.\u003C\u002Fp\u003E\n\u003Cp\u003EAsie má však také své výzvy a problémy, jako jsou například růst městských oblastí, nedostatek vody a znečištění ovzduší. Mnoho zemí se snaží najít řešení těchto problémů a přizpůsobit se moderním trendům, aby mohly udržet krok s ostatními zeměmi světa.\u003C\u002Fp\u003E\n\u003Cp\u003EPokud tě láká cestování po Asii, měl by sis předem zajistit víza a informovat se o místních zvycích a tradicích. Rozhodně se ti však naskytne možnost prozkoumat jedinečnou kulturu, přírodu a historii tohoto fascinujícího kontinentu.\u003C\u002Fp\u003E\u003Cp\u003EAsie má také neuvěřitelně bohatou gastronomii, která se v jednotlivých zemích a regionech liší. Mezi nejznámější asijské pokrmy patří sushi, různé druhy nudlí, curry, dim sum, pho a další. Kromě toho se v Asii pěstuje mnoho exotických plodin, jako jsou duriany, mangostany, rambutany a další ovoce, které můžeš ochutnat pouze zde.\n\u003C\u002Fp\u003E\u003Cp\u003E\nAsie je také známá svými svátky a tradicemi, jako je například čínský Nový rok, Diwali v Indii, nebo Songkran v Thajsku. Tyto svátky jsou většinou spojeny s různými rituály a zvyky, které se v průběhu staletí vyvinuly a jsou dodnes respektovány.\n\u003C\u002Fp\u003E\u003Cp\u003E\nKromě toho má Asie mnoho přírodních krás, jako jsou Himálaje, pouště Gobi a Thar, nebo tropické pralesy v Indonésii a Malajsii. V Asii se také nachází mnoho národních parků a chráněných oblastí, kde žijí ohrožené druhy zvířat, jako jsou tygři, pandy, sloni a další.\n\u003C\u002Fp\u003E\u003Cp\u003E\nZkrátka, Asie je kontinent plný kontrastů a neuvěřitelných zážitků, které stojí za to objevovat. Budeš-li mít příležitost navštívit některou z asijských zemí, neváhej a vydaj se na dobrodružnou cestu plnou nových objevů a nezapomenutelných zážitků.\u003C\u002Fp\u003E",coordinates:[{latitude:47.751076,longitude:92.475286}],type_place:"continent",id_image_cover:e,id_image_hero:e,zoom:[{google:f,booking:e}]}],placesCities:[{id:760,id_image_cover:a,slug:"batumi",type_place:c,name:"Batumi",biggest:b},{id:764,id_image_cover:a,slug:"gori",type_place:c,name:"Gori",biggest:b},{id:766,id_image_cover:a,slug:"khashuri",type_place:c,name:"Khashuri",biggest:b},{id:759,id_image_cover:a,slug:"kutaisi",type_place:c,name:"Kutaisi",biggest:b},{id:763,id_image_cover:a,slug:"poti",type_place:c,name:"Poti",biggest:b},{id:761,id_image_cover:a,slug:"rustavi",type_place:c,name:"Rustavi",biggest:b},{id:v,id_image_cover:a,slug:"tbilisi",type_place:c,name:"Tbilisi",biggest:b},{id:j,id_image_cover:a,slug:m,type_place:c,name:l,biggest:b},{id:767,id_image_cover:a,slug:"zestafoni",type_place:c,name:"Zestafoni",biggest:b},{id:762,id_image_cover:a,slug:"zugdidi",type_place:c,name:"Zugdidi",biggest:b}],imagesCities:a,videos:[],imagesVideos:a,posts:[],imagesPosts:a}],fetch:{"OrganismsoPlaceTeaserSpotsComponent:0":{spots:[],images:a},"OrganismsoSidebarListComponent:0":{events:[],images:a}},mutations:void 0}}(null,"yes","city","05-2023",3,2,"112",1,256,765,79,"Telavi","telavi","booking",true,"gruzie","04-2021","06-2021","12-2020","Ostatní","state",758)));