__NUXT_JSONP__("/svet/stat/rwanda/default", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w){return {data:[{place:[{id:m,tld:".rw",name:"Rwanda",area:26338,population:12952209,slug:n,id_continent:g,information_chatgpt:"\u003Cp\u003ERwanda je stát v Africe, který se nachází východně od Demokratické republiky Kongo, jižně od Ugandy, východně od Tanzanie a severně od Burundi. Má rozlohu 26 338 km² a žije zde přibližně 12 milionů obyvatel.\u003C\u002Fp\u003E\n\u003Cp\u003ERwanda se proslavila zejména svou tragickou historií genocidy v roce 1994, která si vyžádala přibližně 800 000 obětí. Nicméně v posledních letech se stala vzorem pro celou Afriku, díky obrovskému ekonomickému růstu a snaze o změnu a uzdravení země. Díky politické stabilitě a posilování demokracie se z Rwanda stává populární cíl turistů.\u003C\u002Fp\u003E\n\u003Cp\u003ERwanda nabízí návštěvníkům mnoho krásných a zajímavých míst, mezi které patří například národní parky, jako jsou například Akagera a Volcanoes, kde můžete pozorovat gorily v přirozeném prostředí. Kromě toho se v Rwanda nachází také krásné jezera, z nichž nejznámější jsou Kivu, Burera a Ruhondo.\u003C\u002Fp\u003E\n\u003Cp\u003ERwanda je také známá pro svou kulturu, tradice a řemeslnou výrobu. Kromě toho můžete v Rwanda ochutnat mnoho tradičních jídel, jako je například irio (směs brambor a fazolí), umutsima (kukuřičná kaše), nebo ikinyiga (telecí maso s banány).\u003C\u002Fp\u003E\n\u003Cp\u003EZkrátka, Rwanda je země plná krásné přírody, kultury a historie, která stojí za to navštívit.\u003C\u002Fp\u003E",currency_code:"RWF",currency_name:"Rwandský frank",money_prices:[{name:"Taxi (cena za km)",value:o,date_update:b},{name:"Hlavní jídlo",value:3500,date_update:b},{name:"Chleba",value:p,date_update:b},{name:"Mléko",value:600,date_update:b},{name:"Voda (500ml)",value:p,date_update:b},{name:"Espresso",value:h,date_update:b},{name:"Zmrzlina (jeden kopeček)",value:300,date_update:b},{name:"Pivo",value:h,date_update:b},{name:"Krabička cigaret",value:h,date_update:b},{name:"Big Mac",value:a,date_update:b},{name:"Coca Cola (500ml)",value:o,date_update:b}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:"112",date_update:c},{name:"Policie",value:q,date_update:c},{name:"Hasiči",value:q,date_update:c},{name:"Zdravotnická záchranná služba",value:"912",date_update:c}],phone_prefix:"+250",coordinates:[{latitude:"-1.9403",longitude:"29.8739"}],people_religion:[{name:"Křesťanství",value:"93%",date_update:f},{name:"Tradicionální náboženství",value:"5%",date_update:f},{name:"Muslimové",value:i,date_update:f},{name:"Jiná náboženství",value:i,date_update:f},{name:"Bez vyznání",value:"0%",date_update:f}],people_nationality:[{name:"Hutuové",value:"85%",date_update:j},{name:"Tutsiové",value:"14%",date_update:j},{name:"Twa",value:i,date_update:j}],visitors_entry:[{name:"Občanský průkaz",value:"Občanský průkaz nestačí pro vstup do Rwandy. Pro vstup je nutné mít platný cestovní pas.",date_update:c},{name:"Cestovní pas",value:"Pro vstup do Rwandy je nutné mít platný cestovní pas.",date_update:c},{name:"Vízum",value:"Občané České republiky (a tedy i Evropské unie a Schengenského prostoru) mohou vstoupit do Rwandy bez víza na dobu maximálně 90 dnů v rámci turistického pobytu. Pro delší pobyt nebo jiné účely je nutné žádat o vízum předem na rwandském velvyslanectví.",date_update:c},{name:"Očkování",value:"Při vstupu do Rwandy není vyžadováno žádné povinné očkování. Nicméně je doporučeno očkování proti žluté zimnici, zejména pokud cestujete z oblasti s rizikem výskytu této nemoci. Dále je vhodné být očkován proti tetanu, hepatitidě A a B a být chráněn proti malárii.",date_update:c},{name:"Cestovní pojištění",value:"Pro vstup do Rwandy není vyžadováno cestovní nebo zdravotní pojištění, ale doporučuje se si ho sjednat kvůli případnému zdravotnímu ošetření nebo repatriaci.",date_update:c},{name:"Bezpečnostní situace",value:"Rwanda je obecně považována za bezpečnou zemi pro turisty. Nicméně, jako všude jinde na světě, může dojít ke zločinům jako jsou krádeže a loupeže. Doporučuje se turistům dodržovat základní bezpečnostní opatření jako je neukazování cenností a peněz na veřejnosti a používání sejfů v hotelových pokojích.",date_update:c},{name:"Další informace",value:"Při návštěvě Rwandy je důležité respektovat místní zvyklosti a tradice. Například je vhodné se oblékat střídmě a zakrývat ramena a kolena v náboženských místech. V oblasti vulkanických hor Virunga je možné sledovat gorily, ale návštěva této oblasti vyžaduje speciální povolení a je nutné mít s sebou certifikát o negativním testu na COVID-19.",date_update:c}],type_place:r,id_image_cover:k,id_image_hero:k,id_city_main:l,affiliate:[{name:s,value:t}],mpz:"RW",alerts:a,zoom:[{google:u,booking:u}],ids_neighboring_countries:a,organization:a,apps:a,links:a,information_author:a}],imagePlace:[{id:k,name:n,source:"\u002Fworld\u002Fstates\u002F",date_create:"2023-05-03T20:14:00.749527+00:00",type:r,author:"pixabay.com"}],placesStatesNeighboring:a,imagesStatesNeighboring:a,placeContinent:[{id:g,slug:"afrika",name:"Afrika",area:30370000,population:1340000000,population_density:44.12,number_states:54,information_chatgpt:"\u003Cp\u003EAfrika je největší kontinent na Zemi a rozkládá se na ploše přesahující 30 milionů kilometrů čtverečních. Nachází se zde více než 50 zemí a žije zde více než 1,3 miliardy lidí. \u003C\u002Fp\u003E\n\u003Cp\u003EJižní Afrika, Egypt, Maroko a Keňa patří mezi nejoblíbenější turistické cíle v Africe. V Jihoafrické republice se můžete podívat na slavné Krugerův národní park a sledovat zde život zvířat v jejich přirozeném prostředí. Egypt je zase proslulý svými pyramidami, které se nacházejí v Gíze. V Maroku můžete navštívit tradiční trhy, zatímco v Keni se můžete setkat s Masajským kmenem a podívat se na úžasnou přírodu.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má bohatou historii a kulturu. První lidé, kteří se usadili v Africe, byli loveckým a sběračským národem. Poté se v této oblasti objevily první civilizace, jako je Kúšská říše, Egyptská říše nebo Království Aksum. Afrika byla také kolonizována evropskými mocnostmi a toto období mělo značný dopad na dnešní politickou situaci na kontinentu.\u003C\u002Fp\u003E\n\u003Cp\u003EJe důležité zmínit, že Afrika má velké množství jazyků a kultur. Zahrnuje mnoho různých národů a etnických skupin, a to má vliv na různorodost obyvatelstva a kultury. Nejrozšířenějším jazykem je arabština, která se mluví především v severní Africe a v některých částech západní Afriky. Dalšími důležitými jazyky jsou svahilština, francouzština, portugalština, angličtina a mnoho dalších.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má také nádhernou přírodu. Nachází se zde nejvyšší hora světa, Kilimandžáro, která se nachází na území Tanzanie a Keňy. Kromě toho se v Africe nachází také největší pouště na světě, Sahara, a největší prales, Kongský prales. Na tomto kontinentě můžete vidět také mnoho zvířat, jako jsou sloni, lvi, gepardi, zebry a mnoho dalších druhů. V Africe se také nachází několik nejvýznamnějších řek světa, jako je Nil, Kongo, Niger a Zambezi.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je bohužel také známá pro své problémy. Mnoho zemí se potýká s chudobou, nedostatkem potravin a vody, nemocemi a válečnými konflikty. Mnoho afrických zemí závisí na zemědělství a rybolovu, což však často není dostatečně výnosné, aby zabezpečilo potřeby obyvatelstva. Kromě toho jsou některé země v Africe trvale postižené extrémními přírodními katastrofami, jako jsou sucha, povodně a bouře.\u003C\u002Fp\u003E\n\u003Cp\u003ENa druhou stranu se v Africe také nachází mnoho lidí, kteří se snaží změnit situaci a zlepšit životy svých spoluobčanů. Vznikají neziskové organizace, které se snaží pomáhat při řešení těchto problémů a zlepšovat kvalitu života v mnoha afrických zemích. Zároveň se v posledních letech objevují nové možnosti pro ekonomický růst a rozvoj, jako jsou technologické start-upy a zahraniční investice.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika je fascinující kontinent, který nabízí mnoho zážitků a zároveň v sobě ukrývá mnoho výzev. Pokud se rozhodnete navštívit Afriku, můžete se těšit na krásnou přírodu, bohatou kulturu a nezapomenutelné zážitky. Je však také důležité být obezřetný a připravený na místní podmínky, protože některé oblasti mohou být nebezpečné kvůli konfliktům nebo zločinnosti.\u003C\u002Fp\u003E",coordinates:[{latitude:1.650801,longitude:17.371642}],type_place:"continent",id_image_cover:g,id_image_hero:g,zoom:[{google:2,booking:3}],information_author:a}],placesCities:[{id:1882,id_image_cover:a,slug:"butare",type_place:d,name:"Butare",biggest:e},{id:1886,id_image_cover:a,slug:"byumba",type_place:d,name:"Byumba",biggest:e},{id:1887,id_image_cover:a,slug:"cyangugu",type_place:d,name:"Cyangugu",biggest:e},{id:1885,id_image_cover:a,slug:"gisenyi",type_place:d,name:"Gisenyi",biggest:e},{id:1883,id_image_cover:a,slug:"gitarama",type_place:d,name:"Gitarama",biggest:e},{id:1888,id_image_cover:a,slug:"kibuye",type_place:d,name:"Kibuye",biggest:e},{id:l,id_image_cover:a,slug:v,type_place:d,name:w,biggest:e},{id:1889,id_image_cover:a,slug:"nyanza",type_place:d,name:"Nyanza",biggest:e},{id:1884,id_image_cover:a,slug:"ruhengeri",type_place:d,name:"Ruhengeri",biggest:e},{id:1890,id_image_cover:a,slug:"rwamagana",type_place:d,name:"Rwamagana",biggest:e}],imagesCities:a,placeCityMain:[{id:l,id_state:m,name:w,population:1262000,area:738,altitude:1567,slug:v,information_chatgpt:a,coordinates:[{latitude:-1.9536,longitude:30.0609}],biggest:e,type_place:d,id_image_cover:a,id_image_hero:a,affiliate:[{name:s,value:t}],alerts:a,zoom:[{google:10,booking:11}],parking:a,information_author:a}],imageCityMain:a,videos:[],imagesVideos:a,posts:[],imagesPosts:a}],fetch:{"OrganismsoSidebarListComponent:0":{events:[],images:a}},mutations:void 0}}(null,"09-2021","05-2023","city","yes","04-2023",5,1000,"1%","04-2021",349,1881,192,"rwanda",500,200,"112 nebo 912","state","booking",true,8,"kigali","Kigali")));