__NUXT_JSONP__("/jidlo/sushi", (function(a,b,c,d,e,f,g,h,i,j,k,l){return {data:[{food:[{id:29,slug:"sushi",created_at:"2023-12-02T22:58:31.019538+00:00",name:"Sushi",description:"\u003Cp\u003ESushi je japonské jídlo, které se stalo populárním po celém světě. Skládá se z malých kousků rýže, které jsou obvykle obalené v řase nori a doplněné rybou nebo mořskými plody.\u003C\u002Fp\u003E\n    \u003Cp\u003EPříprava sushi je umění, ačkoliv existuje několik základních stylů, jako například nigiri, sashimi, a maki. Nigiri je kousek rýže s plátkem ryby nahoře, zatímco sashimi jsou plátky syrové ryby bez rýže. Maki je zase rýže, ryba a další přísady zabaleny v řase nori.\u003C\u002Fp\u003E\n    \u003Cp\u003EHistorie sushi sahá až do 8. století v Japonsku, kde se používalo k fermentaci ryb s rýží. S postupem času se sushi vyvinulo a získalo svou moderní podobu. Dnes existuje mnoho variant sushi, které se liší regionálně i podle preferencí kuchařů.\u003C\u002Fp\u003E\n    \u003Cp\u003ESushi se často podává s omáčkou sojovou, zázvorem a wasabi. Jeho chutě a textury spojují jedinečný zážitek, který ocení milovníci gastronomie po celém světě.\u003C\u002Fp\u003E",ingredients:a,id_image_cover:a,id_image_hero:a,ids_states:[{id:f}],seo_tags:a,recipe:a,name_language:a}],imageFood:a,placesStates:[{tld:".jp",name:"Japonsko",area:377944,population:126150000,slug:g,id_continent:3,information_chatgpt:"\u003Cp\u003EJaponsko je ostrovní země v Tichomoří, která se skládá ze 6 852 ostrovů. Je známé svou bohatou kulturou, technologiemi a krásnými krajinami. Města jako Tokyo, Kyoto nebo Hiroshima patří mezi nejnavštěvovanější turistické destinace na světě.\u003C\u002Fp\u003E\n\u003Cp\u003EJaponsko je domovem několika UNESCO památek, jako je Kjóto s jeho chrámy a zahradami, nebo Hiroshima s památníkem míru. Mezi další zajímavá místa patří Fuji, sopka Mount Takao nebo Nara park s jeleny.\u003C\u002Fp\u003E\n\u003Cp\u003EJaponsko je známé i díky svým kulturním tradicím, jako jsou čajové obřady, bonsaje, kimonos, nebo karaoke. Japonská kuchyně, jako jsou sushi, ramen nebo tempura, je také světoznámá a oblíbená mezi turisty.\u003C\u002Fp\u003E\n\u003Cp\u003EJaponsko je také předním světovým hráčem v oblasti technologií, což se projevuje v jeho rychlosti a efektivnosti dopravního systému, mobilních technologiích nebo v robotických technologiích. Na druhou stranu, japonská společnost je velmi disciplinovaná a řízená pravidly a tradicemi.\u003C\u002Fp\u003E\n\u003Cp\u003EPři cestování do Japonska je důležité mít na paměti, že země má svá vlastní pravidla a zvyklosti, jako například nošení pantoflí ve vnitřních prostorách nebo obdarování lidí darovaným zbožím. Japonsko je však zemí s vysokou úrovní bezpečnosti, čistoty a vstřícností.\u003C\u002Fp\u003E",currency_code:"JPY",currency_name:"japonský jen",money_prices:[{name:"Taxi (cena za km)",value:260,date_update:c},{name:"Hlavní jídlo",value:800,date_update:c},{name:"Chleba",value:200,date_update:c},{name:"Mléko",value:h,date_update:c},{name:"Voda (500ml)",value:100,date_update:c},{name:"Espresso",value:300,date_update:c},{name:"Zmrzlina (jeden kopeček)",value:250,date_update:c},{name:"Pivo",value:i,date_update:c},{name:"Krabička cigaret",value:i,date_update:c},{name:"Big Mac",value:390,date_update:c},{name:"Coca Cola (500ml)",value:h,date_update:c}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:"911",date_update:b},{name:"Policie",value:"110",date_update:b},{name:"Hasiči",value:j,date_update:b},{name:"Zdravotnická záchranná služba",value:j,date_update:b},{name:"Turistická policie",value:"03-3503-8484",date_update:b},{name:"Horská záchranná služba",value:"0186-72-1122",date_update:b},{name:"Pobřežní záchranná služba",value:"118",date_update:b}],phone_prefix:"+81",coordinates:[{latitude:"35.6762",longitude:"139.6503"}],people_religion:[{name:"Šintoismus",value:"70%"},{name:"Buddhismus",value:"69%"},{name:"Křesťanství",value:"1%"},{name:"Jiná náboženství",value:"0,2%"}],people_nationality:[{name:"Japonci",value:"98 %"},{name:"Korejci",value:k},{name:"Číňané",value:k},{name:"Brazílci japonského původu",value:"0,4 %"},{name:"Filipínci",value:"0,2 %"},{name:"Vietnamci",value:d},{name:"Američané",value:d},{name:"Taiwané",value:d},{name:"Indonésané",value:d},{name:"Rusové",value:d}],visitors_entry:[{name:"Občanský průkaz",value:"Občanský průkaz nestačí pro vstup do Japonska. Pro vstup do Japonska je pro občany České republiky vyžadován platný cestovní pas.",date_update:b},{name:"Cestovní pas",value:"Pro vstup do Japonska je pro občany České republiky vyžadován platný cestovní pas. Pokud cestujete za účelem práce nebo studia, může být potřeba speciální vízum.",date_update:b},{name:"Vízum",value:"Pro občany České republiky (tedy EU a Schengen) není potřeba vízum, pokud plánují pobyt v Japonsku kratší než 90 dní. Pokud cestujete za účelem práce nebo studia, může být potřeba speciální vízum. Více informací na stránkách Velvyslanectví Japonska v ČR.",date_update:b},{name:"Očkování",value:"Pro vstup do Japonska nejsou vyžadována žádná specifická očkování. Doporučuje se však být očkován proti základním nemocem, jako jsou hepatitida A a B, tetanus a klíšťová encefalitida.",date_update:b},{name:"Cestovní pojištění",value:"Při vstupu do Japonska není cestovní pojištění povinné, ale je silně doporučeno mít ho uzavřené kvůli vysokým zdravotním nákladům v zemi.",date_update:b},{name:"Bezpečnostní situace",value:"Japonsko patří k bezpečným zemím s nízkou mírou kriminality. Nicméně je třeba dodržovat základní bezpečnostní opatření, jako je udržování osobních věcí v bezpečí a vyhnout se osamoceným nebezpečným oblastem v noci. V poslední době dochází k některým demonstracím proti vládě, které mohou být občas násilné.",date_update:b},{name:"Další informace",value:"Při vstupu do Japonska může být vyžadováno odbavení zavazadel a prohlášení celní deklarace. V zemi je také důležité dodržovat zvyklosti a kulturu, například při návštěvě chrámů a svatyní. V obchodních a restauračních zařízeních se očekává slušné a respektující chování. V některých oblastech Japonska je také běžné vysoké vlhkost a teplo v létě, takže je třeba být dobře připraven oblečením a pitím vody.",date_update:b}],type_place:l,id_image_cover:e,id_image_hero:e,id_city_main:1108,affiliate:[{name:"booking",value:true}],mpz:"JP",alerts:a,zoom:[{google:4,booking:5}],ids_neighboring_countries:[],organization:a,apps:a,links:a,information_author:a,seo_tags:a,language_phrases:a,wall_sockets:a,id:f,facts_place:a}],imagesStates:[{id:e,name:g,source:"\u002Fworld\u002Fstates\u002F",date_create:"2023-05-03T20:14:00.749527+00:00",type:l,author:"pixabay.com"}]}],fetch:{},mutations:[]}}(null,"05-2023","01-2021","0,1 %",276,114,"japonsko",150,500,"119","0,5 %","state")));