__NUXT_JSONP__("/svet/mesto/rijad", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v){return {data:[{place:[{id:l,id_state:n,name:o,population:8154059,area:1560,altitude:612,slug:p,information_chatgpt:a,coordinates:[{latitude:24.7136,longitude:46.6753}],biggest:"yes",type_place:c,id_image_cover:a,id_image_hero:a,affiliate:[{name:q,value:r}],alerts:a,zoom:[{google:10,booking:11}],parking:a,information_author:a,importance:d,seo_tags:a,created_at:"2023-12-27T16:36:26.534285+00:00",updated_at:"2023-12-27T17:06:48.496338+00:00"}],imagePlace:[],placeState:[{tld:".sa",name:"Saúdská Arábie",area:2149690,population:35041038,slug:s,id_continent:h,information_chatgpt:"\u003Cp\u003ESaúdská Arábie je největší arabský stát nacházející se na Arabském poloostrově, s rozlohou 2 149 690 km² a s populací přibližně 35 milionů obyvatel. Tento stát je jedním z nejvýznamnějších producentů ropy na světě a má také důležitou strategickou polohu.\u003C\u002Fp\u003E\n\u003Cp\u003EV Saúdské Arábii se nachází mnoho turistických atrakcí, z nichž nejznámější je asi Mekka, posvátné město muslimů. Každý rok sem míří miliony poutníků, aby se zúčastnili hadždže, jedné z nejvýznamnějších náboženských událostí pro muslimy.\u003C\u002Fp\u003E\n\u003Cp\u003EKromě Mekky má Saúdská Arábie mnoho dalších zajímavých míst pro turisty, například historické památky, jako je Al-Diriyah, starobylé město z 18. století, nebo Madain Saleh, národní památník s nádhernými skalními útvary. Mezi další turistické atrakce patří také krásné pláže, jako například Jeddah Corniche, a rozmanité obchodní centra a trhy, jako například Souq Al Alawi v Džiddě.\u003C\u002Fp\u003E\n\u003Cp\u003EJe však třeba si uvědomit, že Saúdská Arábie je konzervativní země, která má přísná pravidla pro turisty, a tak se turisté musí řídit místními zákony a zvyklostmi. Například ženy musí nosit dlouhé a volné šaty a hlavu si zakrývat šálou, zatímco alkohol a jiné návykové látky jsou zakázány.\u003C\u002Fp\u003E\n\u003Cp\u003EZkrátka, Saúdská Arábie je země s bohatou historií a kulturním dědictvím, ale také s přísnými zákony a pravidly. Pokud se rozhodnete navštívit tuto zemi, je důležité být respektující a dodržovat místní zvyklosti a pravidla.\u003C\u002Fp\u003E",currency_code:"SAR",currency_name:"Saúdskoarabský rial",money_prices:[{name:"Taxi (cena za km)",value:1.25,date_update:e},{name:"Hlavní jídlo",value:30,date_update:e},{name:"Chleba",value:i,date_update:e},{name:"Mléko",value:t,date_update:e},{name:"Voda (500ml)",value:.5,date_update:e},{name:"Espresso",value:t,date_update:e},{name:"Zmrzlina (jeden kopeček)",value:u,date_update:e},{name:"Pivo",value:15,date_update:e},{name:"Krabička cigaret",value:20,date_update:e},{name:"Bic Mac",value:"Není k dispozici v Saúdské Arábii",date_update:e},{name:"Coca Cola (500ml)",value:i,date_update:e}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:"112",date_update:f},{name:"Policie",value:"999",date_update:f},{name:"Hasiči",value:"998",date_update:f},{name:"Zdravotnická záchranná služba",value:"997",date_update:f},{name:"Turistická policie",value:"966125409000",date_update:f},{name:"Horská záchranná služba",value:"966114304555",date_update:f},{name:"Pobřežní záchranná služba",value:"17888888",date_update:f}],phone_prefix:"+966",coordinates:[{latitude:"23.8859",longitude:"45.0792"}],people_religion:[{name:"Islám",value:"100%",date_update:g},{name:"Křesťanství",value:j,date_update:g},{name:"Hinduismus",value:j,date_update:g},{name:"Buddhismus",value:j,date_update:g},{name:"Judaismus",value:j,date_update:g},{name:"Ateismus\u002Fagnosticismus",value:"\u003C1%",date_update:g}],people_nationality:[{name:"Saudové",value:"90%",date_update:b},{name:"Pákistánci",value:"1,3%",date_update:b},{name:"Indové",value:"1,2%",date_update:b},{name:"Jordánci",value:"1%",date_update:b},{name:"Egypťané",value:k,date_update:b},{name:"Jemenité",value:k,date_update:b},{name:"Filipínci",value:k,date_update:b},{name:"Bangladéšané",value:k,date_update:b},{name:"Přistěhovalci z různých zemí",value:"3,8%",date_update:b}],visitors_entry:[{name:"Občanský průkaz",value:"Pro vstup do Saúdské Arábie je třeba mít platný cestovní pas, občanský průkaz nestačí.",date_update:b},{name:"Cestovní pas",value:"Pro vstup do Saúdské Arábie je třeba mít platný cestovní pas.",date_update:b},{name:"Vízum",value:"Pro vstup do Saúdské Arábie je třeba mít platné vízum. Občané některých zemí, včetně některých zemí Evropské unie, mohou získat vízum po příletu na letišti v Saúdské Arábii. Nicméně pro většinu cestovatelů je doporučeno získat vízum předem v saúdskoarabské ambasádě.",date_update:b},{name:"Očkování",value:"Pro vstup do Saúdské Arábie není povinné očkování, ale doporučuje se očkování proti některým chorobám, jako je například žlutá zimnice. Cestovatelé by měli konzultovat s odborníkem v cestovní medicíně, které očkování jsou pro návštěvu Saúdské Arábie vhodné.",date_update:b},{name:"Cestovní pojištění",value:"Pro vstup do Saúdské Arábie není vyžadováno cestovní ani zdravotní pojištění, ale doporučuje se jej sjednat před cestou.",date_update:b},{name:"Bezpečnostní situace",value:"V Saúdské Arábii platí vysoká úroveň bezpečnosti, ale doporučuje se být opatrný a sledovat aktuální zpravodajství. Turisté by měli být respektováni místními zvyklostmi a kulturou a dodržovat místní zákony a předpisy. V některých částech země může být nutné dodržovat určitá omezení pohybu a chování, zejména v období ramadánu.",date_update:b},{name:"Další informace",value:"Při cestování do Saúdské Arábie je třeba mít na paměti, že alkohol je zakázán a kouření je povoleno pouze na určitých místech. Pokud cestujete do Saúdské Arábie za prací, je třeba mít připravené všechny potřebné pracovní povolení a doklady. V případě, že máte podezření na porušení vašich práv, můžete se obrátit na velvyslanectví ČR v Rijádu.",date_update:b}],type_place:v,id_image_cover:m,id_image_hero:m,id_city_main:l,affiliate:[{name:q,value:r}],mpz:"SA",alerts:a,zoom:[{google:u,booking:5}],ids_neighboring_countries:[{id:i},{id:107},{id:113},{id:123},{id:172},{id:187},{id:245}],organization:a,apps:a,links:a,information_author:a,seo_tags:a,language_phrases:a,wall_sockets:a,id:n,facts_place:a,created_at:"2023-12-27T16:33:10.517062+00:00",updated_at:"2023-12-27T17:07:31.480426+00:00"}],imageState:[{id:m,name:s,source:"\u002Fworld\u002Fstates\u002F",type:v,author:"pixabay.com",created_at:"2023-05-03T20:14:00.749527+00:00",updated_at:"2023-12-27T20:42:18.520959+00:00"}],placeContinent:[{id:h,slug:"asie",name:"Asie",area:44580000,population:4630000000,population_density:104.03,number_states:50,information_chatgpt:"\u003Cp\u003E\u003Cb\u003EAsie\u003C\u002Fb\u003E se rozkládá na ploše téměř 44,6 milionu kilometrů čtverečních a žije zde více než 4,5 miliardy lidí, což představuje téměř 60 % celkového počtu obyvatel planety. Kontinent zahrnuje celkem 48 zemí, mezi největší patří \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Frusko\"\u003ERusko\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fcina\"\u003EČína\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Findie\"\u003EIndie\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fjaponsko\"\u003EJaponsko\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Findonesie\"\u003EIndonésie\u003C\u002Fa\u003E\u003C\u002Fb\u003E.\u003C\u002Fp\u003E\n\u003Cp\u003E\u003Cb\u003EAsie\u003C\u002Fb\u003E je domovem mnoha významných světových kultur, jako jsou hinduismus, buddhismus, islám a taoismus. V každé zemi se můžeš setkat s odlišnými tradicemi, zvyky a jazyky. Mezi nejnavštěvovanější turistické destinace patří například \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fjaponsko\"\u003EJaponsko\u003C\u002Fa\u003E\u003C\u002Fb\u003E s jeho futuristickými městy, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fthajsko\"\u003EThajsko\u003C\u002Fa\u003E\u003C\u002Fb\u003E s bohatou kulturou a krásnými plážemi, nebo \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Findonesie\"\u003EIndonésie\u003C\u002Fa\u003E\u003C\u002Fb\u003E s neuvěřitelnou přírodou a množstvím ostrovů.\u003C\u002Fp\u003E\n\u003Cp\u003E\u003Cb\u003EAsie\u003C\u002Fb\u003E také nabízí mnoho kulturních a historických památek, jako je \u003Cb\u003EVelká čínská zeď\u003C\u002Fb\u003E, \u003Cb\u003ETaj Mahal\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Findie\"\u003EIndii\u003C\u002Fa\u003E\u003C\u002Fb\u003E, buddhistické chrámy v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fthajsko\"\u003EThajsku\u003C\u002Fa\u003E\u003C\u002Fb\u003E a mnoho dalšího. Zajímavou zkušeností může být i návštěva tradičních trhů, kde se dají ochutnat místní speciality a nakoupit různé exotické produkty.\u003C\u002Fp\u003E\n\u003Cp\u003E\u003Cb\u003EAsie\u003C\u002Fb\u003E má však také své výzvy a problémy, jako jsou například růst městských oblastí, nedostatek vody a znečištění ovzduší. Mnoho zemí se snaží najít řešení těchto problémů a přizpůsobit se moderním trendům, aby mohly udržet krok s ostatními zeměmi světa.\u003C\u002Fp\u003E\n\u003Cp\u003EPokud tě láká cestování po \u003Cb\u003EAsii\u003C\u002Fb\u003E, měl by sis předem zajistit víza a informovat se o místních zvycích a tradicích. Rozhodně se ti však naskytne možnost prozkoumat jedinečnou kulturu, přírodu a historii tohoto fascinujícího kontinentu.\u003C\u002Fp\u003E\u003Cp\u003E\u003Cb\u003EAsie\u003C\u002Fb\u003E má také neuvěřitelně bohatou gastronomii, která se v jednotlivých zemích a regionech liší. Mezi nejznámější asijské pokrmy patří \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fjidlo\u002Fsushi\"\u003Esushi\u003C\u002Fa\u003E\u003C\u002Fb\u003E, různé druhy nudlí, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fjidlo\u002Fcurry\"\u003Ecurry\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003Edim sum\u003C\u002Fb\u003E, \u003Cb\u003Epho\u003C\u002Fb\u003E a další. Kromě toho se v \u003Cb\u003EAsii\u003C\u002Fb\u003E pěstuje mnoho exotických plodin, jako jsou \u003Cb\u003Eduriany\u003C\u002Fb\u003E, \u003Cb\u003Emangostany\u003C\u002Fb\u003E, \u003Cb\u003Erambutany\u003C\u002Fb\u003E a další ovoce, které můžeš ochutnat pouze zde.\n\u003C\u002Fp\u003E\u003Cp\u003E\n\u003Cb\u003EAsie\u003C\u002Fb\u003E je také známá svými svátky a tradicemi, jako je například \u003Cb\u003Ečínský Nový rok\u003C\u002Fb\u003E, \u003Cb\u003EDiwali\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Findie\"\u003EIndii\u003C\u002Fa\u003E\u003C\u002Fb\u003E, nebo \u003Cb\u003ESongkran\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fthajsko\"\u003EThajsku\u003C\u002Fa\u003E\u003C\u002Fb\u003E,. Tyto svátky jsou většinou spojeny s různými rituály a zvyky, které se v průběhu staletí vyvinuly a jsou dodnes respektovány.\n\u003C\u002Fp\u003E\u003Cp\u003E\nKromě toho má \u003Cb\u003EAsie\u003C\u002Fb\u003E mnoho přírodních krás, jako jsou \u003Cb\u003EHimálaje\u003C\u002Fb\u003E, pouště \u003Cb\u003EGobi\u003C\u002Fb\u003E a \u003Cb\u003EThar\u003C\u002Fb\u003E, nebo tropické pralesy v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Findonesie\"\u003EIndonésii\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmalajsie\"\u003EMalajsii\u003C\u002Fa\u003E\u003C\u002Fb\u003E. V \u003Cb\u003EAsii\u003C\u002Fb\u003E se také nachází mnoho národních parků a chráněných oblastí, kde žijí ohrožené druhy zvířat, jako jsou \u003Cb\u003Etygři\u003C\u002Fb\u003E, \u003Cb\u003Epandy\u003C\u002Fb\u003E, \u003Cb\u003Esloni\u003C\u002Fb\u003E a další.\n\u003C\u002Fp\u003E\u003Cp\u003E\nZkrátka, \u003Cb\u003EAsie\u003C\u002Fb\u003E je kontinent plný kontrastů a neuvěřitelných zážitků, které stojí za to objevovat. Budeš-li mít příležitost navštívit některou z asijských zemí, neváhej a vydaj se na dobrodružnou cestu plnou nových objevů a nezapomenutelných zážitků.\u003C\u002Fp\u003E",coordinates:[{latitude:47.751076,longitude:92.475286}],type_place:"continent",id_image_cover:h,id_image_hero:h,zoom:[{google:i,booking:h}],information_author:a,seo_tags:a,created_at:"2023-12-27T16:35:37.460505+00:00",updated_at:"2023-12-27T17:07:06.531847+00:00"}],placesCities:[{id:1900,id_image_cover:a,importance:d,slug:"abha",type_place:c,name:"Abhá"},{id:1895,id_image_cover:a,importance:d,slug:"ad-dammam",type_place:c,name:"Ad-Dammám"},{id:1897,id_image_cover:a,importance:d,slug:"burajdah",type_place:c,name:"Burajdah"},{id:1899,id_image_cover:a,importance:d,slug:"chubar",type_place:c,name:"Chubar"},{id:1892,id_image_cover:a,importance:d,slug:"dzidda",type_place:c,name:"Džidda"},{id:1894,id_image_cover:a,importance:d,slug:"medina",type_place:c,name:"Medína"},{id:1893,id_image_cover:a,importance:d,slug:"mekka",type_place:c,name:"Mekka"},{id:l,id_image_cover:a,importance:d,slug:p,type_place:c,name:o},{id:1896,id_image_cover:a,importance:d,slug:"ta-if",type_place:c,name:"Ta'if"},{id:1898,id_image_cover:a,importance:d,slug:"tabuk",type_place:c,name:"Tabúk"}],imagesCities:a}],fetch:{"OrganismsoPlaceTeaserSpotsComponent:0":{spots:[],images:a},"OrganismsoSidebarListComponent:0":{events:[],images:a}},mutations:[]}}(null,"09-2021","city","biggest","02-2021","05-2023","01-2021",3,2,"0%","0,9%",1891,353,193,"Rijád","rijad","booking",true,"saudska-arabie",6,4,"state")));