__NUXT_JSONP__("/svet/mesto/vejle", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w,x,y,z,A,B,C,D,E,F,G,H,I,J,K,L,M,N,O,P,Q){return {data:[{place:[{id:o,id_state:p,name:q,population:57028,area:29.84,altitude:31,slug:k,information_chatgpt:"\u003Cp\u003EVejle je malebné město ležící ve státě Dánsko. Nachází se východně od poloostrova Jutsko, na březích stejnojmenného fjordu. S bohatou historií sahající až do doby Vikingů, Vejle nabízí návštěvníkům kombinaci malebné přírody, architektury a kulturního dědictví.\u003C\u002Fp\u003E\n\u003Cp\u003EJedním z hlavních lákadel Vejle je jeho historické centrum, které je plné malebných uliček, tržišť a zachovalých budov. V centru města se nachází také ikonický Vejle Muzeum, které poskytuje fascinující pohled do minulosti města a regionu. Mezi další významné památky patří Vejle Radnice s jejím charakteristickým věžovým hodinovým strojem a Vejlehohus, který slouží jako koncertní síň a kulturní centrum.\u003C\u002Fp\u003E\n\u003Cp\u003EPřírodní krása Vejle je neodmyslitelnou součástí města. Vejle Fjord je malebným místem pro procházky podél pobřeží, jízdu na kole nebo vodní aktivity. Nejznámějším přírodním útvarem je nejspíše Špička Vejle (Vejle Ådal), která je údolím s výhledem na fjord a je vyhlášeným místem pro turistiku a outdoorové aktivity.\u003C\u002Fp\u003E\n\u003Cp\u003EVejle je také výborným místem pro milovníky moderní architektury. ICONO, moderní budova navržená architektem Nielsem, je jedním z nejzajímavějších a nejvýznamnějších děl moderní architektury ve městě. Vejle nabízí také bohatý kulturní život, s množstvím festivalů, koncertů a výstav po celý rok.\u003C\u002Fp\u003E\n\u003Cp\u003ENakonec, nezapomeňme zmínit i vynikající dánskou kuchyni, kterou můžete ochutnat v některé z místních restaurací. Vejle je známé svými kvalitními restauracemi a kavárnami, kde si můžete vychutnat tradiční dánská jídla, jako je například smørrebrød (opečený chléb s různými náplněmi) nebo ryby a mořské plody.\u003C\u002Fp\u003E",coordinates:[{latitude:55.7092,longitude:9.5367}],biggest:"yes",type_place:a,id_image_cover:j,id_image_hero:j,affiliate:[{name:r,value:s}],alerts:g,zoom:[{google:10,booking:11}],parking:g,information_author:g,importance:d,seo_tags:g,created_at:"2023-12-27T16:36:26.534285+00:00",updated_at:"2023-12-27T17:06:48.496338+00:00"}],imagePlace:[{id:j,name:k,source:e,type:a,author:h,created_at:f,updated_at:c}],placeState:[{tld:".dk",name:"Dánsko",area:43094,population:5822763,slug:t,id_continent:l,information_chatgpt:"\u003Cp\u003EDánsko, oficiálně nazývané Dánské království, je skandinávská země nacházející se na severu Evropy. Sousedí s Německem na jihu a s vodou na ostatních stranách, včetně Baltského a Severního moře.\u003C\u002Fp\u003E\n\u003Cp\u003EDánsko má bohatou historii a kulturní dědictví. Můžete navštívit starobylé hrady, jako například hrad Kronborg v Helsingøru, který inspiroval Shakespearův Hamlet, nebo zámek Frederiksborg v Hillerødu. Dánsko je také známé pro své moderní architekturu, například v Kodani najdete ikonickou budovu Opery a novou čtvrť Nordhavn, která se stala symbolem udržitelného rozvoje.\u003C\u002Fp\u003E\n\u003Cp\u003EDánsko je také domovem mnoha slavných osobností, včetně spisovatele Hanse Christiana Andersena a filozofa Sørena Kierkegaarda. Místní kuchyně je bohatá a nabízí mnoho specialit, jako například smørrebrød (tradiční dánský chléb s různými náplněmi) a hřebíčkovou medovinu, dánské pivo.\u003C\u002Fp\u003E\n\u003Cp\u003EPro cestovatele, kteří hledají dobrodružství, nabízí Dánsko nádhernou přírodu. Národní park Mols Bjerge nabízí krásné scenérie a mnoho příležitostí pro turistiku a cyklistiku. Dánsko je také známé pro své mnoho ostrovů, které lze navštívit trajektem a užít si tak nádherné pobřeží a pláže.\u003C\u002Fp\u003E\n\u003Cp\u003EKromě toho je Dánsko také jedním z nejšťastnějších a nejrozvinutějších států na světě. Dánská vláda zajišťuje svým občanům vysokou kvalitu života a příležitosti v oblasti zdravotnictví, vzdělávání a zaměstnanosti.\u003C\u002Fp\u003E",currency_code:"DKK",currency_name:"dánská koruna",money_prices:[{name:"Taxi (cena za km)",value:8.87,date_update:b},{name:"Hlavní jídlo",value:150,date_update:b},{name:"Chleba",value:u,date_update:b},{name:"Mléko",value:7.5,date_update:b},{name:"Voda (500ml)",value:12,date_update:b},{name:"Espresso",value:25,date_update:b},{name:"Zmrzlina (jeden kopeček)",value:15,date_update:b},{name:"Pivo",value:35,date_update:b},{name:"Krabička cigaret",value:48,date_update:b},{name:"Bic Mac",value:38,date_update:b},{name:"Coca Cola (500ml)",value:u,date_update:b}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:m,date_update:i},{name:"Policie",value:"114",date_update:i},{name:"Hasiči",value:m,date_update:i},{name:"Zdravotnická záchranná služba",value:m,date_update:i}],phone_prefix:"+45",coordinates:[{latitude:"56.2639",longitude:"9.5018"}],people_religion:[{name:"bez náboženské víry",value:"75%"},{name:"křesťanství",value:"19%"},{name:"islám",value:"2%"},{name:"jiné náboženství",value:"4%"}],people_nationality:[{name:"Dánové",value:"86%"},{name:"jiné národnosti",value:"14%"}],visitors_entry:[{name:"Občanský průkaz",value:"Pro vstup do Dánska je třeba mít platný cestovní doklad. Pro občany Evropské unie stačí tedy pouze občanský průkaz.",date_update:v},{name:"Cestovní pas",value:"Pro občany Evropské unie není pro vstup do Dánska potřeba mít cestovní pas.",date_update:v},{name:"Vízum",value:"Pro občany České republiky a dalších zemí Evropské unie a Schengenského prostoru není nutné mít pro krátkodobý pobyt v Dánsku vízum. Pokud plánujete pobyt déle než 90 dnů, je nutné se registrovat u dánského úřadu a získat povolení k pobytu. Pro občany mimo EU nebo Schengenského prostoru může být potřeba vízum. Podrobné informace ohledně víz a pobytových povolení poskytuje dánské velvyslanectví v ČR.",date_update:b},{name:"Očkování",value:"Pro vstup do Dánska není aktuálně vyžadováno očkování proti COVID-19. Nicméně, Dánsko v rámci opatření k omezení šíření nákazy COVID-19 může mít některá pravidla, která cestovatelé musí dodržovat. Před cestou do Dánska je tedy vhodné zkontrolovat aktuální informace o pravidlech vstupu a pobytu na stránkách dánského ministerstva zahraničních věcí.",date_update:b},{name:"Cestovní pojištění",value:"Pro vstup do Dánska není vyžadováno cestovní nebo zdravotní pojištění, ale doporučuje se ho mít, aby se předešlo nečekaným výdajům v případě zdravotních problémů nebo nehod.",date_update:i},{name:"Bezpečnostní situace",value:"Dánsko je považováno za bezpečnou zemi s nízkou mírou kriminality. Nicméně, stejně jako v jiných evropských zemích, turisté by měli být opatrní a mít na paměti základní bezpečnostní opatření, jako například nezanechávat cennosti v autě nebo na veřejných místech.",date_update:i},{name:"Další informace",value:"V Dánsku se platí dánskou korunou (DKK). Většina obchodů a restaurací přijímá platební karty, ale stále je dobré mít hotovost s sebou, zejména na menších místech. Dánsko je známo svým chladným a deštivým počasím, takže byste měli být připraveni na případné nepříznivé klimatické podmínky. Mezinárodní řidičský průkaz není potřebný, ale pro jistotu si ho můžete vzít s sebou. Dánská kuchyně je velmi chutná, ale také trochu dražší než v jiných evropských zemích. Dánsko je známé svými designovými výrobky a módními značkami, takže pokud máte zájem o módu nebo design, budete mít v Dánsku mnoho možností pro nákupy.",date_update:i}],type_place:w,id_image_cover:n,id_image_hero:n,id_city_main:x,affiliate:[{name:r,value:s}],mpz:"DK",alerts:[],zoom:[{google:6,booking:7}],ids_neighboring_countries:[{id:57}],organization:[],apps:[],links:[],information_author:[],seo_tags:[],language_phrases:[],wall_sockets:g,id:p,facts_place:g,created_at:"2023-12-27T16:33:10.517062+00:00",updated_at:"2023-12-27T17:07:31.480426+00:00"}],imageState:[{id:n,name:t,source:"\u002Fworld\u002Fstates\u002F",type:w,author:h,created_at:"2023-05-02T18:49:47.851894+00:00",updated_at:c}],placeContinent:[{id:l,slug:"evropa",name:"Evropa",area:10180000,population:746000000,population_density:73.22,number_states:44,information_chatgpt:"\u003Cp\u003E\u003Cb\u003EEvropa\u003C\u002Fb\u003E je druhým nejmenším kontinentem na světě, ale zároveň se jedná o jednu z nejvíce rozmanitých oblastí na planetě. Nachází se zde mnoho historických a kulturních památek, překrásných přírodních scenérií a mnoho dalších atrakcí, což z ní dělá jednu z nejnavštěvovanějších turistických destinací na světě.\u003C\u002Fp\u003E\n\u003Cp\u003E\u003Cb\u003EEvropa\u003C\u002Fb\u003E má dlouhou a bohatou historii. Na kontinentě se odehrály mnohé důležité události, jako například římská říše, středověké křížové výpravy, renesance, průmyslová revoluce a dvě světové války. Tato historie se odráží v architektuře, muzeích a dalších kulturních památkách, které jsou na kontinentu k vidění.\u003C\u002Fp\u003E\n\u003Cp\u003EV \u003Cb\u003EEvropě\u003C\u002Fb\u003E se nachází mnoho zajímavých měst. Například \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fpariz\"\u003EPaříž\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Flondyn\"\u003ELondýn\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Frim\"\u003EŘím\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fbarcelona\"\u003EBarcelona\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fpraha\"\u003EPraha\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fberlin\"\u003EBerlín\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Famsterdam\"\u003EAmsterdam\u003C\u002Fa\u003E\u003C\u002Fb\u003E jsou velmi oblíbenými cíli turistů. Tyto města nabízejí nejen historické památky, ale také vynikající kuchyni, noční život a mnoho možností pro nákupy.\u003C\u002Fp\u003E\n\u003Cp\u003EPřírodní krása \u003Cb\u003EEvropy\u003C\u002Fb\u003E je také úžasná. Nachází se zde krásné pláže, hory, řeky a jezera. \u003Cb\u003EAlpské hory\u003C\u002Fb\u003E nabízejí překrásné scenérie, skvělé podmínky pro lyžování a další outdoorové aktivity. Národní parky, jako třeba \u003Cb\u003EPlitvická jezera\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fchorvatsko\"\u003EChorvatsku\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003EKrkonošský národní park\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fcesko\"\u003EČeské republice\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003ENárodní park Durmitor\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fcerna-hora\"\u003EČerné Hoře\u003C\u002Fa\u003E\u003C\u002Fb\u003E, jsou dalšími příklady nádherných přírodních scenérií, které stojí za návštěvu.\u003C\u002Fp\u003E\n  \u003Cp\u003E\u003Cb\u003EEvropa\u003C\u002Fb\u003E také nabízí širokou škálu kulturních aktivit. Existují zde mnohé umělecké festivaly, výstavy, divadelní představení a koncerty, které jsou pořádány po celém kontinentu. Mnoho měst také pořádá tradiční slavnosti a jarmarky, kde se můžete seznámit s místními tradicemi a kulturou.\u003C\u002Fp\u003E\n  \u003Cp\u003EPři návštěvě \u003Cb\u003EEvropy\u003C\u002Fb\u003E si určitě nenechte ujít místní kuchyni. Každá země má své vlastní speciality a chuťě\u003C\u002Fp\u003E\u003Cp\u003EMezi nejznámější evropské kuchyně patří \u003Cb\u003Eitalská\u003C\u002Fb\u003E, \u003Cb\u003Efrancouzská\u003C\u002Fb\u003E a \u003Cb\u003Ešpanělská\u003C\u002Fb\u003E. \u003Cb\u003EItalská kuchyně\u003C\u002Fb\u003E je známá svými \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fjidlo\u002Fpasta\"\u003Etěstovinami\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fjidlo\u002Fpizza\"\u003Epizzou\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003Ezmrzlinou\u003C\u002Fb\u003E. \u003Cb\u003EFrancouzská kuchyně\u003C\u002Fb\u003E nabízí širokou škálu různých pokrmů, jako například sýry, pečivo, maso a víno. \u003Cb\u003EŠpanělská kuchyně\u003C\u002Fb\u003E je známá svými \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fjidlo\u002Ftapas\"\u003Etapas\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fjidlo\u002Fpaella\"\u003Epaellou\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003Esangriou\u003C\u002Fb\u003E. Kromě těchto kuchyní jsou v \u003Cb\u003EEvropě\u003C\u002Fb\u003E také velmi oblíbené pokrmy jako \u003Cb\u003Eněmecké klobásy\u003C\u002Fb\u003E, \u003Cb\u003Eanglické snídaně\u003C\u002Fb\u003E, \u003Cb\u003Eholandský sýr\u003C\u002Fb\u003E a mnoho dalšího.\u003C\u002Fp\u003E\n  \u003Cp\u003E\u003Cb\u003EEvropa\u003C\u002Fb\u003E také nabízí mnoho možností pro sportovní aktivity. Mezi nejpopulárnější sporty patří fotbal, hokej, tenis a cyklistika. Na kontinentu se také nachází mnoho golfových hřišť, lyžařských středisek a dalších sportovních zařízení. V posledních letech se stále více lidí zajímá o ekoturistiku, a tak se nabízí možnost podniknout pěší túru nebo cyklistický výlet a objevovat krásy evropské přírody.\u003C\u002Fp\u003E\n\u003Cp\u003E\u003Cb\u003EEvropa\u003C\u002Fb\u003E je jedním z nejlepších míst pro cestování, nabízí mnoho možností pro každého cestovatele. Díky rozmanitosti kontinentu si každý najde to svoje, co ho zaujme. Záleží pouze na vás, zda si zvolíte kulturní zážitky, přírodní krásy, sportovní aktivity nebo si jen tak užijete místní kuchyni a pohodu.\u003C\u002Fp\u003E",coordinates:[{latitude:47.497912,longitude:19.040235}],type_place:"continent",id_image_cover:l,id_image_hero:l,zoom:[{google:y,booking:y}],information_author:g,seo_tags:g,created_at:"2023-12-27T16:35:37.460505+00:00",updated_at:"2023-12-27T17:07:06.531847+00:00"}],placesCities:[{id:561,id_image_cover:z,importance:d,slug:A,type_place:a,name:"Aalborg"},{id:559,id_image_cover:B,importance:d,slug:C,type_place:a,name:"Aarhus"},{id:562,id_image_cover:D,importance:d,slug:E,type_place:a,name:"Esbjerg"},{id:565,id_image_cover:F,importance:d,slug:G,type_place:a,name:"Horsens"},{id:x,id_image_cover:H,importance:d,slug:I,type_place:a,name:"Kodaň"},{id:564,id_image_cover:J,importance:d,slug:K,type_place:a,name:"Kolding"},{id:560,id_image_cover:L,importance:d,slug:M,type_place:a,name:"Odense"},{id:563,id_image_cover:N,importance:d,slug:O,type_place:a,name:"Randers"},{id:567,id_image_cover:P,importance:d,slug:Q,type_place:a,name:"Roskilde"},{id:o,id_image_cover:j,importance:d,slug:k,type_place:a,name:q}],imagesCities:[{id:N,name:O,source:e,type:a,author:"https:\u002F\u002Fcommons.wikimedia.org\u002Fwiki\u002FFile:2016-06-19-det-gamle-raadhus-i-randers.jpg",created_at:f,updated_at:c},{id:F,name:G,source:e,type:a,author:"https:\u002F\u002Fcommons.wikimedia.org\u002Fwiki\u002FFile:Horsens_-_g%C3%A5gaden.jpg",created_at:f,updated_at:c},{id:P,name:Q,source:e,type:a,author:h,created_at:f,updated_at:c},{id:j,name:k,source:e,type:a,author:h,created_at:f,updated_at:c},{id:J,name:K,source:e,type:a,author:h,created_at:f,updated_at:c},{id:D,name:E,source:e,type:a,author:h,created_at:f,updated_at:c},{id:z,name:A,source:e,type:a,author:h,created_at:f,updated_at:c},{id:L,name:M,source:e,type:a,author:h,created_at:f,updated_at:c},{id:B,name:C,source:e,type:a,author:h,created_at:f,updated_at:c},{id:H,name:I,source:e,type:a,author:h,created_at:f,updated_at:c}]}],fetch:{"OrganismsoPlaceTeaserSpotsComponent:0":{spots:[],images:g},"OrganismsoSidebarListComponent:0":{events:[],images:g}},mutations:[]}}("city","09-2021","2023-12-27T20:42:18.520959+00:00","biggest","\u002Fworld\u002Fcities\u002F","2023-06-12T21:06:20.035276+00:00",null,"pixabay.com","05-2023",642,"vejle",4,"112",237,566,59,"Vejle","booking",true,"dansko",20,"12-2023","state",558,3,639,"aalborg",637,"aarhus",640,"esbjerg",644,"horsens",636,"kodan",641,"kolding",638,"odense",645,"randers",643,"roskilde")));