__NUXT_JSONP__("/svet/stat/namibie/default", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w,x,y,z,A,B,C,D,E,F,G,H,I,J,K){return {data:[{place:[{tld:".na",name:"Namibie",area:825418,population:2540905,slug:q,id_continent:g,information_chatgpt:"\u003Cp\u003ENamibie je země ležící v jihozápadní Africe, s rozlohou přes 800 000 km² a s populací přibližně 2,5 milionu obyvatel. Hlavním městem Namibie je Windhoek.\u003C\u002Fp\u003E\n\u003Cp\u003ENamibie je známá svými ohromujícími přírodními krásami, zahrnujícími pouště, národní parky, útesy a pláže. Mezi nejpopulárnější turistické atrakce patří národní park Etosha, kde je možné pozorovat velkou divokou zvěř, jako jsou sloni, lvi a nosorožci. Dále zde lze navštívit horský masiv Brandberg, kde se nachází nejvyšší hora Namibie a mnoho skalních maleb a rytin, které jsou zdejšími kulturami staré přes 2 000 let. \u003C\u002Fp\u003E\n\u003Cp\u003ENamibie je také domovem mnoha etnických skupin, z nichž největší je Owambo. Země má 13 oficiálních jazyků, mezi nimiž patří angličtina, němčina a různé bantuské jazyky. Místní kuchyně je ovlivněna sousední Jihoafrickou republikou a zahrnuje pokrmy z masa, ryb a zeleniny, jako jsou bobotie, biltong a braai. \u003C\u002Fp\u003E\n\u003Cp\u003ENamibie je také bohatá na nerostné suroviny, včetně diamantů, mědi a uranu. Země má také stabilní politickou situaci a relativně vysokou úroveň hospodářského růstu. Navzdory tomu však Namibie stále čelí mnoha sociálním a ekonomickým výzvám, jako je vysoká nezaměstnanost a chudoba, zejména v zemědělských oblastech.\u003C\u002Fp\u003E\n\u003Cp\u003EPro turisty, kteří hledají krásné přírodní scenérie, kulturní zážitky a ochutnávky místní kuchyně, je Namibie skvělou volbou.\u003C\u002Fp\u003E",currency_code:"NAD",currency_name:"namibijský dolar",money_prices:[{name:"Taxi (cena za km)",value:12.5,date_update:b},{name:"Hlavní jídlo",value:70,date_update:b},{name:"Chleba",value:k,date_update:b},{name:"Mléko",value:l,date_update:b},{name:"Voda (500ml)",value:6,date_update:b},{name:"Espresso",value:m,date_update:b},{name:"Zmrzlina (jeden kopeček)",value:l,date_update:b},{name:"Pivo",value:14,date_update:b},{name:"Krabička cigaret",value:n,date_update:b},{name:"Bic Mac",value:a,date_update:b},{name:"Coca Cola (500ml)",value:k,date_update:b}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:r,date_update:c},{name:"Policie",value:"10111",date_update:c},{name:"Hasiči",value:r,date_update:c},{name:"Zdravotnická záchranná služba",value:"10177",date_update:c}],phone_prefix:"+264",coordinates:[{latitude:"-22.95764",longitude:"18.49041"}],people_religion:[{name:"Křesťanství",value:"93 %"},{name:"Tradiční africká náboženství",value:s},{name:"Jiná náboženství",value:t}],people_nationality:[{name:"Ovambové",value:"50 %"},{name:"Kavangové",value:"9 %"},{name:"Hererové",value:u},{name:"Damara",value:u},{name:"Náma",value:s},{name:"Capriviané",value:"4 %"},{name:"Basters",value:v},{name:"Sanové",value:v},{name:"Wambové",value:t},{name:"Jiné",value:"10 %"}],visitors_entry:[{name:"Občanský průkaz",value:"Občanský průkaz není platným dokladem pro vstup do Namibie. Pro vstup je nutné mít platný cestovní pas.",date_update:c},{name:"Cestovní pas",value:"Pro vstup do Namibie je nutné mít platný cestovní pas s platností minimálně 6 měsíců od data příjezdu.",date_update:c},{name:"Vízum",value:"Pro občany České republiky (EU) není potřeba víza pro pobyt kratší než 90 dnů. Pro delší pobyt je nutné požádat o vízum. Pokud je pobyt delší než 90 dnů, je nutné registrovat se na Ministerstvu vnitra během prvních 90 dnů pobytu.",date_update:c},{name:"Očkování",value:"Při vstupu do Namibie nejsou nutná žádná očkování. Nicméně jsou doporučována očkování proti hepatitidě A a B a tetanu.",date_update:c},{name:"Cestovní pojištění",value:"Při vstupu do Namibie není nutné mít cestovní pojištění, ale je velmi doporučeno, zejména pokud plánujete cestovat mimo větší města a turistické oblasti. Vzhledem k tomu, že zdravotní péče v Namibii může být velmi drahá, je dobré mít kvalitní zdravotní pojištění s krytím případného transportu do nemocnice v případě potřeby.",date_update:c},{name:"Bezpečností situace",value:"Namibie je obecně považována za bezpečnou zemi, avšak jako všude na světě, i zde může dojít ke krádežím a dalším drobným kriminalitěm. V některých oblastech Namibie mohou být přítomni divocí zvířata a je třeba dodržovat všechna varování a zásady bezpečnosti. Vzhledem k občasné přítomnosti silného větru a prachu, je vhodné mít s sebou ochranné brýle a šátek na ústa.",date_update:c},{name:"Další informace",value:"V Namibii je běžně používána hotovost v Namibijských dolarech a v některých turistických oblastech také v eurech. Vzhledem k tomu, že se jedná o rozlehlou zemi, je vhodné plánovat si cestování dopředu a připravit se na delší přesuny. Při cestování mimo větší města a turistické oblasti je vhodné mít s sebou dostatek zásob vody a jídla.",date_update:c}],type_place:f,id_image_cover:o,id_image_hero:o,id_city_main:p,affiliate:[{name:w,value:x}],mpz:"NA",alerts:a,zoom:[{google:g,booking:g}],ids_neighboring_countries:[{id:k},{id:n},{id:y},{id:z}],organization:a,apps:a,links:a,information_author:a,seo_tags:a,language_phrases:a,wall_sockets:a,id:A,facts_place:a,created_at:"2023-12-27T16:33:10.517062+00:00",updated_at:"2023-12-27T17:07:31.480426+00:00"}],imagePlace:[{id:o,name:q,source:h,type:f,author:i,created_at:B,updated_at:j}],placesStatesNeighboring:[{id:z,id_image_cover:C,slug:D,type_place:f,name:"Zambie"},{id:y,id_image_cover:E,slug:F,type_place:f,name:"Jihoafrická republika"},{id:n,id_image_cover:G,slug:H,type_place:f,name:"Botswana"},{id:k,id_image_cover:m,slug:I,type_place:f,name:"Angola"}],imagesStatesNeighboring:[{id:C,name:D,source:h,type:f,author:i,created_at:"2023-05-04T07:17:51.463854+00:00",updated_at:j},{id:E,name:F,source:h,type:f,author:i,created_at:B,updated_at:j},{id:G,name:H,source:h,type:f,author:i,created_at:"2023-05-02T18:49:47.851894+00:00",updated_at:j},{id:m,name:I,source:h,type:f,author:i,created_at:"2023-04-10T23:51:59.681083+00:00",updated_at:j}],placeContinent:[{id:g,slug:"afrika",name:"Afrika",area:30370000,population:1340000000,population_density:44.12,number_states:54,information_chatgpt:"\u003Cp\u003EAfrika je největší kontinent na Zemi a rozkládá se na ploše přesahující 30 milionů kilometrů čtverečních. Nachází se zde více než 50 zemí a žije zde více než 1,3 miliardy lidí. \u003C\u002Fp\u003E\n\u003Cp\u003EJižní Afrika, Egypt, Maroko a Keňa patří mezi nejoblíbenější turistické cíle v Africe. V Jihoafrické republice se můžete podívat na slavné Krugerův národní park a sledovat zde život zvířat v jejich přirozeném prostředí. Egypt je zase proslulý svými pyramidami, které se nacházejí v Gíze. V Maroku můžete navštívit tradiční trhy, zatímco v Keni se můžete setkat s Masajským kmenem a podívat se na úžasnou přírodu.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má bohatou historii a kulturu. První lidé, kteří se usadili v Africe, byli loveckým a sběračským národem. Poté se v této oblasti objevily první civilizace, jako je Kúšská říše, Egyptská říše nebo Království Aksum. Afrika byla také kolonizována evropskými mocnostmi a toto období mělo značný dopad na dnešní politickou situaci na kontinentu.\u003C\u002Fp\u003E\n\u003Cp\u003EJe důležité zmínit, že Afrika má velké množství jazyků a kultur. Zahrnuje mnoho různých národů a etnických skupin, a to má vliv na různorodost obyvatelstva a kultury. Nejrozšířenějším jazykem je arabština, která se mluví především v severní Africe a v některých částech západní Afriky. Dalšími důležitými jazyky jsou svahilština, francouzština, portugalština, angličtina a mnoho dalších.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má také nádhernou přírodu. Nachází se zde nejvyšší hora světa, Kilimandžáro, která se nachází na území Tanzanie a Keňy. Kromě toho se v Africe nachází také největší pouště na světě, Sahara, a největší prales, Kongský prales. Na tomto kontinentě můžete vidět také mnoho zvířat, jako jsou sloni, lvi, gepardi, zebry a mnoho dalších druhů. V Africe se také nachází několik nejvýznamnějších řek světa, jako je Nil, Kongo, Niger a Zambezi.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je bohužel také známá pro své problémy. Mnoho zemí se potýká s chudobou, nedostatkem potravin a vody, nemocemi a válečnými konflikty. Mnoho afrických zemí závisí na zemědělství a rybolovu, což však často není dostatečně výnosné, aby zabezpečilo potřeby obyvatelstva. Kromě toho jsou některé země v Africe trvale postižené extrémními přírodními katastrofami, jako jsou sucha, povodně a bouře.\u003C\u002Fp\u003E\n\u003Cp\u003ENa druhou stranu se v Africe také nachází mnoho lidí, kteří se snaží změnit situaci a zlepšit životy svých spoluobčanů. Vznikají neziskové organizace, které se snaží pomáhat při řešení těchto problémů a zlepšovat kvalitu života v mnoha afrických zemích. Zároveň se v posledních letech objevují nové možnosti pro ekonomický růst a rozvoj, jako jsou technologické start-upy a zahraniční investice.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika je fascinující kontinent, který nabízí mnoho zážitků a zároveň v sobě ukrývá mnoho výzev. Pokud se rozhodnete navštívit Afriku, můžete se těšit na krásnou přírodu, bohatou kulturu a nezapomenutelné zážitky. Je však také důležité být obezřetný a připravený na místní podmínky, protože některé oblasti mohou být nebezpečné kvůli konfliktům nebo zločinnosti.\u003C\u002Fp\u003E",coordinates:[{latitude:1.650801,longitude:17.371642}],type_place:"continent",id_image_cover:g,id_image_hero:g,zoom:[{google:2,booking:3}],information_author:a,seo_tags:a,created_at:"2023-12-27T16:35:37.460505+00:00",updated_at:"2023-12-27T17:07:06.531847+00:00"}],placesCities:[{id:1574,id_image_cover:a,importance:d,slug:"grootfontein",type_place:e,name:"Grootfontein"},{id:1573,id_image_cover:a,importance:d,slug:"katima-mulilo",type_place:e,name:"Katima Mulilo"},{id:1577,id_image_cover:a,importance:d,slug:"keetmanshoop",type_place:e,name:"Keetmanshoop"},{id:1571,id_image_cover:a,importance:d,slug:"oshakati",type_place:e,name:"Oshakati"},{id:1576,id_image_cover:a,importance:d,slug:"otjiwarongo",type_place:e,name:"Otjiwarongo"},{id:1575,id_image_cover:a,importance:d,slug:"rehoboth",type_place:e,name:"Rehoboth"},{id:1569,id_image_cover:a,importance:d,slug:"rundu",type_place:e,name:"Rundu"},{id:1572,id_image_cover:a,importance:d,slug:"swakopmund",type_place:e,name:"Swakopmund"},{id:1570,id_image_cover:a,importance:d,slug:"walvis-bay",type_place:e,name:"Walvis Bay"},{id:p,id_image_cover:a,importance:d,slug:J,type_place:e,name:K}],imagesCities:a,placeCityMain:[{id:p,id_state:A,name:K,population:431000,area:645,altitude:1654,slug:J,information_chatgpt:"\u003Cp\u003EWindhoek, hlavní město Namibie, je fascinujícím spojením moderní architektury a africké kultury. Město je domovem různorodé populace, kde se střetávají různé etnické skupiny, jako jsou Nama, Herero a Ovambo.\u003C\u002Fp\u003E\n\u003Cp\u003EJedním z významných symbolů Windhoeku je Christuskirche, gotický luteránský kostel postavený v 20. století německými kolonizátory. V historickém centru města naleznete Tintenpalast, což je sídlo namibijského parlamentu, s okouzlujícím německým architektonickým vlivem.\u003C\u002Fp\u003E\n\u003Cp\u003EPro milovníky umění a kultury je tu Národní muzeum Namibie, kde můžete objevit bohatou historii a tradice této fascinující země. Naopak trh Craft Centre nabízí autentické řemeslné výrobky místních umělců, perfektní suvenýry z cesty.\u003C\u002Fp\u003E\n\u003Cp\u003EWindhoek je známý svou pestrým gastronomickým světem. Vyzkoušejte tradiční namibijská jídla jako je biltong (sušené maso) a kapana (grilované maso), které můžete ochutnat v místních restauracích.\u003C\u002Fp\u003E\n\u003Cp\u003ENepřehlédněte také okolní přírodní krásy.  Příznivci outdoorových aktivit mohou podniknout výlety do nedalekého Národního parku Daan Viljoen, kde mohou obdivovat africkou faunu a flóru.\u003C\u002Fp\u003E\n\u003Cp\u003EWindhoek je takovým křižovatkou minulosti a současnosti, kde se snoubí různé kultury a historie, což z něj činí zajímavý cíl pro cestovatele hledající jedinečný pohled na Namibii.\u003C\u002Fp\u003E",coordinates:[{latitude:-22.57,longitude:17.0836}],biggest:"yes",type_place:e,id_image_cover:a,id_image_hero:a,affiliate:[{name:w,value:x}],alerts:a,zoom:[{google:l,booking:11}],parking:a,information_author:a,importance:d,seo_tags:a,created_at:"2023-12-27T16:36:26.534285+00:00",updated_at:"2023-12-27T17:06:48.496338+00:00"}],imageCityMain:a}],fetch:{"OrganismsoSidebarListComponent:0":{events:[],images:a}},mutations:[]}}(null,"09-2021","05-2023","biggest","city","state",5,"\u002Fworld\u002Fstates\u002F","pixabay.com","2023-12-27T20:42:18.520959+00:00",8,10,15,35,323,1568,"namibie","112","5 %","2 %","7 %","3 %","booking",true,247,248,160,"2023-05-03T20:14:00.749527+00:00",391,"zambie",278,"jihoafricka-republika",225,"botswana","angola","windhoek","Windhoek")));