__NUXT_JSONP__("/svet/stat/peru/ceny", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w,x,y,z,A,B,C,D,E,F,G,H,I,J,K,L,M,N,O,P,Q){return {data:[{place:[{tld:".pe",name:"Peru",area:1285216,population:32971854,slug:q,id_continent:g,information_chatgpt:"\u003Cp\u003EPeru je země nacházející se v Jižní Americe. Je to země s bohatou historií a kulturou, kterou si turisté mohou prohlédnout v mnoha městech a archeologických nalezištích. Nejznámější atrakcí je pravděpodobně Machu Picchu, starověké město Inků, které je zapsáno na seznamu světového dědictví UNESCO a je jedním z nejnavštěvovanějších turistických míst v Jižní Americe.\u003C\u002Fp\u003E\n\u003Cp\u003EKromě Machu Picchu, turisté mohou navštívit také hlavní město Lima, které nabízí mnoho památek a trhů s místními suvenýry. V Cuscu, bývalém hlavním městě Inků, se turisté mohou procházet po kamenitých ulicích a navštívit mnoho historických budov, včetně katedrály a chrámů.\u003C\u002Fp\u003E\n\u003Cp\u003EPeru je rovněž známé svou vynikající kuchyní, která kombinuje prvky ze španělské, indiánské a africké kuchyně. Mezi oblíbená jídla patří ceviche, tradiční pokrm z ryb a mořských plodů, a lomo saltado, pokrm z masa a zeleniny, obvykle podávaný s rýží.\u003C\u002Fp\u003E\n\u003Cp\u003EPeru je také domovem mnoha přírodních divů, jako jsou například Amazonie, Andské hory a jezero Titicaca, nejvyšší jezero na světě, kde turisté mohou navštívit plovoucí ostrovy Uros a poznat tradiční život lidí, kteří zde žijí.\u003C\u002Fp\u003E\n\u003Cp\u003EPro turisty, kteří chtějí zažít dobrodružství, Peru nabízí mnoho možností pro turistiku, horolezectví a rafting. Turisté se mohou také věnovat surfování na plážích v oblasti Lima nebo pozorování dravců a kondorů v Národním parku Colca Canyon. \u003C\u002Fp\u003E\n\u003Cp\u003EPeru je země plná přírodních krás a kulturního dědictví, a pro turisty, kteří chtějí prozkoumat tuto zemi, je zde mnoho zajímavých míst k návštěvě a aktivit, které lze podniknout. \u003C\u002Fp",currency_code:"PEN",currency_name:"Peruánský sol",money_prices:[{name:"Taxi (cena za km)",value:2.5,date_update:d},{name:"Hlavní jídlo",value:30,date_update:d},{name:"Chleba",value:g,date_update:d},{name:"Mléko",value:r,date_update:d},{name:"Voda (500ml)",value:g,date_update:d},{name:"Espresso",value:n,date_update:d},{name:"Zmrzlina (jeden kopeček)",value:g,date_update:d},{name:"Pivo",value:8,date_update:d},{name:"Krabička cigaret",value:15,date_update:d},{name:"Bic Mac",value:a,date_update:d},{name:"Coca Cola (500ml)",value:n,date_update:d}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:"911",date_update:b},{name:"Policie",value:"105",date_update:b},{name:"Hasiči",value:"116",date_update:b},{name:"Zdravotnická záchranná služba",value:"117",date_update:b},{name:"Turistická policie",value:"0800-22221",date_update:b},{name:"Horská záchranná služba",value:"118",date_update:b},{name:"Pobřežní záchranná služba",value:"119",date_update:b}],phone_prefix:"+51",coordinates:[{latitude:"-9.189967",longitude:"-75.015152"}],people_religion:[{name:"Římskokatolické křesťanství",value:"76%",date_update:h},{name:"Evangeličtí křesťané",value:"14%",date_update:h},{name:"Jiná křesťanská vyznání",value:"6%",date_update:h},{name:"Nevěřící",value:s,date_update:h},{name:"Judaismus",value:t,date_update:h},{name:"Jiná náboženství",value:t,date_update:h}],people_nationality:[{name:"Mestici",value:"60%",date_update:i},{name:"Indiáni",value:"25%",date_update:i},{name:"Běloši",value:u,date_update:i},{name:"Afroperuánci",value:"3%",date_update:i},{name:"Asiaté",value:s,date_update:i},{name:"Ostatní",value:u,date_update:i}],visitors_entry:[{name:"Občanský průkaz",value:"Pro vstup do Peru je třeba mít platný cestovní doklad s fotografií. Občanský průkaz není platným cestovním dokladem pro vstup do země.",date_update:b},{name:"Cestovní pas",value:"Pro vstup do Peru je třeba mít platný cestovní pas. Občané České republiky mohou vstoupit do země s platným pasem bez nutnosti získání víza na pobyt do 183 dnů.",date_update:b},{name:"Vízum",value:"Pro občany České republiky není potřeba žádné vízum pro vstup do Peru na pobyt do 183 dnů. Při vstupu do země je nutné mít platný cestovní pas a předložit doklad o zpáteční letence nebo doklad o pokračujícím cestování.",date_update:b},{name:"Očkování",value:"Pro vstup do Peru nejsou povinné žádné očkování. Avšak je doporučeno očkování proti žloutence A a B, hepatitidě A a B, obrně, tyfu a žloutence typu B. V některých případech může být doporučeno i očkování proti žluté zimnici, pokud cestujete z oblasti, kde se tato nemoc vyskytuje.",date_update:b},{name:"Cestovní pojištění",value:"Pro vstup do Peru není vyžadováno povinné cestovní ani zdravotní pojištění. Nicméně, z důvodu nebezpečí krádeží a ztráty cestovních dokladů, se doporučuje mít sjednané cestovní pojištění, které pokryje náklady na případné lékařské ošetření, úrazy a krádeže během pobytu v zemi.",date_update:b},{name:"Bezpečností situace",value:"V Peru jsou určitá rizika z hlediska bezpečnosti a kriminality. Zvláštní opatrnost se doporučuje v oblasti hranic s Kolumbií a Ekvádorem, kde operují drogové gangy a teroristické organizace. Dále je potřeba být opatrný v centrálních částech měst Lima a Cusco, kde dochází ke krádežím a kapsářství. Při cestování mimo turistické oblasti je vhodné se dopředu informovat o bezpečnostní situaci v dané oblasti a necestovat v noci.",date_update:b},{name:"Další informace",value:"V Peru se setkáte s mnoha kulturními zvyklostmi, jako jsou tradiční tance, oblečení a gastronomie. Je vhodné respektovat místní kulturu a zvyky, zejména v případě návštěvy historických památek a kostelů. Dále je potřeba být opatrný při konzumaci vody a potravin, a preferovat balenou vodu a restaurace s dobrou hygienou.",date_update:b}],type_place:c,id_image_cover:o,id_image_hero:o,id_city_main:p,affiliate:[{name:v,value:w}],mpz:"PE",alerts:a,zoom:[{google:r,booking:5}],ids_neighboring_countries:[{id:x},{id:y},{id:z},{id:A},{id:B}],organization:a,apps:a,links:a,information_author:a,seo_tags:a,language_phrases:a,wall_sockets:a,id:C,facts_place:a,created_at:"2023-12-27T16:33:10.517062+00:00",updated_at:"2023-12-27T17:07:31.480426+00:00"}],imagePlace:[{id:o,name:q,source:j,type:c,author:k,created_at:D,updated_at:l}],placesStatesNeighboring:[{id:B,id_image_cover:E,slug:F,type_place:c,name:"Ekvádor"},{id:A,id_image_cover:G,slug:H,type_place:c,name:"Kolumbie"},{id:z,id_image_cover:I,slug:J,type_place:c,name:"Chile"},{id:y,id_image_cover:K,slug:L,type_place:c,name:"Brazílie"},{id:x,id_image_cover:M,slug:N,type_place:c,name:"Bolívie"}],imagesStatesNeighboring:[{id:G,name:H,source:j,type:c,author:k,created_at:D,updated_at:l},{id:E,name:F,source:j,type:c,author:k,created_at:m,updated_at:l},{id:I,name:J,source:j,type:c,author:k,created_at:m,updated_at:l},{id:K,name:L,source:j,type:c,author:k,created_at:m,updated_at:l},{id:M,name:N,source:j,type:c,author:k,created_at:m,updated_at:l}],placeContinent:[{id:g,slug:"jizni-amerika",name:"Jižní Amerika",area:17840000,population:430000000,population_density:24.11,number_states:12,information_chatgpt:"\u003Cp\u003EJižní Amerika je kontinent nacházející se v jižní polovině zeměkoule. Je druhým největším kontinentem v Americe a čtvrtým největším kontinentem na světě. Rozprostírá se od Karibiku na severu po Průliv Drakea na jihu, což představuje přibližně 7 000 kilometrů. Tento kontinent obývá více než 420 milionů lidí a skládá se ze 12 různých států.\u003C\u002Fp\u003E\n\u003Cp\u003EJižní Amerika má velmi rozmanitou geografii. Na západní straně kontinentu se nachází Andské pohoří, které je nejdelší pohoří na světě a táhne se přes 7 zemí. Na východní straně se nachází rozsáhlé deštné pralesy a oblasti savan. Kromě toho se zde nachází také největší řeka na světě - Amazonka, která má neuvěřitelný vliv na floru a faunu regionu.\u003C\u002Fp\u003E\n\u003Cp\u003EJižní Amerika má také velmi bohatou historii a kulturu. Před příchodem Evropanů byla tato oblast domovem mnoha starověkých civilizací, jako jsou Inkové, Mayové a Aztékové. Tyto civilizace zanechaly pozoruhodné památky, jako jsou Machu Picchu, Chichén Itzá a Tiahuanaco. Později byla tato oblast ovlivněna kolonizací ze strany Španělska a Portugalska, což má dodnes vliv na jazyky, kulturu a způsob života.\u003C\u002Fp\u003E\n\u003Cp\u003EJižní Amerika je také velmi oblíbenou turistickou destinací. V Argentině a Brazílii se nacházejí největší vodopády světa - Iguazú a Niagarské vodopády. Peru je domovem Machu Picchu, jedné z nejnavštěvovanějších archeologických lokalit na světě. Kromě toho jsou zde také krásné pláže, horské túry, národní parky a další přírodní krásy.\u003C\u002Fp\u003E\n\u003Cp\u003EV Jižní Americe se také nachází mnoho kulturních festivalů a událostí. Například karnevaly v Rio de Janeiru a Buenos Aires jsou světoznámé pro svou atmosféru a pestrost barev. Také zde můžete ochutnat mnoho unikátních kulinářských specialit, jako jsou arepas v Kolumbii, ceviche v Peru a asado v Argentině.\u003C\u002Fp\u003E\n\u003Cp\u003EZkrátka, Jižní Amerika je kontinent plnýkrás, kulturního bohatství a přírodních divů. Mnoho turistů sem přijíždí za poznáním exotického prostředí, ale také kvůli možnostem dobrodružství a sportovních aktivit. Například v Brazílii je oblíbená plážová fotbalová hra zvaná futevôlei, zatímco v Chile si turisté užívají snowboardování na Andách.\u003C\u002Fp\u003E\n\u003Cp\u003EJižní Amerika má také své problémy, jako je sociální nerovnost, kriminalita a korupce. Některé oblasti jsou považovány za nebezpečné pro turisty, takže je třeba být obezřetní a využívat služby kvalitních turistických průvodců.\u003C\u002Fp\u003E\n\u003Cp\u003EZávěrem lze říci, že Jižní Amerika je fascinující kontinent, který nabízí mnoho příležitostí pro poznání, zážitky a dobrodružství. Bez ohledu na to, jestli plánujete návštěvu kvůli přírodě, kultuře nebo jen na odpočinek na pláži, určitě nebudete zklamáni.\u003C\u002Fp\u003E",coordinates:[{latitude:-14.235004,longitude:-51.92528}],type_place:"continent",id_image_cover:g,id_image_hero:g,zoom:[{google:g,booking:n}],information_author:a,seo_tags:a,created_at:"2023-12-27T16:35:37.460505+00:00",updated_at:"2023-12-27T17:07:06.531847+00:00"}],placesCities:[{id:1709,id_image_cover:a,importance:e,slug:"arequipa",type_place:f,name:"Arequipa"},{id:1711,id_image_cover:a,importance:e,slug:"chiclayo",type_place:f,name:"Chiclayo"},{id:1715,id_image_cover:a,importance:e,slug:"chimbote",type_place:f,name:"Chimbote"},{id:1714,id_image_cover:a,importance:e,slug:"cusco",type_place:f,name:"Cusco"},{id:1716,id_image_cover:a,importance:e,slug:"huancayo",type_place:f,name:"Huancayo"},{id:1713,id_image_cover:a,importance:e,slug:"iquitos",type_place:f,name:"Iquitos"},{id:p,id_image_cover:a,importance:e,slug:O,type_place:f,name:P},{id:1712,id_image_cover:a,importance:e,slug:"piura",type_place:f,name:"Piura"},{id:1717,id_image_cover:a,importance:e,slug:"tacna",type_place:f,name:"Tacna"},{id:1710,id_image_cover:a,importance:e,slug:"trujillo",type_place:f,name:"Trujillo"}],imagesCities:a,placeCityMain:[{id:p,id_state:C,name:P,population:9525274,area:2672.28,altitude:0,slug:O,information_chatgpt:a,coordinates:[{latitude:-12.046374,longitude:-77.042793}],biggest:"yes",type_place:f,id_image_cover:a,id_image_hero:a,affiliate:[{name:v,value:w}],alerts:[],zoom:[{google:10,booking:11}],parking:[],information_author:[{text:"\u003Cp\u003E\u003Cb\u003ELima\u003C\u002Fb\u003E, hlavní město \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fperu\"\u003EPeru\u003C\u002Fa\u003E\u003C\u002Fb\u003E, je fascinujícím spojením historie, kultury a moderního života. Leží na pobřeží \u003Cb\u003ETichého oceánu\u003C\u002Fb\u003E a nabízí návštěvníkům bohatý kulturní zážitek. \u003Cb\u003EStaré Město (Centro Histórico)\u003C\u002Fb\u003E je domovem historických památek, jako je \u003Cb\u003EPlaza Mayor de Lima\u003C\u002Fb\u003E, s katedrálou a prezidentským palácem.\u003C\u002Fp\u003E\n\u003Cp\u003EMezi klíčové turistické atrakce patří také \u003Cb\u003Eklášter San Francisco(Convento de San Francisco de Jesús El Grande)\u003C\u002Fb\u003E s podzemními katakombami a unikátními malbami. Malebná čtvrť \u003Cb\u003EBarranco\u003C\u002Fb\u003E láká na svou bohémskou atmosféru, umělecké galerie a barevnou architekturu.\u003C\u002Fp\u003E\n  \u003Cp\u003EPřímořská lokalita nabízí nádherné pláže, jako je \u003Cb\u003EPlaya Redondo\u003C\u002Fb\u003E, kde si můžete užívat slunce a ochutnávat čerstvé mořské plody. Oblast \u003Cb\u003EMiraflores\u003C\u002Fb\u003E s nákupními centry, parky a výhledem na oceán představuje moderní tvář \u003Cb\u003ELimy\u003C\u002Fb\u003E.\u003C\u002Fp\u003E\n  \u003Cp\u003E\u003Cb\u003ELima\u003C\u002Fb\u003E si zachovává kulturní dědictví s tradičními festivity, jako je \u003Cb\u003EInti Raymi\u003C\u002Fb\u003E a \u003Cb\u003EDía de la Independencia del Perú\u003C\u002Fb\u003E. Město je domovem i několika muzeí, jako je \u003Cb\u003EMuzeum Larco(Museo Larco)\u003C\u002Fb\u003E, ukazující bohatství peruánské historie a archeologie.\u003C\u002Fp\u003E\n\u003Cp\u003ESvětovým fenoménem jsou také peruvští hudebníci, kteří vytvářejí unikátní směsici tradičních a moderních rytmů. \u003Cb\u003ELima\u003C\u002Fb\u003E tak poskytuje pohled do pestrého a dynamického světa peruánské kultury.\u003C\u002Fp\u003E",date_create:"2024-01-03",date_update:Q,author_create:"1",author_update:Q}],importance:e,seo_tags:[],created_at:"2023-12-27T16:36:26.534285+00:00",updated_at:"2024-01-03T20:38:09.900598+00:00"}],imageCityMain:a}],fetch:{"OrganismsoSidebarListComponent:0":{events:[],images:a}},mutations:[]}}(null,"05-2023","state","09-2021","biggest","city",2,"01-2021","04-2023","\u002Fworld\u002Fstates\u002F","pixabay.com","2023-12-27T20:42:18.520959+00:00","2023-05-02T18:49:47.851894+00:00",3,341,1708,"peru",4,"2%","1%","5%","booking",true,29,31,46,49,63,174,"2023-05-03T20:14:00.749527+00:00",240,"ekvador",290,"kolumbie",230,"chile",226,"brazilie",224,"bolivie","lima","Lima","")));