__NUXT_JSONP__("/svet/stat/vietnam/flora", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o){return {data:[{tabsLoad:{tabWhatToSee:f,tabPrice:f,tabPeople:f,tabTrip:f,tabContact:f,tabHotel:f,tabNeighboring:e,tabVideos:g,tabArticles:g,tabWallSockets:e,tabChains:e,tabFauna:g,tabFlora:g,tabFoods:g,tabBrands:g},place:[{tld:".vn",name:"Vietnam",area:331210,population:97429061,slug:k,id_continent:e,information_chatgpt:"\u003Cp\u003EVietnam se nachází v jihovýchodní Asii a je známý svou bohatou historií a kulturou. Země má mnoho turistických atrakcí, jako jsou například krásné pláže, starobylé chrámy a památky, nádherné přírodní scenérie a skvělá jídla.\u003C\u002Fp\u003E\n\u003Cp\u003EBěhem 20. století byl Vietnam zapleten do několika konfliktů, včetně války ve Vietnamu, která skončila v roce 1975. Od té doby se země stala jednou z nejrychleji rostoucích ekonomik v Asii, což vedlo ke zlepšení životních podmínek pro mnoho obyvatel.\u003C\u002Fp\u003E\n\u003Cp\u003EVietnamská kuchyně je známá po celém světě a nabízí širokou škálu jídel, jako jsou phở (polévka z nudlových kusů a hovězího masa), bánh mì (bageta s vepřovým, drůbežím nebo tofu), a nước mắm (rybí omáčka). Vietnamská káva je také velmi oblíbená, připravuje se s kondenzovaným mlékem a servíruje se s ledem.\u003C\u002Fp\u003E\n\u003Cp\u003EVietnam má také mnoho přírodních krás, jako jsou zátoky Ha Long a Nha Trang, pohoří Sapa a mnoho dalších. Mnoho turistů také navštěvuje města Hanoj a Ho Či Minovo město, aby viděli historické památky a poznali vietnamskou kulturu.\u003C\u002Fp\u003E\n\u003Cp\u003EJe důležité vědět, že Vietnam je země s rozvíjející se ekonomikou a mnoho obyvatel zde stále žije v chudobě. Při cestování byste měli být ohleduplní k místním obyvatelům a respektovat jejich kulturu a zvyky.\u003C\u002Fp\u003E",currency_code:"VND",currency_name:"Vietnamský dong",money_prices:[{name:"Taxi (cena za km)",value:h,date_update:d},{name:"Hlavní jídlo",value:50000,date_update:d},{name:"Chleba",value:h,date_update:d},{name:"Mléko",value:i,date_update:d},{name:"Voda (500ml)",value:5000,date_update:d},{name:"Espresso",value:i,date_update:d},{name:"Zmrzlina (jeden kopeček)",value:h,date_update:d},{name:"Pivo",value:i,date_update:d},{name:"Krabička cigaret",value:b,date_update:d},{name:"Big Mac",value:b,date_update:d},{name:"Coca Cola (500ml)",value:h,date_update:d}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:l,date_update:c},{name:"Policie",value:"113",date_update:c},{name:"Hasiči",value:"114",date_update:c},{name:"Zdravotnická záchranná služba",value:l,date_update:c},{name:"Turistická policie",value:"069 234 2486",date_update:c}],phone_prefix:"+84",coordinates:[{latitude:"14.0583",longitude:"108.2772"}],people_religion:[{name:"Buddhismus",value:"70%",date_update:a},{name:"Křesťanství",value:"7%",date_update:a},{name:"Cao Đài",value:"3%",date_update:a},{name:"Hòa Hảo",value:"2%",date_update:a},{name:"Islám",value:"0.1%",date_update:a},{name:"Judaismus",value:"0.01%",date_update:a},{name:"Nejisté\u002Fvíce náboženství",value:"16.4%",date_update:a},{name:"Bez vyznání",value:"1.49%",date_update:a}],people_nationality:[{name:"Kinh",value:"86%",date_update:a},{name:"Tày",value:"1.9%",date_update:a},{name:"Thái",value:"1.8%",date_update:a},{name:"Mường",value:m,date_update:a},{name:"Khmerové",value:m,date_update:a},{name:"Hoavé",value:n,date_update:a},{name:"Nùng",value:n,date_update:a},{name:"Mèové",value:"0.9%",date_update:a},{name:"Dao",value:"0.8%",date_update:a},{name:"Gia-rai",value:"0.5%",date_update:a}],visitors_entry:[{name:"Občanský průkaz",value:"Pro vstup do Vietnamu je třeba mít platný cestovní pas. Občanský průkaz není platným dokladem pro vstup do země.",date_update:c},{name:"Cestovní pas",value:"Pro vstup do Vietnamu je třeba mít platný cestovní pas.",date_update:c},{name:"Vízum",value:"Pro vstup do Vietnamu je většinou třeba mít vízum. Občané České republiky mohou vstoupit do Vietnamu na dobu do 15 dnů bez víza, pokud cestují za účelem turistiky. Pokud je plánován delší pobyt nebo jiný druh cesty, je třeba si předem zajistit vízum.",date_update:c},{name:"Očkování",value:"Pro vstup do Vietnamu není povinné očkování. Nicméně se doporučuje očkování proti hepatitidě A a B, tyfu a klíšťové encefalitidě. Vzhledem k aktuální situaci s pandemií COVID-19 mohou být také požadovány další omezení a opatření, jako například negativní test na COVID-19, karanténa po příjezdu apod. Proto je nutné být informován o aktuálních pravidlech a doporučeních před cestou do Vietnamu.",date_update:c},{name:"Cestovní pojištění",value:"Pro vstup do Vietnamu není povinné mít cestovní ani zdravotní pojištění, ale doporučuje se si ho sjednat kvůli případnému nečekanému výpadku zdraví nebo úrazu.",date_update:c},{name:"Bezpečnostní situace",value:"Vietnam je obecně bezpečná země pro cestování. Avšak turisté by měli být obezřetní v místech s vysokou koncentrací lidí, jako jsou například nádraží, trhy a turistické atrakce. Dále by se měli vyhýbat místům, kde dochází k protestům a politickým shromážděním.",date_update:c},{name:"Další informace",value:"Vietnam je země s bohatou kulturou a zvyklostmi, které se mohou lišit od těch západních. Například se očekává, že návštěvníci budou respektovat místní zvyky a tradice, jako je například zdvořilost, pokora a úcta k rodině. Dále by měli být turisté obezřetní při konzumaci potravin a nápojů, a měli by se vyhýbat neupraveným potravinám a vodě z neznámých zdrojů.",date_update:c}],type_place:o,id_image_cover:j,id_image_hero:j,id_city_main:2375,affiliate:[{name:"booking",value:f}],mpz:"VN",alerts:b,zoom:[{google:4,booking:5}],ids_neighboring_countries:[{id:48},{id:117},{id:126}],organization:b,apps:b,links:b,information_author:b,seo_tags:b,language_phrases:b,wall_sockets:b,id:241,facts_place:b,created_at:"2023-12-27T16:33:10.517062+00:00",updated_at:"2023-12-27T17:07:31.480426+00:00"}],imagePlace:[{id:j,name:k,source:"\u002Fworld\u002Fstates\u002F",type:o,author:"pixabay.com",created_at:"2023-05-04T07:17:51.463854+00:00",updated_at:"2023-12-27T20:42:18.520959+00:00"}],placeContinent:[{id:e,slug:"asie",name:"Asie",area:44580000,population:4630000000,population_density:104.03,number_states:50,information_chatgpt:"\u003Cp\u003E\u003Cb\u003EAsie\u003C\u002Fb\u003E se rozkládá na ploše téměř 44,6 milionu kilometrů čtverečních a žije zde více než 4,5 miliardy lidí, což představuje téměř 60 % celkového počtu obyvatel planety. Kontinent zahrnuje celkem 48 zemí, mezi největší patří \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Frusko\"\u003ERusko\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fcina\"\u003EČína\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Findie\"\u003EIndie\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fjaponsko\"\u003EJaponsko\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Findonesie\"\u003EIndonésie\u003C\u002Fa\u003E\u003C\u002Fb\u003E.\u003C\u002Fp\u003E\n\u003Cp\u003E\u003Cb\u003EAsie\u003C\u002Fb\u003E je domovem mnoha významných světových kultur, jako jsou hinduismus, buddhismus, islám a taoismus. V každé zemi se můžeš setkat s odlišnými tradicemi, zvyky a jazyky. Mezi nejnavštěvovanější turistické destinace patří například \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fjaponsko\"\u003EJaponsko\u003C\u002Fa\u003E\u003C\u002Fb\u003E s jeho futuristickými městy, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fthajsko\"\u003EThajsko\u003C\u002Fa\u003E\u003C\u002Fb\u003E s bohatou kulturou a krásnými plážemi, nebo \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Findonesie\"\u003EIndonésie\u003C\u002Fa\u003E\u003C\u002Fb\u003E s neuvěřitelnou přírodou a množstvím ostrovů.\u003C\u002Fp\u003E\n\u003Cp\u003E\u003Cb\u003EAsie\u003C\u002Fb\u003E také nabízí mnoho kulturních a historických památek, jako je \u003Cb\u003EVelká čínská zeď\u003C\u002Fb\u003E, \u003Cb\u003ETaj Mahal\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Findie\"\u003EIndii\u003C\u002Fa\u003E\u003C\u002Fb\u003E, buddhistické chrámy v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fthajsko\"\u003EThajsku\u003C\u002Fa\u003E\u003C\u002Fb\u003E a mnoho dalšího. Zajímavou zkušeností může být i návštěva tradičních trhů, kde se dají ochutnat místní speciality a nakoupit různé exotické produkty.\u003C\u002Fp\u003E\n\u003Cp\u003E\u003Cb\u003EAsie\u003C\u002Fb\u003E má však také své výzvy a problémy, jako jsou například růst městských oblastí, nedostatek vody a znečištění ovzduší. Mnoho zemí se snaží najít řešení těchto problémů a přizpůsobit se moderním trendům, aby mohly udržet krok s ostatními zeměmi světa.\u003C\u002Fp\u003E\n\u003Cp\u003EPokud tě láká cestování po \u003Cb\u003EAsii\u003C\u002Fb\u003E, měl by sis předem zajistit víza a informovat se o místních zvycích a tradicích. Rozhodně se ti však naskytne možnost prozkoumat jedinečnou kulturu, přírodu a historii tohoto fascinujícího kontinentu.\u003C\u002Fp\u003E\u003Cp\u003E\u003Cb\u003EAsie\u003C\u002Fb\u003E má také neuvěřitelně bohatou gastronomii, která se v jednotlivých zemích a regionech liší. Mezi nejznámější asijské pokrmy patří \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fjidlo\u002Fsushi\"\u003Esushi\u003C\u002Fa\u003E\u003C\u002Fb\u003E, různé druhy nudlí, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fjidlo\u002Fcurry\"\u003Ecurry\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003Edim sum\u003C\u002Fb\u003E, \u003Cb\u003Epho\u003C\u002Fb\u003E a další. Kromě toho se v \u003Cb\u003EAsii\u003C\u002Fb\u003E pěstuje mnoho exotických plodin, jako jsou \u003Cb\u003Eduriany\u003C\u002Fb\u003E, \u003Cb\u003Emangostany\u003C\u002Fb\u003E, \u003Cb\u003Erambutany\u003C\u002Fb\u003E a další ovoce, které můžeš ochutnat pouze zde.\n\u003C\u002Fp\u003E\u003Cp\u003E\n\u003Cb\u003EAsie\u003C\u002Fb\u003E je také známá svými svátky a tradicemi, jako je například \u003Cb\u003Ečínský Nový rok\u003C\u002Fb\u003E, \u003Cb\u003EDiwali\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Findie\"\u003EIndii\u003C\u002Fa\u003E\u003C\u002Fb\u003E, nebo \u003Cb\u003ESongkran\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fthajsko\"\u003EThajsku\u003C\u002Fa\u003E\u003C\u002Fb\u003E,. Tyto svátky jsou většinou spojeny s různými rituály a zvyky, které se v průběhu staletí vyvinuly a jsou dodnes respektovány.\n\u003C\u002Fp\u003E\u003Cp\u003E\nKromě toho má \u003Cb\u003EAsie\u003C\u002Fb\u003E mnoho přírodních krás, jako jsou \u003Cb\u003EHimálaje\u003C\u002Fb\u003E, pouště \u003Cb\u003EGobi\u003C\u002Fb\u003E a \u003Cb\u003EThar\u003C\u002Fb\u003E, nebo tropické pralesy v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Findonesie\"\u003EIndonésii\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmalajsie\"\u003EMalajsii\u003C\u002Fa\u003E\u003C\u002Fb\u003E. V \u003Cb\u003EAsii\u003C\u002Fb\u003E se také nachází mnoho národních parků a chráněných oblastí, kde žijí ohrožené druhy zvířat, jako jsou \u003Cb\u003Etygři\u003C\u002Fb\u003E, \u003Cb\u003Epandy\u003C\u002Fb\u003E, \u003Cb\u003Esloni\u003C\u002Fb\u003E a další.\n\u003C\u002Fp\u003E\u003Cp\u003E\nZkrátka, \u003Cb\u003EAsie\u003C\u002Fb\u003E je kontinent plný kontrastů a neuvěřitelných zážitků, které stojí za to objevovat. Budeš-li mít příležitost navštívit některou z asijských zemí, neváhej a vydaj se na dobrodružnou cestu plnou nových objevů a nezapomenutelných zážitků.\u003C\u002Fp\u003E",coordinates:[{latitude:47.751076,longitude:92.475286}],type_place:"continent",id_image_cover:e,id_image_hero:e,zoom:[{google:2,booking:e}],information_author:b,seo_tags:b,created_at:"2023-12-27T16:35:37.460505+00:00",updated_at:"2023-12-27T17:07:06.531847+00:00"}]}],fetch:{"OrganismsoSidebarListComponent:0":{events:[],images:b}},mutations:[]}}("12-2020",null,"05-2023","05-2021",3,true,0,10000,20000,389,"vietnam","115","1.5%","1.1%","state")));