__NUXT_JSONP__("/svet/mesto/jerevan", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w,x,y){return {data:[{place:[{id:h,id_state:i,name:r,population:1075800,area:223,altitude:989,slug:j,information_chatgpt:a,coordinates:[{latitude:40.1811,longitude:44.5136}],biggest:"yes",type_place:b,id_image_cover:f,id_image_hero:f,affiliate:[{name:m,value:n}],alerts:[],zoom:[{google:10,booking:11}],parking:[],information_author:[],importance:c,seo_tags:a,created_at:"2023-12-27T16:36:26.534285+00:00",updated_at:"2023-12-27T17:06:48.496338+00:00",name_original:a}],imagePlace:[{id:f,name:j,source:s,type:b,author:o,created_at:t,updated_at:k}],placeState:[{tld:".am",name:"Arménie",area:29743,population:2963234,slug:u,id_continent:g,information_chatgpt:"\u003Cp\u003EArménie je horský stát v Kavkazském regionu, který se nachází mezi Evropou a Asií. Má bohatou historii a kulturu, jako první země přijala křesťanství jako státní náboženství. Arménie je domovem mnoha starobylých klášterů, kostelů a hrobek, z nichž některé jsou zapsány na seznamu světového dědictví UNESCO.\u003C\u002Fp\u003E\n\u003Cp\u003EHlavním městem Arménie je Jerevan, který je plný historických památek a moderních budov. Jerevan je také centrem kulinářského umění a nabízí vynikající tradiční arménská jídla, jako jsou kebab, khash a lavash.\u003C\u002Fp\u003E\n\u003Cp\u003EArménie je známá svou krásnou přírodou, zahrnující hory, lesy, jezera a řeky. Nejvyšší horou v Arménii je Ararat, na které se podle biblického příběhu měla přistát Noemova archa. Arménská krajina je také plná divokých květin a bylin, jako je levandule, máta a šalvěj.\u003C\u002Fp\u003E\n\u003Cp\u003EPokud se chystáte do Arménie, určitě byste měli navštívit kláštery Khor Virap, Haghpat a Geghard, zámek Amberd, a také vodopády v Garni a Jermuk. Navíc můžete navštívit národní park Dilijan a lyžařská střediska v Tsakhkadzoru a Jermuku.\u003C\u002Fp\u003E",currency_code:"AMD",currency_name:"arménský dram",money_prices:[{name:"Taxi (cena za km)",value:a,date_update:d},{name:"Hlavní jídlo",value:3500,date_update:d},{name:"Chleba",value:220,date_update:d},{name:"Mléko",value:400,date_update:d},{name:"Voda (500ml)",value:100,date_update:d},{name:"Espresso",value:v,date_update:d},{name:"Zmrzlina (jeden kopeček)",value:a,date_update:d},{name:"Pivo",value:500,date_update:d},{name:"Krabička cigaret",value:a,date_update:d},{name:"Bic Mac",value:a,date_update:d},{name:"Coca Cola (500ml)",value:v,date_update:d}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:"112",date_update:l},{name:"Policie",value:"102",date_update:l},{name:"Hasiči",value:"101",date_update:l},{name:"Zdravotnická záchranná služba",value:"103",date_update:l}],phone_prefix:"+374",coordinates:[{latitude:"40.0691",longitude:"45.0382"}],people_religion:[{name:"Arménská apoštolská církev",value:"92.6%"},{name:"Pravoslavné církve",value:"0.5%"},{name:"Křesťanství bez příslušnosti ke konkrétní církvi",value:"0.3%"},{name:"Jiné náboženství",value:"1.6%"},{name:"Bez vyznání",value:"5%"}],people_nationality:[{name:"Arméni",value:98.1},{name:"Rusové",value:.5},{name:"Jezídové",value:1.3}],visitors_entry:[{name:"Občanský průkaz",value:"Občanský průkaz není platným cestovním dokladem pro vstup do Arménie. Je třeba mít platný cestovní pas.",date_update:e},{name:"Cestovní pas",value:"Pro vstup do Arménie je třeba mít platný cestovní pas. Platnost pasu musí být alespoň 6 měsíců od data příjezdu. Pas by měl obsahovat alespoň jednu prázdnou stránku pro razítko.",date_update:e},{name:"Vízum",value:"Pro občany České republiky není nutné mít vízum pro pobyt do 180 dnů. Pokud cestujete za jiným účelem než za turistikou, může být třeba žádat o vízum před cestou.",date_update:e},{name:"Očkování",value:"Pro vstup do Arménie není povinné očkování proti žádným nemocem. Avšak, jako u každé cesty, je vhodné zvážit, zda si nechat doporučit některá očkování podle aktuálních epidemiologických podmínek.",date_update:e},{name:"Cestovní pojištění",value:"Cestovní pojištění není povinné, ale je silně doporučeno mít ho sjednané před cestou kvůli zajištění úhrady lékařské péče v případě potřeby.",date_update:e},{name:"Bezpečnostní situace",value:"Arménie je v současné době bezpečnou zemí, nicméně občané by měli být obezřetní v okolí hranic s Ázerbájdžánem a Náhorním Karabachem, kde docházelo k ozbrojeným konfliktům v minulosti.",date_update:e},{name:"Další informace",value:"V Arménii je běžné uvítání polibkem na tvář, při setkání s místními lidmi by měli cestovatelé dodržovat obecné zásady slušného chování. Výměna peněz se nejlépe provádí v měnárnách a bankách. V Arménii je také běžné, že místní lidé kouří, ale kouření je zakázáno v některých veřejných prostorech.",date_update:e}],type_place:w,id_image_cover:p,id_image_hero:p,id_city_main:h,affiliate:[{name:m,value:n}],mpz:"AM",alerts:[],zoom:[{google:i,booking:8}],ids_neighboring_countries:[{id:16},{id:79},{id:108},{id:225}],organization:[],apps:[],links:[],information_author:[],seo_tags:[],language_phrases:[],wall_sockets:a,id:i,facts_place:a,created_at:"2023-12-27T16:33:10.517062+00:00",updated_at:"2023-12-27T17:07:31.480426+00:00",name_original:a}],imageState:[{id:p,name:u,source:"\u002Fworld\u002Fstates\u002F",type:w,author:o,created_at:"2023-04-10T23:51:52.042149+00:00",updated_at:k}],placeContinent:[{id:g,slug:"asie",name:"Asie",area:44580000,population:4630000000,population_density:104.03,number_states:50,information_chatgpt:"\u003Cp\u003E\u003Cb\u003EAsie\u003C\u002Fb\u003E se rozkládá na ploše téměř 44,6 milionu kilometrů čtverečních a žije zde více než 4,5 miliardy lidí, což představuje téměř 60 % celkového počtu obyvatel planety. Kontinent zahrnuje celkem 48 zemí, mezi největší patří \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Frusko\"\u003ERusko\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fcina\"\u003EČína\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Findie\"\u003EIndie\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fjaponsko\"\u003EJaponsko\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Findonesie\"\u003EIndonésie\u003C\u002Fa\u003E\u003C\u002Fb\u003E.\u003C\u002Fp\u003E\n\u003Cp\u003E\u003Cb\u003EAsie\u003C\u002Fb\u003E je domovem mnoha významných světových kultur, jako jsou hinduismus, buddhismus, islám a taoismus. V každé zemi se můžeš setkat s odlišnými tradicemi, zvyky a jazyky. Mezi nejnavštěvovanější turistické destinace patří například \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fjaponsko\"\u003EJaponsko\u003C\u002Fa\u003E\u003C\u002Fb\u003E s jeho futuristickými městy, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fthajsko\"\u003EThajsko\u003C\u002Fa\u003E\u003C\u002Fb\u003E s bohatou kulturou a krásnými plážemi, nebo \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Findonesie\"\u003EIndonésie\u003C\u002Fa\u003E\u003C\u002Fb\u003E s neuvěřitelnou přírodou a množstvím ostrovů.\u003C\u002Fp\u003E\n\u003Cp\u003E\u003Cb\u003EAsie\u003C\u002Fb\u003E také nabízí mnoho kulturních a historických památek, jako je \u003Cb\u003EVelká čínská zeď\u003C\u002Fb\u003E, \u003Cb\u003ETaj Mahal\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Findie\"\u003EIndii\u003C\u002Fa\u003E\u003C\u002Fb\u003E, buddhistické chrámy v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fthajsko\"\u003EThajsku\u003C\u002Fa\u003E\u003C\u002Fb\u003E a mnoho dalšího. Zajímavou zkušeností může být i návštěva tradičních trhů, kde se dají ochutnat místní speciality a nakoupit různé exotické produkty.\u003C\u002Fp\u003E\n\u003Cp\u003E\u003Cb\u003EAsie\u003C\u002Fb\u003E má však také své výzvy a problémy, jako jsou například růst městských oblastí, nedostatek vody a znečištění ovzduší. Mnoho zemí se snaží najít řešení těchto problémů a přizpůsobit se moderním trendům, aby mohly udržet krok s ostatními zeměmi světa.\u003C\u002Fp\u003E\n\u003Cp\u003EPokud tě láká cestování po \u003Cb\u003EAsii\u003C\u002Fb\u003E, měl by sis předem zajistit víza a informovat se o místních zvycích a tradicích. Rozhodně se ti však naskytne možnost prozkoumat jedinečnou kulturu, přírodu a historii tohoto fascinujícího kontinentu.\u003C\u002Fp\u003E\u003Cp\u003E\u003Cb\u003EAsie\u003C\u002Fb\u003E má také neuvěřitelně bohatou gastronomii, která se v jednotlivých zemích a regionech liší. Mezi nejznámější asijské pokrmy patří \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fjidlo\u002Fsushi\"\u003Esushi\u003C\u002Fa\u003E\u003C\u002Fb\u003E, různé druhy nudlí, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fjidlo\u002Fcurry\"\u003Ecurry\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003Edim sum\u003C\u002Fb\u003E, \u003Cb\u003Epho\u003C\u002Fb\u003E a další. Kromě toho se v \u003Cb\u003EAsii\u003C\u002Fb\u003E pěstuje mnoho exotických plodin, jako jsou \u003Cb\u003Eduriany\u003C\u002Fb\u003E, \u003Cb\u003Emangostany\u003C\u002Fb\u003E, \u003Cb\u003Erambutany\u003C\u002Fb\u003E a další ovoce, které můžeš ochutnat pouze zde.\n\u003C\u002Fp\u003E\u003Cp\u003E\n\u003Cb\u003EAsie\u003C\u002Fb\u003E je také známá svými svátky a tradicemi, jako je například \u003Cb\u003Ečínský Nový rok\u003C\u002Fb\u003E, \u003Cb\u003EDiwali\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Findie\"\u003EIndii\u003C\u002Fa\u003E\u003C\u002Fb\u003E, nebo \u003Cb\u003ESongkran\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fthajsko\"\u003EThajsku\u003C\u002Fa\u003E\u003C\u002Fb\u003E,. Tyto svátky jsou většinou spojeny s různými rituály a zvyky, které se v průběhu staletí vyvinuly a jsou dodnes respektovány.\n\u003C\u002Fp\u003E\u003Cp\u003E\nKromě toho má \u003Cb\u003EAsie\u003C\u002Fb\u003E mnoho přírodních krás, jako jsou \u003Cb\u003EHimálaje\u003C\u002Fb\u003E, pouště \u003Cb\u003EGobi\u003C\u002Fb\u003E a \u003Cb\u003EThar\u003C\u002Fb\u003E, nebo tropické pralesy v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Findonesie\"\u003EIndonésii\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmalajsie\"\u003EMalajsii\u003C\u002Fa\u003E\u003C\u002Fb\u003E. V \u003Cb\u003EAsii\u003C\u002Fb\u003E se také nachází mnoho národních parků a chráněných oblastí, kde žijí ohrožené druhy zvířat, jako jsou \u003Cb\u003Etygři\u003C\u002Fb\u003E, \u003Cb\u003Epandy\u003C\u002Fb\u003E, \u003Cb\u003Esloni\u003C\u002Fb\u003E a další.\n\u003C\u002Fp\u003E\u003Cp\u003E\nZkrátka, \u003Cb\u003EAsie\u003C\u002Fb\u003E je kontinent plný kontrastů a neuvěřitelných zážitků, které stojí za to objevovat. Budeš-li mít příležitost navštívit některou z asijských zemí, neváhej a vydaj se na dobrodružnou cestu plnou nových objevů a nezapomenutelných zážitků.\u003C\u002Fp\u003E",coordinates:[{latitude:47.751076,longitude:92.475286}],type_place:"continent",id_image_cover:g,id_image_hero:g,zoom:[{google:2,booking:g}],information_author:a,seo_tags:a,created_at:"2023-12-27T16:35:37.460505+00:00",updated_at:"2023-12-27T17:07:06.531847+00:00",name_original:a}],placesCities:[{id:68,id_image_cover:a,importance:c,slug:"ararat",type_place:b,name:"Ararat"},{id:67,id_image_cover:a,importance:c,slug:"armavir",type_place:b,name:"Armavir"},{id:69,id_image_cover:a,importance:c,slug:"ashtarak",type_place:b,name:"Ashtarak"},{id:64,id_image_cover:a,importance:c,slug:"ejmiacin",type_place:b,name:"Ejmiacin"},{id:62,id_image_cover:a,importance:c,slug:"gjumri",type_place:b,name:"Gjumri"},{id:66,id_image_cover:a,importance:c,slug:"hrazdan",type_place:b,name:"Hrazdan"},{id:h,id_image_cover:f,importance:c,slug:j,type_place:b,name:r},{id:65,id_image_cover:a,importance:c,slug:"kapan",type_place:b,name:"Kapan"},{id:70,id_image_cover:a,importance:c,slug:"sevan",type_place:b,name:"Sevan"},{id:63,id_image_cover:a,importance:c,slug:"vanadzor",type_place:b,name:"Vanadzor"}],imagesCities:[{id:f,name:j,source:s,type:b,author:o,created_at:t,updated_at:k}]}],fetch:{"OrganismsoPlaceTeaserSpotsComponent:0":{spots:[{id:44,id_city:h,id_image_cover:q,id_image_hero:q,created_at:"2023-08-24T16:16:37.648438+00:00",slug:x,name:"Yerevan Botanical Garden",information_chatgpt:"\u003Cp\u003EJerevanská botanická zahrada, známá také jako Yerevan Botanical Garden, je botanická zahrada nacházející se ve městě Jerevan, hlavním městě Arménie. Tato botanická zahrada je jednou z nejstarších v regionu a byla založena v roce 1935. Zahrada má rozlohu více než 70 hektarů a nachází se na svazích hory Gorcev.\u003C\u002Fp\u003E\n\n\u003Cp\u003EMezi hlavní rysy a zajímavosti Jerevanské botanické zahrady patří:\u003C\u002Fp\u003E\n\n\u003Cul\u003E\n  \u003Cli\u003E\u003Cstrong\u003EDruhová rozmanitost:\u003C\u002Fstrong\u003E Zahrada hostí tisíce druhů rostlin, včetně vzácných a endemických druhů z Arménie a okolních regionů. Zahrnuje rostliny z různých klimatických zón, od horských až po suché stepi.\u003C\u002Fli\u003E\n  \u003Cli\u003E\u003Cstrong\u003EZahrady a sekce:\u003C\u002Fstrong\u003E Zahrada je rozdělena do různých sekcí a zahrad, zahrnujících například alpinské zahrady, exotické rostliny, zahrady s aromatickými bylinami a mnoho dalších.\u003C\u002Fli\u003E\n  \u003Cli\u003E\u003Cstrong\u003EVýzkum a vzdělávání:\u003C\u002Fstrong\u003E Kromě toho, že slouží jako místo rekreačního využití, má zahrada také vědecký a výzkumný význam. Provádí se zde výzkum místní flóry a fauna, jakož i vzdělávací aktivity pro veřejnost.\u003C\u002Fli\u003E\n  \u003Cli\u003E\u003Cstrong\u003EKoncerty a události:\u003C\u002Fstrong\u003E Během letních měsíců se v zahradě často konají kulturní akce, koncerty a výstavy, které přilákají návštěvníky nejen kvůli rostlinám, ale i kvůli atmosféře.\u003C\u002Fli\u003E\n  \u003Cli\u003E\u003Cstrong\u003EPřírodní krása:\u003C\u002Fstrong\u003E Díky své poloze na svahu hory Gorcev nabízí zahrada nádherné výhledy na město Jerevan a okolní krajinu. Je to skvělé místo pro procházky a odpočinek.\u003C\u002Fli\u003E\n\u003C\u002Ful\u003E\n\n\u003Cp\u003EJerevanská botanická zahrada je tedy důležitým místem pro zachování a výzkum místní flóry a přírody. Mnoho návštěvníků sem přichází, aby si užilo krásné rostliny, relaxovalo v příjemném prostředí a využilo vzdělávacích možností.\u003C\u002Fp\u003E\n",type_place:y,coordinates:[{latitude:40.211633,longitude:44.556221}],zoom:[{google:15,booking:13}],affiliate:[{name:m,value:n}],id_state:i,altitude:1239,information_author:[],seo_tags:a,information_duration:[],updated_at:"2023-12-27T17:07:23.53669+00:00",name_original:a,type_spot:a}],images:[{id:q,name:x,source:"\u002Fworld\u002Fspots\u002F",type:y,author:"James Kerwin via Reddit",created_at:"2023-08-24T16:30:03.719066+00:00",updated_at:k}]},"OrganismsoSidebarListComponent:0":{events:[],images:a}},mutations:[]}}(null,"city","biggest","04-2021","04-2023",680,3,61,7,"jerevan","2023-12-27T20:42:18.520959+00:00","05-2023","booking",true,"pixabay.com",14,679,"Jerevan","\u002Fworld\u002Fcities\u002F","2023-08-24T18:41:44.151741+00:00","armenie",250,"state","yerevan-botanical-garden","spot")));