__NUXT_JSONP__("/svet/stat/belgie/sousedni-staty", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w,x,y,z,A,B,C,D,E,F,G,H){return {data:[{tabsLoad:{tabWhatToSee:d,tabPrice:d,tabPeople:d,tabTrip:d,tabContact:d,tabHotel:d,tabNeighboring:f,tabVideos:g,tabArticles:g,tabWallSockets:m,tabChains:6,tabFauna:g,tabFlora:g,tabFoods:5,tabBrands:g},place:[{tld:".be",name:"Belgie",area:30528,population:11555997,slug:q,id_continent:f,information_chatgpt:"\u003Cp\u003EBelgie se nachází v západní Evropě a je známá svým bohatým kulturním dědictvím, chutnou kuchyní a různorodou architekturou.\u003C\u002Fp\u003E\n\u003Cp\u003EHlavním městem Belgie je Brusel, který je také sídlem Evropské unie. Brusel je znám svým krásným náměstím Grand Place, které je zapsáno na seznamu světového dědictví UNESCO. Mezi další zajímavá místa patří Manneken Pis, Atomium nebo královský palác.\u003C\u002Fp\u003E\n\u003Cp\u003EBelgie je také známá svými městy plnými umění a architektury, jako je například Gent, Bruggy nebo Antverpy. Tyto města nabízejí návštěvníkům nádherné historické centrum, muzea a galerie, stejně jako mnoho kaváren, restaurací a obchodů.\u003C\u002Fp\u003E\n\u003Cp\u003EBelgie je také země s vynikající kuchyní, kde se míchají francouzské, nizozemské a německé vlivy. Mezi nejoblíbenější jídla patří bruselské krekry, hovězí karbanátky, vafle a čokoláda. Kromě toho je Belgie také známá svým pivem a nabízí mnoho malých pivovarů a pivních zahrad.\u003C\u002Fp\u003E\n\u003Cp\u003EPřírodní krásy Belgie mohou být snadno objeveny díky vynikající dopravní síti. Mezi nejoblíbenější přírodní lokality patří Ardenský les, vodopády Coo, Dunajské delty a národní park Hoge Kempen.\u003C\u002Fp\u003E\n\u003Cp\u003EBelgie je země s dlouhou historií a bohatým kulturním dědictvím, které může být viděno v mnoha historických památkách a muzeích. Země také nabízí návštěvníkům vynikající jídlo, pivo a krásnou přírodu. To vše činí Belgie populárním cílem pro turisty z celého světa.\u003C\u002Fp\u003E",currency_code:"EUR",currency_name:"euro",money_prices:[{name:"Taxi (cena za km)",value:1.8,date_update:a},{name:"Hlavní jídlo",value:n,date_update:a},{name:"Chleba",value:r,date_update:a},{name:"Mléko",value:s,date_update:a},{name:"Voda (500ml)",value:t,date_update:a},{name:"Espresso",value:r,date_update:a},{name:"Zmrzlina (jeden kopeček)",value:t,date_update:a},{name:"Pivo",value:3.5,date_update:a},{name:"Krabička cigaret",value:k,date_update:a},{name:"Bic Mac",value:4.5,date_update:a},{name:"Coca Cola (500ml)",value:m,date_update:a}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:u,date_update:a},{name:"Policie",value:"101",date_update:a},{name:"Hasiči",value:"100",date_update:a},{name:"Zdravotnická záchranná služba",value:u,date_update:a}],phone_prefix:"+32",coordinates:[{latitude:"50.5039",longitude:"4.4699"}],people_religion:[{name:"Katolické křesťanství",value:o},{name:"Bez vyznání",value:l},{name:"Muslimové",value:k},{name:"Protestantství",value:m},{name:"Pravoslavné křesťanství",value:s},{name:"Judaismus",value:.4},{name:"Jiné náboženství",value:5.6}],people_nationality:[{name:"Belgičané",value:66.5},{name:"Cizinci",value:33.5}],visitors_entry:[{name:"Občanský průkaz",value:"Pro vstup do Belgie stačí občanský průkaz pro občany EU a Schengenského prostoru",date_update:e},{name:"Cestovní pas",value:"Pro vstup do Belgie není nutný cestovní pas pro občany EU a Schengenského prostoru, avšak pro případ kontroly totožnosti může být doporučeno pas mít u sebe",date_update:e},{name:"Vízum",value:"Občané EU a Schengenského prostoru nemusí mít pro pobyt kratší než 90 dní vízum. Pro delší pobyty je nutné vízum žádat.",date_update:e},{name:"Očkování",value:"Pro vstup do Belgie není vyžadováno žádné specifické očkování. Avšak může být doporučeno mít očkování proti některým nemocem, např. tetanu, žloutence A a B, nebo klíšťové encefalitidě.",date_update:e},{name:"Cestovní pojištění",value:"Cestovní pojištění není povinné, ale doporučuje se mít ho sjednané. V případě potřeby zdravotní péče v Belgii by náklady mohly být vysoké, pokud cestovatel nemá platné cestovní pojištění.",date_update:e},{name:"Bezpečnostní situace",value:"Belgie je obecně bezpečnou zemí, ale jako v každé zemi, je třeba být obezřetný a dodržovat základní bezpečnostní opatření. Turisté by měli být opatrní zejména v turisticky frekventovaných oblastech, jako jsou nádraží, letiště a turistická centra, kde existuje větší riziko kapesních krádeží a podobných trestných činů.",date_update:e},{name:"Další informace",value:"V Belgii se používá euro. Elektrické zásuvky mají standardní evropský typ F. V některých oblastech mohou být cedule pouze v nizozemštině nebo francouzštině, takže je užitečné umět alespoň základní fráze v těchto jazycích. V Belgii je běžné dávat spropitné v restauracích a za taxi služby.",date_update:e}],type_place:c,id_image_cover:l,id_image_hero:l,id_city_main:181,affiliate:[{name:v,value:d}],mpz:"BE",alerts:[],zoom:[{google:k,booking:k}],ids_neighboring_countries:[{id:w},{id:x},{id:o},{id:y}],organization:[],apps:[],links:[],information_author:[],seo_tags:[],language_phrases:[],wall_sockets:b,id:n,facts_place:b,created_at:"2023-12-27T16:33:10.517062+00:00",updated_at:"2023-12-27T17:07:31.480426+00:00",name_original:b}],imagePlace:[{id:l,name:q,source:h,type:c,author:i,created_at:"2023-04-10T23:53:14.463861+00:00",updated_at:j}],placesStatesNeighboring:[{id:y,id_image_cover:z,slug:A,type_place:c,name:"Nizozemsko"},{id:x,id_image_cover:B,slug:C,type_place:c,name:"Lucembursko"},{id:w,id_image_cover:D,slug:E,type_place:c,name:"Francie"},{id:o,id_image_cover:F,slug:G,type_place:c,name:"Německo"}],imagesStatesNeighboring:[{id:z,name:A,source:h,type:c,author:i,created_at:p,updated_at:j},{id:F,name:G,source:h,type:c,author:i,created_at:p,updated_at:j},{id:B,name:C,source:h,type:c,author:i,created_at:p,updated_at:j},{id:D,name:E,source:h,type:c,author:i,created_at:"2023-05-02T18:49:47.851894+00:00",updated_at:j}],placeContinent:[{id:f,slug:"evropa",name:"Evropa",area:10180000,population:746000000,population_density:73.22,number_states:44,information_chatgpt:"\u003Cp\u003E\u003Cb\u003EEvropa\u003C\u002Fb\u003E je druhým nejmenším kontinentem na světě, ale zároveň se jedná o jednu z nejvíce rozmanitých oblastí na planetě. Nachází se zde mnoho historických a kulturních památek, překrásných přírodních scenérií a mnoho dalších atrakcí, což z ní dělá jednu z nejnavštěvovanějších turistických destinací na světě.\u003C\u002Fp\u003E\n\u003Cp\u003E\u003Cb\u003EEvropa\u003C\u002Fb\u003E má dlouhou a bohatou historii. Na kontinentě se odehrály mnohé důležité události, jako například římská říše, středověké křížové výpravy, renesance, průmyslová revoluce a dvě světové války. Tato historie se odráží v architektuře, muzeích a dalších kulturních památkách, které jsou na kontinentu k vidění.\u003C\u002Fp\u003E\n\u003Cp\u003EV \u003Cb\u003EEvropě\u003C\u002Fb\u003E se nachází mnoho zajímavých měst. Například \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fpariz\"\u003EPaříž\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Flondyn\"\u003ELondýn\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Frim\"\u003EŘím\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fbarcelona\"\u003EBarcelona\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fpraha\"\u003EPraha\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fberlin\"\u003EBerlín\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Famsterdam\"\u003EAmsterdam\u003C\u002Fa\u003E\u003C\u002Fb\u003E jsou velmi oblíbenými cíli turistů. Tyto města nabízejí nejen historické památky, ale také vynikající kuchyni, noční život a mnoho možností pro nákupy.\u003C\u002Fp\u003E\n\u003Cp\u003EPřírodní krása \u003Cb\u003EEvropy\u003C\u002Fb\u003E je také úžasná. Nachází se zde krásné pláže, hory, řeky a jezera. \u003Cb\u003EAlpské hory\u003C\u002Fb\u003E nabízejí překrásné scenérie, skvělé podmínky pro lyžování a další outdoorové aktivity. Národní parky, jako třeba \u003Cb\u003EPlitvická jezera\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fchorvatsko\"\u003EChorvatsku\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003EKrkonošský národní park\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fcesko\"\u003EČeské republice\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003ENárodní park Durmitor\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fcerna-hora\"\u003EČerné Hoře\u003C\u002Fa\u003E\u003C\u002Fb\u003E, jsou dalšími příklady nádherných přírodních scenérií, které stojí za návštěvu.\u003C\u002Fp\u003E\n  \u003Cp\u003E\u003Cb\u003EEvropa\u003C\u002Fb\u003E také nabízí širokou škálu kulturních aktivit. Existují zde mnohé umělecké festivaly, výstavy, divadelní představení a koncerty, které jsou pořádány po celém kontinentu. Mnoho měst také pořádá tradiční slavnosti a jarmarky, kde se můžete seznámit s místními tradicemi a kulturou.\u003C\u002Fp\u003E\n  \u003Cp\u003EPři návštěvě \u003Cb\u003EEvropy\u003C\u002Fb\u003E si určitě nenechte ujít místní kuchyni. Každá země má své vlastní speciality a chuťě\u003C\u002Fp\u003E\u003Cp\u003EMezi nejznámější evropské kuchyně patří \u003Cb\u003Eitalská\u003C\u002Fb\u003E, \u003Cb\u003Efrancouzská\u003C\u002Fb\u003E a \u003Cb\u003Ešpanělská\u003C\u002Fb\u003E. \u003Cb\u003EItalská kuchyně\u003C\u002Fb\u003E je známá svými \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fjidlo\u002Fpasta\"\u003Etěstovinami\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fjidlo\u002Fpizza\"\u003Epizzou\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003Ezmrzlinou\u003C\u002Fb\u003E. \u003Cb\u003EFrancouzská kuchyně\u003C\u002Fb\u003E nabízí širokou škálu různých pokrmů, jako například sýry, pečivo, maso a víno. \u003Cb\u003EŠpanělská kuchyně\u003C\u002Fb\u003E je známá svými \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fjidlo\u002Ftapas\"\u003Etapas\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fjidlo\u002Fpaella\"\u003Epaellou\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003Esangriou\u003C\u002Fb\u003E. Kromě těchto kuchyní jsou v \u003Cb\u003EEvropě\u003C\u002Fb\u003E také velmi oblíbené pokrmy jako \u003Cb\u003Eněmecké klobásy\u003C\u002Fb\u003E, \u003Cb\u003Eanglické snídaně\u003C\u002Fb\u003E, \u003Cb\u003Eholandský sýr\u003C\u002Fb\u003E a mnoho dalšího.\u003C\u002Fp\u003E\n  \u003Cp\u003E\u003Cb\u003EEvropa\u003C\u002Fb\u003E také nabízí mnoho možností pro sportovní aktivity. Mezi nejpopulárnější sporty patří fotbal, hokej, tenis a cyklistika. Na kontinentu se také nachází mnoho golfových hřišť, lyžařských středisek a dalších sportovních zařízení. V posledních letech se stále více lidí zajímá o ekoturistiku, a tak se nabízí možnost podniknout pěší túru nebo cyklistický výlet a objevovat krásy evropské přírody.\u003C\u002Fp\u003E\n\u003Cp\u003E\u003Cb\u003EEvropa\u003C\u002Fb\u003E je jedním z nejlepších míst pro cestování, nabízí mnoho možností pro každého cestovatele. Díky rozmanitosti kontinentu si každý najde to svoje, co ho zaujme. Záleží pouze na vás, zda si zvolíte kulturní zážitky, přírodní krásy, sportovní aktivity nebo si jen tak užijete místní kuchyni a pohodu.\u003C\u002Fp\u003E",coordinates:[{latitude:47.497912,longitude:19.040235}],type_place:"continent",id_image_cover:f,id_image_hero:f,zoom:[{google:H,booking:H}],information_author:b,seo_tags:b,created_at:"2023-12-27T16:35:37.460505+00:00",updated_at:"2023-12-27T17:07:06.531847+00:00",name_original:b}]}],fetch:{"OrganismsoSidebarListComponent:0":{events:[{id:15,id_state:n,id_city:b,id_spot:b,id_image_cover:b,id_image_hero:b,created_at:"2024-01-10T19:27:28.063137+00:00",slug:"grand-prix-belgie",name:"Grand Prix Belgie",description:"",coordinates:[{latitude:50.4385,longitude:5.970429}],zoom:[{google:14,booking:13}],affiliate:[{name:v,value:d}],prices:[],links:[],date_start:"2024-07-27T22:00:00+00:00",date_end:"2024-07-28T21:59:00+00:00",id_region:b,seo_tags:[],updated_at:"2024-01-10T21:44:18.422562+00:00"}],images:b}},mutations:[]}}("05-2023",null,"state",true,"04-2023",4,0,"\u002Fworld\u002Fstates\u002F","pixabay.com","2023-12-27T20:42:18.520959+00:00",7,27,2,20,57,"2023-05-03T20:14:00.749527+00:00","belgie",2.5,1,1.5,"112","booking",75,134,166,329,"nizozemsko",303,"lucembursko",248,"francie",324,"nemecko",3)));