__NUXT_JSONP__("/cestovatelsky-slovnik/kontinentalni-snidane", (function(a,b,c,d){return {data:[{travelDictionary:[{id:13,slug:"kontinentalni-snidane",created_at:"2023-07-15T23:31:14.851554+00:00",name:"Kontinentální snídaně",description:"\u003Cp\u003EKontinentální snídaně je termín používaný pro druh snídaně, který je typický pro země Evropy, zejména západní a střední Evropy. Tato snídaně se liší od tzv. anglické snídaně, která je bohatší a obsahuje vařená vejce, slaninu, fazole a další teplé pokrmy.\u003C\u002Fp\u003E\n\u003Cp\u003EKontinentální snídaně je obvykle lehčí a jednodušší. Typické součásti této snídaně zahrnují kávu nebo čaj, čerstvé pečivo (např. croissanty, bagety nebo rohlíky), máslo, džemy a med, někdy také sýry, šunky a další studené masové výrobky. Ovoce, jogurty a cereálie jsou také běžnou součástí kontinentální snídaně.\u003C\u002Fp\u003E\n\u003Cp\u003EKontinentální snídaně se často podává ve formě bufetu nebo včetně příchodu hosta na stůl. Je oblíbenou volbou v hotelích, penzionech a kavárnách v Evropě. Mnoho lidí si vychutnává tuto snídani jako rychlý a jednoduchý způsob doplnění energie před začátkem pracovního dne nebo při cestování.\u003C\u002Fp\u003E",id_image_cover:a,id_image_hero:a,seo_tags:a,updated_at:"2023-12-27T17:08:03.040177+00:00"}],imageTravelDictionary:a}],fetch:{"OrganismsoSidebarListTravelDictionaryComponent:0":{travelDictionaries:[{id:14,id_image_cover:a,slug:"kosmopolitni",name:"Kosmopolitní"},{id:12,id_image_cover:a,slug:"boarding-time",name:"Boarding time"},{id:11,id_image_cover:b,slug:c,name:"Boarding"},{id:10,id_image_cover:a,slug:"hop-on-hop-off",name:"hop-on, hop-off"},{id:9,id_image_cover:a,slug:"mezonet",name:"Mezonet"}],images:[{id:b,name:c,source:"\u002Ftravel_dictionaries\u002F",type:"travelDictionaries",author:"pixabay.com",created_at:d,updated_at:d}],isLoading:false}},mutations:[]}}(null,781,"boarding","2024-01-12T21:51:28.411938+00:00")));