__NUXT_JSONP__("/jidlo/porkolt", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o){return {data:[{food:[{id:85,slug:"porkolt",created_at:"2023-12-08T16:48:44.918367+00:00",name:f,description:"\u003Cp\u003EPörkölt je tradiční maďarské jídlo, které se připravuje vařením masa, obvykle hovězího nebo vepřového, s cibulí, paprikou a rajčaty. Tato chutná a sytá pokrm pochází z maďarské kuchyně a je velmi oblíbený jak v Maďarsku, tak i v dalších částech střední Evropy.\u003C\u002Fp\u003E\u003Cp\u003EPři přípravě Pörköltu se maso dusí v aromatické směsi z cibule a papriky, což mu dodává bohatou chuť a vůni. Někdy se do receptu přidávají i další koření, jako je česnek nebo kmín, aby se posílila chuť jídla.\u003C\u002Fp\u003E\u003Cp\u003EPörkölt se obvykle podává s noky, bramborovým pyré nebo těstovinami. Tato lahůdka je oblíbená nejen pro svůj vynikající chuť, ale také pro svou jednoduchost a tradici, která sahá do dávné minulosti maďarské kuchyně.\u003C\u002Fp\u003E",id_image_cover:a,id_image_hero:a,ids_states:[{id:d}],seo_tags:a,name_language:[{name:f,description:"v českém jazyce"}],updated_at:"2023-12-27T17:06:31.776994+00:00",ingredients:a,recipe:a}],imageFood:a,placesStates:[{tld:"hu",name:"Maďarsko",area:93030,population:9764000,slug:g,id_continent:4,information_chatgpt:"\u003Cp\u003EMaďarsko je stát v střední Evropě s bohatou historií a kulturou. Hlavním městem je Budapešť, která je známá svými historickými památkami, jako je Rybářská bašta, Parlament a řetězový most. Maďarsko má také mnoho dalších krásných měst, jako je Debrecín, Pécs, Szeged a další.\u003C\u002Fp\u003E\n\u003Cp\u003EMaďarsko je známé svými termálními lázněmi a lázeňskými městy, jako je Hévíz, Sárvár a Eger. Tato místa jsou ideální pro odpočinek a relaxaci a nabízejí mnoho různých wellness procedur. Maďarsko má také bohatou kulturní scénu, včetně opery, divadla a muzeí.\u003C\u002Fp\u003E\n\u003Cp\u003EKromě toho je Maďarsko známé svou gastronomií, jako jsou guláš, pörkölt, langos a mnoho dalších specialit. Maďarské víno má také dlouhou tradici a je uznávané po celém světě.\u003C\u002Fp\u003E\n\u003Cp\u003EMaďarsko je obklopeno několika zeměmi, což umožňuje cestovat po Evropě snadno a levně. Navštívit můžete sousední země, jako je Rakousko, Slovensko, Rumunsko a Srbsko. Maďarsko nabízí mnoho aktivit pro turisty, jako jsou procházky v přírodě, cyklistika, plavání a vodní sporty.\u003C\u002Fp\u003E\n\u003Cp\u003EJe důležité mít na paměti, že Maďarsko bylo také postiženo migrací a uprchlickou krizí v posledních letech, což mohlo ovlivnit některé oblasti země. Nicméně, Maďarsko je stále bezpečnou a krásnou destinací pro turisty z celého světa.\u003C\u002Fp\u003E",currency_code:"HUF",currency_name:"forint",money_prices:[{name:"Taxi (cena za km)",value:h,date_update:b},{name:"Hlavní jídlo",value:2000,date_update:b},{name:"Chleba",value:300,date_update:b},{name:"Mléko",value:h,date_update:b},{name:"Voda (500ml)",value:d,date_update:b},{name:"Espresso",value:350,date_update:b},{name:"Zmrzlina (jeden kopeček)",value:i,date_update:b},{name:"Pivo",value:500,date_update:b},{name:"Krabička cigaret",value:j,date_update:k},{name:"Bic Mac",value:j,date_update:k},{name:"Coca Cola (500ml)",value:250,date_update:b}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:"112",date_update:c},{name:"Policie",value:"107",date_update:c},{name:"Hasiči",value:"105",date_update:c},{name:"Zdravotnická záchranná služba",value:"104",date_update:c}],phone_prefix:"+36",coordinates:[{latitude:"47.1625",longitude:"19.5033"}],people_religion:[{name:"Římskokatolické",value:"37,2 %"},{name:"Kalvínské reformované",value:"11,6 %"},{name:"Luteránské",value:"2,2 %"},{name:"Řeckokatolické",value:"1,8 %"},{name:"Ostatní křesťanské",value:"1,5 %"},{name:"Bez vyznání",value:"18,2 %"},{name:"Neznámé",value:"27,5 %"}],people_nationality:[{name:"Maďaři",value:"85%"},{name:"Romové",value:"4,2%"},{name:"Němci",value:"1,2%"},{name:"Slováci",value:"0,4%"},{name:"Rumuni",value:l},{name:"Srbové",value:l},{name:"Chorvati",value:"0,2%"},{name:"Rusíni",value:m},{name:"Češi",value:m},{name:"Ostatní",value:"8,2%"}],visitors_entry:[{name:"Občanský průkaz",value:"Pro občany Evropské unie je pro vstup do Maďarska postačující občanský průkaz. Není tedy potřeba mít cestovní pas.",date_update:n},{name:"Cestovní pas",value:"Pro vstup do Maďarska není pro občany Evropské unie vyžadován cestovní pas.",date_update:n},{name:"Vízum",value:"Občané Evropské unie a Švýcarska nemusí pro vstup do Maďarska mít vízum, pokud pobývají v zemi kratší dobu než 90 dnů. Pro pobyt delší než 90 dnů je nutné požádat o vízum před cestou na maďarské velvyslanectví.",date_update:c},{name:"Očkování",value:"Pro vstup do Maďarska je prokázání o očkování proti COVID-19 nezbytné. Maďarsko uznává certifikáty o očkování, které jsou vydány v rámci Evropské unie, ale také některé certifikáty o očkování vydávané mimo EU (např. certifikáty vydávané USA nebo Velkou Británií). Pokud nemáte potvrzení o očkování, můžete vstoupit do Maďarska s negativním PCR testem, který není starší než 72 hodin, nebo s potvrzením o prodělání nemoci COVID-19, které není starší než 180 dní.",date_update:c},{name:"Cestovní pojištění",value:"Při vstupu na území Maďarska není nutné dokládat cestovní ani zdravotní pojištění, avšak doporučuje se mít s sebou platnou kartu evropského zdravotního pojištění.",date_update:c},{name:"Bezpečnostní situace",value:"Maďarsko je považováno za bezpečnou zemi, nicméně je třeba dodržovat obvyklá opatření při cestování do zahraničí. Zvláštní pozornost by měla být věnována větším městům a turisticky frekventovaným oblastem, kde může docházet ke krádežím a dalším drobným trestným činům.",date_update:c},{name:"Další informace",value:"V Maďarsku je zvykem např. nezakládat při setkání rukou, pokud Vám ji nenabídnou jako první. Také se doporučuje být opatrný při placení kartou, neboť se může stát, že Vám účtují poplatky za transakce, i když jsou tyto poplatky v některých případech zakázány. Maďarsko také patří k zemím, kde se traduje kultura pití vína a piva, která je spojena s bohatou historií země.",date_update:c}],type_place:o,id_image_cover:e,id_image_hero:e,id_city_main:968,affiliate:[{name:"booking",value:true}],mpz:"HU",alerts:a,zoom:[{google:6,booking:7}],ids_neighboring_countries:[{id:12},{id:98},{id:189},{id:190},{id:i},{id:202},{id:230}],organization:a,apps:a,links:a,information_author:a,seo_tags:a,language_phrases:a,wall_sockets:a,id:d,facts_place:a,created_at:"2023-12-27T16:33:10.517062+00:00",updated_at:"2023-12-27T17:07:31.480426+00:00",name_original:a}],imagesStates:[{id:e,name:g,source:"\u002Fworld\u002Fstates\u002F",type:o,author:"pixabay.com",created_at:"2023-05-03T20:14:00.749527+00:00",updated_at:"2023-12-27T20:42:18.520959+00:00"}]}],fetch:{},mutations:[]}}(null,"04-2021","05-2023",100,306,"Pörkölt","madarsko",280,200,"Není k dispozici","","0,3%","0,1%","12-2023","state")));