__NUXT_JSONP__("/svet/mesto/freetown--stat-sierra-leone", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w){return {data:[{place:[{id:h,id_state:l,name:m,population:1049323,area:357.04,altitude:24,slug:n,information_chatgpt:a,coordinates:[{latitude:8.484,longitude:-13.22994}],biggest:"yes",type_place:d,id_image_cover:a,id_image_hero:a,affiliate:[{name:o,value:f}],alerts:a,zoom:[{google:10,booking:11}],parking:a,information_author:a,importance:e,seo_tags:a,created_at:"2023-12-27T16:36:26.534285+00:00",updated_at:"2024-03-02T11:05:40.678938+00:00",name_original:a,setting_top:f}],imagePlace:[],placeState:[{tld:".sl",name:"Sierra Leone",area:71740,population:7976985,slug:p,id_continent:g,information_chatgpt:"\u003Cp\u003ESierra Leone je země v západní Africe, která je známá svou pestrou kulturou a krásnými plážemi. Hlavním městem je Freetown, které je největším městem a hlavním přístavem země.\u003C\u002Fp\u003E\n\u003Cp\u003ESierra Leone má bohatou historii, kterou můžete objevovat v mnoha muzeích a památkách, jako například v Národním muzeu nebo na ostrově Bunce Island, který je známý pro svou roli v obchodu s otroky. Kromě historických památek můžete také navštívit přírodní rezervace, jako například Tiwai Island, kde můžete vidět vzácné druhy zvířat a ptáků.\u003C\u002Fp\u003E\n\u003Cp\u003EPláže v Sierra Leone jsou považovány za některé z nejkrásnějších v Africe. Populárními plážemi jsou například Lumley Beach v Freetownu nebo River Number Two Beach v přírodní rezervaci Western Area Peninsula. Na plážích si můžete užít slunce a moře, ale také můžete vyzkoušet místní rybářské techniky nebo se projet na vodním skútru.\u003C\u002Fp\u003E\n\u003Cp\u003EKulturní scéna v Sierra Leone je také velmi zajímavá. Můžete si poslechnout místní hudbu, jako je například bubnování nebo křesťanskou gospelovou hudbu. Můžete také ochutnat místní kuchyni, která je ovlivněna africkými, britskými a portugalskými kořeny. Specialitou jsou například rybí polévky, rýže s fazolemi a vařené banány.\u003C\u002Fp\u003E",money_prices:[{name:"Taxi (cena za km)",value:2000,date_update:b},{name:"Hlavní jídlo",value:11000,date_update:b},{name:"Chleba",value:q,date_update:b},{name:"Mléko",value:6000,date_update:b},{name:"Voda (500ml)",value:3000,date_update:b},{name:"Espresso",value:r,date_update:b},{name:"Zmrzlina (jeden kopeček)",value:q,date_update:b},{name:"Pivo",value:r,date_update:b},{name:"Krabička cigaret",value:15000,date_update:b},{name:"Bic Mac",value:20000,date_update:b},{name:"Coca Cola (500ml)",value:5000,date_update:b}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:"112",date_update:b},{name:"Policie",value:i,date_update:b},{name:"Hasiči",value:i,date_update:b},{name:"Zdravotnická záchranná služba",value:i,date_update:b}],phone_prefix:"+232",coordinates:[{latitude:"8.4606",longitude:"-11.7799"}],people_religion:[{name:"Muslimové",value:"78%",date_update:c},{name:"Křesťané",value:"20%",date_update:c},{name:"Nevěřící",value:j,date_update:c}],people_nationality:[{name:"Mende",value:"33%",date_update:c},{name:"Temne",value:"31%",date_update:c},{name:"Limbů",value:"8%",date_update:c},{name:"Kru",value:s,date_update:c},{name:"Loko",value:"6%",date_update:c},{name:"Kono",value:"4%",date_update:c},{name:"Kuranko",value:j,date_update:c},{name:"Sherbro",value:j,date_update:c},{name:"Další národnosti",value:s,date_update:c}],visitors_entry:[{name:"Občanský průkaz",value:"Pro vstup do Sierra Leone není občanský průkaz platným dokladem totožnosti. Je třeba mít platný cestovní pas.",date_update:b},{name:"Cestovní pas",value:"Pro vstup do Sierra Leone je potřeba mít platný cestovní pas s minimální platností 6 měsíců od data příjezdu.",date_update:b},{name:"Vízum",value:"Pro vstup do Sierra Leone je pro většinu cizinců potřeba mít platné vízum. Občané Evropské unie mohou získat vízum po příletu na letišti Freetown, ale doporučuje se ho získat předem. Podrobné informace o vízových podmínkách lze získat na webových stránkách Velvyslanectví Sierra Leone v zemi vašeho trvalého pobytu.",date_update:b},{name:"Očkování",value:"Pro vstup do Sierra Leone nejsou povinná žádná očkování. Nicméně, doporučuje se očkování proti žluté zimnici a hepatitidě A a B. Pro další informace se obraťte na svého lékaře nebo cestovní kliniku.",date_update:b},{name:"Cestovní pojištění",value:"Cestovní pojištění není vyžadováno pro vstup do Sierra Leone. Nicméně, doporučuje se sjednat si cestovní pojištění, které kryje zdravotní péči a náklady na lékařskou evakuaci v případě potřeby.",date_update:b},{name:"Bezpečnostní situace",value:"Bezpečnostní situace v Sierra Leone se zlepšila po občanské válce, která skončila v roce 2002. Nicméně, stále existuje vysoká míra kriminality a je třeba být obezřetný, zejména v noci a na osamělých místech. Doporučuje se cestovat pouze za světla a využívat služeb důvěryhodných dopravců.",date_update:b},{name:"Další informace",value:"V Sierra Leone je místním zvykem nosit decentní oblečení a vyvarovat se odhalování příliš mnoho kůže. Je třeba respektovat místní kulturu a zvyky. Vzhledem k vysoké vlhkosti a teplotám se doporučuje pít hodně vody a chránit se před sluncem. V zemi existuje omezená infrastruktura a je třeba mít na paměti, že cesty mohou být špatné kvality a může docházet k výpadkům elektřiny a dalším technickým problémům.",date_update:b}],type_place:t,id_image_cover:k,id_image_hero:k,id_city_main:h,affiliate:[{name:o,value:f}],mpz:"SL",alerts:a,zoom:[{google:u,booking:u}],ids_neighboring_countries:[{id:86},{id:131}],organization:a,apps:a,links:a,information_author:a,seo_tags:a,language_phrases:a,wall_sockets:a,id:l,facts_place:a,created_at:"2023-12-27T16:33:10.517062+00:00",updated_at:"2024-03-02T11:05:26.667745+00:00",name_original:a,currency:[{code:"SLL",name:"Leone"}],setting_top:f}],imageState:[{id:k,name:p,source:"\u002Fworld\u002Fstates\u002F",type:t,author:"pixabay.com",created_at:"2023-05-03T20:14:00.749527+00:00",updated_at:"2023-12-27T20:42:18.520959+00:00"}],placeContinent:[{id:g,slug:"afrika",name:"Afrika",area:30370000,population:1340000000,population_density:44.12,number_states:54,information_chatgpt:"\u003Cp\u003EAfrika je největší kontinent na Zemi a rozkládá se na ploše přesahující 30 milionů kilometrů čtverečních. Nachází se zde více než 50 zemí a žije zde více než 1,3 miliardy lidí. \u003C\u002Fp\u003E\n\u003Cp\u003EJižní Afrika, Egypt, Maroko a Keňa patří mezi nejoblíbenější turistické cíle v Africe. V Jihoafrické republice se můžete podívat na slavné Krugerův národní park a sledovat zde život zvířat v jejich přirozeném prostředí. Egypt je zase proslulý svými pyramidami, které se nacházejí v Gíze. V Maroku můžete navštívit tradiční trhy, zatímco v Keni se můžete setkat s Masajským kmenem a podívat se na úžasnou přírodu.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má bohatou historii a kulturu. První lidé, kteří se usadili v Africe, byli loveckým a sběračským národem. Poté se v této oblasti objevily první civilizace, jako je Kúšská říše, Egyptská říše nebo Království Aksum. Afrika byla také kolonizována evropskými mocnostmi a toto období mělo značný dopad na dnešní politickou situaci na kontinentu.\u003C\u002Fp\u003E\n\u003Cp\u003EJe důležité zmínit, že Afrika má velké množství jazyků a kultur. Zahrnuje mnoho různých národů a etnických skupin, a to má vliv na různorodost obyvatelstva a kultury. Nejrozšířenějším jazykem je arabština, která se mluví především v severní Africe a v některých částech západní Afriky. Dalšími důležitými jazyky jsou svahilština, francouzština, portugalština, angličtina a mnoho dalších.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má také nádhernou přírodu. Nachází se zde nejvyšší hora světa, Kilimandžáro, která se nachází na území Tanzanie a Keňy. Kromě toho se v Africe nachází také největší pouště na světě, Sahara, a největší prales, Kongský prales. Na tomto kontinentě můžete vidět také mnoho zvířat, jako jsou sloni, lvi, gepardi, zebry a mnoho dalších druhů. V Africe se také nachází několik nejvýznamnějších řek světa, jako je Nil, Kongo, Niger a Zambezi.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je bohužel také známá pro své problémy. Mnoho zemí se potýká s chudobou, nedostatkem potravin a vody, nemocemi a válečnými konflikty. Mnoho afrických zemí závisí na zemědělství a rybolovu, což však často není dostatečně výnosné, aby zabezpečilo potřeby obyvatelstva. Kromě toho jsou některé země v Africe trvale postižené extrémními přírodními katastrofami, jako jsou sucha, povodně a bouře.\u003C\u002Fp\u003E\n\u003Cp\u003ENa druhou stranu se v Africe také nachází mnoho lidí, kteří se snaží změnit situaci a zlepšit životy svých spoluobčanů. Vznikají neziskové organizace, které se snaží pomáhat při řešení těchto problémů a zlepšovat kvalitu života v mnoha afrických zemích. Zároveň se v posledních letech objevují nové možnosti pro ekonomický růst a rozvoj, jako jsou technologické start-upy a zahraniční investice.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika je fascinující kontinent, který nabízí mnoho zážitků a zároveň v sobě ukrývá mnoho výzev. Pokud se rozhodnete navštívit Afriku, můžete se těšit na krásnou přírodu, bohatou kulturu a nezapomenutelné zážitky. Je však také důležité být obezřetný a připravený na místní podmínky, protože některé oblasti mohou být nebezpečné kvůli konfliktům nebo zločinnosti.\u003C\u002Fp\u003E",coordinates:[{latitude:1.650801,longitude:17.371642}],type_place:"continent",id_image_cover:g,id_image_hero:g,zoom:[{google:2,booking:3}],information_author:[{text:"\u003Cp\u003E\u003Cb\u003EAfrika\u003C\u002Fb\u003E, druhý největší a druhý nejlidnatější kontinent na světě, láká cestovatele svou neuvěřitelnou rozmanitostí kultur, přírodními krásami a bohatou historií. Při cestování touto obrovskou plochou se otevírá mozaika různorodých zážitků, které zahrnují odlesky moderního městského života, divokou přírodu až po historická místa.\u003C\u002Fp\u003E\u003Cp\u003EJižní Afrika, domov několika národních parků a rezervací, je rájem pro milovníky safari. \u003Cb\u003EKrugerův národní park (Kruger National Park)\u003C\u002Fb\u003E nabízí úžasné setkání s \"Velkým pětkou\" - slony, nosorožci, buvoly, lvy a levharty. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fcape-town\"\u003EKapské Město (Cape Town)\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s ikonickou horou \u003Cb\u003EMaclear's Beacon\u003C\u002Fb\u003E, poskytuje nezapomenutelný výhled na město a okolní krajinu.\u003C\u002Fp\u003E\u003Cp\u003EVýchodní Afrika je známá svými ohromujícími sopkami. \u003Cb\u003EKilimandžáro\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E, nejvyšší hora Afriky, přitahuje dobrodruhy a horolezce z celého světa. Návštěva \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003EZanzibaru\u003C\u002Fa\u003E\u003C\u002Fb\u003E s jeho bílými plážemi a historickým centrem \u003Cb\u003EStone Town\u003C\u002Fb\u003E přidává do cestovatelského mixu kouzlo exotiky.\u003C\u002Fp\u003E\u003Cp\u003EStřední Afrika, s obrovskou rovníkovou pralesní plochou, skrývá některé z nejzajímavějších biodiverzit na světě. \u003Cb\u003EKonžský deštný prales\u003C\u002Fb\u003E je po amazonském druhý největší na světě, je domovem vzácných druhů zvířat a rostlin, a expedice sem mohou odhalit tajemství dosud neobjevená.\u003C\u002Fp\u003E\u003Cp\u003ESeverní Afrika je poznamenána starověkými civilizacemi a písečnými pouštěmi. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fegypt\"\u003EEgypt\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pyramidami a chrámy na březích \u003Cb\u003ENilu\u003C\u002Fb\u003E je jednou z nejnavštěvovanějších destinací v této části kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmaroko\"\u003EMaroko\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s mistrovským spojením tradice a moderny v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fmarrakes\"\u003EMarrákeši\u003C\u002Fa\u003E\u003C\u002Fb\u003E, je fascinujícím mixem barev, vůní a architektury.\u003C\u002Fp\u003E\u003Cp\u003EZápadní Afrika, bohatá na kulturní dědictví, nabízí autentické zážitky. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fghana\"\u003EGhana\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pevnostmi, které připomínají období otroctví, a kultovními festivaly představuje hluboký pohled do minulosti. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fsenegal\"\u003ESenegal\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s historickým ostrovem \u003Cb\u003EGorée\u003C\u002Fb\u003E, který sehral roli s obchodem s otroky, kde najdete koloniální budovy a muzeum \u003Cb\u003EHouse of Slaves (Maison des esclaves)\u003C\u002Fb\u003E.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je rovněž známá svými divokými zvířaty. Kromě safari v jižní části kontinentu můžete zažít jedinečnou příležitost sledovat migraci tisíců zeber a gazel v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fkena\"\u003EKeni\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E. Návštěva gorilí v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Frwanda\"\u003ERwandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fuganda\"\u003EUgandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E je neopakovatelným zážitkem pro ty, kteří touží po setkání s těmito ohroženými tvory.\u003C\u002Fp\u003E\u003Cp\u003EPřírodní skvosty nejsou omezeny pouze na pevninskou část kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fseychely\"\u003ESeychely\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMauricius\u003C\u002Fa\u003E\u003C\u002Fb\u003E, dvě ostrůvky v \u003Cb\u003EIndickém oceánu\u003C\u002Fb\u003E, lákají svými bílými plážemi, průzračnými vodami a jedinečnou faunou a florou.\u003C\u002Fp\u003E\u003Cp\u003EAfrika rovněž hýří kulturou a historií. Starověké památky jako \u003Cb\u003EGreat Zimbabwe National Monument\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fzimbabwe\"\u003EZimbabwe\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003ETimbuktu (Tombouctou)\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMali\u003C\u002Fa\u003E\u003C\u002Fb\u003E jsou svědectvím o bohaté historii a rozvoji obchodu a kultury na africkém kontinentu. Každý region má své vlastní tradiční tance, hudbu a umění, které odrážejí místní identitu a historii.\u003C\u002Fp\u003E\u003Cp\u003ECestování Afrikou je také o interakci s místními lidmi. Přívětivost a pohostinnost afrických komunit jsou nezapomenutelnými součástmi cestování. Místní trhy nabízejí pestrou škálu barev, vůní a chutí, zatímco setkání s kmeny a etnickými skupinami přináší pochopení různorodosti kultur a tradic.\u003C\u002Fp\u003E\u003Cp\u003EJe však důležité si být vědomý sociálních, ekonomických a environmentálních výzev, kterým mnohé africké země čelí. Cestovatelé by měli být respektující vůči místním zvyklostem a životnímu prostředí a přispívat k udržitelnému turismu, který podporuje místní ekonomiky a ochranu přírodních zdrojů.\u003C\u002Fp\u003E\u003Cp\u003ECelkově lze říci, že cestování po Africe je dobrodružstvím naplněným kontrasty a překvapeními. Od rozlehlých savan po exotické pláže, od starověkých ruin po moderní metropole. Afrika nabízí cestovatelům nekonečné možnosti objevování a poznávání. Je to kontinent, který zanechává nezapomenutelný dojem na každém, kdo se odhodlá k jeho prozkoumání.\u003C\u002Fp\u003E",date_create:v,date_update:v,author_create:w,author_update:w}],seo_tags:[],created_at:"2023-12-27T16:35:37.460505+00:00",updated_at:"2024-03-02T11:45:40.694514+00:00",name_original:"Africa",setting_top:f}],placesCities:[{id:2004,id_image_cover:a,importance:e,slug:"bo",type_place:d,name:"Bo"},{id:h,id_image_cover:a,importance:e,slug:n,type_place:d,name:m},{id:2010,id_image_cover:a,importance:e,slug:"kabala",type_place:d,name:"Kabala"},{id:2005,id_image_cover:a,importance:e,slug:"kenema",type_place:d,name:"Kenema"},{id:2007,id_image_cover:a,importance:e,slug:"koidu",type_place:d,name:"Koidu"},{id:2008,id_image_cover:a,importance:e,slug:"lunsar",type_place:d,name:"Lunsar"},{id:2006,id_image_cover:a,importance:e,slug:"makeni",type_place:d,name:"Makeni"},{id:2009,id_image_cover:a,importance:e,slug:"port-loko",type_place:d,name:"Port Loko"},{id:2012,id_image_cover:a,importance:e,slug:"segbwema",type_place:d,name:"Segbwema"},{id:2011,id_image_cover:a,importance:e,slug:"waterloo",type_place:d,name:"Waterloo"}],imagesCities:a}],fetch:{"OrganismsoPlaceTeaserSpotsComponent:0":{spots:[],images:a},"OrganismsoSidebarEventComponent:0":{events:[],images:a}},mutations:[]}}(null,"05-2023","03-2021","city","biggest",true,5,2003,"999","2%",358,203,"Freetown","freetown--stat-sierra-leone","booking","sierra-leone",4000,6500,"7%","state",7,"2024-03-01","1")));