__NUXT_JSONP__("/svet/misto/hercules-caves", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w,x,y,z,A,B,C,D,E,F,G,H,I,J){return {data:[{place:[{id:u,id_city:p,id_image_cover:a,id_image_hero:a,created_at:v,slug:w,name:x,information_chatgpt:k,type_place:f,coordinates:[{latitude:y,longitude:z}],zoom:[{google:q,booking:g}],affiliate:[{name:e,value:b}],id_state:d,altitude:g,information_author:[],seo_tags:[],information_duration:[],updated_at:h,name_original:A,type_spot:[{type:B}],setting_top:b}],imagePlace:[],placeCity:[{id:p,id_state:d,name:"Tanger",population:1122381,area:116,altitude:23,slug:C,information_chatgpt:"\u003Cp\u003ETanger je město v severním Maroku, které je známé pro svůj přístav a bohatou historii. Bylo založeno Féničany v 5. století př. n. l. a v průběhu let bylo pod vládou mnoha kultur, včetně Římanů, Arabů a Francouzů. Dnes je Tanger kosmopolitním městem s bohatou architekturou a kulturou, které spojuje evropské a arabské prvky.\u003C\u002Fp\u003E\n\u003Cp\u003EJedním z nejvýznamnějších míst v Tangeru je Kasbah, což je opevněné staré město, které se nachází na kopci nad přístavem. Kasbah byla kdysi rezidencí sultánů a je plná úzkých uliček, malebných domů a nádherných paláců. Dalším místem, které stojí za návštěvu, je Grand Socco, což je velké náměstí, kde se konají tradiční trhy a nachází se zde mnoho kaváren.\u003C\u002Fp\u003E\n\u003Cp\u003ETanger je také známý pro svou plážovou scenérii. Pláže v Tangeru jsou obklopeny horami a jsou ideální pro surfování, jízdu na vlnách a jiné vodní sporty. Nejpopulárnějšími plážemi jsou Achakkar Beach, Plage Municipale a Plage de Tanger.\u003C\u002Fp\u003E\n\u003Cp\u003EPokud jde o jídlo, Tanger nabízí širokou škálu marockých specialit, jako jsou tagine, couscous, harira a pastilla. Tyto pokrmy jsou nejlepší ochutnat v místních tradičních restauracích. Pro nákupy se můžete vydat na tradiční trhy, jako jsou Souk Dakhel a Souk Bara, kde najdete mnoho místních výrobků, včetně koberců, koženého zboží a keramiky.\u003C\u002Fp\u003E\n\u003Cp\u003ETanger má také velkou uměleckou scénu. Město je domovem mnoha umělců a galerií, včetně Galerie Delacroix a Galerie Conil. Každoročně se v Tangeru koná Mezinárodní filmový festival, který přitahuje řadu hvězd filmového průmyslu z celého světa.\u003C\u002Fp\u003E\n\u003Cp\u003EZkrátka, Tanger je zajímavé a krásné město s mnoha historickými, kulturními a zábavními atrakcemi, které stojí za návštěvu pro všechny cestovatele, kteří se chtějí ponořit do marocké kultury a historie.\u003C\u002Fp\u003E",coordinates:[{latitude:35.7596,longitude:-5.833}],biggest:"yes",type_place:D,id_image_cover:r,id_image_hero:r,affiliate:[{name:e,value:b}],alerts:a,zoom:[{google:s,booking:11}],parking:a,information_author:a,importance:"biggest",seo_tags:a,created_at:"2023-12-27T16:36:26.534285+00:00",updated_at:"2024-03-02T11:05:40.678938+00:00",name_original:a,setting_top:b}],imagePlaceCity:[{id:r,name:C,source:"\u002Fworld\u002Fcities\u002F",type:D,author:"pixabay.com",created_at:"2023-05-09T07:12:29.147744+00:00",updated_at:"2023-12-27T20:42:18.520959+00:00"}],placeState:[{tld:".ma",name:"Maroko",area:446550,population:37252824,slug:"maroko",id_continent:i,information_chatgpt:"\u003Cp\u003EMaroko je severoafrický stát, který se nachází na pobřeží Atlantského oceánu a Středozemního moře. Má rozlohu 446 550 km² a v roce 2021 zde žilo přibližně 37,8 milionu lidí. Hlavním městem je Rabat.\u003C\u002Fp\u003E\n\u003Cp\u003EMaroko je známé svými rozmanitými krajinami, jako jsou pouště, hory, pláže a oázy. Nejvyšší horou je Toubkal s nadmořskou výškou 4 167 metrů. Mezi nejpopulárnější turistické destinace patří města Marrákeš, Fés a Essaouira, ale také pouštní město Merzouga, kde si turisté mohou vychutnat jízdu na velbloudovi a pozorovat západ slunce v poušti.\u003C\u002Fp\u003E\n\u003Cp\u003EMaroko má také bohatou kulturu a historii, která sahá až do antických dob. V zemi se nachází mnoho historických památek, jako jsou medíny, mešity a paláce, z nichž mnohé jsou zapsány na seznamu UNESCO.\u003C\u002Fp\u003E\n\u003Cp\u003EKromě toho je Maroko také známé svou kuchyní, ve které se často používají koření, jako je koriandr, kmín a kurkuma. Mezi nejznámější marocká jídla patří tagine, harira a couscous.\u003C\u002Fp\u003E\n\u003Cp\u003EV Maroku se také nachází mnoho přírodních parků a rezervací, které slouží k ochraně místních druhů rostlin a živočichů, jako jsou například opice berberské, pštrosi a gazely. \u003C\u002Fp\u003E",money_prices:[{name:"Taxi (cena za km)",value:E,date_update:c},{name:"Hlavní jídlo",value:70,date_update:c},{name:"Chleba",value:F,date_update:c},{name:"Mléko",value:6,date_update:c},{name:"Voda (500ml)",value:G,date_update:c},{name:"Espresso",value:s,date_update:c},{name:"Zmrzlina (jeden kopeček)",value:i,date_update:c},{name:"Pivo",value:20,date_update:c},{name:"Krabička cigaret",value:a,date_update:c},{name:"Big Mac",value:a,date_update:c},{name:"Coca Cola (500ml)",value:i,date_update:c}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:"112",date_update:l},{name:"Policie",value:"19",date_update:l},{name:"Hasiči",value:H,date_update:l},{name:"Zdravotnická záchranná služba",value:H,date_update:l}],phone_prefix:"+212",coordinates:[{latitude:"31.7917",longitude:"-7.0926"}],people_religion:[{name:"Islám",value:"99 %"},{name:"Jiné náboženství",value:"1 %"}],people_nationality:[{name:"Arabové",value:"80 %"},{name:"Berbeři",value:"20 %"}],visitors_entry:[{name:"Občanský průkaz",value:"Pro vstup do Maroka jako občan EU není občanský průkaz platným dokladem totožnosti. Je nutné mít platný cestovní pas.",date_update:j},{name:"Cestovní pas",value:"Pro vstup do Maroka je nutné mít platný cestovní pas, který musí být platný minimálně po dobu celého pobytu. Délka povoleného pobytu je 90 dnů.",date_update:j},{name:"Vízum",value:"Pro občany České republiky není nutné vízum pro krátkodobý pobyt (do 90 dnů) v Maroku. Pokud však plánujete dlouhodobý pobyt, je nutné získat vízum předem na marockém velvyslanectví.",date_update:j},{name:"Očkování",value:"Pro vstup do Maroka není vyžadováno žádné specifické očkování. Nicméně, je doporučeno očkování proti hepatitidě A a B, žluté zimnici a břišnímu tyfu. Důležité je také mít platné očkování proti běžným nemocem, jako jsou tetanus, spála, zarděnky a chřipka.",date_update:j},{name:"Cestovní pojištění",value:"Pro vstup do Maroka není povinné mít cestovní pojištění, ale je silně doporučováno. V případě potřeby lékařské péče mohou být náklady velmi vysoké, a tak by mít pojištění mohlo ušetřit cestovatelům mnoho finančních problémů.",date_update:j},{name:"Bezpečností situace",value:"Maroko je obecně bezpečnou zemí pro turisty, ale je důležité být obezřetný a dodržovat základní bezpečnostní opatření, zejména v turistických oblastech. Zvláštní opatrnost se doporučuje v okolí hranic s Alžírskem a Západní Saharou, kde jsou možné bezpečnostní rizika v důsledku separatistických skupin a teroristických hrozeb.",date_update:j},{name:"Další informace",value:"V Maroku je dobrým zvykem vyjednávat o cenách u místních prodejců, a to zejména na trzích a v obchodech s turistickými suvenýry. Dále je nutné být obezřetný ohledně pitné vody a konzumovat pouze balenou vodu. V Maroku je možné platit hotovostí i platebními kartami, ale je třeba mít na paměti, že v menších městech a obcích nejsou všechny obchody vybaveny platebními terminály.",date_update:j}],type_place:"state",id_image_cover:I,id_image_hero:I,id_city_main:1339,affiliate:[{name:e,value:b}],mpz:"MA",alerts:[],zoom:[{google:i,booking:E}],ids_neighboring_countries:[{id:62},{id:66}],organization:[],apps:[],links:[],information_author:[{text:"\u003Cp\u003EMaroko je severoafrický stát, který se nachází na pobřeží \u003Cb\u003EAtlantského oceánu\u003C\u002Fb\u003E a \u003Cb\u003EStředozemního moře\u003C\u002Fb\u003E. Má rozlohu 446 550 km² a v roce 2021 zde žilo přibližně 37,8 milionu lidí. Hlavním městem je \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Frabat\"\u003ERabat\u003C\u002Fa\u003E\u003C\u002Fb\u003E.\u003C\u002Fp\u003E\u003Cp\u003EMaroko je známé svými rozmanitými krajinami, jako jsou pouště, hory, pláže a oázy. Nejvyšší horou je \u003Cb\u003EDžabal Tubkal (توبقال)\u003C\u002Fb\u003E s nadmořskou výškou 4 167 metrů. Mezi nejpopulárnější turistické destinace patří města \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fmarrakes\"\u003EMarrákeš\u003C\u002Fa\u003E\u003C\u002Fb\u003E, \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Ffes\"\u003EFés\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003EEssaouira\u003C\u002Fb\u003E, ale také pouštní město \u003Cb\u003EMerzouga\u003C\u002Fb\u003E, kde si turisté mohou vychutnat jízdu na velbloudovi a pozorovat západ slunce v poušti.\u003C\u002Fp\u003E\u003Ch3\u003EHistorická a kulturní bohatství\u003C\u002Fh3\u003E\u003Ch4\u003EMedína ve Fesu\u003C\u002Fh4\u003E\u003Cp\u003EFes je domovem jedné z největších a nejstarších medín na světě. Procházení úzkými uličkami a prozkoumávání tržišť je jako cesta časem. Medína je také známá svou unikátní architekturou a historickými památkami.\u003C\u002Fp\u003E\u003Ch4\u003EJemaa el-Fnaa\u003C\u002Fh4\u003E\u003Cp\u003E\u003Cb\u003EJemaa el-Fnaa (ساحة جامع الفناء)\u003C\u002Fb\u003E je náměstí v Marrákeši, kde se setkávají tradiční kouzlo a moderní exotika. Za denního světla zde najdete nespočet trhovců, zatímco večer se náměstí proměňuje v pestré spektrum pouličních umělců a prodavačů.\u003C\u002Fp\u003E\u003Ch4\u003ESite Archéologique de Volubilis\u003C\u002Fh4\u003E\u003Cp\u003E\u003Cb\u003ESite Archéologique de Volubilis (موقع وليلي الأثري)\u003C\u002Fb\u003E se nachází nedaleko města \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fmeknes\"\u003EMeknes\u003C\u002Fa\u003E\u003C\u002Fb\u003E a jedná se o archeologické naleziště starověkého římského města. Volubilis je známé pro své dobře zachované mozaiky a ruiny, které poskytují pohled do bohaté historie této oblasti.\u003C\u002Fp\u003E\u003Ch3\u003EPřírodní krásy\u003C\u002Fh3\u003E\u003Ch4\u003EPohoří Atlas\u003C\u002Fh4\u003E\u003Cp\u003EMaroko hostí působivý pohoří Atlas, které se táhne podél celé země. Nejvyšší hora, Toubkal, dosahuje výšky 4 167 metrů. Tyto hory nabízejí nádherné výhledy a jsou oblíbeným místem pro turisty a horolezce.\u003C\u002Fp\u003E\u003Ch3\u003EDoprava\u003C\u002Fh3\u003E\u003Ch4\u003EŽeleznice\u003C\u002Fh4\u003E\u003Cp\u003EMaroko má dobře vyvinutou železniční síť, která spojuje hlavní města a další důležitá místa. Moderní vlaky nabízejí pohodlné cestování s krásnými výhledy na krajinu.\u003C\u002Fp\u003E\u003Ch4\u003EGrand Taxi\u003C\u002Fh4\u003E\u003Cp\u003EPro kratší vzdálenosti je oblíbeným způsobem dopravy tzv. \"Grand Taxi\". Jedná se o sdílené taxi, které operuje na pevně stanovených trasách a spojuje města a vesnice.\u003C\u002Fp\u003E\u003Ch4\u003EAutobusová doprava\u003C\u002Fh4\u003E\u003Cp\u003EPro cestování ve městech a menších vzdáleností jsou k dispozici místní autobusy. Jsou to ekonomická možnost pro cestování po Maroku.\u003C\u002Fp\u003E",date_create:m,date_update:m,author_create:n,author_update:n}],seo_tags:[{tag:"Severní Afrika"},{tag:"Arabština"},{tag:"Co vidět v Maroku"}],language_phrases:[{czech:"Zdravím!",foreign_latin:"Salam aleikum!",foreign_original:"سلام عليكم!",phonetic_transcription:"salam ealaykum!"}],wall_sockets:a,id:d,facts_place:[],created_at:"2023-12-27T16:33:10.517062+00:00",updated_at:"2024-03-02T11:05:26.667745+00:00",name_original:"المغرب‎",currency:[{code:"MAD",name:"marocký dirham"}],setting_top:b}],placeContinent:[{id:i,slug:"afrika",name:"Afrika",area:30370000,population:1340000000,population_density:44.12,number_states:54,information_chatgpt:"\u003Cp\u003EAfrika je největší kontinent na Zemi a rozkládá se na ploše přesahující 30 milionů kilometrů čtverečních. Nachází se zde více než 50 zemí a žije zde více než 1,3 miliardy lidí. \u003C\u002Fp\u003E\n\u003Cp\u003EJižní Afrika, Egypt, Maroko a Keňa patří mezi nejoblíbenější turistické cíle v Africe. V Jihoafrické republice se můžete podívat na slavné Krugerův národní park a sledovat zde život zvířat v jejich přirozeném prostředí. Egypt je zase proslulý svými pyramidami, které se nacházejí v Gíze. V Maroku můžete navštívit tradiční trhy, zatímco v Keni se můžete setkat s Masajským kmenem a podívat se na úžasnou přírodu.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má bohatou historii a kulturu. První lidé, kteří se usadili v Africe, byli loveckým a sběračským národem. Poté se v této oblasti objevily první civilizace, jako je Kúšská říše, Egyptská říše nebo Království Aksum. Afrika byla také kolonizována evropskými mocnostmi a toto období mělo značný dopad na dnešní politickou situaci na kontinentu.\u003C\u002Fp\u003E\n\u003Cp\u003EJe důležité zmínit, že Afrika má velké množství jazyků a kultur. Zahrnuje mnoho různých národů a etnických skupin, a to má vliv na různorodost obyvatelstva a kultury. Nejrozšířenějším jazykem je arabština, která se mluví především v severní Africe a v některých částech západní Afriky. Dalšími důležitými jazyky jsou svahilština, francouzština, portugalština, angličtina a mnoho dalších.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má také nádhernou přírodu. Nachází se zde nejvyšší hora světa, Kilimandžáro, která se nachází na území Tanzanie a Keňy. Kromě toho se v Africe nachází také největší pouště na světě, Sahara, a největší prales, Kongský prales. Na tomto kontinentě můžete vidět také mnoho zvířat, jako jsou sloni, lvi, gepardi, zebry a mnoho dalších druhů. V Africe se také nachází několik nejvýznamnějších řek světa, jako je Nil, Kongo, Niger a Zambezi.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je bohužel také známá pro své problémy. Mnoho zemí se potýká s chudobou, nedostatkem potravin a vody, nemocemi a válečnými konflikty. Mnoho afrických zemí závisí na zemědělství a rybolovu, což však často není dostatečně výnosné, aby zabezpečilo potřeby obyvatelstva. Kromě toho jsou některé země v Africe trvale postižené extrémními přírodními katastrofami, jako jsou sucha, povodně a bouře.\u003C\u002Fp\u003E\n\u003Cp\u003ENa druhou stranu se v Africe také nachází mnoho lidí, kteří se snaží změnit situaci a zlepšit životy svých spoluobčanů. Vznikají neziskové organizace, které se snaží pomáhat při řešení těchto problémů a zlepšovat kvalitu života v mnoha afrických zemích. Zároveň se v posledních letech objevují nové možnosti pro ekonomický růst a rozvoj, jako jsou technologické start-upy a zahraniční investice.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika je fascinující kontinent, který nabízí mnoho zážitků a zároveň v sobě ukrývá mnoho výzev. Pokud se rozhodnete navštívit Afriku, můžete se těšit na krásnou přírodu, bohatou kulturu a nezapomenutelné zážitky. Je však také důležité být obezřetný a připravený na místní podmínky, protože některé oblasti mohou být nebezpečné kvůli konfliktům nebo zločinnosti.\u003C\u002Fp\u003E",coordinates:[{latitude:1.650801,longitude:17.371642}],type_place:"continent",id_image_cover:i,id_image_hero:i,zoom:[{google:F,booking:G}],information_author:[{text:"\u003Cp\u003E\u003Cb\u003EAfrika\u003C\u002Fb\u003E, druhý největší a druhý nejlidnatější kontinent na světě, láká cestovatele svou neuvěřitelnou rozmanitostí kultur, přírodními krásami a bohatou historií. Při cestování touto obrovskou plochou se otevírá mozaika různorodých zážitků, které zahrnují odlesky moderního městského života, divokou přírodu až po historická místa.\u003C\u002Fp\u003E\u003Cp\u003EJižní Afrika, domov několika národních parků a rezervací, je rájem pro milovníky safari. \u003Cb\u003EKrugerův národní park (Kruger National Park)\u003C\u002Fb\u003E nabízí úžasné setkání s \"Velkým pětkou\" - slony, nosorožci, buvoly, lvy a levharty. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fcape-town\"\u003EKapské Město (Cape Town)\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s ikonickou horou \u003Cb\u003EMaclear's Beacon\u003C\u002Fb\u003E, poskytuje nezapomenutelný výhled na město a okolní krajinu.\u003C\u002Fp\u003E\u003Cp\u003EVýchodní Afrika je známá svými ohromujícími sopkami. \u003Cb\u003EKilimandžáro\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E, nejvyšší hora Afriky, přitahuje dobrodruhy a horolezce z celého světa. Návštěva \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003EZanzibaru\u003C\u002Fa\u003E\u003C\u002Fb\u003E s jeho bílými plážemi a historickým centrem \u003Cb\u003EStone Town\u003C\u002Fb\u003E přidává do cestovatelského mixu kouzlo exotiky.\u003C\u002Fp\u003E\u003Cp\u003EStřední Afrika, s obrovskou rovníkovou pralesní plochou, skrývá některé z nejzajímavějších biodiverzit na světě. \u003Cb\u003EKonžský deštný prales\u003C\u002Fb\u003E je po amazonském druhý největší na světě, je domovem vzácných druhů zvířat a rostlin, a expedice sem mohou odhalit tajemství dosud neobjevená.\u003C\u002Fp\u003E\u003Cp\u003ESeverní Afrika je poznamenána starověkými civilizacemi a písečnými pouštěmi. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fegypt\"\u003EEgypt\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pyramidami a chrámy na březích \u003Cb\u003ENilu\u003C\u002Fb\u003E je jednou z nejnavštěvovanějších destinací v této části kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmaroko\"\u003EMaroko\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s mistrovským spojením tradice a moderny v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fmarrakes\"\u003EMarrákeši\u003C\u002Fa\u003E\u003C\u002Fb\u003E, je fascinujícím mixem barev, vůní a architektury.\u003C\u002Fp\u003E\u003Cp\u003EZápadní Afrika, bohatá na kulturní dědictví, nabízí autentické zážitky. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fghana\"\u003EGhana\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pevnostmi, které připomínají období otroctví, a kultovními festivaly představuje hluboký pohled do minulosti. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fsenegal\"\u003ESenegal\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s historickým ostrovem \u003Cb\u003EGorée\u003C\u002Fb\u003E, který sehral roli s obchodem s otroky, kde najdete koloniální budovy a muzeum \u003Cb\u003EHouse of Slaves (Maison des esclaves)\u003C\u002Fb\u003E.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je rovněž známá svými divokými zvířaty. Kromě safari v jižní části kontinentu můžete zažít jedinečnou příležitost sledovat migraci tisíců zeber a gazel v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fkena\"\u003EKeni\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E. Návštěva gorilí v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Frwanda\"\u003ERwandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fuganda\"\u003EUgandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E je neopakovatelným zážitkem pro ty, kteří touží po setkání s těmito ohroženými tvory.\u003C\u002Fp\u003E\u003Cp\u003EPřírodní skvosty nejsou omezeny pouze na pevninskou část kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fseychely\"\u003ESeychely\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMauricius\u003C\u002Fa\u003E\u003C\u002Fb\u003E, dvě ostrůvky v \u003Cb\u003EIndickém oceánu\u003C\u002Fb\u003E, lákají svými bílými plážemi, průzračnými vodami a jedinečnou faunou a florou.\u003C\u002Fp\u003E\u003Cp\u003EAfrika rovněž hýří kulturou a historií. Starověké památky jako \u003Cb\u003EGreat Zimbabwe National Monument\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fzimbabwe\"\u003EZimbabwe\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003ETimbuktu (Tombouctou)\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMali\u003C\u002Fa\u003E\u003C\u002Fb\u003E jsou svědectvím o bohaté historii a rozvoji obchodu a kultury na africkém kontinentu. Každý region má své vlastní tradiční tance, hudbu a umění, které odrážejí místní identitu a historii.\u003C\u002Fp\u003E\u003Cp\u003ECestování Afrikou je také o interakci s místními lidmi. Přívětivost a pohostinnost afrických komunit jsou nezapomenutelnými součástmi cestování. Místní trhy nabízejí pestrou škálu barev, vůní a chutí, zatímco setkání s kmeny a etnickými skupinami přináší pochopení různorodosti kultur a tradic.\u003C\u002Fp\u003E\u003Cp\u003EJe však důležité si být vědomý sociálních, ekonomických a environmentálních výzev, kterým mnohé africké země čelí. Cestovatelé by měli být respektující vůči místním zvyklostem a životnímu prostředí a přispívat k udržitelnému turismu, který podporuje místní ekonomiky a ochranu přírodních zdrojů.\u003C\u002Fp\u003E\u003Cp\u003ECelkově lze říci, že cestování po Africe je dobrodružstvím naplněným kontrasty a překvapeními. Od rozlehlých savan po exotické pláže, od starověkých ruin po moderní metropole. Afrika nabízí cestovatelům nekonečné možnosti objevování a poznávání. Je to kontinent, který zanechává nezapomenutelný dojem na každém, kdo se odhodlá k jeho prozkoumání.\u003C\u002Fp\u003E",date_create:m,date_update:m,author_create:n,author_update:n}],seo_tags:[],created_at:"2023-12-27T16:35:37.460505+00:00",updated_at:"2024-03-02T11:45:40.694514+00:00",name_original:"Africa",setting_top:b}]}],fetch:{"OrganismsoPlaceTeaserSpotsComponent:0":{spots:[{id:u,id_city:p,id_image_cover:a,id_image_hero:a,created_at:v,slug:w,name:x,information_chatgpt:k,type_place:f,coordinates:[{latitude:y,longitude:z}],zoom:[{google:q,booking:g}],affiliate:[{name:e,value:b}],id_state:d,altitude:g,information_author:[],seo_tags:[],information_duration:[],updated_at:h,name_original:A,type_spot:[{type:B}],setting_top:b},{id:45,id_city:a,id_image_cover:a,id_image_hero:a,created_at:"2023-09-11T13:36:32.076377+00:00",slug:"tinmel-mosque",name:"Tinmel Mosque",information_chatgpt:"\u003Cp\u003EMešita Tinmal je historickou mešitou v oblasti High Atlas v Maroku. Tato mešita má významné místo v marocké historii, protože byla jedním z klíčových center pro almorávidskou dynastii, která hrála důležitou roli v období středověkého Maroka.\u003C\u002Fp\u003E\n\n    \u003Cul\u003E\n        \u003Cli\u003E\n            \u003Cstrong\u003EHistorie a význam:\u003C\u002Fstrong\u003E\n            Mešita Tinmal byla postavena v roce 1153 zakladatelem almorávidské dynastie, Abdalláhem ibn Júsufem. Toto místo sloužilo nejen jako modlitební centrum, ale také jako vojenská pevnost a kulturní centrum. Almorávidové zde organizovali své vojenské tažení a řídili své územní državy.\n        \u003C\u002Fli\u003E\n        \u003Cli\u003E\n            \u003Cstrong\u003EArchitektura:\u003C\u002Fstrong\u003E\n            Tinmal Mosque je známá svou jednoduchou, ale krásnou architekturou. Stavba je postavena z cihel a vápence, typické pro islámskou architekturu té doby. Mešita má čtyři kulaté věže, z nichž jedna slouží jako minaret, ze kterého se vyzývá k modlitbě. Uvnitř mešity najdete krásně zdobené stropy a sloupy.\n        \u003C\u002Fli\u003E\n        \u003Cli\u003E\n            \u003Cstrong\u003EUnikátní místo:\u003C\u002Fstrong\u003E\n            Tinmal Mosque je unikátní v tom, že byla v průběhu staletí velmi dobře zachována. Je to jedna z mála mešit v Maroku, která je otevřena ne-muslimským návštěvníkům. To umožňuje cizincům prozkoumat tuto historickou památku a ocenit její kulturní a historický význam.\n        \u003C\u002Fli\u003E\n        \u003Cli\u003E\n            \u003Cstrong\u003EPřístup pro návštěvníky:\u003C\u002Fstrong\u003E\n            Tinmal Mosque se nachází v odlehlé oblasti High Atlas, což z ní činí menší turistickou atrakci ve srovnání s některými jinými místy v Maroku. Nicméně, pro ty, kteří se rozhodnou navštívit tuto mešitu, nabízí to jedinečnou příležitost pro ponoření se do bohaté historie a kultury Maroka.\n        \u003C\u002Fli\u003E\n    \u003C\u002Ful\u003E",type_place:f,coordinates:[{latitude:30.984582,longitude:-8.228261}],zoom:[{google:q,booking:g}],affiliate:[{name:e,value:b}],id_state:d,altitude:1266,information_author:[],seo_tags:a,information_duration:[],updated_at:h,name_original:a,type_spot:a,setting_top:b},{id:39,id_city:t,id_image_cover:a,id_image_hero:a,created_at:"2023-08-18T06:41:24.129276+00:00",slug:"fes-medina",name:"Fes Medina",information_chatgpt:k,type_place:f,coordinates:[{latitude:34.0631192,longitude:-4.9738}],zoom:[{google:o,booking:J}],affiliate:[{name:e,value:b}],id_state:d,altitude:280,information_author:[],seo_tags:a,information_duration:[],updated_at:h,name_original:a,type_spot:a,setting_top:b},{id:40,id_city:t,id_image_cover:a,id_image_hero:a,created_at:"2023-08-18T06:46:46.465365+00:00",slug:"jardin-jnan-sbil",name:"Jardin Jnan Sbil",information_chatgpt:k,type_place:f,coordinates:[{latitude:34.0586247,longitude:-4.9880719}],zoom:[{google:g,booking:J}],affiliate:[{name:e,value:b}],id_state:d,altitude:380,information_author:[],seo_tags:a,information_duration:[],updated_at:h,name_original:a,type_spot:a,setting_top:b},{id:41,id_city:t,id_image_cover:a,id_image_hero:a,created_at:"2023-08-18T06:52:28.718564+00:00",slug:"chouara-tannery",name:"Chouara Tannery",information_chatgpt:k,type_place:f,coordinates:[{latitude:34.0659178,longitude:-4.9709917}],zoom:[{google:g,booking:o}],affiliate:[{name:e,value:b}],id_state:d,altitude:276,information_author:[],seo_tags:a,information_duration:[],updated_at:h,name_original:a,type_spot:a,setting_top:b},{id:59,id_city:1338,id_image_cover:a,id_image_hero:a,created_at:"2024-01-15T11:44:34.639058+00:00",slug:"mesita-hasana-ii",name:"Mešita Hasana II.",information_chatgpt:"\u003Cp\u003EMešita Hassana II. je monumentálním islámským náboženským a kulturním centrem v Casablance, Maroko. Postavena byla v letech 1986 až 1993 a nese jméno krále Hassana II., který byl iniciátorem její výstavby. Jedná se o jednu z největších mešit na světě, a to jak co do velikosti, tak i ohledně své architektonické a umělecké hodnoty.\u003C\u002Fp\u003E\n\n\u003Cp\u003EMešita je umístěna na náspu nad Atlantským oceánem, což poskytuje nádherný výhled na moře. S jejím celkovým kapacitním potenciálem pro více než 100 000 věřících zahrnuje rozlehlý mešitní komplex také islámskou školu, lázně a muzeum.\u003C\u002Fp\u003E\n\n\u003Cp\u003EArchitektura mešity kombinuje tradiční marocké prvky s moderním stylem. Je postavena z bílého mramoru a zdobena uměleckými prvky, jako jsou mozaiky a dřevořezby. Nejvýraznějším prvkem je vysoká minaret, která dosahuje výšky 210 metrů, což z ní činí jednu z nejvyšších minaretů na světě.\u003C\u002Fp\u003E\n\n\u003Cp\u003EMešita Hassana II. slouží nejen jako náboženské místo, ale také jako kulturní a vzdělávací centrum. Věřící i turisté mohou obdivovat bohatství marockého umění a architektury, které se v mešitě odráží. Je to místo, kde se snoubí tradiční hodnoty islámu s moderním pohledem na svět.\u003C\u002Fp\u003E",type_place:f,coordinates:[{latitude:33.607742,longitude:-7.632777}],zoom:[{google:o,booking:o}],affiliate:[{name:"boooking",value:b}],id_state:d,altitude:s,information_author:[],seo_tags:[],information_duration:[],updated_at:h,name_original:a,type_spot:a,setting_top:b}],images:a},"OrganismsoSidebarEventComponent:0":{events:[],images:a}},mutations:[]}}(null,true,"01-2021",137,"booking","spot",14,"2024-03-02T11:05:47.137951+00:00",5,"09-2021","","05-2023","2024-03-01","1",13,1341,17,454,10,1340,105,"2024-03-01T23:59:24.97766+00:00","hercules-caves","Hercules Caves",35.75991,-5.93927,"مغارة هرقل","jeskyně","tanger","city",7,2,3,"15",312,12)));