__NUXT_JSONP__("/svet/stat/cad/sousedni-staty", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w,x,y,z,A,B,C,D,E,F,G,H,I,J,K,L,M){return {data:[{tabsLoad:{tabWhatToSee:e,tabPrice:e,tabPeople:e,tabTrip:e,tabContact:e,tabHotel:e,tabNeighboring:o,tabVideos:i,tabArticles:i,tabWallSockets:4,tabChains:i,tabFauna:1,tabFlora:i,tabFoods:i,tabBrands:i},place:[{tld:".td",name:"Čad",area:1284000,population:16425859,slug:p,id_continent:l,information_chatgpt:"\u003Cp\u003EČad je vnitrozemský stát v Africe, sousedící s Libyí, Nigerem, Nigérií, Kamerunem, Středoafrickou republikou a Súdánem. Země je bohatá na nerostné suroviny, jako jsou ropa, zlato a uran, ale bohužel trpí politickou nestabilitou a chudobou.\u003C\u002Fp\u003E\n\u003Cp\u003EČad má různorodou krajinu, zahrnující pouště, savany a hory. V národním parku Zakouma můžete vidět slony, antilopy a jiná zvířata. V pohoří Tibesti se nachází sopky a krátery, které jsou přístupné pouze pro zkušené horolezce.\u003C\u002Fp\u003E\n\u003Cp\u003EKapitálou a největším městem Čadu je N'Djamena, kde se nachází mnoho historických památek, jako například Grande Mosquée, katedrála Notre-Dame a Národní muzeum. Většina obyvatelstva žije v menších městech a vesnicích, kde se zabývají zemědělstvím a pastevectvím.\u003C\u002Fp\u003E\n\u003Cp\u003EČad má také bohatou kulturu a tradice. Mezi nejznámější patří taneční a hudební představení, které jsou většinou spojeny s náboženskými svátky. Místní kuchyně je plná exotických koření a používá se zde hodně kari, rýže, fazole a zeleniny.\u003C\u002Fp\u003E\n\u003Cp\u003EPro cestovatele není Čad jednoduchou destinací. Země je zasažena politickou nestabilitou a mnoho oblastí je považováno za nebezpečné. Přesto pokud se rozhodnete navštívit Čad, můžete objevovat jeho krásy a setkat se s místními lidmi, kteří jsou velmi pohostinní.\u003C\u002Fp\u003E",money_prices:[{name:"Taxi (cena za km)",value:a,date_update:b},{name:"Hlavní jídlo",value:a,date_update:b},{name:"Chleba",value:a,date_update:b},{name:"Mléko",value:a,date_update:b},{name:"Voda (500ml)",value:250,date_update:b},{name:"Espresso",value:a,date_update:b},{name:"Zmrzlina (jeden kopeček)",value:a,date_update:b},{name:"Pivo",value:1000,date_update:b},{name:"Krabička cigaret",value:a,date_update:b},{name:"Big Mac",value:a,date_update:b},{name:"Coca Cola (500ml)",value:a,date_update:b}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:"225 2525",date_update:b},{name:"Policie",value:"17",date_update:b},{name:"Hasiči",value:"18",date_update:b},{name:"Zdravotnická záchranná služba",value:"2251 3255",date_update:b}],phone_prefix:"+235",coordinates:[{latitude:"15.4542",longitude:"18.7322"}],people_religion:[{name:"muslimové",value:"52,1%",date_update:m},{name:"křesťané",value:"44,1%",date_update:m},{name:"animisté",value:q,date_update:m},{name:"jiné",value:q,date_update:m}],people_nationality:[{name:"Arabové",value:"12%",date_update:d},{name:"Budžové",value:"9%",date_update:d},{name:"Kotokoli",value:r,date_update:d},{name:"Zaghawa",value:r,date_update:d},{name:"Sara",value:"5%",date_update:d},{name:"Masaové",value:"4%",date_update:d},{name:"Mundang",value:"3%",date_update:d},{name:"Mbaye",value:s,date_update:d},{name:"Fulbové",value:s,date_update:d},{name:"Autres",value:"51%",date_update:d}],visitors_entry:[{name:"Občanský průkaz",value:"Občanský průkaz není platným cestovním dokladem pro vstup do Čadu. Je nutné mít platný cestovní pas.",date_update:f},{name:"Cestovní pas",value:"Pro vstup do Čadu je nutné mít platný cestovní pas. Občanský průkaz není platným cestovním dokladem.",date_update:f},{name:"Vízum",value:"Pro vstup do Čadu je pro většinu cizinců vyžadováno vízum. Občané Evropské unie a několika dalších zemí jsou od této povinnosti osvobozeni a mohou pobývat v zemi bez víza po dobu 90 dnů. Ostatní cizinci musí žádat o vízum před vstupem do země.",date_update:f},{name:"Očkování",value:"Pro vstup do Čadu není nutné mít povinné očkování, ale je doporučeno očkování proti žluté zimnici a při delším pobytu v zemi i proti meningokokovým nákazám. Je vhodné konzultovat očkování s lékařem.",date_update:f},{name:"Cestovní pojištění",value:"Pro vstup do Čadu není vyžadováno mít cestovní ani zdravotní pojištění, nicméně vzhledem k vysokému riziku onemocnění a nehod na místě se doporučuje mít vhodné pojištění, které pokryje náklady na lékařskou péči a případnou evakuaci země.",date_update:f},{name:"Bezpečnostní situace",value:"Bezpečnostní situace v Čadu je nestabilní a země trpí vysokou mírou kriminality a teroristických útoků. Doporučuje se cestovat pouze v nezbytných případech a mít na paměti, že cestování mimo hlavní město Ndžameny je velmi nebezpečné. Výskyt ozbrojených útoků, únosců a krádeží je běžný.",date_update:f},{name:"Další informace",value:"V Čadu se setkáte s různými zvyklostmi, včetně zvyklostí týkajících se oblečení a chování. Doporučuje se respektovat místní zvyklosti a vyvarovat se nevhodného chování. Vyhýbejte se politickým shromážděním a obecně se držte na veřejnosti opatrně. Nedoporučuje se cestovat v noci nebo sami a mít při sobě cennosti.",date_update:f}],type_place:c,id_image_cover:n,id_image_hero:n,id_city_main:2123,affiliate:[{name:"booking",value:e}],mpz:"TD",alerts:a,zoom:[{google:o,booking:7}],ids_neighboring_countries:[{id:t},{id:u},{id:v},{id:w},{id:x},{id:y}],organization:a,apps:a,links:a,information_author:a,seo_tags:a,language_phrases:a,wall_sockets:a,id:215,facts_place:a,created_at:"2023-12-27T16:33:10.517062+00:00",updated_at:"2024-03-02T11:05:26.667745+00:00",name_original:a,currency:[{code:"XAF",name:"CFA frank"}],setting_top:e}],imagePlace:[{id:n,name:p,source:g,type:c,author:j,created_at:"2023-05-04T07:17:51.463854+00:00",updated_at:h}],placesStatesNeighboring:[{id:y,id_image_cover:z,slug:A,type_place:c,name:"Súdán"},{id:x,id_image_cover:B,slug:C,type_place:c,name:"Nigérie"},{id:w,id_image_cover:D,slug:E,type_place:c,name:"Niger"},{id:v,id_image_cover:F,slug:G,type_place:c,name:"Libye"},{id:u,id_image_cover:H,slug:I,type_place:c,name:"Kamerun"},{id:t,id_image_cover:J,slug:K,type_place:c,name:"Středoafrická republika"}],imagesStatesNeighboring:[{id:J,name:K,source:g,type:c,author:"Brice Blondel for HDPTCAR - hdptcar.net",created_at:"2023-05-06T09:27:42.033044+00:00",updated_at:h},{id:z,name:A,source:g,type:c,author:j,created_at:k,updated_at:h},{id:B,name:C,source:g,type:c,author:j,created_at:k,updated_at:h},{id:D,name:E,source:g,type:c,author:j,created_at:k,updated_at:h},{id:F,name:G,source:g,type:c,author:j,created_at:k,updated_at:h},{id:H,name:I,source:g,type:c,author:j,created_at:k,updated_at:h}],placeContinent:[{id:l,slug:"afrika",name:"Afrika",area:30370000,population:1340000000,population_density:44.12,number_states:54,information_chatgpt:"\u003Cp\u003EAfrika je největší kontinent na Zemi a rozkládá se na ploše přesahující 30 milionů kilometrů čtverečních. Nachází se zde více než 50 zemí a žije zde více než 1,3 miliardy lidí. \u003C\u002Fp\u003E\n\u003Cp\u003EJižní Afrika, Egypt, Maroko a Keňa patří mezi nejoblíbenější turistické cíle v Africe. V Jihoafrické republice se můžete podívat na slavné Krugerův národní park a sledovat zde život zvířat v jejich přirozeném prostředí. Egypt je zase proslulý svými pyramidami, které se nacházejí v Gíze. V Maroku můžete navštívit tradiční trhy, zatímco v Keni se můžete setkat s Masajským kmenem a podívat se na úžasnou přírodu.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má bohatou historii a kulturu. První lidé, kteří se usadili v Africe, byli loveckým a sběračským národem. Poté se v této oblasti objevily první civilizace, jako je Kúšská říše, Egyptská říše nebo Království Aksum. Afrika byla také kolonizována evropskými mocnostmi a toto období mělo značný dopad na dnešní politickou situaci na kontinentu.\u003C\u002Fp\u003E\n\u003Cp\u003EJe důležité zmínit, že Afrika má velké množství jazyků a kultur. Zahrnuje mnoho různých národů a etnických skupin, a to má vliv na různorodost obyvatelstva a kultury. Nejrozšířenějším jazykem je arabština, která se mluví především v severní Africe a v některých částech západní Afriky. Dalšími důležitými jazyky jsou svahilština, francouzština, portugalština, angličtina a mnoho dalších.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má také nádhernou přírodu. Nachází se zde nejvyšší hora světa, Kilimandžáro, která se nachází na území Tanzanie a Keňy. Kromě toho se v Africe nachází také největší pouště na světě, Sahara, a největší prales, Kongský prales. Na tomto kontinentě můžete vidět také mnoho zvířat, jako jsou sloni, lvi, gepardi, zebry a mnoho dalších druhů. V Africe se také nachází několik nejvýznamnějších řek světa, jako je Nil, Kongo, Niger a Zambezi.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je bohužel také známá pro své problémy. Mnoho zemí se potýká s chudobou, nedostatkem potravin a vody, nemocemi a válečnými konflikty. Mnoho afrických zemí závisí na zemědělství a rybolovu, což však často není dostatečně výnosné, aby zabezpečilo potřeby obyvatelstva. Kromě toho jsou některé země v Africe trvale postižené extrémními přírodními katastrofami, jako jsou sucha, povodně a bouře.\u003C\u002Fp\u003E\n\u003Cp\u003ENa druhou stranu se v Africe také nachází mnoho lidí, kteří se snaží změnit situaci a zlepšit životy svých spoluobčanů. Vznikají neziskové organizace, které se snaží pomáhat při řešení těchto problémů a zlepšovat kvalitu života v mnoha afrických zemích. Zároveň se v posledních letech objevují nové možnosti pro ekonomický růst a rozvoj, jako jsou technologické start-upy a zahraniční investice.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika je fascinující kontinent, který nabízí mnoho zážitků a zároveň v sobě ukrývá mnoho výzev. Pokud se rozhodnete navštívit Afriku, můžete se těšit na krásnou přírodu, bohatou kulturu a nezapomenutelné zážitky. Je však také důležité být obezřetný a připravený na místní podmínky, protože některé oblasti mohou být nebezpečné kvůli konfliktům nebo zločinnosti.\u003C\u002Fp\u003E",coordinates:[{latitude:1.650801,longitude:17.371642}],type_place:"continent",id_image_cover:l,id_image_hero:l,zoom:[{google:2,booking:3}],information_author:[{text:"\u003Cp\u003E\u003Cb\u003EAfrika\u003C\u002Fb\u003E, druhý největší a druhý nejlidnatější kontinent na světě, láká cestovatele svou neuvěřitelnou rozmanitostí kultur, přírodními krásami a bohatou historií. Při cestování touto obrovskou plochou se otevírá mozaika různorodých zážitků, které zahrnují odlesky moderního městského života, divokou přírodu až po historická místa.\u003C\u002Fp\u003E\u003Cp\u003EJižní Afrika, domov několika národních parků a rezervací, je rájem pro milovníky safari. \u003Cb\u003EKrugerův národní park (Kruger National Park)\u003C\u002Fb\u003E nabízí úžasné setkání s \"Velkým pětkou\" - slony, nosorožci, buvoly, lvy a levharty. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fcape-town\"\u003EKapské Město (Cape Town)\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s ikonickou horou \u003Cb\u003EMaclear's Beacon\u003C\u002Fb\u003E, poskytuje nezapomenutelný výhled na město a okolní krajinu.\u003C\u002Fp\u003E\u003Cp\u003EVýchodní Afrika je známá svými ohromujícími sopkami. \u003Cb\u003EKilimandžáro\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E, nejvyšší hora Afriky, přitahuje dobrodruhy a horolezce z celého světa. Návštěva \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003EZanzibaru\u003C\u002Fa\u003E\u003C\u002Fb\u003E s jeho bílými plážemi a historickým centrem \u003Cb\u003EStone Town\u003C\u002Fb\u003E přidává do cestovatelského mixu kouzlo exotiky.\u003C\u002Fp\u003E\u003Cp\u003EStřední Afrika, s obrovskou rovníkovou pralesní plochou, skrývá některé z nejzajímavějších biodiverzit na světě. \u003Cb\u003EKonžský deštný prales\u003C\u002Fb\u003E je po amazonském druhý největší na světě, je domovem vzácných druhů zvířat a rostlin, a expedice sem mohou odhalit tajemství dosud neobjevená.\u003C\u002Fp\u003E\u003Cp\u003ESeverní Afrika je poznamenána starověkými civilizacemi a písečnými pouštěmi. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fegypt\"\u003EEgypt\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pyramidami a chrámy na březích \u003Cb\u003ENilu\u003C\u002Fb\u003E je jednou z nejnavštěvovanějších destinací v této části kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmaroko\"\u003EMaroko\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s mistrovským spojením tradice a moderny v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fmarrakes\"\u003EMarrákeši\u003C\u002Fa\u003E\u003C\u002Fb\u003E, je fascinujícím mixem barev, vůní a architektury.\u003C\u002Fp\u003E\u003Cp\u003EZápadní Afrika, bohatá na kulturní dědictví, nabízí autentické zážitky. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fghana\"\u003EGhana\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pevnostmi, které připomínají období otroctví, a kultovními festivaly představuje hluboký pohled do minulosti. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fsenegal\"\u003ESenegal\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s historickým ostrovem \u003Cb\u003EGorée\u003C\u002Fb\u003E, který sehral roli s obchodem s otroky, kde najdete koloniální budovy a muzeum \u003Cb\u003EHouse of Slaves (Maison des esclaves)\u003C\u002Fb\u003E.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je rovněž známá svými divokými zvířaty. Kromě safari v jižní části kontinentu můžete zažít jedinečnou příležitost sledovat migraci tisíců zeber a gazel v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fkena\"\u003EKeni\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E. Návštěva gorilí v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Frwanda\"\u003ERwandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fuganda\"\u003EUgandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E je neopakovatelným zážitkem pro ty, kteří touží po setkání s těmito ohroženými tvory.\u003C\u002Fp\u003E\u003Cp\u003EPřírodní skvosty nejsou omezeny pouze na pevninskou část kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fseychely\"\u003ESeychely\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMauricius\u003C\u002Fa\u003E\u003C\u002Fb\u003E, dvě ostrůvky v \u003Cb\u003EIndickém oceánu\u003C\u002Fb\u003E, lákají svými bílými plážemi, průzračnými vodami a jedinečnou faunou a florou.\u003C\u002Fp\u003E\u003Cp\u003EAfrika rovněž hýří kulturou a historií. Starověké památky jako \u003Cb\u003EGreat Zimbabwe National Monument\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fzimbabwe\"\u003EZimbabwe\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003ETimbuktu (Tombouctou)\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMali\u003C\u002Fa\u003E\u003C\u002Fb\u003E jsou svědectvím o bohaté historii a rozvoji obchodu a kultury na africkém kontinentu. Každý region má své vlastní tradiční tance, hudbu a umění, které odrážejí místní identitu a historii.\u003C\u002Fp\u003E\u003Cp\u003ECestování Afrikou je také o interakci s místními lidmi. Přívětivost a pohostinnost afrických komunit jsou nezapomenutelnými součástmi cestování. Místní trhy nabízejí pestrou škálu barev, vůní a chutí, zatímco setkání s kmeny a etnickými skupinami přináší pochopení různorodosti kultur a tradic.\u003C\u002Fp\u003E\u003Cp\u003EJe však důležité si být vědomý sociálních, ekonomických a environmentálních výzev, kterým mnohé africké země čelí. Cestovatelé by měli být respektující vůči místním zvyklostem a životnímu prostředí a přispívat k udržitelnému turismu, který podporuje místní ekonomiky a ochranu přírodních zdrojů.\u003C\u002Fp\u003E\u003Cp\u003ECelkově lze říci, že cestování po Africe je dobrodružstvím naplněným kontrasty a překvapeními. Od rozlehlých savan po exotické pláže, od starověkých ruin po moderní metropole. Afrika nabízí cestovatelům nekonečné možnosti objevování a poznávání. Je to kontinent, který zanechává nezapomenutelný dojem na každém, kdo se odhodlá k jeho prozkoumání.\u003C\u002Fp\u003E",date_create:L,date_update:L,author_create:M,author_update:M}],seo_tags:[],created_at:"2023-12-27T16:35:37.460505+00:00",updated_at:"2024-03-02T11:45:40.694514+00:00",name_original:"Africa",setting_top:e}]}],fetch:{},mutations:[]}}(null,"05-2023","state","05-2020",true,"09-2021","\u002Fworld\u002Fstates\u002F","2023-12-27T20:42:18.520959+00:00",0,"pixabay.com","2023-05-03T20:14:00.749527+00:00",5,"01-2021",394,6,"cad","1,9%","6%","2%",41,47,136,162,164,196,367,"sudan",327,"nigerie",326,"niger",299,"libye",283,"kamerun",426,"stredoafricka-republika","2024-03-01","1")));