__NUXT_JSONP__("/svet/stat/pobrezi-slonoviny/sousedni-staty", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w,x,y,z,A,B,C,D,E,F,G,H,I){return {data:[{tabsLoad:{tabWhatToSee:d,tabPrice:d,tabPeople:d,tabTrip:d,tabContact:d,tabHotel:d,tabNeighboring:i,tabVideos:f,tabArticles:f,tabWallSockets:m,tabChains:1,tabFauna:f,tabFlora:f,tabFoods:f,tabBrands:f},place:[{tld:".ci",name:"Pobřeží slonoviny",area:322463,population:26378274,slug:n,id_continent:i,information_chatgpt:"\u003Cp\u003EPobřeží slonoviny, oficiálně Côte d'Ivoire, je západoafrický stát s bohatou historií a kulturou. Země je pojmenována po slonovině, která byla v minulosti důležitým vývozním produktem.\u003C\u002Fp\u003E\n\u003Cp\u003EPobřeží slonoviny má rozlohu přibližně 322 000 km² a počet obyvatel se odhaduje na více než 26 milionů. Hlavním městem je Yamoussoukro, ale největším a nejvýznamnějším městem je Abidžan, který je ekonomickým centrem země.\u003C\u002Fp\u003E\n\u003Cp\u003EEkonomika Pobřeží slonoviny je založena na těžbě a vývozu kakaa, kávy, palmového oleje a dřeva. V posledních letech se země snaží diverzifikovat svoji ekonomiku a rozvíjet turistický průmysl.\u003C\u002Fp\u003E\n\u003Cp\u003EPobřeží slonoviny má bohatou kulturu a tradice. Mezi nejvýznamnější kulturní události patří slavnosti Goli, během kterých se lidé v tradičních maskách a kostýmech tančí a představují různé historické a mytické postavy.\u003C\u002Fp\u003E\n\u003Cp\u003EPokud plánujete návštěvu Pobřeží slonoviny, určitě navštivte Abidžan, kde se nachází mnoho zajímavých památek, jako je katedrála svatého Pavla, bazilika Notre-Dame de la Paix nebo Národní muzeum. Zajímavou zastávkou může být také město Yamoussoukro, kde se nachází největší bazilika na světě - Bazilika Naší Paní Versailleské.\u003C\u002Fp\u003E",money_prices:[{name:"Taxi (cena za km)",value:k,date_update:a},{name:"Hlavní jídlo",value:4000,date_update:a},{name:"Chleba",value:k,date_update:a},{name:"Mléko",value:o,date_update:a},{name:"Voda (500ml)",value:400,date_update:a},{name:"Espresso",value:o,date_update:a},{name:"Zmrzlina (jeden kopeček)",value:k,date_update:a},{name:"Pivo",value:1000,date_update:a},{name:"Krabička cigaret",value:1200,date_update:a},{name:"Big Mac",value:2500,date_update:a},{name:"Coca Cola (500ml)",value:600,date_update:a}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:"112",date_update:a},{name:"Policie",value:"111",date_update:a},{name:"Hasiči",value:"180",date_update:a},{name:"Zdravotnická záchranná služba",value:"185",date_update:a}],phone_prefix:"+225",coordinates:[{latitude:"7.5400",longitude:"-5.5471"}],people_religion:[{name:"Muslímští věřící",value:"42%"},{name:"Křesťané",value:"32%"},{name:"Nestátní náboženské skupiny",value:"17%"},{name:"Bez vyznání",value:"7%"},{name:"Tradicionální náboženství",value:p}],people_nationality:[{name:"Akanové",value:"28%"},{name:"Voltové",value:"16%"},{name:"Národnostní skupiny",value:"14%"},{name:"Báulé",value:"11%"},{name:"Dioulové",value:"5%"},{name:"Malinké",value:"3%"},{name:"Mandejci",value:p},{name:"Jiné",value:"21%"}],visitors_entry:[{name:"Občanský průkaz",value:"Občanský průkaz nestačí, je nutné mít platný cestovní pas.",date_update:e},{name:"Cestovní pas",value:"Ano, je nutné mít platný cestovní pas.",date_update:e},{name:"Vízum",value:"Pro pobyt kratší než 90 dní není potřeba vízum pro občany ČR a dalších států EU. Pokud plánujete pobyt delší než 90 dní nebo jiný druh cesty, zkontrolujte vízové požadavky s konzulárním oddělením Pobřeží slonoviny.",date_update:e},{name:"Očkování",value:"Očkování není povinné, ale jsou doporučována očkování proti žluté zimnici, hepatitidě A a B, tyfu a meningokokové meningitidě.",date_update:e},{name:"Cestovní pojištění",value:"Cestovní pojištění není povinné, ale doporučuje se mít ho uzavřené v případě potřeby lékařské péče. Některé zdravotnické služby mohou být dostupné pouze pro držitele platného cestovního pojištění.",date_update:e},{name:"Bezpečnostní situace",value:"V posledních letech došlo ke zlepšení bezpečnostní situace v zemi, ale stále existuje riziko teroristických útoků, zejména v oblastech na severu a východě země. Turistům se doporučuje být opatrní a sledovat situaci v médiích a na oficiálních webových stránkách země.",date_update:e},{name:"Další informace",value:"V Pobřeží slonoviny jsou obvyklé místní zvyky a tradice. Při návštěvě kostelů a dalších náboženských památek je nutné mít na sobě slušné a kryjící oblečení. Důležité je také dodržování základních pravidel chování a respektování místních zvyklostí a kultur.",date_update:e}],type_place:b,id_image_cover:l,id_image_hero:l,id_city_main:412,affiliate:[{name:"booking",value:d}],mpz:"CI",alerts:c,zoom:[{google:6,booking:7}],ids_neighboring_countries:[{id:q},{id:r},{id:s},{id:t},{id:u}],organization:c,apps:c,links:c,information_author:c,seo_tags:c,language_phrases:c,wall_sockets:c,id:44,facts_place:c,created_at:"2023-12-27T16:33:10.517062+00:00",updated_at:"2024-03-02T11:05:26.667745+00:00",name_original:c,currency:[{code:"XOF",name:"Západoafrický frank"}],setting_top:d}],imagePlace:[{id:l,name:n,source:g,type:b,author:"unsplash.com\u002F@giulio_eugeni",created_at:v,updated_at:h}],placesStatesNeighboring:[{id:s,id_image_cover:w,slug:x,type_place:b,name:"Mali"},{id:u,id_image_cover:y,slug:z,type_place:b,name:"Libérie"},{id:t,id_image_cover:A,slug:B,type_place:b,name:"Guinea"},{id:q,id_image_cover:C,slug:D,type_place:b,name:"Ghana"},{id:r,id_image_cover:E,slug:F,type_place:b,name:"Burkina Faso"}],imagesStatesNeighboring:[{id:y,name:z,source:g,type:b,author:"unsplash.com\u002F@bethlaird",created_at:v,updated_at:h},{id:w,name:x,source:g,type:b,author:j,created_at:"2023-05-03T20:14:00.749527+00:00",updated_at:h},{id:A,name:B,source:g,type:b,author:j,created_at:G,updated_at:h},{id:C,name:D,source:g,type:b,author:j,created_at:G,updated_at:h},{id:E,name:F,source:g,type:b,author:j,created_at:"2023-04-10T23:58:19.292421+00:00",updated_at:h}],placeContinent:[{id:i,slug:"afrika",name:"Afrika",area:30370000,population:1340000000,population_density:44.12,number_states:54,information_chatgpt:"\u003Cp\u003EAfrika je největší kontinent na Zemi a rozkládá se na ploše přesahující 30 milionů kilometrů čtverečních. Nachází se zde více než 50 zemí a žije zde více než 1,3 miliardy lidí. \u003C\u002Fp\u003E\n\u003Cp\u003EJižní Afrika, Egypt, Maroko a Keňa patří mezi nejoblíbenější turistické cíle v Africe. V Jihoafrické republice se můžete podívat na slavné Krugerův národní park a sledovat zde život zvířat v jejich přirozeném prostředí. Egypt je zase proslulý svými pyramidami, které se nacházejí v Gíze. V Maroku můžete navštívit tradiční trhy, zatímco v Keni se můžete setkat s Masajským kmenem a podívat se na úžasnou přírodu.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má bohatou historii a kulturu. První lidé, kteří se usadili v Africe, byli loveckým a sběračským národem. Poté se v této oblasti objevily první civilizace, jako je Kúšská říše, Egyptská říše nebo Království Aksum. Afrika byla také kolonizována evropskými mocnostmi a toto období mělo značný dopad na dnešní politickou situaci na kontinentu.\u003C\u002Fp\u003E\n\u003Cp\u003EJe důležité zmínit, že Afrika má velké množství jazyků a kultur. Zahrnuje mnoho různých národů a etnických skupin, a to má vliv na různorodost obyvatelstva a kultury. Nejrozšířenějším jazykem je arabština, která se mluví především v severní Africe a v některých částech západní Afriky. Dalšími důležitými jazyky jsou svahilština, francouzština, portugalština, angličtina a mnoho dalších.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má také nádhernou přírodu. Nachází se zde nejvyšší hora světa, Kilimandžáro, která se nachází na území Tanzanie a Keňy. Kromě toho se v Africe nachází také největší pouště na světě, Sahara, a největší prales, Kongský prales. Na tomto kontinentě můžete vidět také mnoho zvířat, jako jsou sloni, lvi, gepardi, zebry a mnoho dalších druhů. V Africe se také nachází několik nejvýznamnějších řek světa, jako je Nil, Kongo, Niger a Zambezi.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je bohužel také známá pro své problémy. Mnoho zemí se potýká s chudobou, nedostatkem potravin a vody, nemocemi a válečnými konflikty. Mnoho afrických zemí závisí na zemědělství a rybolovu, což však často není dostatečně výnosné, aby zabezpečilo potřeby obyvatelstva. Kromě toho jsou některé země v Africe trvale postižené extrémními přírodními katastrofami, jako jsou sucha, povodně a bouře.\u003C\u002Fp\u003E\n\u003Cp\u003ENa druhou stranu se v Africe také nachází mnoho lidí, kteří se snaží změnit situaci a zlepšit životy svých spoluobčanů. Vznikají neziskové organizace, které se snaží pomáhat při řešení těchto problémů a zlepšovat kvalitu života v mnoha afrických zemích. Zároveň se v posledních letech objevují nové možnosti pro ekonomický růst a rozvoj, jako jsou technologické start-upy a zahraniční investice.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika je fascinující kontinent, který nabízí mnoho zážitků a zároveň v sobě ukrývá mnoho výzev. Pokud se rozhodnete navštívit Afriku, můžete se těšit na krásnou přírodu, bohatou kulturu a nezapomenutelné zážitky. Je však také důležité být obezřetný a připravený na místní podmínky, protože některé oblasti mohou být nebezpečné kvůli konfliktům nebo zločinnosti.\u003C\u002Fp\u003E",coordinates:[{latitude:1.650801,longitude:17.371642}],type_place:"continent",id_image_cover:i,id_image_hero:i,zoom:[{google:m,booking:3}],information_author:[{text:"\u003Cp\u003E\u003Cb\u003EAfrika\u003C\u002Fb\u003E, druhý největší a druhý nejlidnatější kontinent na světě, láká cestovatele svou neuvěřitelnou rozmanitostí kultur, přírodními krásami a bohatou historií. Při cestování touto obrovskou plochou se otevírá mozaika různorodých zážitků, které zahrnují odlesky moderního městského života, divokou přírodu až po historická místa.\u003C\u002Fp\u003E\u003Cp\u003EJižní Afrika, domov několika národních parků a rezervací, je rájem pro milovníky safari. \u003Cb\u003EKrugerův národní park (Kruger National Park)\u003C\u002Fb\u003E nabízí úžasné setkání s \"Velkým pětkou\" - slony, nosorožci, buvoly, lvy a levharty. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fcape-town\"\u003EKapské Město (Cape Town)\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s ikonickou horou \u003Cb\u003EMaclear's Beacon\u003C\u002Fb\u003E, poskytuje nezapomenutelný výhled na město a okolní krajinu.\u003C\u002Fp\u003E\u003Cp\u003EVýchodní Afrika je známá svými ohromujícími sopkami. \u003Cb\u003EKilimandžáro\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E, nejvyšší hora Afriky, přitahuje dobrodruhy a horolezce z celého světa. Návštěva \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003EZanzibaru\u003C\u002Fa\u003E\u003C\u002Fb\u003E s jeho bílými plážemi a historickým centrem \u003Cb\u003EStone Town\u003C\u002Fb\u003E přidává do cestovatelského mixu kouzlo exotiky.\u003C\u002Fp\u003E\u003Cp\u003EStřední Afrika, s obrovskou rovníkovou pralesní plochou, skrývá některé z nejzajímavějších biodiverzit na světě. \u003Cb\u003EKonžský deštný prales\u003C\u002Fb\u003E je po amazonském druhý největší na světě, je domovem vzácných druhů zvířat a rostlin, a expedice sem mohou odhalit tajemství dosud neobjevená.\u003C\u002Fp\u003E\u003Cp\u003ESeverní Afrika je poznamenána starověkými civilizacemi a písečnými pouštěmi. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fegypt\"\u003EEgypt\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pyramidami a chrámy na březích \u003Cb\u003ENilu\u003C\u002Fb\u003E je jednou z nejnavštěvovanějších destinací v této části kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmaroko\"\u003EMaroko\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s mistrovským spojením tradice a moderny v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fmarrakes\"\u003EMarrákeši\u003C\u002Fa\u003E\u003C\u002Fb\u003E, je fascinujícím mixem barev, vůní a architektury.\u003C\u002Fp\u003E\u003Cp\u003EZápadní Afrika, bohatá na kulturní dědictví, nabízí autentické zážitky. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fghana\"\u003EGhana\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pevnostmi, které připomínají období otroctví, a kultovními festivaly představuje hluboký pohled do minulosti. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fsenegal\"\u003ESenegal\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s historickým ostrovem \u003Cb\u003EGorée\u003C\u002Fb\u003E, který sehral roli s obchodem s otroky, kde najdete koloniální budovy a muzeum \u003Cb\u003EHouse of Slaves (Maison des esclaves)\u003C\u002Fb\u003E.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je rovněž známá svými divokými zvířaty. Kromě safari v jižní části kontinentu můžete zažít jedinečnou příležitost sledovat migraci tisíců zeber a gazel v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fkena\"\u003EKeni\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E. Návštěva gorilí v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Frwanda\"\u003ERwandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fuganda\"\u003EUgandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E je neopakovatelným zážitkem pro ty, kteří touží po setkání s těmito ohroženými tvory.\u003C\u002Fp\u003E\u003Cp\u003EPřírodní skvosty nejsou omezeny pouze na pevninskou část kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fseychely\"\u003ESeychely\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMauricius\u003C\u002Fa\u003E\u003C\u002Fb\u003E, dvě ostrůvky v \u003Cb\u003EIndickém oceánu\u003C\u002Fb\u003E, lákají svými bílými plážemi, průzračnými vodami a jedinečnou faunou a florou.\u003C\u002Fp\u003E\u003Cp\u003EAfrika rovněž hýří kulturou a historií. Starověké památky jako \u003Cb\u003EGreat Zimbabwe National Monument\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fzimbabwe\"\u003EZimbabwe\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003ETimbuktu (Tombouctou)\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMali\u003C\u002Fa\u003E\u003C\u002Fb\u003E jsou svědectvím o bohaté historii a rozvoji obchodu a kultury na africkém kontinentu. Každý region má své vlastní tradiční tance, hudbu a umění, které odrážejí místní identitu a historii.\u003C\u002Fp\u003E\u003Cp\u003ECestování Afrikou je také o interakci s místními lidmi. Přívětivost a pohostinnost afrických komunit jsou nezapomenutelnými součástmi cestování. Místní trhy nabízejí pestrou škálu barev, vůní a chutí, zatímco setkání s kmeny a etnickými skupinami přináší pochopení různorodosti kultur a tradic.\u003C\u002Fp\u003E\u003Cp\u003EJe však důležité si být vědomý sociálních, ekonomických a environmentálních výzev, kterým mnohé africké země čelí. Cestovatelé by měli být respektující vůči místním zvyklostem a životnímu prostředí a přispívat k udržitelnému turismu, který podporuje místní ekonomiky a ochranu přírodních zdrojů.\u003C\u002Fp\u003E\u003Cp\u003ECelkově lze říci, že cestování po Africe je dobrodružstvím naplněným kontrasty a překvapeními. Od rozlehlých savan po exotické pláže, od starověkých ruin po moderní metropole. Afrika nabízí cestovatelům nekonečné možnosti objevování a poznávání. Je to kontinent, který zanechává nezapomenutelný dojem na každém, kdo se odhodlá k jeho prozkoumání.\u003C\u002Fp\u003E",date_create:H,date_update:H,author_create:I,author_update:I}],seo_tags:[],created_at:"2023-12-27T16:35:37.460505+00:00",updated_at:"2024-03-02T11:45:40.694514+00:00",name_original:"Africa",setting_top:d}]}],fetch:{},mutations:[]}}("05-2023","state",null,true,"04-2023",0,"\u002Fworld\u002Fstates\u002F","2023-12-27T20:42:18.520959+00:00",5,"pixabay.com",500,423,2,"pobrezi-slonoviny",800,"2%",82,21,145,86,131,"2023-05-06T09:27:42.033044+00:00",310,"mali",414,"liberie",262,"guinea",253,"ghana",28,"burkina-faso","2023-05-02T18:49:47.851894+00:00","2024-03-01","1")));