__NUXT_JSONP__("/svet/stat/togo/videa", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o){return {data:[{tabsLoad:{tabWhatToSee:d,tabPrice:d,tabPeople:d,tabTrip:d,tabContact:d,tabHotel:d,tabNeighboring:h,tabVideos:e,tabArticles:e,tabWallSockets:1,tabChains:e,tabFauna:e,tabFlora:e,tabFoods:e,tabBrands:e},place:[{tld:".tg",name:"Togo",area:56785,population:8533041,slug:i,id_continent:f,information_chatgpt:"\u003Cp\u003ETogo je západoafrický stát ležící na pobřeží Guinejského zálivu. Sousedí s Ghanou, Burkina Faso a Beninem a má bohatou kulturu a historii. \u003C\u002Fp\u003E\n\u003Cp\u003ETogo bylo původně osídleno různými etnickými skupinami, které si zachovaly své vlastní tradice a zvyky. Mnoho z nich však bylo ovlivněno francouzskou koloniální vládou a křesťanskou misií, což ovlivnilo i moderní tožskou kulturu.\u003C\u002Fp\u003E\n\u003Cp\u003ETogo má bohaté přírodní zdroje, včetně zemědělské půdy, rybářských oblastí a minerálních surovin, jako jsou fosfáty, diamanty a zlato. Také zde najdete národní parky s mnoha druhy zvířat, jako jsou opice, sloni, krokodýli a želvy.\u003C\u002Fp\u003E\n\u003Cp\u003EHlavním městem Toga je Lomé, které je významným přístavem pro obchod a turistiku. Lomé nabízí mnoho historických památek a kulturních center, jako jsou trhy s místním uměním a řemesly, muzea a památníky. Také zde najdete krásné pláže a možnosti pro surfování a jiné vodní sporty.\u003C\u002Fp\u003E\n\u003Cp\u003EPro turisty, kteří chtějí objevovat tožskou kulturu a přírodu, jsou dalšími zajímavými destinacemi Kpalimé, které je známé pro své umělecké a řemeslnické trhy, a město Kara, kde najdete mnoho historických památek a národní park Fazao s mnoha druhy zvířat a rostlin.\u003C\u002Fp\u003E\n\u003Cp\u003EV Togu je možné najít různé typy ubytování od luxusních hotelů až po levné hostely. Výběr záleží na vašem rozpočtu a preferencích.\u003C\u002Fp\u003E",money_prices:[{name:"Taxi (cena za km)",value:b,date_update:a},{name:"Hlavní jídlo",value:5000,date_update:a},{name:"Chleba",value:400,date_update:a},{name:"Mléko",value:b,date_update:a},{name:"Voda (500ml)",value:j,date_update:a},{name:"Espresso",value:b,date_update:a},{name:"Zmrzlina (jeden kopeček)",value:b,date_update:a},{name:"Pivo",value:1000,date_update:a},{name:"Krabička cigaret",value:b,date_update:a},{name:"Bic Mac",value:b,date_update:a},{name:"Coca Cola (500ml)",value:j,date_update:a}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:"112",date_update:a},{name:"Policie",value:"117",date_update:a},{name:"Hasiči",value:"118",date_update:a},{name:"Zdravotnická záchranná služba",value:"819",date_update:a}],phone_prefix:"+228",coordinates:[{latitude:"8.6195",longitude:"0.8248"}],people_religion:[{name:"Křesťanství",value:"29%",date_update:c},{name:"Tradiční náboženství",value:"50%",date_update:c},{name:"Islám",value:"20%",date_update:c},{name:"Ateismus",value:"1%",date_update:c}],people_nationality:[{name:"Ewe",value:"21%",date_update:c},{name:"Kabye",value:k,date_update:c},{name:"Ana-Ife",value:k,date_update:c},{name:"Kotokoli",value:"6%",date_update:c},{name:"Moba",value:l,date_update:c},{name:"Somba",value:l,date_update:c},{name:"Ostatní",value:"39%",date_update:c}],visitors_entry:[{name:"Občanský průkaz",value:"Občanský průkaz není platným dokladem pro vstup do Toga. Pro vstup je nutné mít platný cestovní pas.",date_update:a},{name:"Cestovní pas",value:"Pro vstup do Toga je nutné mít platný cestovní pas.",date_update:a},{name:"Vízum",value:"Pro vstup do Toga je pro občany České republiky potřeba získat vízum. Vízum lze získat na velvyslanectví Toga v Berlíně, Německo nebo při příletu na letišti v Lomé za poplatek. Platnost víza je 90 dnů.",date_update:a},{name:"Očkování",value:"Pro vstup do Toga není vyžadováno žádné specifické očkování. Nicméně, doporučuje se očkování proti žluté zimnici pro cestovatele, kteří přijíždějí z oblastí s rizikem výskytu této nemoci.",date_update:a},{name:"Cestovní pojištění",value:"Pro vstup do Toga není vyžadováno cestovní nebo zdravotní pojištění. Nicméně, doporučuje se mít sjednané cestovní pojištění, které kryje náklady na léčbu, návrat do vlasti a ztrátu zavazadel.",date_update:a},{name:"Bezpečnostní situace",value:"V Togu existuje vysoká kriminalita a teroristické hrozby. Cestovatelé by měli být obezřetní a mít na paměti bezpečnostní rizika při pobytu v zemi.",date_update:a},{name:"Další informace",value:"V Togu se doporučuje mít vždy u sebe kopii cestovního pasu a víza a ponechat si originály v bezpečí. Dále je vhodné mít u sebe dostatek hotovosti, protože platební karty nejsou všude přijímány. Místní zvyklosti a kultura jsou ovlivněny francouzskou koloniální historií a dominuje zde islámské vyznání. V Togu je rozšířená malárie, proto se doporučuje používat repelenty, moskytiéry a chránit se před komáry.",date_update:a}],type_place:m,id_image_cover:g,id_image_hero:g,id_city_main:2143,affiliate:[{name:"booking",value:d}],mpz:"TG",alerts:b,zoom:[{google:6,booking:7}],ids_neighboring_countries:[{id:21},{id:25},{id:82}],organization:b,apps:b,links:b,information_author:b,seo_tags:b,language_phrases:b,wall_sockets:b,id:217,facts_place:b,created_at:"2023-12-27T16:33:10.517062+00:00",updated_at:"2024-03-02T11:05:26.667745+00:00",name_original:b,currency:[{code:"XOF",name:"CFA frank"}],setting_top:d}],imagePlace:[{id:g,name:i,source:"\u002Fworld\u002Fstates\u002F",type:m,author:"unsplash.com\u002F@planeteelevene",created_at:"2023-05-06T09:27:42.033044+00:00",updated_at:"2023-12-27T20:42:18.520959+00:00"}],placeContinent:[{id:f,slug:"afrika",name:"Afrika",area:30370000,population:1340000000,population_density:44.12,number_states:54,information_chatgpt:"\u003Cp\u003EAfrika je největší kontinent na Zemi a rozkládá se na ploše přesahující 30 milionů kilometrů čtverečních. Nachází se zde více než 50 zemí a žije zde více než 1,3 miliardy lidí. \u003C\u002Fp\u003E\n\u003Cp\u003EJižní Afrika, Egypt, Maroko a Keňa patří mezi nejoblíbenější turistické cíle v Africe. V Jihoafrické republice se můžete podívat na slavné Krugerův národní park a sledovat zde život zvířat v jejich přirozeném prostředí. Egypt je zase proslulý svými pyramidami, které se nacházejí v Gíze. V Maroku můžete navštívit tradiční trhy, zatímco v Keni se můžete setkat s Masajským kmenem a podívat se na úžasnou přírodu.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má bohatou historii a kulturu. První lidé, kteří se usadili v Africe, byli loveckým a sběračským národem. Poté se v této oblasti objevily první civilizace, jako je Kúšská říše, Egyptská říše nebo Království Aksum. Afrika byla také kolonizována evropskými mocnostmi a toto období mělo značný dopad na dnešní politickou situaci na kontinentu.\u003C\u002Fp\u003E\n\u003Cp\u003EJe důležité zmínit, že Afrika má velké množství jazyků a kultur. Zahrnuje mnoho různých národů a etnických skupin, a to má vliv na různorodost obyvatelstva a kultury. Nejrozšířenějším jazykem je arabština, která se mluví především v severní Africe a v některých částech západní Afriky. Dalšími důležitými jazyky jsou svahilština, francouzština, portugalština, angličtina a mnoho dalších.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má také nádhernou přírodu. Nachází se zde nejvyšší hora světa, Kilimandžáro, která se nachází na území Tanzanie a Keňy. Kromě toho se v Africe nachází také největší pouště na světě, Sahara, a největší prales, Kongský prales. Na tomto kontinentě můžete vidět také mnoho zvířat, jako jsou sloni, lvi, gepardi, zebry a mnoho dalších druhů. V Africe se také nachází několik nejvýznamnějších řek světa, jako je Nil, Kongo, Niger a Zambezi.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je bohužel také známá pro své problémy. Mnoho zemí se potýká s chudobou, nedostatkem potravin a vody, nemocemi a válečnými konflikty. Mnoho afrických zemí závisí na zemědělství a rybolovu, což však často není dostatečně výnosné, aby zabezpečilo potřeby obyvatelstva. Kromě toho jsou některé země v Africe trvale postižené extrémními přírodními katastrofami, jako jsou sucha, povodně a bouře.\u003C\u002Fp\u003E\n\u003Cp\u003ENa druhou stranu se v Africe také nachází mnoho lidí, kteří se snaží změnit situaci a zlepšit životy svých spoluobčanů. Vznikají neziskové organizace, které se snaží pomáhat při řešení těchto problémů a zlepšovat kvalitu života v mnoha afrických zemích. Zároveň se v posledních letech objevují nové možnosti pro ekonomický růst a rozvoj, jako jsou technologické start-upy a zahraniční investice.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika je fascinující kontinent, který nabízí mnoho zážitků a zároveň v sobě ukrývá mnoho výzev. Pokud se rozhodnete navštívit Afriku, můžete se těšit na krásnou přírodu, bohatou kulturu a nezapomenutelné zážitky. Je však také důležité být obezřetný a připravený na místní podmínky, protože některé oblasti mohou být nebezpečné kvůli konfliktům nebo zločinnosti.\u003C\u002Fp\u003E",coordinates:[{latitude:1.650801,longitude:17.371642}],type_place:"continent",id_image_cover:f,id_image_hero:f,zoom:[{google:2,booking:h}],information_author:[{text:"\u003Cp\u003E\u003Cb\u003EAfrika\u003C\u002Fb\u003E, druhý největší a druhý nejlidnatější kontinent na světě, láká cestovatele svou neuvěřitelnou rozmanitostí kultur, přírodními krásami a bohatou historií. Při cestování touto obrovskou plochou se otevírá mozaika různorodých zážitků, které zahrnují odlesky moderního městského života, divokou přírodu až po historická místa.\u003C\u002Fp\u003E\u003Cp\u003EJižní Afrika, domov několika národních parků a rezervací, je rájem pro milovníky safari. \u003Cb\u003EKrugerův národní park (Kruger National Park)\u003C\u002Fb\u003E nabízí úžasné setkání s \"Velkým pětkou\" - slony, nosorožci, buvoly, lvy a levharty. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fcape-town\"\u003EKapské Město (Cape Town)\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s ikonickou horou \u003Cb\u003EMaclear's Beacon\u003C\u002Fb\u003E, poskytuje nezapomenutelný výhled na město a okolní krajinu.\u003C\u002Fp\u003E\u003Cp\u003EVýchodní Afrika je známá svými ohromujícími sopkami. \u003Cb\u003EKilimandžáro\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E, nejvyšší hora Afriky, přitahuje dobrodruhy a horolezce z celého světa. Návštěva \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003EZanzibaru\u003C\u002Fa\u003E\u003C\u002Fb\u003E s jeho bílými plážemi a historickým centrem \u003Cb\u003EStone Town\u003C\u002Fb\u003E přidává do cestovatelského mixu kouzlo exotiky.\u003C\u002Fp\u003E\u003Cp\u003EStřední Afrika, s obrovskou rovníkovou pralesní plochou, skrývá některé z nejzajímavějších biodiverzit na světě. \u003Cb\u003EKonžský deštný prales\u003C\u002Fb\u003E je po amazonském druhý největší na světě, je domovem vzácných druhů zvířat a rostlin, a expedice sem mohou odhalit tajemství dosud neobjevená.\u003C\u002Fp\u003E\u003Cp\u003ESeverní Afrika je poznamenána starověkými civilizacemi a písečnými pouštěmi. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fegypt\"\u003EEgypt\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pyramidami a chrámy na březích \u003Cb\u003ENilu\u003C\u002Fb\u003E je jednou z nejnavštěvovanějších destinací v této části kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmaroko\"\u003EMaroko\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s mistrovským spojením tradice a moderny v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fmarrakes\"\u003EMarrákeši\u003C\u002Fa\u003E\u003C\u002Fb\u003E, je fascinujícím mixem barev, vůní a architektury.\u003C\u002Fp\u003E\u003Cp\u003EZápadní Afrika, bohatá na kulturní dědictví, nabízí autentické zážitky. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fghana\"\u003EGhana\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pevnostmi, které připomínají období otroctví, a kultovními festivaly představuje hluboký pohled do minulosti. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fsenegal\"\u003ESenegal\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s historickým ostrovem \u003Cb\u003EGorée\u003C\u002Fb\u003E, který sehral roli s obchodem s otroky, kde najdete koloniální budovy a muzeum \u003Cb\u003EHouse of Slaves (Maison des esclaves)\u003C\u002Fb\u003E.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je rovněž známá svými divokými zvířaty. Kromě safari v jižní části kontinentu můžete zažít jedinečnou příležitost sledovat migraci tisíců zeber a gazel v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fkena\"\u003EKeni\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E. Návštěva gorilí v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Frwanda\"\u003ERwandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fuganda\"\u003EUgandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E je neopakovatelným zážitkem pro ty, kteří touží po setkání s těmito ohroženými tvory.\u003C\u002Fp\u003E\u003Cp\u003EPřírodní skvosty nejsou omezeny pouze na pevninskou část kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fseychely\"\u003ESeychely\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMauricius\u003C\u002Fa\u003E\u003C\u002Fb\u003E, dvě ostrůvky v \u003Cb\u003EIndickém oceánu\u003C\u002Fb\u003E, lákají svými bílými plážemi, průzračnými vodami a jedinečnou faunou a florou.\u003C\u002Fp\u003E\u003Cp\u003EAfrika rovněž hýří kulturou a historií. Starověké památky jako \u003Cb\u003EGreat Zimbabwe National Monument\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fzimbabwe\"\u003EZimbabwe\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003ETimbuktu (Tombouctou)\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMali\u003C\u002Fa\u003E\u003C\u002Fb\u003E jsou svědectvím o bohaté historii a rozvoji obchodu a kultury na africkém kontinentu. Každý region má své vlastní tradiční tance, hudbu a umění, které odrážejí místní identitu a historii.\u003C\u002Fp\u003E\u003Cp\u003ECestování Afrikou je také o interakci s místními lidmi. Přívětivost a pohostinnost afrických komunit jsou nezapomenutelnými součástmi cestování. Místní trhy nabízejí pestrou škálu barev, vůní a chutí, zatímco setkání s kmeny a etnickými skupinami přináší pochopení různorodosti kultur a tradic.\u003C\u002Fp\u003E\u003Cp\u003EJe však důležité si být vědomý sociálních, ekonomických a environmentálních výzev, kterým mnohé africké země čelí. Cestovatelé by měli být respektující vůči místním zvyklostem a životnímu prostředí a přispívat k udržitelnému turismu, který podporuje místní ekonomiky a ochranu přírodních zdrojů.\u003C\u002Fp\u003E\u003Cp\u003ECelkově lze říci, že cestování po Africe je dobrodružstvím naplněným kontrasty a překvapeními. Od rozlehlých savan po exotické pláže, od starověkých ruin po moderní metropole. Afrika nabízí cestovatelům nekonečné možnosti objevování a poznávání. Je to kontinent, který zanechává nezapomenutelný dojem na každém, kdo se odhodlá k jeho prozkoumání.\u003C\u002Fp\u003E",date_create:n,date_update:n,author_create:o,author_update:o}],seo_tags:[],created_at:"2023-12-27T16:35:37.460505+00:00",updated_at:"2024-03-02T11:45:40.694514+00:00",name_original:"Africa",setting_top:d}]}],fetch:{},mutations:[]}}("05-2023",null,"01-2010",true,0,5,435,3,"togo",500,"12%","5%","state","2024-03-01","1")));