__NUXT_JSONP__("/svet/mesto/boddam-island", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w,x,y){return {data:[{place:[{id:m,id_state:n,name:o,population:805,area:.1,altitude:7,slug:p,information_chatgpt:a,coordinates:[{latitude:-7.3289,longitude:72.4239}],biggest:"yes",type_place:b,id_image_cover:a,id_image_hero:a,affiliate:[{name:q,value:f}],alerts:a,zoom:[{google:i,booking:11}],parking:a,information_author:a,importance:c,seo_tags:a,created_at:"2023-12-27T16:36:26.534285+00:00",updated_at:"2024-03-04T19:48:29.85915+00:00",name_original:a,setting_top:f,setting_status_public:j}],imagePlace:[],placeState:[{tld:".io",name:"Britské území v Indickém oceánu",area:r,population:0,slug:s,id_continent:g,information_chatgpt:"\u003Cp\u003EBritské území v Indickém oceánu, někdy nazývané také Diego Garcia, je ostrovní území nacházející se v Indickém oceánu asi 2 000 km východně od afrického pobřeží a 3 500 km na jihovýchod od Indie. Je to malý ostrov o rozloze pouhých 44 km², který je pod kontrolou Spojeného království. \u003C\u002Fp\u003E\n\u003Cp\u003EÚzemí bylo získáno Británií v 60. letech 20. století a bylo využíváno jako vojenská základna Spojených států a Spojeného království. V současné době je ostrov stále vojenskou základnou a nejsou zde žádné stálé civilní obyvatelé. Přestože je to malý ostrov, nabízí návštěvníkům několik zajímavých aktivit.\u003C\u002Fp\u003E\n\u003Cp\u003ENejvýznamnější atrakcí na ostrově je korálový útes, který obklopuje ostrov. Je to jedno z největších korálových útesů na světě a nabízí návštěvníkům skvělé podmínky pro potápění a šnorchlování. Na ostrově je také několik pláží, kde se můžete slunit a koupat. \u003C\u002Fp\u003E\n\u003Cp\u003EKromě toho se na ostrově nachází i několik sportovních zařízení, jako jsou tenisové kurty a hřiště na volejbal a basketbal. Můžete si také pronajmout kajak nebo jiné vodní sporty a prozkoumat okolí ostrova. \u003C\u002Fp\u003E\n\u003Cp\u003EJe třeba poznamenat, že návštěva ostrova není pro každého, protože je to vojenská základna s omezeným přístupem. Vstup na ostrov je povolen pouze s povolením, které musíte získat předem. Přestože se ostrov stále využívá pro vojenské účely, je to zajímavé místo pro ty, kteří hledají neobvyklé dovolené a zážitky.\u003C\u002Fp\u003E",money_prices:[{name:"Taxi (cena za km)",value:40,date_update:d},{name:"Hlavní jídlo",value:300,date_update:d},{name:"Chleba",value:20,date_update:d},{name:"Mléko",value:t,date_update:d},{name:"Voda (500ml)",value:15,date_update:d},{name:"Espresso",value:r,date_update:d},{name:"Zmrzlina (jeden kopeček)",value:50,date_update:d},{name:"Pivo",value:100,date_update:d},{name:"Krabička cigaret",value:180,date_update:d},{name:"Big Mac",value:170,date_update:d},{name:"Coca Cola (500ml)",value:t,date_update:d}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:"112",date_update:e},{name:"Policie",value:k,date_update:e},{name:"Hasiči",value:k,date_update:e},{name:"Zdravotnická záchranná služba",value:k,date_update:e}],phone_prefix:"+246",coordinates:[{latitude:"-7.242918",longitude:"72.304403"}],people_religion:a,people_nationality:a,visitors_entry:[{name:"Občanský průkaz",value:"Občanský průkaz není platným cestovním dokladem, tudíž není dostačující pro vstup na britské území v Indickém oceánu.",date_update:h},{name:"Cestovní pas",value:"Pro vstup na britské území v Indickém oceánu je potřeba mít platný cestovní pas.",date_update:h},{name:"Vízum",value:"Pro vstup na britské území v Indickém oceánu je potřeba mít platné vízum. Pro občany České republiky (EU) není třeba mít vízum, pokud pobyt trvá méně než 6 měsíců. Pokud pobyt trvá déle, je potřeba žádat o vízum předem.",date_update:h},{name:"Očkování",value:"Pro vstup na britské území v Indickém oceánu není nutné mít žádné specifické očkování. Nicméně je vždy dobré mít očkování proti běžným chorobám, jako je například tetanus, žloutenka A a B, dávivý kašel, nebo tyfus.",date_update:h},{name:"Cestovní pojištění",value:"Pro vstup na britské území v Indickém oceánu není povinné mít cestovní nebo zdravotní pojištění. Nicméně se doporučuje mít sjednané cestovní pojištění, které pokryje lékařskou péči v případě potřeby.",date_update:e},{name:"Bezpečností situace",value:"Britské území v Indickém oceánu je relativně bezpečnou destinací pro turisty. V posledních letech nebyly zaznamenány žádné významné bezpečnostní incidenty. Nicméně se doporučuje být obezřetný a dodržovat základní bezpečnostní opatření.",date_update:e},{name:"Další informace",value:"Při cestování na britské území v Indickém oceánu je třeba dodržovat místní zvyklosti a kulturu. Na ostrově se nachází vojenská základna, která je pro veřejnost uzavřená. Také je zakázáno sbírat korály a jiné mořské organismy. Většina obyvatel ostrova hovoří anglicky. Na ostrově se nachází jediné letiště, které je spojeno s Mauriciem a Seychelami.",date_update:e}],type_place:u,id_image_cover:l,id_image_hero:l,id_city_main:v,affiliate:[{name:q,value:f}],mpz:"IO",alerts:a,zoom:[{google:i,booking:i}],ids_neighboring_countries:[],organization:a,apps:a,links:a,information_author:a,seo_tags:a,language_phrases:a,wall_sockets:a,id:n,facts_place:a,created_at:"2023-12-27T16:33:10.517062+00:00",updated_at:"2024-03-04T19:48:12.84066+00:00",name_original:a,currency:[{code:w,name:w}],setting_top:f,setting_status_public:j}],imageState:[{id:l,name:s,source:"\u002Fworld\u002Fstates\u002F",type:u,author:"USGS\u002FNASA Landsat data\u002FGallo Images\u002FGetty Images",created_at:"2023-05-06T09:27:42.033044+00:00",updated_at:"2023-12-27T20:42:18.520959+00:00"}],placeContinent:[{id:g,slug:"afrika",name:"Afrika",area:30370000,population:1340000000,population_density:44.12,number_states:54,information_chatgpt:"\u003Cp\u003EAfrika je největší kontinent na Zemi a rozkládá se na ploše přesahující 30 milionů kilometrů čtverečních. Nachází se zde více než 50 zemí a žije zde více než 1,3 miliardy lidí. \u003C\u002Fp\u003E\n\u003Cp\u003EJižní Afrika, Egypt, Maroko a Keňa patří mezi nejoblíbenější turistické cíle v Africe. V Jihoafrické republice se můžete podívat na slavné Krugerův národní park a sledovat zde život zvířat v jejich přirozeném prostředí. Egypt je zase proslulý svými pyramidami, které se nacházejí v Gíze. V Maroku můžete navštívit tradiční trhy, zatímco v Keni se můžete setkat s Masajským kmenem a podívat se na úžasnou přírodu.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má bohatou historii a kulturu. První lidé, kteří se usadili v Africe, byli loveckým a sběračským národem. Poté se v této oblasti objevily první civilizace, jako je Kúšská říše, Egyptská říše nebo Království Aksum. Afrika byla také kolonizována evropskými mocnostmi a toto období mělo značný dopad na dnešní politickou situaci na kontinentu.\u003C\u002Fp\u003E\n\u003Cp\u003EJe důležité zmínit, že Afrika má velké množství jazyků a kultur. Zahrnuje mnoho různých národů a etnických skupin, a to má vliv na různorodost obyvatelstva a kultury. Nejrozšířenějším jazykem je arabština, která se mluví především v severní Africe a v některých částech západní Afriky. Dalšími důležitými jazyky jsou svahilština, francouzština, portugalština, angličtina a mnoho dalších.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má také nádhernou přírodu. Nachází se zde nejvyšší hora světa, Kilimandžáro, která se nachází na území Tanzanie a Keňy. Kromě toho se v Africe nachází také největší pouště na světě, Sahara, a největší prales, Kongský prales. Na tomto kontinentě můžete vidět také mnoho zvířat, jako jsou sloni, lvi, gepardi, zebry a mnoho dalších druhů. V Africe se také nachází několik nejvýznamnějších řek světa, jako je Nil, Kongo, Niger a Zambezi.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je bohužel také známá pro své problémy. Mnoho zemí se potýká s chudobou, nedostatkem potravin a vody, nemocemi a válečnými konflikty. Mnoho afrických zemí závisí na zemědělství a rybolovu, což však často není dostatečně výnosné, aby zabezpečilo potřeby obyvatelstva. Kromě toho jsou některé země v Africe trvale postižené extrémními přírodními katastrofami, jako jsou sucha, povodně a bouře.\u003C\u002Fp\u003E\n\u003Cp\u003ENa druhou stranu se v Africe také nachází mnoho lidí, kteří se snaží změnit situaci a zlepšit životy svých spoluobčanů. Vznikají neziskové organizace, které se snaží pomáhat při řešení těchto problémů a zlepšovat kvalitu života v mnoha afrických zemích. Zároveň se v posledních letech objevují nové možnosti pro ekonomický růst a rozvoj, jako jsou technologické start-upy a zahraniční investice.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika je fascinující kontinent, který nabízí mnoho zážitků a zároveň v sobě ukrývá mnoho výzev. Pokud se rozhodnete navštívit Afriku, můžete se těšit na krásnou přírodu, bohatou kulturu a nezapomenutelné zážitky. Je však také důležité být obezřetný a připravený na místní podmínky, protože některé oblasti mohou být nebezpečné kvůli konfliktům nebo zločinnosti.\u003C\u002Fp\u003E",coordinates:[{latitude:1.650801,longitude:17.371642}],type_place:"continent",id_image_cover:g,id_image_hero:g,zoom:[{google:2,booking:3}],information_author:[{text:"\u003Cp\u003E\u003Cb\u003EAfrika\u003C\u002Fb\u003E, druhý největší a druhý nejlidnatější kontinent na světě, láká cestovatele svou neuvěřitelnou rozmanitostí kultur, přírodními krásami a bohatou historií. Při cestování touto obrovskou plochou se otevírá mozaika různorodých zážitků, které zahrnují odlesky moderního městského života, divokou přírodu až po historická místa.\u003C\u002Fp\u003E\u003Cp\u003EJižní Afrika, domov několika národních parků a rezervací, je rájem pro milovníky safari. \u003Cb\u003EKrugerův národní park (Kruger National Park)\u003C\u002Fb\u003E nabízí úžasné setkání s \"Velkým pětkou\" - slony, nosorožci, buvoly, lvy a levharty. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fcape-town\"\u003EKapské Město (Cape Town)\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s ikonickou horou \u003Cb\u003EMaclear's Beacon\u003C\u002Fb\u003E, poskytuje nezapomenutelný výhled na město a okolní krajinu.\u003C\u002Fp\u003E\u003Cp\u003EVýchodní Afrika je známá svými ohromujícími sopkami. \u003Cb\u003EKilimandžáro\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E, nejvyšší hora Afriky, přitahuje dobrodruhy a horolezce z celého světa. Návštěva \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003EZanzibaru\u003C\u002Fa\u003E\u003C\u002Fb\u003E s jeho bílými plážemi a historickým centrem \u003Cb\u003EStone Town\u003C\u002Fb\u003E přidává do cestovatelského mixu kouzlo exotiky.\u003C\u002Fp\u003E\u003Cp\u003EStřední Afrika, s obrovskou rovníkovou pralesní plochou, skrývá některé z nejzajímavějších biodiverzit na světě. \u003Cb\u003EKonžský deštný prales\u003C\u002Fb\u003E je po amazonském druhý největší na světě, je domovem vzácných druhů zvířat a rostlin, a expedice sem mohou odhalit tajemství dosud neobjevená.\u003C\u002Fp\u003E\u003Cp\u003ESeverní Afrika je poznamenána starověkými civilizacemi a písečnými pouštěmi. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fegypt\"\u003EEgypt\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pyramidami a chrámy na březích \u003Cb\u003ENilu\u003C\u002Fb\u003E je jednou z nejnavštěvovanějších destinací v této části kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmaroko\"\u003EMaroko\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s mistrovským spojením tradice a moderny v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fmarrakes\"\u003EMarrákeši\u003C\u002Fa\u003E\u003C\u002Fb\u003E, je fascinujícím mixem barev, vůní a architektury.\u003C\u002Fp\u003E\u003Cp\u003EZápadní Afrika, bohatá na kulturní dědictví, nabízí autentické zážitky. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fghana\"\u003EGhana\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pevnostmi, které připomínají období otroctví, a kultovními festivaly představuje hluboký pohled do minulosti. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fsenegal\"\u003ESenegal\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s historickým ostrovem \u003Cb\u003EGorée\u003C\u002Fb\u003E, který sehral roli s obchodem s otroky, kde najdete koloniální budovy a muzeum \u003Cb\u003EHouse of Slaves (Maison des esclaves)\u003C\u002Fb\u003E.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je rovněž známá svými divokými zvířaty. Kromě safari v jižní části kontinentu můžete zažít jedinečnou příležitost sledovat migraci tisíců zeber a gazel v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fkena\"\u003EKeni\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E. Návštěva gorilí v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Frwanda\"\u003ERwandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fuganda\"\u003EUgandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E je neopakovatelným zážitkem pro ty, kteří touží po setkání s těmito ohroženými tvory.\u003C\u002Fp\u003E\u003Cp\u003EPřírodní skvosty nejsou omezeny pouze na pevninskou část kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fseychely\"\u003ESeychely\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMauricius\u003C\u002Fa\u003E\u003C\u002Fb\u003E, dvě ostrůvky v \u003Cb\u003EIndickém oceánu\u003C\u002Fb\u003E, lákají svými bílými plážemi, průzračnými vodami a jedinečnou faunou a florou.\u003C\u002Fp\u003E\u003Cp\u003EAfrika rovněž hýří kulturou a historií. Starověké památky jako \u003Cb\u003EGreat Zimbabwe National Monument\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fzimbabwe\"\u003EZimbabwe\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003ETimbuktu (Tombouctou)\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMali\u003C\u002Fa\u003E\u003C\u002Fb\u003E jsou svědectvím o bohaté historii a rozvoji obchodu a kultury na africkém kontinentu. Každý region má své vlastní tradiční tance, hudbu a umění, které odrážejí místní identitu a historii.\u003C\u002Fp\u003E\u003Cp\u003ECestování Afrikou je také o interakci s místními lidmi. Přívětivost a pohostinnost afrických komunit jsou nezapomenutelnými součástmi cestování. Místní trhy nabízejí pestrou škálu barev, vůní a chutí, zatímco setkání s kmeny a etnickými skupinami přináší pochopení různorodosti kultur a tradic.\u003C\u002Fp\u003E\u003Cp\u003EJe však důležité si být vědomý sociálních, ekonomických a environmentálních výzev, kterým mnohé africké země čelí. Cestovatelé by měli být respektující vůči místním zvyklostem a životnímu prostředí a přispívat k udržitelnému turismu, který podporuje místní ekonomiky a ochranu přírodních zdrojů.\u003C\u002Fp\u003E\u003Cp\u003ECelkově lze říci, že cestování po Africe je dobrodružstvím naplněným kontrasty a překvapeními. Od rozlehlých savan po exotické pláže, od starověkých ruin po moderní metropole. Afrika nabízí cestovatelům nekonečné možnosti objevování a poznávání. Je to kontinent, který zanechává nezapomenutelný dojem na každém, kdo se odhodlá k jeho prozkoumání.\u003C\u002Fp\u003E",date_create:x,date_update:x,author_create:y,author_update:y}],seo_tags:[],created_at:"2023-12-27T16:35:37.460505+00:00",updated_at:"2024-03-04T19:48:06.634661+00:00",name_original:"Africa",setting_top:f,setting_status_public:j}],placesCities:[{id:m,id_image_cover:a,importance:c,slug:p,type_place:b,name:o},{id:1031,id_image_cover:a,importance:c,slug:"belostrov",type_place:b,name:"Bělostrov"},{id:1029,id_image_cover:a,importance:c,slug:"camp-justice",type_place:b,name:"Camp Justice"},{id:1030,id_image_cover:a,importance:c,slug:"camp-thunder-cove",type_place:b,name:"Camp Thunder Cove"},{id:v,id_image_cover:a,importance:c,slug:"diego-garcia",type_place:b,name:"Diego Garcia"},{id:1032,id_image_cover:a,importance:c,slug:"eagle-pier",type_place:b,name:"Eagle Pier"},{id:1035,id_image_cover:a,importance:c,slug:"east-point",type_place:b,name:"East Point"},{id:1033,id_image_cover:a,importance:c,slug:"seagull-village",type_place:b,name:"Seagull Village"},{id:1034,id_image_cover:a,importance:c,slug:"south-island",type_place:b,name:"South Island"},{id:1036,id_image_cover:a,importance:c,slug:"west-island--stat-britske-uzemi-v-indickem-oceanu",type_place:b,name:"West Island"}],imagesCities:a}],fetch:{"OrganismsoPlaceTeaserSpotsComponent:0":{spots:[],images:a},"OrganismsoSidebarEventComponent:0":{events:[],images:a}},mutations:[]}}(null,"city","biggest","04-2023","05-2023",true,5,"09-2021",10,1,"999",404,1037,106,"Boddam Island","boddam-island","booking",60,"britske-uzemi-v-indickem-oceanu",35,"state",1028,"USD","2024-03-01","1")));