__NUXT_JSONP__("/svet/stat/uganda/fauna", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p){return {data:[{tabsLoad:{tabWhatToSee:d,tabPrice:d,tabPeople:d,tabTrip:d,tabContact:d,tabHotel:d,tabNeighboring:f,tabVideos:e,tabArticles:e,tabWallSockets:h,tabChains:h,tabFauna:e,tabFlora:e,tabFoods:e,tabBrands:e},place:[{tld:".ug",name:"Uganda",area:241551,population:44901387,slug:l,id_continent:f,information_chatgpt:"\u003Cp\u003EUganda je východoafrický stát s rozlohou 241 038 km². Má přibližně 44 milionů obyvatel a hlavním městem je Kampala. Úředními jazyky jsou angličtina a svahilština, avšak ve vnitrozemí se používají i různé místní jazyky.\u003C\u002Fp\u003E\n\u003Cp\u003EUganda je země s bohatou přírodou a mnoha druhy zvířat, včetně goril, slonů, lvů a žiraf. Mezi nejznámější turistické atrakce patří národní parky Bwindi a Queen Elizabeth, řeka Nil a Viktoriino jezero.\u003C\u002Fp\u003E\n\u003Cp\u003EVýznamnou součástí kultury Ugandy je hudba a tanec. Mezi nejznámější styly patří kadongo kamu a acholi dance. Kromě toho jsou Uganda i proslulá svými různými druhy kávy a čaje.\u003C\u002Fp\u003E\n\u003Cp\u003EUganda má však také řadu problémů, včetně chudoby a korupce. Mnoho Ugandčanů žije v extrémní chudobě a zdravotnictví i vzdělávání jsou na nízké úrovni. Navíc se v zemi vyskytuje i politická nestabilita a násilí.\u003C\u002Fp\u003E\n\u003Cp\u003EPokud plánujete cestu do Ugandy, doporučuje se očkování proti žluté zimnici a dalším nemocem, jako je malárie. Vzhledem k nedostatečné infrastruktuře je doporučeno využívat služeb místních cestovních kanceláří.\u003C\u002Fp\u003E",money_prices:[{name:"Taxi (cena za km)",value:i,date_update:b},{name:"Hlavní jídlo",value:10000,date_update:b},{name:"Chleba",value:i,date_update:b},{name:"Mléko",value:2500,date_update:b},{name:"Voda (500ml)",value:1000,date_update:b},{name:"Espresso",value:5000,date_update:b},{name:"Zmrzlina (jeden kopeček)",value:i,date_update:b},{name:"Pivo",value:3000,date_update:b},{name:"Krabička cigaret",value:7000,date_update:b},{name:"Bic Mac",value:c,date_update:b},{name:"Coca Cola (500ml)",value:i,date_update:b}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:"112",date_update:a},{name:"Policie",value:m,date_update:a},{name:"Hasiči",value:m,date_update:a},{name:"Zdravotnická záchranná služba",value:"911",date_update:a},{name:"Turistická policie",value:"+256 414 342 999",date_update:a},{name:"Horská záchranná služba",value:"+256 772 333 333",date_update:a},{name:"Pobřežní záchranná služba",value:"+256 414 320 011",date_update:a}],phone_prefix:"+256",coordinates:[{latitude:"1.3733",longitude:"32.2903"}],people_religion:[{name:"Křesťanství",value:"85,2%",date_update:j},{name:"Islám",value:"12,1%",date_update:j},{name:"Tradiční náboženství",value:"1,6%",date_update:j},{name:"Jiní\u002Fneuvedeno",value:"1,1%",date_update:j}],people_nationality:[{name:"Baganda",value:"16,9%",date_update:g},{name:"Banyankole",value:"9,5%",date_update:g},{name:"Basoga",value:"8,4%",date_update:g},{name:"Bakiga",value:"6,9%",date_update:g},{name:"Iteso",value:"6,4%",date_update:g}],visitors_entry:[{name:"Občanský průkaz",value:"Pro vstup do Ugandy je nutné mít platný cestovní pas, občanský průkaz není dostačující.",date_update:a},{name:"Cestovní pas",value:"Pro vstup do Ugandy je nutné mít platný cestovní pas.",date_update:a},{name:"Vízum",value:"Pro občany České republiky je nutné mít před vstupem do Ugandy získat vízum. To lze získat buď při příletu na letišti, nebo předem na ugandské ambasádě v Praze.",date_update:a},{name:"Očkování",value:"Pro vstup do Ugandy nejsou vyžadována žádná specifická očkování. Nicméně, doporučuje se očkování proti žluté zimnici a případně další cestovní očkování doporučené odborníky v oblasti tropické medicíny.",date_update:a},{name:"Cestovní pojištění",value:"Při vstupu do Ugandy není vyžadováno cestovní nebo zdravotní pojištění. Nicméně, doporučuje se mít takové pojištění uzavřené, aby byla zajištěna kvalitní a rychlá zdravotní péče v případě potřeby.",date_update:a},{name:"Bezpečností situace",value:"V Ugandě existuje určité nebezpečí kriminality a terorismu. Doporučuje se být obezřetný, zejména v nočních hodinách a v odlehlých oblastech. Měli byste sledovat aktuální informace o bezpečnostní situaci v zemi a řídit se pokyny místních úřadů a bezpečnostních složek.",date_update:a},{name:"Další informace",value:"Při pobytu v Ugandě je třeba respektovat místní kulturu a zvyklosti, zejména v náboženských oblastech a na veřejnosti. Je také vhodné mít vždy u sebe dostatek hotovosti, protože ne všude jsou přijímány platební karty. Místní doprava je zajištěna zejména taxíky a mototaxi (bodaboda), ale tyto služby jsou spojeny s určitým rizikem. Doporučuje se také mít při sobě kopie důležitých dokladů a kontakt na české velvyslanectví v Kampale.",date_update:a}],type_place:n,id_image_cover:k,id_image_hero:k,id_city_main:2283,affiliate:[{name:"booking",value:d}],mpz:"UG",alerts:c,zoom:[{google:6,booking:7}],ids_neighboring_countries:[{id:40},{id:115},{id:192},{id:208},{id:229}],organization:c,apps:c,links:c,information_author:c,seo_tags:c,language_phrases:c,wall_sockets:c,id:231,facts_place:c,created_at:"2023-12-27T16:33:10.517062+00:00",updated_at:"2024-03-04T19:48:12.84066+00:00",name_original:c,currency:[{code:"UGX",name:"Ugandský šilink"}],setting_top:d,setting_status_public:h}],imagePlace:[{id:k,name:l,source:"\u002Fworld\u002Fstates\u002F",type:n,author:"pixabay.com",created_at:"2023-05-04T07:17:51.463854+00:00",updated_at:"2023-12-27T20:42:18.520959+00:00"}],placeContinent:[{id:f,slug:"afrika",name:"Afrika",area:30370000,population:1340000000,population_density:44.12,number_states:54,information_chatgpt:"\u003Cp\u003EAfrika je největší kontinent na Zemi a rozkládá se na ploše přesahující 30 milionů kilometrů čtverečních. Nachází se zde více než 50 zemí a žije zde více než 1,3 miliardy lidí. \u003C\u002Fp\u003E\n\u003Cp\u003EJižní Afrika, Egypt, Maroko a Keňa patří mezi nejoblíbenější turistické cíle v Africe. V Jihoafrické republice se můžete podívat na slavné Krugerův národní park a sledovat zde život zvířat v jejich přirozeném prostředí. Egypt je zase proslulý svými pyramidami, které se nacházejí v Gíze. V Maroku můžete navštívit tradiční trhy, zatímco v Keni se můžete setkat s Masajským kmenem a podívat se na úžasnou přírodu.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má bohatou historii a kulturu. První lidé, kteří se usadili v Africe, byli loveckým a sběračským národem. Poté se v této oblasti objevily první civilizace, jako je Kúšská říše, Egyptská říše nebo Království Aksum. Afrika byla také kolonizována evropskými mocnostmi a toto období mělo značný dopad na dnešní politickou situaci na kontinentu.\u003C\u002Fp\u003E\n\u003Cp\u003EJe důležité zmínit, že Afrika má velké množství jazyků a kultur. Zahrnuje mnoho různých národů a etnických skupin, a to má vliv na různorodost obyvatelstva a kultury. Nejrozšířenějším jazykem je arabština, která se mluví především v severní Africe a v některých částech západní Afriky. Dalšími důležitými jazyky jsou svahilština, francouzština, portugalština, angličtina a mnoho dalších.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má také nádhernou přírodu. Nachází se zde nejvyšší hora světa, Kilimandžáro, která se nachází na území Tanzanie a Keňy. Kromě toho se v Africe nachází také největší pouště na světě, Sahara, a největší prales, Kongský prales. Na tomto kontinentě můžete vidět také mnoho zvířat, jako jsou sloni, lvi, gepardi, zebry a mnoho dalších druhů. V Africe se také nachází několik nejvýznamnějších řek světa, jako je Nil, Kongo, Niger a Zambezi.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je bohužel také známá pro své problémy. Mnoho zemí se potýká s chudobou, nedostatkem potravin a vody, nemocemi a válečnými konflikty. Mnoho afrických zemí závisí na zemědělství a rybolovu, což však často není dostatečně výnosné, aby zabezpečilo potřeby obyvatelstva. Kromě toho jsou některé země v Africe trvale postižené extrémními přírodními katastrofami, jako jsou sucha, povodně a bouře.\u003C\u002Fp\u003E\n\u003Cp\u003ENa druhou stranu se v Africe také nachází mnoho lidí, kteří se snaží změnit situaci a zlepšit životy svých spoluobčanů. Vznikají neziskové organizace, které se snaží pomáhat při řešení těchto problémů a zlepšovat kvalitu života v mnoha afrických zemích. Zároveň se v posledních letech objevují nové možnosti pro ekonomický růst a rozvoj, jako jsou technologické start-upy a zahraniční investice.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika je fascinující kontinent, který nabízí mnoho zážitků a zároveň v sobě ukrývá mnoho výzev. Pokud se rozhodnete navštívit Afriku, můžete se těšit na krásnou přírodu, bohatou kulturu a nezapomenutelné zážitky. Je však také důležité být obezřetný a připravený na místní podmínky, protože některé oblasti mohou být nebezpečné kvůli konfliktům nebo zločinnosti.\u003C\u002Fp\u003E",coordinates:[{latitude:1.650801,longitude:17.371642}],type_place:"continent",id_image_cover:f,id_image_hero:f,zoom:[{google:2,booking:3}],information_author:[{text:"\u003Cp\u003E\u003Cb\u003EAfrika\u003C\u002Fb\u003E, druhý největší a druhý nejlidnatější kontinent na světě, láká cestovatele svou neuvěřitelnou rozmanitostí kultur, přírodními krásami a bohatou historií. Při cestování touto obrovskou plochou se otevírá mozaika různorodých zážitků, které zahrnují odlesky moderního městského života, divokou přírodu až po historická místa.\u003C\u002Fp\u003E\u003Cp\u003EJižní Afrika, domov několika národních parků a rezervací, je rájem pro milovníky safari. \u003Cb\u003EKrugerův národní park (Kruger National Park)\u003C\u002Fb\u003E nabízí úžasné setkání s \"Velkým pětkou\" - slony, nosorožci, buvoly, lvy a levharty. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fcape-town\"\u003EKapské Město (Cape Town)\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s ikonickou horou \u003Cb\u003EMaclear's Beacon\u003C\u002Fb\u003E, poskytuje nezapomenutelný výhled na město a okolní krajinu.\u003C\u002Fp\u003E\u003Cp\u003EVýchodní Afrika je známá svými ohromujícími sopkami. \u003Cb\u003EKilimandžáro\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E, nejvyšší hora Afriky, přitahuje dobrodruhy a horolezce z celého světa. Návštěva \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003EZanzibaru\u003C\u002Fa\u003E\u003C\u002Fb\u003E s jeho bílými plážemi a historickým centrem \u003Cb\u003EStone Town\u003C\u002Fb\u003E přidává do cestovatelského mixu kouzlo exotiky.\u003C\u002Fp\u003E\u003Cp\u003EStřední Afrika, s obrovskou rovníkovou pralesní plochou, skrývá některé z nejzajímavějších biodiverzit na světě. \u003Cb\u003EKonžský deštný prales\u003C\u002Fb\u003E je po amazonském druhý největší na světě, je domovem vzácných druhů zvířat a rostlin, a expedice sem mohou odhalit tajemství dosud neobjevená.\u003C\u002Fp\u003E\u003Cp\u003ESeverní Afrika je poznamenána starověkými civilizacemi a písečnými pouštěmi. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fegypt\"\u003EEgypt\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pyramidami a chrámy na březích \u003Cb\u003ENilu\u003C\u002Fb\u003E je jednou z nejnavštěvovanějších destinací v této části kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmaroko\"\u003EMaroko\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s mistrovským spojením tradice a moderny v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fmarrakes\"\u003EMarrákeši\u003C\u002Fa\u003E\u003C\u002Fb\u003E, je fascinujícím mixem barev, vůní a architektury.\u003C\u002Fp\u003E\u003Cp\u003EZápadní Afrika, bohatá na kulturní dědictví, nabízí autentické zážitky. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fghana\"\u003EGhana\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pevnostmi, které připomínají období otroctví, a kultovními festivaly představuje hluboký pohled do minulosti. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fsenegal\"\u003ESenegal\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s historickým ostrovem \u003Cb\u003EGorée\u003C\u002Fb\u003E, který sehral roli s obchodem s otroky, kde najdete koloniální budovy a muzeum \u003Cb\u003EHouse of Slaves (Maison des esclaves)\u003C\u002Fb\u003E.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je rovněž známá svými divokými zvířaty. Kromě safari v jižní části kontinentu můžete zažít jedinečnou příležitost sledovat migraci tisíců zeber a gazel v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fkena\"\u003EKeni\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E. Návštěva gorilí v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Frwanda\"\u003ERwandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fuganda\"\u003EUgandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E je neopakovatelným zážitkem pro ty, kteří touží po setkání s těmito ohroženými tvory.\u003C\u002Fp\u003E\u003Cp\u003EPřírodní skvosty nejsou omezeny pouze na pevninskou část kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fseychely\"\u003ESeychely\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMauricius\u003C\u002Fa\u003E\u003C\u002Fb\u003E, dvě ostrůvky v \u003Cb\u003EIndickém oceánu\u003C\u002Fb\u003E, lákají svými bílými plážemi, průzračnými vodami a jedinečnou faunou a florou.\u003C\u002Fp\u003E\u003Cp\u003EAfrika rovněž hýří kulturou a historií. Starověké památky jako \u003Cb\u003EGreat Zimbabwe National Monument\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fzimbabwe\"\u003EZimbabwe\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003ETimbuktu (Tombouctou)\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMali\u003C\u002Fa\u003E\u003C\u002Fb\u003E jsou svědectvím o bohaté historii a rozvoji obchodu a kultury na africkém kontinentu. Každý region má své vlastní tradiční tance, hudbu a umění, které odrážejí místní identitu a historii.\u003C\u002Fp\u003E\u003Cp\u003ECestování Afrikou je také o interakci s místními lidmi. Přívětivost a pohostinnost afrických komunit jsou nezapomenutelnými součástmi cestování. Místní trhy nabízejí pestrou škálu barev, vůní a chutí, zatímco setkání s kmeny a etnickými skupinami přináší pochopení různorodosti kultur a tradic.\u003C\u002Fp\u003E\u003Cp\u003EJe však důležité si být vědomý sociálních, ekonomických a environmentálních výzev, kterým mnohé africké země čelí. Cestovatelé by měli být respektující vůči místním zvyklostem a životnímu prostředí a přispívat k udržitelnému turismu, který podporuje místní ekonomiky a ochranu přírodních zdrojů.\u003C\u002Fp\u003E\u003Cp\u003ECelkově lze říci, že cestování po Africe je dobrodružstvím naplněným kontrasty a překvapeními. Od rozlehlých savan po exotické pláže, od starověkých ruin po moderní metropole. Afrika nabízí cestovatelům nekonečné možnosti objevování a poznávání. Je to kontinent, který zanechává nezapomenutelný dojem na každém, kdo se odhodlá k jeho prozkoumání.\u003C\u002Fp\u003E",date_create:o,date_update:o,author_create:p,author_update:p}],seo_tags:[],created_at:"2023-12-27T16:35:37.460505+00:00",updated_at:"2024-03-04T19:48:06.634661+00:00",name_original:"Africa",setting_top:d,setting_status_public:h}]}],fetch:{},mutations:[]}}("05-2023","12-2021",null,true,0,5,"01-2021",1,2000,"06-2014",382,"uganda","999","state","2024-03-01","1")));