__NUXT_JSONP__("/svet/stat/seychely/lide", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q){return {data:[{tabsLoad:{tabWhatToSee:c,tabPrice:c,tabPeople:c,tabTrip:c,tabContact:c,tabHotel:c,tabNeighboring:d,tabVideos:d,tabArticles:d,tabWallSockets:h,tabChains:d,tabFauna:d,tabFlora:d,tabFoods:d,tabBrands:d},place:[{tld:".sc",name:"Seychely",area:455,population:98008,slug:k,id_continent:g,information_chatgpt:"\u003Cp\u003ESeychely jsou ostrovní stát v Indickém oceánu, nacházející se východně od Afriky. Tvoří ho 115 ostrovů, z nichž největší jsou Mahé, Praslin a La Digue. Seychely jsou známé svými bílými plážemi, tyrkysovými vodami a exotickou faunou a flórou. \u003C\u002Fp\u003E\n\u003Cp\u003ESeychely jsou populárním cílem pro turisty, kteří sem přijíždějí za odpočinkem na plážích a potápěním v korálových útesech. Mnoho ostrovů je zde neobydlených a nabízí tak neuvěřitelně klidné a odlehlé pobytové možnosti. \u003C\u002Fp\u003E\n\u003Cp\u003EKromě svých přírodních krás Seychely nabízejí i kulturní zážitky. Místní kuchyně je ovlivněna africkými, francouzskými a indickými tradicemi. Zajímavé jsou také tradiční tance a hudba, stejně jako řemesla jako je tkaní košíků a výroba palmových střech. \u003C\u002Fp\u003E\n\u003Cp\u003ESeychely mají tropické podnebí s vysokou vlhkostí a teplotami v rozmezí 24-32°C. Nejlepší období pro návštěvu je od května do září, kdy jsou nižší teploty a srážky. Mezinárodní letiště na Mahé umožňuje snadný přístup k Seychelám z celého světa.\u003C\u002Fp\u003E",money_prices:[{name:"Taxi (cena za km)",value:b,date_update:a},{name:"Hlavní jídlo",value:b,date_update:a},{name:"Chleba",value:b,date_update:a},{name:"Mléko",value:b,date_update:a},{name:"Voda (500ml)",value:b,date_update:a},{name:"Espresso",value:b,date_update:a},{name:"Zmrzlina (jeden kopeček)",value:b,date_update:a},{name:"Pivo",value:b,date_update:a},{name:"Krabička cigaret",value:b,date_update:a},{name:"Big Mac",value:b,date_update:a},{name:"Coca Cola (500ml)",value:b,date_update:a}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:"112",date_update:a},{name:"Policie",value:"133",date_update:a},{name:"Hasiči",value:"999",date_update:a},{name:"Zdravotnická záchranná služba",value:"151",date_update:a},{name:"Turistická policie",value:"114",date_update:a},{name:"Horská záchranná služba",value:"2724200",date_update:a},{name:"Pobřežní záchranná služba",value:"281515",date_update:a}],phone_prefix:"+248",coordinates:[{latitude:"-4.6796",longitude:"55.4920"}],people_religion:[{name:"Římskokatolická církev",value:l,date_update:e},{name:"Anglikánská církev",value:"6%",date_update:e},{name:"Hinduismus",value:m,date_update:e},{name:"Islám",value:i,date_update:e},{name:"Buddhismus",value:i,date_update:e},{name:"Ostatní náboženství",value:n,date_update:e},{name:"Bez vyznání",value:"10%",date_update:e}],people_nationality:[{name:"Kreolové",value:l,date_update:f},{name:"Francouzi",value:n,date_update:f},{name:"Indové",value:m,date_update:f},{name:"Číňané",value:i,date_update:f},{name:"Ostatní",value:"17%",date_update:f}],visitors_entry:[{name:"Občanský průkaz",value:"Pro vstup na Seychely je potřeba cestovní pas, občanský průkaz nestačí.",date_update:a},{name:"Cestovní pas",value:"Pro vstup na Seychely je potřeba cestovní pas. Občanský průkaz nestačí.",date_update:a},{name:"Vízum",value:"Pro občany Evropské unie není potřeba vízum, pokud pobyt nepřesáhne 90 dnů. Pokud bude pobyt delší než 90 dnů, je nutné žádat o vízum. Více informací lze najít na webových stránkách Seychelského velvyslanectví.",date_update:a},{name:"Očkování",value:"Při vstupu na Seychely není nutné mít očkování. Nicméně, pokud cestujete z oblasti, kde se vyskytují určité nemoci (např. žlutá zimnice), může být vyžadováno očkování jako podmínka vstupu. Doporučuje se konzultovat s odborníkem na cestovní medicínu.",date_update:a},{name:"Cestovní pojištění",value:"Cestovní pojištění není povinné, ale doporučuje se sjednat si jej před cestou na Seychely. Některé země vstup s cestovním pojištěním vyžadují.",date_update:a},{name:"Bezpečnostní situace",value:"Seychely jsou považovány za relativně bezpečnou destinaci, nicméně turistům se doporučuje být obezřetní a dodržovat základní bezpečnostní opatření, jako například nechávat cennosti v hotelovém trezoru a nechodit po tmě osamoceně po plážích.",date_update:a},{name:"Další informace",value:"Při návštěvě Seychel je dobré mít větší množství hotovosti nebo platební kartu, protože v některých místech nemusí být možné platit kartou. Turisté by měli také dbát na to, aby neohrožovali místní flóru a faunu a dodržovat zákaz lovu korálů a odnášení písku z pláží. Místní obyvatelé jsou velmi přátelští a zdvořilí a je vhodné respektovat jejich kulturu a zvyklosti.",date_update:a}],type_place:o,id_image_cover:j,id_image_hero:j,id_city_main:1911,affiliate:[{name:"booking",value:c}],mpz:"SC",alerts:b,zoom:[{google:9,booking:10}],ids_neighboring_countries:[],organization:b,apps:b,links:b,information_author:b,seo_tags:b,language_phrases:b,wall_sockets:b,id:195,facts_place:b,created_at:"2023-12-27T16:33:10.517062+00:00",updated_at:"2024-03-04T19:48:12.84066+00:00",name_original:b,currency:[{code:"SCR",name:"Seychelská rupie"}],setting_top:c,setting_status_public:h}],imagePlace:[{id:j,name:k,source:"\u002Fworld\u002Fstates\u002F",type:o,author:"pixabay.com",created_at:"2023-05-03T20:14:00.749527+00:00",updated_at:"2023-12-27T20:42:18.520959+00:00"}],placeContinent:[{id:g,slug:"afrika",name:"Afrika",area:30370000,population:1340000000,population_density:44.12,number_states:54,information_chatgpt:"\u003Cp\u003EAfrika je největší kontinent na Zemi a rozkládá se na ploše přesahující 30 milionů kilometrů čtverečních. Nachází se zde více než 50 zemí a žije zde více než 1,3 miliardy lidí. \u003C\u002Fp\u003E\n\u003Cp\u003EJižní Afrika, Egypt, Maroko a Keňa patří mezi nejoblíbenější turistické cíle v Africe. V Jihoafrické republice se můžete podívat na slavné Krugerův národní park a sledovat zde život zvířat v jejich přirozeném prostředí. Egypt je zase proslulý svými pyramidami, které se nacházejí v Gíze. V Maroku můžete navštívit tradiční trhy, zatímco v Keni se můžete setkat s Masajským kmenem a podívat se na úžasnou přírodu.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má bohatou historii a kulturu. První lidé, kteří se usadili v Africe, byli loveckým a sběračským národem. Poté se v této oblasti objevily první civilizace, jako je Kúšská říše, Egyptská říše nebo Království Aksum. Afrika byla také kolonizována evropskými mocnostmi a toto období mělo značný dopad na dnešní politickou situaci na kontinentu.\u003C\u002Fp\u003E\n\u003Cp\u003EJe důležité zmínit, že Afrika má velké množství jazyků a kultur. Zahrnuje mnoho různých národů a etnických skupin, a to má vliv na různorodost obyvatelstva a kultury. Nejrozšířenějším jazykem je arabština, která se mluví především v severní Africe a v některých částech západní Afriky. Dalšími důležitými jazyky jsou svahilština, francouzština, portugalština, angličtina a mnoho dalších.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má také nádhernou přírodu. Nachází se zde nejvyšší hora světa, Kilimandžáro, která se nachází na území Tanzanie a Keňy. Kromě toho se v Africe nachází také největší pouště na světě, Sahara, a největší prales, Kongský prales. Na tomto kontinentě můžete vidět také mnoho zvířat, jako jsou sloni, lvi, gepardi, zebry a mnoho dalších druhů. V Africe se také nachází několik nejvýznamnějších řek světa, jako je Nil, Kongo, Niger a Zambezi.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je bohužel také známá pro své problémy. Mnoho zemí se potýká s chudobou, nedostatkem potravin a vody, nemocemi a válečnými konflikty. Mnoho afrických zemí závisí na zemědělství a rybolovu, což však často není dostatečně výnosné, aby zabezpečilo potřeby obyvatelstva. Kromě toho jsou některé země v Africe trvale postižené extrémními přírodními katastrofami, jako jsou sucha, povodně a bouře.\u003C\u002Fp\u003E\n\u003Cp\u003ENa druhou stranu se v Africe také nachází mnoho lidí, kteří se snaží změnit situaci a zlepšit životy svých spoluobčanů. Vznikají neziskové organizace, které se snaží pomáhat při řešení těchto problémů a zlepšovat kvalitu života v mnoha afrických zemích. Zároveň se v posledních letech objevují nové možnosti pro ekonomický růst a rozvoj, jako jsou technologické start-upy a zahraniční investice.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika je fascinující kontinent, který nabízí mnoho zážitků a zároveň v sobě ukrývá mnoho výzev. Pokud se rozhodnete navštívit Afriku, můžete se těšit na krásnou přírodu, bohatou kulturu a nezapomenutelné zážitky. Je však také důležité být obezřetný a připravený na místní podmínky, protože některé oblasti mohou být nebezpečné kvůli konfliktům nebo zločinnosti.\u003C\u002Fp\u003E",coordinates:[{latitude:1.650801,longitude:17.371642}],type_place:"continent",id_image_cover:g,id_image_hero:g,zoom:[{google:2,booking:3}],information_author:[{text:"\u003Cp\u003E\u003Cb\u003EAfrika\u003C\u002Fb\u003E, druhý největší a druhý nejlidnatější kontinent na světě, láká cestovatele svou neuvěřitelnou rozmanitostí kultur, přírodními krásami a bohatou historií. Při cestování touto obrovskou plochou se otevírá mozaika různorodých zážitků, které zahrnují odlesky moderního městského života, divokou přírodu až po historická místa.\u003C\u002Fp\u003E\u003Cp\u003EJižní Afrika, domov několika národních parků a rezervací, je rájem pro milovníky safari. \u003Cb\u003EKrugerův národní park (Kruger National Park)\u003C\u002Fb\u003E nabízí úžasné setkání s \"Velkým pětkou\" - slony, nosorožci, buvoly, lvy a levharty. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fcape-town\"\u003EKapské Město (Cape Town)\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s ikonickou horou \u003Cb\u003EMaclear's Beacon\u003C\u002Fb\u003E, poskytuje nezapomenutelný výhled na město a okolní krajinu.\u003C\u002Fp\u003E\u003Cp\u003EVýchodní Afrika je známá svými ohromujícími sopkami. \u003Cb\u003EKilimandžáro\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E, nejvyšší hora Afriky, přitahuje dobrodruhy a horolezce z celého světa. Návštěva \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003EZanzibaru\u003C\u002Fa\u003E\u003C\u002Fb\u003E s jeho bílými plážemi a historickým centrem \u003Cb\u003EStone Town\u003C\u002Fb\u003E přidává do cestovatelského mixu kouzlo exotiky.\u003C\u002Fp\u003E\u003Cp\u003EStřední Afrika, s obrovskou rovníkovou pralesní plochou, skrývá některé z nejzajímavějších biodiverzit na světě. \u003Cb\u003EKonžský deštný prales\u003C\u002Fb\u003E je po amazonském druhý největší na světě, je domovem vzácných druhů zvířat a rostlin, a expedice sem mohou odhalit tajemství dosud neobjevená.\u003C\u002Fp\u003E\u003Cp\u003ESeverní Afrika je poznamenána starověkými civilizacemi a písečnými pouštěmi. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fegypt\"\u003EEgypt\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pyramidami a chrámy na březích \u003Cb\u003ENilu\u003C\u002Fb\u003E je jednou z nejnavštěvovanějších destinací v této části kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmaroko\"\u003EMaroko\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s mistrovským spojením tradice a moderny v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fmarrakes\"\u003EMarrákeši\u003C\u002Fa\u003E\u003C\u002Fb\u003E, je fascinujícím mixem barev, vůní a architektury.\u003C\u002Fp\u003E\u003Cp\u003EZápadní Afrika, bohatá na kulturní dědictví, nabízí autentické zážitky. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fghana\"\u003EGhana\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pevnostmi, které připomínají období otroctví, a kultovními festivaly představuje hluboký pohled do minulosti. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fsenegal\"\u003ESenegal\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s historickým ostrovem \u003Cb\u003EGorée\u003C\u002Fb\u003E, který sehral roli s obchodem s otroky, kde najdete koloniální budovy a muzeum \u003Cb\u003EHouse of Slaves (Maison des esclaves)\u003C\u002Fb\u003E.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je rovněž známá svými divokými zvířaty. Kromě safari v jižní části kontinentu můžete zažít jedinečnou příležitost sledovat migraci tisíců zeber a gazel v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fkena\"\u003EKeni\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E. Návštěva gorilí v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Frwanda\"\u003ERwandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fuganda\"\u003EUgandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E je neopakovatelným zážitkem pro ty, kteří touží po setkání s těmito ohroženými tvory.\u003C\u002Fp\u003E\u003Cp\u003EPřírodní skvosty nejsou omezeny pouze na pevninskou část kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fseychely\"\u003ESeychely\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMauricius\u003C\u002Fa\u003E\u003C\u002Fb\u003E, dvě ostrůvky v \u003Cb\u003EIndickém oceánu\u003C\u002Fb\u003E, lákají svými bílými plážemi, průzračnými vodami a jedinečnou faunou a florou.\u003C\u002Fp\u003E\u003Cp\u003EAfrika rovněž hýří kulturou a historií. Starověké památky jako \u003Cb\u003EGreat Zimbabwe National Monument\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fzimbabwe\"\u003EZimbabwe\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003ETimbuktu (Tombouctou)\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMali\u003C\u002Fa\u003E\u003C\u002Fb\u003E jsou svědectvím o bohaté historii a rozvoji obchodu a kultury na africkém kontinentu. Každý region má své vlastní tradiční tance, hudbu a umění, které odrážejí místní identitu a historii.\u003C\u002Fp\u003E\u003Cp\u003ECestování Afrikou je také o interakci s místními lidmi. Přívětivost a pohostinnost afrických komunit jsou nezapomenutelnými součástmi cestování. Místní trhy nabízejí pestrou škálu barev, vůní a chutí, zatímco setkání s kmeny a etnickými skupinami přináší pochopení různorodosti kultur a tradic.\u003C\u002Fp\u003E\u003Cp\u003EJe však důležité si být vědomý sociálních, ekonomických a environmentálních výzev, kterým mnohé africké země čelí. Cestovatelé by měli být respektující vůči místním zvyklostem a životnímu prostředí a přispívat k udržitelnému turismu, který podporuje místní ekonomiky a ochranu přírodních zdrojů.\u003C\u002Fp\u003E\u003Cp\u003ECelkově lze říci, že cestování po Africe je dobrodružstvím naplněným kontrasty a překvapeními. Od rozlehlých savan po exotické pláže, od starověkých ruin po moderní metropole. Afrika nabízí cestovatelům nekonečné možnosti objevování a poznávání. Je to kontinent, který zanechává nezapomenutelný dojem na každém, kdo se odhodlá k jeho prozkoumání.\u003C\u002Fp\u003E",date_create:p,date_update:p,author_create:q,author_update:q}],seo_tags:[],created_at:"2023-12-27T16:35:37.460505+00:00",updated_at:"2024-03-04T19:48:06.634661+00:00",name_original:"Africa",setting_top:c,setting_status_public:h}]}],fetch:{},mutations:[]}}("05-2023",null,true,0,"01-2023","04-2023",5,1,"1%",357,"seychely","76%","2%","4%","state","2024-03-01","1")));