__NUXT_JSONP__("/svet/stat/gambie", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w,x,y,z,A,B,C){return {data:[{tabsLoad:{tabWhatToSee:c,tabPrice:c,tabPeople:c,tabTrip:c,tabContact:c,tabHotel:c,tabNeighboring:f,tabVideos:g,tabArticles:g,tabWallSockets:f,tabChains:g,tabFauna:f,tabFlora:g,tabFoods:g,tabBrands:g},place:[{tld:".gm",name:"Gambie",area:11295,population:2416668,slug:m,id_continent:h,information_chatgpt:"\u003Cp\u003EGambie je malý západoafrický stát, který se rozkládá na pobřeží Atlantského oceánu. S rozlohou pouhých 11 000 km² a populací přibližně 2,5 milionů lidí patří Gambie mezi nejmenší státy Afriky. Gambie je také jediným státem v Africe, kde je úplně obklopen jiným státem (Senegalem).\u003C\u002Fp\u003E\n\u003Cp\u003EGambie má tropické podnebí a během většiny roku je zde horko a vlhko. Nejlepší dobou k návštěvě jsou měsíce od listopadu do května, kdy jsou teploty mírnější a srážky nižší. Turisté se zde mohou těšit na krásné pláže, přírodní rezervace a národní parky, jako je například národní park Kiang West, který je domovem mnoha druhů zvířat, včetně opic, krokodýlů a slonů.\u003C\u002Fp\u003E\n\u003Cp\u003EHospodářství Gambie se opírá především o zemědělství a rybolov. Mezi nejvýznamnější plodiny patří arašídy, rýže a bavlna. V posledních letech se také rozvíjí turistický průmysl a Gambie se snaží stát se oblíbeným cílem pro evropské turisty.\u003C\u002Fp\u003E\n\u003Cp\u003EGambie má bohatou kulturu a tradice. Země je domovem mnoha etnických skupin, jako jsou Mandinkové, Wolofové a Fulaové, každá s vlastními zvyky a jazykem. Dominantní náboženství je islám, který sem byl přinesen v 9. století, ale v Gambii najdete také křesťany a animisty.\u003C\u002Fp\u003E\n\u003Cp\u003EPolitická situace v Gambii byla v minulosti nestabilní, avšak v posledních letech došlo k několika pozitivním změnám. V roce 2017 byl po dlouhých letech autoritářského režimu prezident Yahya Jammeh poražen v demokratických volbách, což vedlo ke zlepšení vztahů s mezinárodní společenství a návratu Gambie k demokratickým procesům.\u003C\u002Fp\u003E",money_prices:[{name:"Taxi (cena za km)",value:30,date_update:b},{name:"Hlavní jídlo",value:150,date_update:b},{name:"Chleba",value:i,date_update:b},{name:"Mléko",value:n,date_update:b},{name:"Voda (500ml)",value:h,date_update:b},{name:"Espresso",value:n,date_update:b},{name:"Zmrzlina (jeden kopeček)",value:i,date_update:b},{name:"Pivo",value:40,date_update:b},{name:"Krabička cigaret",value:o,date_update:p},{name:"Big Mac",value:o,date_update:p},{name:"Coca Cola (500ml)",value:i,date_update:b}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:q,date_update:b},{name:"Policie",value:"117",date_update:b},{name:"Hasiči",value:"118",date_update:b},{name:"Zdravotnická záchranná služba",value:q,date_update:b}],phone_prefix:"+220",coordinates:[{latitude:"13.4432",longitude:"-15.3101"}],people_religion:[{name:"Islám",value:"přibližně 95%"},{name:"Křesťanství",value:"přibližně 4%"},{name:"Animismus",value:"přibližně 1%"},{name:"Bahá'í",value:"méně než 1%"}],people_nationality:[{name:"Mandinka",value:"42%"},{name:"Fulbe",value:"18%"},{name:"Wolof",value:"16%"},{name:"Jola",value:"10%"},{name:"Serahule",value:"9%"}],visitors_entry:[{name:"Občanský průkaz",value:"Pro vstup do Gambie není povinné mít občanský průkaz, nicméně je doporučeno mít při sobě platný cestovní doklad.",date_update:b},{name:"Cestovní pas",value:"Pro vstup do Gambie je nutné mít platný cestovní pas s minimální platností 6 měsíců od data vstupu do země.",date_update:b},{name:"Vízum",value:"Pro vstup do Gambie je pro občany České republiky (občany EU) potřeba mít vízum. Vízum lze získat na ambasádě Gambie v Berlíně, nebo na hraničním přechodu v případě, že cestujete z okolních zemí.",date_update:b},{name:"Očkování",value:"Pro vstup do Gambie není povinné očkování. Nicméně se doporučuje očkování proti žluté zimnici, hepatitidě A a B a břišnímu tyfu. Před cestou je vhodné konzultovat s odborníkem v cestovní medicíně.",date_update:b},{name:"Cestovní pojištění",value:"Pro vstup do Gambie není nutné mít cestovní pojištění, ale je doporučeno si ho sjednat pro případné nepředvídané události a komplikace během cesty.",date_update:b},{name:"Bezpečnostní situace",value:"V Gambii může docházet k náhlým protestům a demonstracím. Doporučuje se sledovat aktuální bezpečnostní situaci a vyvarovat se všem místům, kde dochází k těmto událostem. Je také doporučeno být opatrný v nočních hodinách, hlavně v turistických oblastech.",date_update:b},{name:"Další informace",value:"V Gambii je obvyklé vyjednávat o cenách zboží a služeb, jelikož jsou zde běžné velké rozdíly v cenách pro místní a turisty. Důležité je mít dostatek hotovosti, protože ne všude jsou přijímány platební karty. Zdravotní péče je na nízké úrovni, doporučuje se zajistit si předem vakcíny a vybavit se potřebnými léky. Je také nutné dbát na hygienická opatření, například pít pouze balenou vodu a jíst pouze oloupané ovoce a zeleninu.",date_update:b}],type_place:j,id_image_cover:k,id_image_hero:k,id_city_main:l,affiliate:[{name:r,value:c}],mpz:"GM",alerts:[],zoom:[{google:7,booking:8}],ids_neighboring_countries:[{id:s}],organization:[],apps:[],links:[],information_author:[],seo_tags:[],language_phrases:[],wall_sockets:a,id:t,facts_place:a,created_at:"2023-12-27T16:33:10.517062+00:00",updated_at:"2024-03-04T19:48:12.84066+00:00",name_original:a,currency:[{code:"GMD",name:"dalasi"}],setting_top:c,setting_status_public:f}],imagePlace:[{id:k,name:m,source:u,type:j,author:v,created_at:"2023-05-02T18:49:47.851894+00:00",updated_at:w}],placesStatesNeighboring:[{id:s,id_image_cover:x,slug:y,type_place:j,name:"Senegal"}],imagesStatesNeighboring:[{id:x,name:y,source:u,type:j,author:v,created_at:"2023-05-03T20:14:00.749527+00:00",updated_at:w}],placeContinent:[{id:h,slug:"afrika",name:"Afrika",area:30370000,population:1340000000,population_density:44.12,number_states:54,information_chatgpt:"\u003Cp\u003EAfrika je největší kontinent na Zemi a rozkládá se na ploše přesahující 30 milionů kilometrů čtverečních. Nachází se zde více než 50 zemí a žije zde více než 1,3 miliardy lidí. \u003C\u002Fp\u003E\n\u003Cp\u003EJižní Afrika, Egypt, Maroko a Keňa patří mezi nejoblíbenější turistické cíle v Africe. V Jihoafrické republice se můžete podívat na slavné Krugerův národní park a sledovat zde život zvířat v jejich přirozeném prostředí. Egypt je zase proslulý svými pyramidami, které se nacházejí v Gíze. V Maroku můžete navštívit tradiční trhy, zatímco v Keni se můžete setkat s Masajským kmenem a podívat se na úžasnou přírodu.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má bohatou historii a kulturu. První lidé, kteří se usadili v Africe, byli loveckým a sběračským národem. Poté se v této oblasti objevily první civilizace, jako je Kúšská říše, Egyptská říše nebo Království Aksum. Afrika byla také kolonizována evropskými mocnostmi a toto období mělo značný dopad na dnešní politickou situaci na kontinentu.\u003C\u002Fp\u003E\n\u003Cp\u003EJe důležité zmínit, že Afrika má velké množství jazyků a kultur. Zahrnuje mnoho různých národů a etnických skupin, a to má vliv na různorodost obyvatelstva a kultury. Nejrozšířenějším jazykem je arabština, která se mluví především v severní Africe a v některých částech západní Afriky. Dalšími důležitými jazyky jsou svahilština, francouzština, portugalština, angličtina a mnoho dalších.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má také nádhernou přírodu. Nachází se zde nejvyšší hora světa, Kilimandžáro, která se nachází na území Tanzanie a Keňy. Kromě toho se v Africe nachází také největší pouště na světě, Sahara, a největší prales, Kongský prales. Na tomto kontinentě můžete vidět také mnoho zvířat, jako jsou sloni, lvi, gepardi, zebry a mnoho dalších druhů. V Africe se také nachází několik nejvýznamnějších řek světa, jako je Nil, Kongo, Niger a Zambezi.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je bohužel také známá pro své problémy. Mnoho zemí se potýká s chudobou, nedostatkem potravin a vody, nemocemi a válečnými konflikty. Mnoho afrických zemí závisí na zemědělství a rybolovu, což však často není dostatečně výnosné, aby zabezpečilo potřeby obyvatelstva. Kromě toho jsou některé země v Africe trvale postižené extrémními přírodními katastrofami, jako jsou sucha, povodně a bouře.\u003C\u002Fp\u003E\n\u003Cp\u003ENa druhou stranu se v Africe také nachází mnoho lidí, kteří se snaží změnit situaci a zlepšit životy svých spoluobčanů. Vznikají neziskové organizace, které se snaží pomáhat při řešení těchto problémů a zlepšovat kvalitu života v mnoha afrických zemích. Zároveň se v posledních letech objevují nové možnosti pro ekonomický růst a rozvoj, jako jsou technologické start-upy a zahraniční investice.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika je fascinující kontinent, který nabízí mnoho zážitků a zároveň v sobě ukrývá mnoho výzev. Pokud se rozhodnete navštívit Afriku, můžete se těšit na krásnou přírodu, bohatou kulturu a nezapomenutelné zážitky. Je však také důležité být obezřetný a připravený na místní podmínky, protože některé oblasti mohou být nebezpečné kvůli konfliktům nebo zločinnosti.\u003C\u002Fp\u003E",coordinates:[{latitude:1.650801,longitude:17.371642}],type_place:"continent",id_image_cover:h,id_image_hero:h,zoom:[{google:2,booking:3}],information_author:[{text:"\u003Cp\u003E\u003Cb\u003EAfrika\u003C\u002Fb\u003E, druhý největší a druhý nejlidnatější kontinent na světě, láká cestovatele svou neuvěřitelnou rozmanitostí kultur, přírodními krásami a bohatou historií. Při cestování touto obrovskou plochou se otevírá mozaika různorodých zážitků, které zahrnují odlesky moderního městského života, divokou přírodu až po historická místa.\u003C\u002Fp\u003E\u003Cp\u003EJižní Afrika, domov několika národních parků a rezervací, je rájem pro milovníky safari. \u003Cb\u003EKrugerův národní park (Kruger National Park)\u003C\u002Fb\u003E nabízí úžasné setkání s \"Velkým pětkou\" - slony, nosorožci, buvoly, lvy a levharty. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fcape-town\"\u003EKapské Město (Cape Town)\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s ikonickou horou \u003Cb\u003EMaclear's Beacon\u003C\u002Fb\u003E, poskytuje nezapomenutelný výhled na město a okolní krajinu.\u003C\u002Fp\u003E\u003Cp\u003EVýchodní Afrika je známá svými ohromujícími sopkami. \u003Cb\u003EKilimandžáro\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E, nejvyšší hora Afriky, přitahuje dobrodruhy a horolezce z celého světa. Návštěva \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003EZanzibaru\u003C\u002Fa\u003E\u003C\u002Fb\u003E s jeho bílými plážemi a historickým centrem \u003Cb\u003EStone Town\u003C\u002Fb\u003E přidává do cestovatelského mixu kouzlo exotiky.\u003C\u002Fp\u003E\u003Cp\u003EStřední Afrika, s obrovskou rovníkovou pralesní plochou, skrývá některé z nejzajímavějších biodiverzit na světě. \u003Cb\u003EKonžský deštný prales\u003C\u002Fb\u003E je po amazonském druhý největší na světě, je domovem vzácných druhů zvířat a rostlin, a expedice sem mohou odhalit tajemství dosud neobjevená.\u003C\u002Fp\u003E\u003Cp\u003ESeverní Afrika je poznamenána starověkými civilizacemi a písečnými pouštěmi. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fegypt\"\u003EEgypt\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pyramidami a chrámy na březích \u003Cb\u003ENilu\u003C\u002Fb\u003E je jednou z nejnavštěvovanějších destinací v této části kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmaroko\"\u003EMaroko\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s mistrovským spojením tradice a moderny v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fmarrakes\"\u003EMarrákeši\u003C\u002Fa\u003E\u003C\u002Fb\u003E, je fascinujícím mixem barev, vůní a architektury.\u003C\u002Fp\u003E\u003Cp\u003EZápadní Afrika, bohatá na kulturní dědictví, nabízí autentické zážitky. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fghana\"\u003EGhana\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pevnostmi, které připomínají období otroctví, a kultovními festivaly představuje hluboký pohled do minulosti. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fsenegal\"\u003ESenegal\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s historickým ostrovem \u003Cb\u003EGorée\u003C\u002Fb\u003E, který sehral roli s obchodem s otroky, kde najdete koloniální budovy a muzeum \u003Cb\u003EHouse of Slaves (Maison des esclaves)\u003C\u002Fb\u003E.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je rovněž známá svými divokými zvířaty. Kromě safari v jižní části kontinentu můžete zažít jedinečnou příležitost sledovat migraci tisíců zeber a gazel v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fkena\"\u003EKeni\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E. Návštěva gorilí v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Frwanda\"\u003ERwandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fuganda\"\u003EUgandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E je neopakovatelným zážitkem pro ty, kteří touží po setkání s těmito ohroženými tvory.\u003C\u002Fp\u003E\u003Cp\u003EPřírodní skvosty nejsou omezeny pouze na pevninskou část kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fseychely\"\u003ESeychely\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMauricius\u003C\u002Fa\u003E\u003C\u002Fb\u003E, dvě ostrůvky v \u003Cb\u003EIndickém oceánu\u003C\u002Fb\u003E, lákají svými bílými plážemi, průzračnými vodami a jedinečnou faunou a florou.\u003C\u002Fp\u003E\u003Cp\u003EAfrika rovněž hýří kulturou a historií. Starověké památky jako \u003Cb\u003EGreat Zimbabwe National Monument\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fzimbabwe\"\u003EZimbabwe\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003ETimbuktu (Tombouctou)\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMali\u003C\u002Fa\u003E\u003C\u002Fb\u003E jsou svědectvím o bohaté historii a rozvoji obchodu a kultury na africkém kontinentu. Každý region má své vlastní tradiční tance, hudbu a umění, které odrážejí místní identitu a historii.\u003C\u002Fp\u003E\u003Cp\u003ECestování Afrikou je také o interakci s místními lidmi. Přívětivost a pohostinnost afrických komunit jsou nezapomenutelnými součástmi cestování. Místní trhy nabízejí pestrou škálu barev, vůní a chutí, zatímco setkání s kmeny a etnickými skupinami přináší pochopení různorodosti kultur a tradic.\u003C\u002Fp\u003E\u003Cp\u003EJe však důležité si být vědomý sociálních, ekonomických a environmentálních výzev, kterým mnohé africké země čelí. Cestovatelé by měli být respektující vůči místním zvyklostem a životnímu prostředí a přispívat k udržitelnému turismu, který podporuje místní ekonomiky a ochranu přírodních zdrojů.\u003C\u002Fp\u003E\u003Cp\u003ECelkově lze říci, že cestování po Africe je dobrodružstvím naplněným kontrasty a překvapeními. Od rozlehlých savan po exotické pláže, od starověkých ruin po moderní metropole. Afrika nabízí cestovatelům nekonečné možnosti objevování a poznávání. Je to kontinent, který zanechává nezapomenutelný dojem na každém, kdo se odhodlá k jeho prozkoumání.\u003C\u002Fp\u003E",date_create:z,date_update:z,author_create:A,author_update:A}],seo_tags:[],created_at:"2023-12-27T16:35:37.460505+00:00",updated_at:"2024-03-04T19:48:06.634661+00:00",name_original:"Africa",setting_top:c,setting_status_public:f}],placesCities:[{id:821,id_image_cover:a,importance:d,slug:"bakau",type_place:e,name:"Bakau"},{id:l,id_image_cover:a,importance:d,slug:B,type_place:e,name:C},{id:827,id_image_cover:a,importance:d,slug:"bansang",type_place:e,name:"Bansang"},{id:826,id_image_cover:a,importance:d,slug:"barra",type_place:e,name:"Barra"},{id:820,id_image_cover:a,importance:d,slug:"brikama",type_place:e,name:"Brikama"},{id:822,id_image_cover:a,importance:d,slug:"farafenni",type_place:e,name:"Farafenni"},{id:825,id_image_cover:a,importance:d,slug:"gunjur",type_place:e,name:"Gunjur"},{id:823,id_image_cover:a,importance:d,slug:"lamin",type_place:e,name:"Lamin"},{id:819,id_image_cover:a,importance:d,slug:"serekunda",type_place:e,name:"Serekunda"},{id:824,id_image_cover:a,importance:d,slug:"sukuta",type_place:e,name:"Sukuta"}],imagesCities:a,placeCityMain:[{id:l,id_state:t,name:C,population:54646,area:12.5,altitude:9,slug:B,information_chatgpt:a,coordinates:[{latitude:13.4552,longitude:-16.5814}],biggest:"yes",type_place:e,id_image_cover:a,id_image_hero:a,affiliate:[{name:r,value:c}],alerts:a,zoom:[{google:i,booking:11}],parking:a,information_author:a,importance:d,seo_tags:a,created_at:"2023-12-27T16:36:26.534285+00:00",updated_at:"2024-03-04T19:48:29.85915+00:00",name_original:a,setting_top:c,setting_status_public:f}],imageCityMain:a}],fetch:{"OrganismsoSidebarEventComponent:0":{events:[],images:a}},mutations:[]}}(null,"05-2023",true,"biggest","city",1,0,5,10,"state",252,818,"gambie",20,"Není k dispozici","","116","booking",205,85,"\u002Fworld\u002Fstates\u002F","pixabay.com","2023-12-27T20:42:18.520959+00:00",354,"senegal","2024-03-01","1","banjul","Banjul")));