__NUXT_JSONP__("/svet/stat/botswana/co-videt", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q){return {data:[{tabsLoad:{tabWhatToSee:c,tabPrice:c,tabPeople:c,tabTrip:c,tabContact:c,tabHotel:c,tabNeighboring:4,tabVideos:e,tabArticles:e,tabWallSockets:k,tabChains:i,tabFauna:e,tabFlora:e,tabFoods:e,tabBrands:e},place:[{tld:".bw",name:"Botswana",area:581730,population:2351627,slug:l,id_continent:f,information_chatgpt:"\u003Cp\u003EBotswana je vnitrozemský stát v jižní Africe, který je proslulý svými přírodními krásami, jako jsou delty řek a rozlehlé plochy divoké zvěře. Je to jeden z nejstabilnějších a nejrychleji se rozvíjejících států v Africe, s jedním z nejvyšších HDP na kontinentu.\u003C\u002Fp\u003E\n\u003Cp\u003EZemě se pyšní řadou chráněných oblastí a národních parků, včetně slavného Okavango Delta, které je domovem mnoha druhů zvířat, jako jsou sloni, lvi, gepardi a krokodýli. Kromě safari a pozorování zvěře, turisté si mohou užít i další zážitky, jako jsou jízdy na kanoe a pěší túry v divočině.\u003C\u002Fp\u003E\n\u003Cp\u003EBotswana má bohatou kulturu a tradice, zahrnující tance a zpěvy, řemesla a jídlo. Kromě toho je Botswana také známá pro své diamanty, což byl v minulosti hlavní zdroj příjmů pro ekonomiku země. Dnes se země snaží diverzifikovat svou ekonomiku a využívat své přírodní zdroje, jako jsou uhlí, měď a zlato.\u003C\u002Fp\u003E\n\u003Cp\u003EBotswana se pyšní také tím, že je jedním z nejvíce demokratických a progresivních států v Africe, což přispívá k jeho rostoucímu vlivu a významu v regionu.\u003C\u002Fp\u003E",money_prices:[{name:"Taxi (cena za km)",value:10,date_update:b},{name:"Hlavní jídlo",value:80,date_update:b},{name:"Chleba",value:g,date_update:b},{name:"Mléko",value:8,date_update:b},{name:"Voda (500ml)",value:f,date_update:b},{name:"Espresso",value:15,date_update:b},{name:"Zmrzlina (jeden kopeček)",value:g,date_update:b},{name:"Pivo",value:18,date_update:b},{name:"Krabička cigaret",value:45,date_update:b},{name:"Big Mac",value:50,date_update:b},{name:"Coca Cola (500ml)",value:g,date_update:b}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:m,date_update:h},{name:"Policie",value:m,date_update:h},{name:"Hasiči",value:"998",date_update:h},{name:"Zdravotnická záchranná služba",value:"997",date_update:h}],phone_prefix:"+267",coordinates:[{latitude:"-22.3285",longitude:"24.6849"}],people_religion:[{name:"Křesťanství",value:"70.6%"},{name:"Náboženství tradičních afrických kmenů",value:"6.5%"},{name:"Islám",value:"1.9%"},{name:"Bez vyznání",value:"20.6%"},{name:"Jiné náboženství",value:"0.4%"}],people_nationality:[{name:"Tswana",value:"79.1%"},{name:"Kalanga",value:"11.0%"},{name:"Basarwa",value:"3.0%"},{name:"Kgalagadi",value:"2.8%"},{name:"White Botswana",value:"1.0%"},{name:"Other",value:"3.1%"}],visitors_entry:[{name:"Občanský průkaz",value:"Pro vstup do Botswany je vyžadován cestovní pas platný minimálně 6 měsíců od data vstupu do země. Občanský průkaz není akceptován jako platný cestovní dokument.",date_update:d},{name:"Cestovní pas",value:"Pro vstup do Botswany je vyžadován cestovní pas platný minimálně 6 měsíců od data vstupu do země.",date_update:d},{name:"Vízum",value:"Pro občany ČR není vstupní vízum do Botswany vyžadováno na pobyt do 90 dnů. Pokud bude pobyt delší než 90 dnů, je nutné požádat o vízum předem v botswanské ambasádě.",date_update:d},{name:"Očkování",value:"Při vstupu do Botswany není povinné očkování proti žluté zimnici. Nicméně, pokud přijíždíte ze země, kde je žlutá zimnice endemická, musíte předložit potvrzení o očkování proti této nemoci. Dále se doporučuje očkování proti hepatitidě A a B a dalším nemocem jako jsou tyfus, tetanus a obrna.",date_update:d},{name:"Cestovní pojištění",value:"Cestovní pojištění není povinné, ale doporučuje se mít ho sjednané kvůli případným zdravotním potížím nebo nehodám. Je také vhodné mít sjednané pojištění pro případ krádeže či ztráty cenných věcí.",date_update:d},{name:"Bezpečnostní situace",value:"Botswana je obecně považována za bezpečnou zemi s nízkou kriminalitou. Nicméně je třeba být opatrný v některých částech země, zejména v blízkosti hranic s Namibií, Zimbabwe a Jihoafrickou republikou.",date_update:d},{name:"Další informace",value:"Při cestování do Botswany je vhodné mít s sebou dostatek hotovosti, zejména pokud plánujete cestovat mimo větší města. V národních parcích a rezervacích je povoleno pouze řídit na silnicích a stezkách, nesmí se jezdit mimo značené cesty. Pokud plánujete cestovat mimo hlavní turistické sezóny, je vhodné zkontrolovat provozuschopnost silnic, zejména v případě dešťů, které mohou způsobit záplavy a zhoršit stav cest.",date_update:d}],type_place:n,id_image_cover:j,id_image_hero:j,id_city_main:321,affiliate:[{name:"booking",value:c}],mpz:"BW",alerts:a,zoom:[{google:f,booking:g}],ids_neighboring_countries:[{id:248},{id:249},{id:247},{id:160}],organization:a,apps:a,links:a,information_author:a,seo_tags:a,language_phrases:a,wall_sockets:a,id:35,facts_place:a,created_at:"2023-12-27T16:33:10.517062+00:00",updated_at:"2024-03-04T19:48:12.84066+00:00",name_original:a,currency:[{code:"BWP",name:"Botswanská pula"}],setting_top:c,setting_status_public:i}],imagePlace:[{id:j,name:l,source:"\u002Fworld\u002Fstates\u002F",type:n,author:"pixabay.com",created_at:"2023-05-02T18:49:47.851894+00:00",updated_at:"2023-12-27T20:42:18.520959+00:00"}],placeContinent:[{id:f,slug:"afrika",name:"Afrika",area:30370000,population:1340000000,population_density:44.12,number_states:54,information_chatgpt:"\u003Cp\u003EAfrika je největší kontinent na Zemi a rozkládá se na ploše přesahující 30 milionů kilometrů čtverečních. Nachází se zde více než 50 zemí a žije zde více než 1,3 miliardy lidí. \u003C\u002Fp\u003E\n\u003Cp\u003EJižní Afrika, Egypt, Maroko a Keňa patří mezi nejoblíbenější turistické cíle v Africe. V Jihoafrické republice se můžete podívat na slavné Krugerův národní park a sledovat zde život zvířat v jejich přirozeném prostředí. Egypt je zase proslulý svými pyramidami, které se nacházejí v Gíze. V Maroku můžete navštívit tradiční trhy, zatímco v Keni se můžete setkat s Masajským kmenem a podívat se na úžasnou přírodu.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má bohatou historii a kulturu. První lidé, kteří se usadili v Africe, byli loveckým a sběračským národem. Poté se v této oblasti objevily první civilizace, jako je Kúšská říše, Egyptská říše nebo Království Aksum. Afrika byla také kolonizována evropskými mocnostmi a toto období mělo značný dopad na dnešní politickou situaci na kontinentu.\u003C\u002Fp\u003E\n\u003Cp\u003EJe důležité zmínit, že Afrika má velké množství jazyků a kultur. Zahrnuje mnoho různých národů a etnických skupin, a to má vliv na různorodost obyvatelstva a kultury. Nejrozšířenějším jazykem je arabština, která se mluví především v severní Africe a v některých částech západní Afriky. Dalšími důležitými jazyky jsou svahilština, francouzština, portugalština, angličtina a mnoho dalších.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má také nádhernou přírodu. Nachází se zde nejvyšší hora světa, Kilimandžáro, která se nachází na území Tanzanie a Keňy. Kromě toho se v Africe nachází také největší pouště na světě, Sahara, a největší prales, Kongský prales. Na tomto kontinentě můžete vidět také mnoho zvířat, jako jsou sloni, lvi, gepardi, zebry a mnoho dalších druhů. V Africe se také nachází několik nejvýznamnějších řek světa, jako je Nil, Kongo, Niger a Zambezi.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je bohužel také známá pro své problémy. Mnoho zemí se potýká s chudobou, nedostatkem potravin a vody, nemocemi a válečnými konflikty. Mnoho afrických zemí závisí na zemědělství a rybolovu, což však často není dostatečně výnosné, aby zabezpečilo potřeby obyvatelstva. Kromě toho jsou některé země v Africe trvale postižené extrémními přírodními katastrofami, jako jsou sucha, povodně a bouře.\u003C\u002Fp\u003E\n\u003Cp\u003ENa druhou stranu se v Africe také nachází mnoho lidí, kteří se snaží změnit situaci a zlepšit životy svých spoluobčanů. Vznikají neziskové organizace, které se snaží pomáhat při řešení těchto problémů a zlepšovat kvalitu života v mnoha afrických zemích. Zároveň se v posledních letech objevují nové možnosti pro ekonomický růst a rozvoj, jako jsou technologické start-upy a zahraniční investice.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika je fascinující kontinent, který nabízí mnoho zážitků a zároveň v sobě ukrývá mnoho výzev. Pokud se rozhodnete navštívit Afriku, můžete se těšit na krásnou přírodu, bohatou kulturu a nezapomenutelné zážitky. Je však také důležité být obezřetný a připravený na místní podmínky, protože některé oblasti mohou být nebezpečné kvůli konfliktům nebo zločinnosti.\u003C\u002Fp\u003E",coordinates:[{latitude:1.650801,longitude:17.371642}],type_place:"continent",id_image_cover:f,id_image_hero:f,zoom:[{google:2,booking:k}],information_author:[{text:"\u003Cp\u003E\u003Cb\u003EAfrika\u003C\u002Fb\u003E, druhý největší a druhý nejlidnatější kontinent na světě, láká cestovatele svou neuvěřitelnou rozmanitostí kultur, přírodními krásami a bohatou historií. Při cestování touto obrovskou plochou se otevírá mozaika různorodých zážitků, které zahrnují odlesky moderního městského života, divokou přírodu až po historická místa.\u003C\u002Fp\u003E\u003Cp\u003EJižní Afrika, domov několika národních parků a rezervací, je rájem pro milovníky safari. \u003Cb\u003EKrugerův národní park (Kruger National Park)\u003C\u002Fb\u003E nabízí úžasné setkání s \"Velkým pětkou\" - slony, nosorožci, buvoly, lvy a levharty. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fcape-town\"\u003EKapské Město (Cape Town)\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s ikonickou horou \u003Cb\u003EMaclear's Beacon\u003C\u002Fb\u003E, poskytuje nezapomenutelný výhled na město a okolní krajinu.\u003C\u002Fp\u003E\u003Cp\u003EVýchodní Afrika je známá svými ohromujícími sopkami. \u003Cb\u003EKilimandžáro\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E, nejvyšší hora Afriky, přitahuje dobrodruhy a horolezce z celého světa. Návštěva \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003EZanzibaru\u003C\u002Fa\u003E\u003C\u002Fb\u003E s jeho bílými plážemi a historickým centrem \u003Cb\u003EStone Town\u003C\u002Fb\u003E přidává do cestovatelského mixu kouzlo exotiky.\u003C\u002Fp\u003E\u003Cp\u003EStřední Afrika, s obrovskou rovníkovou pralesní plochou, skrývá některé z nejzajímavějších biodiverzit na světě. \u003Cb\u003EKonžský deštný prales\u003C\u002Fb\u003E je po amazonském druhý největší na světě, je domovem vzácných druhů zvířat a rostlin, a expedice sem mohou odhalit tajemství dosud neobjevená.\u003C\u002Fp\u003E\u003Cp\u003ESeverní Afrika je poznamenána starověkými civilizacemi a písečnými pouštěmi. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fegypt\"\u003EEgypt\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pyramidami a chrámy na březích \u003Cb\u003ENilu\u003C\u002Fb\u003E je jednou z nejnavštěvovanějších destinací v této části kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmaroko\"\u003EMaroko\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s mistrovským spojením tradice a moderny v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fmarrakes\"\u003EMarrákeši\u003C\u002Fa\u003E\u003C\u002Fb\u003E, je fascinujícím mixem barev, vůní a architektury.\u003C\u002Fp\u003E\u003Cp\u003EZápadní Afrika, bohatá na kulturní dědictví, nabízí autentické zážitky. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fghana\"\u003EGhana\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pevnostmi, které připomínají období otroctví, a kultovními festivaly představuje hluboký pohled do minulosti. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fsenegal\"\u003ESenegal\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s historickým ostrovem \u003Cb\u003EGorée\u003C\u002Fb\u003E, který sehral roli s obchodem s otroky, kde najdete koloniální budovy a muzeum \u003Cb\u003EHouse of Slaves (Maison des esclaves)\u003C\u002Fb\u003E.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je rovněž známá svými divokými zvířaty. Kromě safari v jižní části kontinentu můžete zažít jedinečnou příležitost sledovat migraci tisíců zeber a gazel v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fkena\"\u003EKeni\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E. Návštěva gorilí v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Frwanda\"\u003ERwandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fuganda\"\u003EUgandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E je neopakovatelným zážitkem pro ty, kteří touží po setkání s těmito ohroženými tvory.\u003C\u002Fp\u003E\u003Cp\u003EPřírodní skvosty nejsou omezeny pouze na pevninskou část kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fseychely\"\u003ESeychely\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMauricius\u003C\u002Fa\u003E\u003C\u002Fb\u003E, dvě ostrůvky v \u003Cb\u003EIndickém oceánu\u003C\u002Fb\u003E, lákají svými bílými plážemi, průzračnými vodami a jedinečnou faunou a florou.\u003C\u002Fp\u003E\u003Cp\u003EAfrika rovněž hýří kulturou a historií. Starověké památky jako \u003Cb\u003EGreat Zimbabwe National Monument\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fzimbabwe\"\u003EZimbabwe\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003ETimbuktu (Tombouctou)\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMali\u003C\u002Fa\u003E\u003C\u002Fb\u003E jsou svědectvím o bohaté historii a rozvoji obchodu a kultury na africkém kontinentu. Každý region má své vlastní tradiční tance, hudbu a umění, které odrážejí místní identitu a historii.\u003C\u002Fp\u003E\u003Cp\u003ECestování Afrikou je také o interakci s místními lidmi. Přívětivost a pohostinnost afrických komunit jsou nezapomenutelnými součástmi cestování. Místní trhy nabízejí pestrou škálu barev, vůní a chutí, zatímco setkání s kmeny a etnickými skupinami přináší pochopení různorodosti kultur a tradic.\u003C\u002Fp\u003E\u003Cp\u003EJe však důležité si být vědomý sociálních, ekonomických a environmentálních výzev, kterým mnohé africké země čelí. Cestovatelé by měli být respektující vůči místním zvyklostem a životnímu prostředí a přispívat k udržitelnému turismu, který podporuje místní ekonomiky a ochranu přírodních zdrojů.\u003C\u002Fp\u003E\u003Cp\u003ECelkově lze říci, že cestování po Africe je dobrodružstvím naplněným kontrasty a překvapeními. Od rozlehlých savan po exotické pláže, od starověkých ruin po moderní metropole. Afrika nabízí cestovatelům nekonečné možnosti objevování a poznávání. Je to kontinent, který zanechává nezapomenutelný dojem na každém, kdo se odhodlá k jeho prozkoumání.\u003C\u002Fp\u003E",date_create:o,date_update:o,author_create:p,author_update:p}],seo_tags:[],created_at:"2023-12-27T16:35:37.460505+00:00",updated_at:"2024-03-04T19:48:06.634661+00:00",name_original:"Africa",setting_top:c,setting_status_public:i}]}],fetch:{"OrganismsoPlaceTeaserSpotsComponent:0":{spots:[],images:a},"OrganismsoPlaceTeaserRegionsComponent:0":{regions:[],images:a},"OrganismsoPlaceTeaserCitiesComponent:0":{_error:{type:"system",errno:q,code:q,erroredSysCall:"write",message:"request to https:\u002F\u002Fapi.frytolnacestach.cz\u002Fapi\u002Fplaces-cities-id-state\u002F35 failed, reason: write EPROTO 4602607104:error:1408F10B:SSL routines:ssl3_get_record:wrong version number:..\u002Fdeps\u002Fopenssl\u002Fopenssl\u002Fssl\u002Frecord\u002Fssl3_record.c:332:\n",statusCode:500}},"OrganismsoSidebarEventComponent:0":{events:[],images:a}},mutations:[]}}(null,"04-2021",true,"04-2023",0,5,6,"05-2023",1,225,3,"botswana","999","state","2024-03-01","1","EPROTO")));