__NUXT_JSONP__("/svet/mesto/mutare", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w){return {data:[{place:[{id:m,id_state:n,name:o,population:188243,area:23.4,altitude:1079,slug:p,information_chatgpt:a,coordinates:[{latitude:-18.9707,longitude:32.6589}],biggest:"yes",type_place:c,id_image_cover:a,id_image_hero:a,affiliate:[{name:q,value:g}],alerts:a,zoom:[{google:10,booking:11}],parking:a,information_author:a,importance:d,seo_tags:a,created_at:"2023-12-27T16:36:26.534285+00:00",updated_at:"2024-03-04T19:48:29.85915+00:00",name_original:a,setting_top:g,setting_status_public:e}],imagePlace:[],placeState:[{tld:".zw",name:"Zimbabwe",area:390757,population:14899771,slug:r,id_continent:h,information_chatgpt:"\u003Cp\u003EZimbabwe je vnitrozemský stát v jižní Africe s bohatou historií a kulturou. Hlavním městem je Harare a největším městem je Bulawayo. Zimbabwe má úžasnou přírodní krásu, včetně tří národních parků, kde lze pozorovat zvířata jako jsou sloni, lvi, nosorožci a další.\u003C\u002Fp\u003E\n\u003Cp\u003EZimbabwe má také dlouhou historii a bohatou kulturu, což se odráží v jeho mnoha historických památkách a artefaktech. K nejznámějším patří Velké Zimbabwe, rozsáhlý komplex z kamene, který sloužil jako hlavní město středověké říše. Dalšími zajímavými památkami jsou například Khami Ruins a Matobo Hills, které jsou zapsány na seznamu světového dědictví UNESCO.\u003C\u002Fp\u003E\n\u003Cp\u003EV současné době je Zimbabwe trvale obýváno asi 14 miliony lidí a převažující většinu z nich tvoří domorodé kmeny, jako jsou Shona, Ndebele a Tonga. Oficiálním jazykem je angličtina, ale mluví se zde také několika dalšími jazyky.\u003C\u002Fp\u003E\n\u003Cp\u003EZimbabwe má také bohatou a rozmanitou kuchyni, která je ovlivněna mnoha kulturami, včetně afrických, evropských a asijských. Mezi nejznámější pokrmy patří sadza, která se podává s masovým nebo zeleninovým ragú, a biltong, sušené maso, které se konzumuje jako snack.\u003C\u002Fp\u003E\n\u003Cp\u003EZimbabwe má spoustu co nabídnout návštěvníkům, od krásné přírody a historických památek po bohatou kulturu a kuchyni. Pokud tedy hledáte exotickou destinaci pro své další dovolenou, Zimbabwe může být tou pravou volbou.\u003C\u002Fp\u003E",money_prices:[{name:"Taxi (cena za km)",value:k,date_update:b},{name:"Hlavní jídlo",value:h,date_update:b},{name:"Chleba",value:k,date_update:b},{name:"Mléko",value:e,date_update:b},{name:"Voda (500ml)",value:k,date_update:b},{name:"Espresso",value:e,date_update:b},{name:"Zmrzlina (jeden kopeček)",value:e,date_update:b},{name:"Pivo",value:1.5,date_update:b},{name:"Krabička cigaret",value:3.5,date_update:b},{name:"Big Mac",value:4,date_update:b},{name:"Coca Cola (500ml)",value:e,date_update:b}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:"112",date_update:b},{name:"Policie",value:"995",date_update:b},{name:"Hasiči",value:"993",date_update:b},{name:"Zdravotnická záchranná služba",value:"994",date_update:b}],phone_prefix:"+263",coordinates:[{latitude:"-19.0154",longitude:"29.1549"}],people_religion:[{name:"Křesťanství",value:"85%",date_update:i},{name:"Tradiční africká náboženství",value:"10%",date_update:i},{name:"Islám",value:j,date_update:i},{name:"Jiná náboženství",value:j,date_update:i},{name:"Bez vyznání",value:"3%",date_update:i}],people_nationality:[{name:"Shona",value:"82%",date_update:f},{name:"Ndebele",value:"14%",date_update:f},{name:"Kalanga",value:"2%",date_update:f},{name:"Nambya",value:j,date_update:f},{name:"Tonga",value:j,date_update:f},{name:"Ostatní",value:j,date_update:f}],visitors_entry:[{name:"Občanský průkaz",value:"Pro vstup do Zimbabwe není pro občany České republiky dostatečný občanský průkaz, je potřeba cestovní pas.",date_update:b},{name:"Cestovní pas",value:"Pro vstup do Zimbabwe je potřeba cestovní pas pro občany České republiky.",date_update:b},{name:"Vízum",value:"Pro vstup do Zimbabwe je potřeba vízum pro občany České republiky. Vízum lze získat buď při příjezdu do Zimbabwe, nebo předem vyzkoušet v ambasádě Zimbabwe v Praze.",date_update:b},{name:"Očkování",value:"Pro vstup do Zimbabwe není povinné očkování, nicméně jsou doporučována očkování proti hepatitidě A a B, žluté zimnici a břišnímu tyfu. Doporučuje se také vakcinace proti COVID-19.",date_update:b},{name:"Cestovní pojištění",value:"Pro vstup do Zimbabwe není nutné mít cestovní ani zdravotní pojištění. Nicméně doporučuje se mít sjednané zdravotní pojištění, neboť lékařská péče v Zimbabwe může být velmi nákladná.",date_update:b},{name:"Bezpečnostní situace",value:"Bezpečnostní situace v Zimbabwe se zlepšila v posledních letech. Nicméně doporučuje se být obezřetný a vyhnout se nebezpečným oblastem, zejména v nočních hodinách. V případě pochybností je vhodné kontaktovat místní policii.",date_update:b},{name:"Další informace",value:"Při cestování do Zimbabwe je dobré mít s sebou dostatek hotovosti v místní měně (USD), protože kreditní karty nejsou všude přijímány. Je také důležité si dát pozor na místní zvyklosti a kulturu, například se vyvarovat nošení drahých šperků a hodinek na veřejnosti. Při návštěvě národních parků je nutné dodržovat pravidla a instrukce průvodců.",date_update:b}],type_place:s,id_image_cover:l,id_image_hero:l,id_city_main:t,affiliate:[{name:q,value:g}],mpz:"ZW",alerts:a,zoom:[{google:u,booking:u}],ids_neighboring_countries:[{id:35},{id:159},{id:247},{id:248}],organization:a,apps:a,links:a,information_author:a,seo_tags:a,language_phrases:a,wall_sockets:a,id:n,facts_place:a,created_at:"2023-12-27T16:33:10.517062+00:00",updated_at:"2024-03-04T19:48:12.84066+00:00",name_original:a,currency:[{code:"ZWL",name:"Zimbabwský dolar"}],setting_top:g,setting_status_public:e}],imageState:[{id:l,name:r,source:"\u002Fworld\u002Fstates\u002F",type:s,author:"pixabay.com",created_at:"2023-05-04T07:17:51.463854+00:00",updated_at:"2023-12-27T20:42:18.520959+00:00"}],placeContinent:[{id:h,slug:"afrika",name:"Afrika",area:30370000,population:1340000000,population_density:44.12,number_states:54,information_chatgpt:"\u003Cp\u003EAfrika je největší kontinent na Zemi a rozkládá se na ploše přesahující 30 milionů kilometrů čtverečních. Nachází se zde více než 50 zemí a žije zde více než 1,3 miliardy lidí. \u003C\u002Fp\u003E\n\u003Cp\u003EJižní Afrika, Egypt, Maroko a Keňa patří mezi nejoblíbenější turistické cíle v Africe. V Jihoafrické republice se můžete podívat na slavné Krugerův národní park a sledovat zde život zvířat v jejich přirozeném prostředí. Egypt je zase proslulý svými pyramidami, které se nacházejí v Gíze. V Maroku můžete navštívit tradiční trhy, zatímco v Keni se můžete setkat s Masajským kmenem a podívat se na úžasnou přírodu.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má bohatou historii a kulturu. První lidé, kteří se usadili v Africe, byli loveckým a sběračským národem. Poté se v této oblasti objevily první civilizace, jako je Kúšská říše, Egyptská říše nebo Království Aksum. Afrika byla také kolonizována evropskými mocnostmi a toto období mělo značný dopad na dnešní politickou situaci na kontinentu.\u003C\u002Fp\u003E\n\u003Cp\u003EJe důležité zmínit, že Afrika má velké množství jazyků a kultur. Zahrnuje mnoho různých národů a etnických skupin, a to má vliv na různorodost obyvatelstva a kultury. Nejrozšířenějším jazykem je arabština, která se mluví především v severní Africe a v některých částech západní Afriky. Dalšími důležitými jazyky jsou svahilština, francouzština, portugalština, angličtina a mnoho dalších.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má také nádhernou přírodu. Nachází se zde nejvyšší hora světa, Kilimandžáro, která se nachází na území Tanzanie a Keňy. Kromě toho se v Africe nachází také největší pouště na světě, Sahara, a největší prales, Kongský prales. Na tomto kontinentě můžete vidět také mnoho zvířat, jako jsou sloni, lvi, gepardi, zebry a mnoho dalších druhů. V Africe se také nachází několik nejvýznamnějších řek světa, jako je Nil, Kongo, Niger a Zambezi.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je bohužel také známá pro své problémy. Mnoho zemí se potýká s chudobou, nedostatkem potravin a vody, nemocemi a válečnými konflikty. Mnoho afrických zemí závisí na zemědělství a rybolovu, což však často není dostatečně výnosné, aby zabezpečilo potřeby obyvatelstva. Kromě toho jsou některé země v Africe trvale postižené extrémními přírodními katastrofami, jako jsou sucha, povodně a bouře.\u003C\u002Fp\u003E\n\u003Cp\u003ENa druhou stranu se v Africe také nachází mnoho lidí, kteří se snaží změnit situaci a zlepšit životy svých spoluobčanů. Vznikají neziskové organizace, které se snaží pomáhat při řešení těchto problémů a zlepšovat kvalitu života v mnoha afrických zemích. Zároveň se v posledních letech objevují nové možnosti pro ekonomický růst a rozvoj, jako jsou technologické start-upy a zahraniční investice.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika je fascinující kontinent, který nabízí mnoho zážitků a zároveň v sobě ukrývá mnoho výzev. Pokud se rozhodnete navštívit Afriku, můžete se těšit na krásnou přírodu, bohatou kulturu a nezapomenutelné zážitky. Je však také důležité být obezřetný a připravený na místní podmínky, protože některé oblasti mohou být nebezpečné kvůli konfliktům nebo zločinnosti.\u003C\u002Fp\u003E",coordinates:[{latitude:1.650801,longitude:17.371642}],type_place:"continent",id_image_cover:h,id_image_hero:h,zoom:[{google:2,booking:3}],information_author:[{text:"\u003Cp\u003E\u003Cb\u003EAfrika\u003C\u002Fb\u003E, druhý největší a druhý nejlidnatější kontinent na světě, láká cestovatele svou neuvěřitelnou rozmanitostí kultur, přírodními krásami a bohatou historií. Při cestování touto obrovskou plochou se otevírá mozaika různorodých zážitků, které zahrnují odlesky moderního městského života, divokou přírodu až po historická místa.\u003C\u002Fp\u003E\u003Cp\u003EJižní Afrika, domov několika národních parků a rezervací, je rájem pro milovníky safari. \u003Cb\u003EKrugerův národní park (Kruger National Park)\u003C\u002Fb\u003E nabízí úžasné setkání s \"Velkým pětkou\" - slony, nosorožci, buvoly, lvy a levharty. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fcape-town\"\u003EKapské Město (Cape Town)\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s ikonickou horou \u003Cb\u003EMaclear's Beacon\u003C\u002Fb\u003E, poskytuje nezapomenutelný výhled na město a okolní krajinu.\u003C\u002Fp\u003E\u003Cp\u003EVýchodní Afrika je známá svými ohromujícími sopkami. \u003Cb\u003EKilimandžáro\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E, nejvyšší hora Afriky, přitahuje dobrodruhy a horolezce z celého světa. Návštěva \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003EZanzibaru\u003C\u002Fa\u003E\u003C\u002Fb\u003E s jeho bílými plážemi a historickým centrem \u003Cb\u003EStone Town\u003C\u002Fb\u003E přidává do cestovatelského mixu kouzlo exotiky.\u003C\u002Fp\u003E\u003Cp\u003EStřední Afrika, s obrovskou rovníkovou pralesní plochou, skrývá některé z nejzajímavějších biodiverzit na světě. \u003Cb\u003EKonžský deštný prales\u003C\u002Fb\u003E je po amazonském druhý největší na světě, je domovem vzácných druhů zvířat a rostlin, a expedice sem mohou odhalit tajemství dosud neobjevená.\u003C\u002Fp\u003E\u003Cp\u003ESeverní Afrika je poznamenána starověkými civilizacemi a písečnými pouštěmi. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fegypt\"\u003EEgypt\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pyramidami a chrámy na březích \u003Cb\u003ENilu\u003C\u002Fb\u003E je jednou z nejnavštěvovanějších destinací v této části kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmaroko\"\u003EMaroko\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s mistrovským spojením tradice a moderny v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fmarrakes\"\u003EMarrákeši\u003C\u002Fa\u003E\u003C\u002Fb\u003E, je fascinujícím mixem barev, vůní a architektury.\u003C\u002Fp\u003E\u003Cp\u003EZápadní Afrika, bohatá na kulturní dědictví, nabízí autentické zážitky. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fghana\"\u003EGhana\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pevnostmi, které připomínají období otroctví, a kultovními festivaly představuje hluboký pohled do minulosti. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fsenegal\"\u003ESenegal\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s historickým ostrovem \u003Cb\u003EGorée\u003C\u002Fb\u003E, který sehral roli s obchodem s otroky, kde najdete koloniální budovy a muzeum \u003Cb\u003EHouse of Slaves (Maison des esclaves)\u003C\u002Fb\u003E.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je rovněž známá svými divokými zvířaty. Kromě safari v jižní části kontinentu můžete zažít jedinečnou příležitost sledovat migraci tisíců zeber a gazel v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fkena\"\u003EKeni\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E. Návštěva gorilí v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Frwanda\"\u003ERwandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fuganda\"\u003EUgandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E je neopakovatelným zážitkem pro ty, kteří touží po setkání s těmito ohroženými tvory.\u003C\u002Fp\u003E\u003Cp\u003EPřírodní skvosty nejsou omezeny pouze na pevninskou část kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fseychely\"\u003ESeychely\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMauricius\u003C\u002Fa\u003E\u003C\u002Fb\u003E, dvě ostrůvky v \u003Cb\u003EIndickém oceánu\u003C\u002Fb\u003E, lákají svými bílými plážemi, průzračnými vodami a jedinečnou faunou a florou.\u003C\u002Fp\u003E\u003Cp\u003EAfrika rovněž hýří kulturou a historií. Starověké památky jako \u003Cb\u003EGreat Zimbabwe National Monument\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fzimbabwe\"\u003EZimbabwe\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003ETimbuktu (Tombouctou)\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMali\u003C\u002Fa\u003E\u003C\u002Fb\u003E jsou svědectvím o bohaté historii a rozvoji obchodu a kultury na africkém kontinentu. Každý region má své vlastní tradiční tance, hudbu a umění, které odrážejí místní identitu a historii.\u003C\u002Fp\u003E\u003Cp\u003ECestování Afrikou je také o interakci s místními lidmi. Přívětivost a pohostinnost afrických komunit jsou nezapomenutelnými součástmi cestování. Místní trhy nabízejí pestrou škálu barev, vůní a chutí, zatímco setkání s kmeny a etnickými skupinami přináší pochopení různorodosti kultur a tradic.\u003C\u002Fp\u003E\u003Cp\u003EJe však důležité si být vědomý sociálních, ekonomických a environmentálních výzev, kterým mnohé africké země čelí. Cestovatelé by měli být respektující vůči místním zvyklostem a životnímu prostředí a přispívat k udržitelnému turismu, který podporuje místní ekonomiky a ochranu přírodních zdrojů.\u003C\u002Fp\u003E\u003Cp\u003ECelkově lze říci, že cestování po Africe je dobrodružstvím naplněným kontrasty a překvapeními. Od rozlehlých savan po exotické pláže, od starověkých ruin po moderní metropole. Afrika nabízí cestovatelům nekonečné možnosti objevování a poznávání. Je to kontinent, který zanechává nezapomenutelný dojem na každém, kdo se odhodlá k jeho prozkoumání.\u003C\u002Fp\u003E",date_create:v,date_update:v,author_create:w,author_update:w}],seo_tags:[],created_at:"2023-12-27T16:35:37.460505+00:00",updated_at:"2024-03-04T19:48:06.634661+00:00",name_original:"Africa",setting_top:g,setting_status_public:e}],placesCities:[{id:2455,id_image_cover:a,importance:d,slug:"bulawayo",type_place:c,name:"Bulawayo"},{id:2463,id_image_cover:a,importance:d,slug:"chinhoyi",type_place:c,name:"Chinhoyi"},{id:2456,id_image_cover:a,importance:d,slug:"chitungwiza",type_place:c,name:"Chitungwiza"},{id:2459,id_image_cover:a,importance:d,slug:"epworth",type_place:c,name:"Epworth"},{id:2458,id_image_cover:a,importance:d,slug:"gweru",type_place:c,name:"Gweru"},{id:t,id_image_cover:a,importance:d,slug:"harare",type_place:c,name:"Harare"},{id:2461,id_image_cover:a,importance:d,slug:"kadoma",type_place:c,name:"Kadoma"},{id:2460,id_image_cover:a,importance:d,slug:"kwekwe",type_place:c,name:"Kwekwe"},{id:2462,id_image_cover:a,importance:d,slug:"masvingo",type_place:c,name:"Masvingo"},{id:m,id_image_cover:a,importance:d,slug:p,type_place:c,name:o}],imagesCities:a}],fetch:{"OrganismsoPlaceTeaserSpotsComponent:0":{spots:[],images:a},"OrganismsoSidebarEventComponent:0":{events:[],images:a}},mutations:[]}}(null,"05-2023","city","biggest",1,"12-2012",true,5,"12-2020","1%",.5,392,2457,249,"Mutare","mutare","booking","zimbabwe","state",2454,6,"2024-03-01","1")));