__NUXT_JSONP__("/svet/stat/etiopie/flora", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o){return {data:[{tabsLoad:{tabWhatToSee:c,tabPrice:c,tabPeople:c,tabTrip:c,tabContact:c,tabHotel:c,tabNeighboring:f,tabVideos:d,tabArticles:d,tabWallSockets:4,tabChains:d,tabFauna:h,tabFlora:d,tabFoods:d,tabBrands:d},place:[{tld:".et",name:"Etiopie",area:1104300,population:117876000,slug:j,id_continent:e,information_chatgpt:"\u003Cp\u003EEtiopie je země v Africe, která nabízí bohatou historii, kulturu a přírodní krásy. S rozlohou přes 1,1 milionu km² je Etiopie druhou nejlidnatější zemí na africkém kontinentě a má mnoho různých regionů a etnických skupin, které se liší jazykem, kulturou a tradicemi.\u003C\u002Fp\u003E\n\u003Cp\u003EHlavním městem Etiopie je Addis Abeba, která je kulturním a hospodářským centrem země. Mezi další populární města patří například Lalibela s unikátními skalními kostely, Gondar s historickými paláci a pevnostmi a Bahir Dar s nádhernými vodopády a jezerem Tana, které je domovem mnoha klášterů a chrámů.\u003C\u002Fp\u003E\n\u003Cp\u003EEtiopie je také známá svým kávovým průmyslem a je považována za kolébku kávy. Navštívit můžete mnoho kávových farem a ochutnat zdejší kávu, která má unikátní chuťové vlastnosti. Etiopie je také domovem mnoha etnických skupin a kultury, které jsou odlišné od ostatních afrických zemí. Můžete se setkat s lidmi Oromo, Amhara, Tigray, Somálska a dalších, kteří mají své vlastní tradice a zvyklosti.\u003C\u002Fp\u003E\n\u003Cp\u003EEtiopie má také mnoho přírodních krás, jako jsou například Simien Mountains s horskými vrcholy a endemickými druhy zvířat, jako jsou gelada, ibex a volavky. Dalšími krásnými přírodními destinacemi jsou například Danakil Depression s lávovými poli a solnými jezery, Bale Mountains s divokou přírodou a původními etnickými skupinami a Národní park Awash s řekou a vodopády.\u003C\u002Fp\u003E\n\u003Cp\u003ENavštívit Etiopii můžete kdykoliv během roku, ale nejlepší období pro návštěvu závisí na vašich zájmech a plánech. Letní měsíce jsou ideální pro návštěvu hor a přírody, zatímco zimní měsíce nabízejí příležitost k ochutnání tradičních etiopských jídel a prozkoumání měst a kulturních památek.\u003C\u002Fp\u003E",money_prices:[{name:"Taxi (cena za km)",value:13.56,date_update:a},{name:"Hlavní jídlo",value:50,date_update:a},{name:"Chleba",value:f,date_update:a},{name:"Mléko",value:22,date_update:a},{name:"Voda (500ml)",value:e,date_update:a},{name:"Espresso",value:9,date_update:a},{name:"Zmrzlina (jeden kopeček)",value:10,date_update:a},{name:"Pivo",value:k,date_update:a},{name:"Krabička cigaret",value:k,date_update:a},{name:"Bic Mac",value:g,date_update:a},{name:"Coca Cola (500ml)",value:f,date_update:a}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:"112",date_update:b},{name:"Policie",value:"911",date_update:b},{name:"Hasiči",value:"939",date_update:b},{name:"Zdravotnická záchranná služba",value:"907",date_update:b}],phone_prefix:"+251",coordinates:[{latitude:"9.1450",longitude:"40.4897"}],people_religion:[{name:"Etiopská pravoslavná církev",value:"43.5%"},{name:"Muslimové",value:"33.9%"},{name:"Katolická církev",value:"0.7%"},{name:"Protestanti",value:"18.6%"},{name:"Jiná náboženství",value:"2.2%"},{name:"Bez vyznání",value:"1.1%"}],people_nationality:[{name:"Oromové",value:"34.4%"},{name:"Amharové",value:"27.0%"},{name:"Somálci",value:"6.2%"},{name:"Tigrajové",value:"6.1%"},{name:"Sidamové",value:"4.0%"},{name:"Guragové",value:"2.5%"},{name:"Wolaitové",value:"2.3%"},{name:"Hadiyové",value:l},{name:"Afarové",value:l},{name:"Gambelové",value:"1.6%"},{name:"Jemenité",value:"0.9%"},{name:"Ostatní",value:"11.6%"}],visitors_entry:[{name:"Občanský průkaz",value:"Pro vstup do Etiopie není možné použít občanský průkaz. Pro vstup je nutné mít cestovní pas platný minimálně 6 měsíců od data vstupu do země.",date_update:b},{name:"Cestovní pas",value:"Pro vstup do Etiopie je nezbytné mít platný cestovní pas s minimální platností 6 měsíců od data vstupu do země.",date_update:b},{name:"Vízum",value:"Pro vstup do Etiopie je potřeba mít platné vízum. Občané České republiky (EU) mohou vstoupit do Etiopie bez víza na dobu maximálně 90 dnů. Pro delší pobyt nebo v případě jiného účelu cesty je nutné získat vízum. Víza lze získat na velvyslanectví Etiopie v Praze nebo v Addis Abebě na letišti Bole International Airport.",date_update:b},{name:"Očkování",value:"Při vstupu do Etiopie není vyžadováno očkování proti žluté zimnici. Nicméně jsou doporučována očkování proti hepatitidě A a B, tyfu a obrně.",date_update:b},{name:"Cestovní pojištění",value:"Pro vstup do Etiopie není vyžadováno cestovní ani zdravotní pojištění. Nicméně je doporučeno si ho sjednat pro případné lékařské potíže nebo nehody během pobytu.",date_update:b},{name:"Bezpečností situace",value:"Etiopie je v posledních letech obecně považována za bezpečnou zemi, nicméně v některých regionech v zemi stále přetrvávají konflikty a teroristické hrozby. Doporučuje se proto být opatrný a vyhnout se cestování do nebezpečných oblastí. Před cestou do země je vhodné se informovat o aktuální bezpečnostní situaci na Ministerstvu zahraničních věcí ČR nebo na velvyslanectví Etiopie v Praze.",date_update:b},{name:"Další informace",value:"V Etiopii se převážně platí etiopskou měnou birr. V turistických oblastech je možné platit také dolary či eury. Je vhodné mít s sebou hotovost, protože ne všude jsou přijímány platební karty. V zemi se také vyskytuje malárie, takže je vhodné používat repelent a chránit se proti komárům. V Etiopii platí levý provoz a silnice nejsou v nejlepším stavu, takže se doporučuje opatrnost při řízení. V zemi je možné se přesouvat také vlaky nebo autobusy.",date_update:b}],type_place:m,id_image_cover:i,id_image_hero:i,id_city_main:658,affiliate:[{name:"booking",value:c}],mpz:"ET",alerts:[],zoom:[{google:e,booking:f}],ids_neighboring_countries:[{id:58},{id:67},{id:208},{id:115},{id:206},{id:196}],organization:[],apps:[],links:[],information_author:[],seo_tags:[],language_phrases:[],wall_sockets:g,id:69,facts_place:g,created_at:"2023-12-27T16:33:10.517062+00:00",updated_at:"2024-03-04T19:48:12.84066+00:00",name_original:g,currency:[{code:"ETB",name:"etiopský birr"}],setting_top:c,setting_status_public:h}],imagePlace:[{id:i,name:j,source:"\u002Fworld\u002Fstates\u002F",type:m,author:"pixabay.com",created_at:"2023-05-02T18:49:47.851894+00:00",updated_at:"2023-12-27T20:42:18.520959+00:00"}],placeContinent:[{id:e,slug:"afrika",name:"Afrika",area:30370000,population:1340000000,population_density:44.12,number_states:54,information_chatgpt:"\u003Cp\u003EAfrika je největší kontinent na Zemi a rozkládá se na ploše přesahující 30 milionů kilometrů čtverečních. Nachází se zde více než 50 zemí a žije zde více než 1,3 miliardy lidí. \u003C\u002Fp\u003E\n\u003Cp\u003EJižní Afrika, Egypt, Maroko a Keňa patří mezi nejoblíbenější turistické cíle v Africe. V Jihoafrické republice se můžete podívat na slavné Krugerův národní park a sledovat zde život zvířat v jejich přirozeném prostředí. Egypt je zase proslulý svými pyramidami, které se nacházejí v Gíze. V Maroku můžete navštívit tradiční trhy, zatímco v Keni se můžete setkat s Masajským kmenem a podívat se na úžasnou přírodu.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má bohatou historii a kulturu. První lidé, kteří se usadili v Africe, byli loveckým a sběračským národem. Poté se v této oblasti objevily první civilizace, jako je Kúšská říše, Egyptská říše nebo Království Aksum. Afrika byla také kolonizována evropskými mocnostmi a toto období mělo značný dopad na dnešní politickou situaci na kontinentu.\u003C\u002Fp\u003E\n\u003Cp\u003EJe důležité zmínit, že Afrika má velké množství jazyků a kultur. Zahrnuje mnoho různých národů a etnických skupin, a to má vliv na různorodost obyvatelstva a kultury. Nejrozšířenějším jazykem je arabština, která se mluví především v severní Africe a v některých částech západní Afriky. Dalšími důležitými jazyky jsou svahilština, francouzština, portugalština, angličtina a mnoho dalších.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má také nádhernou přírodu. Nachází se zde nejvyšší hora světa, Kilimandžáro, která se nachází na území Tanzanie a Keňy. Kromě toho se v Africe nachází také největší pouště na světě, Sahara, a největší prales, Kongský prales. Na tomto kontinentě můžete vidět také mnoho zvířat, jako jsou sloni, lvi, gepardi, zebry a mnoho dalších druhů. V Africe se také nachází několik nejvýznamnějších řek světa, jako je Nil, Kongo, Niger a Zambezi.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je bohužel také známá pro své problémy. Mnoho zemí se potýká s chudobou, nedostatkem potravin a vody, nemocemi a válečnými konflikty. Mnoho afrických zemí závisí na zemědělství a rybolovu, což však často není dostatečně výnosné, aby zabezpečilo potřeby obyvatelstva. Kromě toho jsou některé země v Africe trvale postižené extrémními přírodními katastrofami, jako jsou sucha, povodně a bouře.\u003C\u002Fp\u003E\n\u003Cp\u003ENa druhou stranu se v Africe také nachází mnoho lidí, kteří se snaží změnit situaci a zlepšit životy svých spoluobčanů. Vznikají neziskové organizace, které se snaží pomáhat při řešení těchto problémů a zlepšovat kvalitu života v mnoha afrických zemích. Zároveň se v posledních letech objevují nové možnosti pro ekonomický růst a rozvoj, jako jsou technologické start-upy a zahraniční investice.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika je fascinující kontinent, který nabízí mnoho zážitků a zároveň v sobě ukrývá mnoho výzev. Pokud se rozhodnete navštívit Afriku, můžete se těšit na krásnou přírodu, bohatou kulturu a nezapomenutelné zážitky. Je však také důležité být obezřetný a připravený na místní podmínky, protože některé oblasti mohou být nebezpečné kvůli konfliktům nebo zločinnosti.\u003C\u002Fp\u003E",coordinates:[{latitude:1.650801,longitude:17.371642}],type_place:"continent",id_image_cover:e,id_image_hero:e,zoom:[{google:2,booking:3}],information_author:[{text:"\u003Cp\u003E\u003Cb\u003EAfrika\u003C\u002Fb\u003E, druhý největší a druhý nejlidnatější kontinent na světě, láká cestovatele svou neuvěřitelnou rozmanitostí kultur, přírodními krásami a bohatou historií. Při cestování touto obrovskou plochou se otevírá mozaika různorodých zážitků, které zahrnují odlesky moderního městského života, divokou přírodu až po historická místa.\u003C\u002Fp\u003E\u003Cp\u003EJižní Afrika, domov několika národních parků a rezervací, je rájem pro milovníky safari. \u003Cb\u003EKrugerův národní park (Kruger National Park)\u003C\u002Fb\u003E nabízí úžasné setkání s \"Velkým pětkou\" - slony, nosorožci, buvoly, lvy a levharty. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fcape-town\"\u003EKapské Město (Cape Town)\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s ikonickou horou \u003Cb\u003EMaclear's Beacon\u003C\u002Fb\u003E, poskytuje nezapomenutelný výhled na město a okolní krajinu.\u003C\u002Fp\u003E\u003Cp\u003EVýchodní Afrika je známá svými ohromujícími sopkami. \u003Cb\u003EKilimandžáro\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E, nejvyšší hora Afriky, přitahuje dobrodruhy a horolezce z celého světa. Návštěva \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003EZanzibaru\u003C\u002Fa\u003E\u003C\u002Fb\u003E s jeho bílými plážemi a historickým centrem \u003Cb\u003EStone Town\u003C\u002Fb\u003E přidává do cestovatelského mixu kouzlo exotiky.\u003C\u002Fp\u003E\u003Cp\u003EStřední Afrika, s obrovskou rovníkovou pralesní plochou, skrývá některé z nejzajímavějších biodiverzit na světě. \u003Cb\u003EKonžský deštný prales\u003C\u002Fb\u003E je po amazonském druhý největší na světě, je domovem vzácných druhů zvířat a rostlin, a expedice sem mohou odhalit tajemství dosud neobjevená.\u003C\u002Fp\u003E\u003Cp\u003ESeverní Afrika je poznamenána starověkými civilizacemi a písečnými pouštěmi. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fegypt\"\u003EEgypt\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pyramidami a chrámy na březích \u003Cb\u003ENilu\u003C\u002Fb\u003E je jednou z nejnavštěvovanějších destinací v této části kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmaroko\"\u003EMaroko\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s mistrovským spojením tradice a moderny v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fmarrakes\"\u003EMarrákeši\u003C\u002Fa\u003E\u003C\u002Fb\u003E, je fascinujícím mixem barev, vůní a architektury.\u003C\u002Fp\u003E\u003Cp\u003EZápadní Afrika, bohatá na kulturní dědictví, nabízí autentické zážitky. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fghana\"\u003EGhana\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pevnostmi, které připomínají období otroctví, a kultovními festivaly představuje hluboký pohled do minulosti. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fsenegal\"\u003ESenegal\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s historickým ostrovem \u003Cb\u003EGorée\u003C\u002Fb\u003E, který sehral roli s obchodem s otroky, kde najdete koloniální budovy a muzeum \u003Cb\u003EHouse of Slaves (Maison des esclaves)\u003C\u002Fb\u003E.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je rovněž známá svými divokými zvířaty. Kromě safari v jižní části kontinentu můžete zažít jedinečnou příležitost sledovat migraci tisíců zeber a gazel v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fkena\"\u003EKeni\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E. Návštěva gorilí v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Frwanda\"\u003ERwandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fuganda\"\u003EUgandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E je neopakovatelným zážitkem pro ty, kteří touží po setkání s těmito ohroženými tvory.\u003C\u002Fp\u003E\u003Cp\u003EPřírodní skvosty nejsou omezeny pouze na pevninskou část kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fseychely\"\u003ESeychely\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMauricius\u003C\u002Fa\u003E\u003C\u002Fb\u003E, dvě ostrůvky v \u003Cb\u003EIndickém oceánu\u003C\u002Fb\u003E, lákají svými bílými plážemi, průzračnými vodami a jedinečnou faunou a florou.\u003C\u002Fp\u003E\u003Cp\u003EAfrika rovněž hýří kulturou a historií. Starověké památky jako \u003Cb\u003EGreat Zimbabwe National Monument\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fzimbabwe\"\u003EZimbabwe\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003ETimbuktu (Tombouctou)\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMali\u003C\u002Fa\u003E\u003C\u002Fb\u003E jsou svědectvím o bohaté historii a rozvoji obchodu a kultury na africkém kontinentu. Každý region má své vlastní tradiční tance, hudbu a umění, které odrážejí místní identitu a historii.\u003C\u002Fp\u003E\u003Cp\u003ECestování Afrikou je také o interakci s místními lidmi. Přívětivost a pohostinnost afrických komunit jsou nezapomenutelnými součástmi cestování. Místní trhy nabízejí pestrou škálu barev, vůní a chutí, zatímco setkání s kmeny a etnickými skupinami přináší pochopení různorodosti kultur a tradic.\u003C\u002Fp\u003E\u003Cp\u003EJe však důležité si být vědomý sociálních, ekonomických a environmentálních výzev, kterým mnohé africké země čelí. Cestovatelé by měli být respektující vůči místním zvyklostem a životnímu prostředí a přispívat k udržitelnému turismu, který podporuje místní ekonomiky a ochranu přírodních zdrojů.\u003C\u002Fp\u003E\u003Cp\u003ECelkově lze říci, že cestování po Africe je dobrodružstvím naplněným kontrasty a překvapeními. Od rozlehlých savan po exotické pláže, od starověkých ruin po moderní metropole. Afrika nabízí cestovatelům nekonečné možnosti objevování a poznávání. Je to kontinent, který zanechává nezapomenutelný dojem na každém, kdo se odhodlá k jeho prozkoumání.\u003C\u002Fp\u003E",date_create:n,date_update:n,author_create:o,author_update:o}],seo_tags:[],created_at:"2023-12-27T16:35:37.460505+00:00",updated_at:"2024-03-04T19:48:06.634661+00:00",name_original:"Africa",setting_top:c,setting_status_public:h}]}],fetch:{},mutations:[]}}("05-2021","05-2023",true,0,5,6,null,1,243,"etiopie",20,"1.7%","state","2024-03-01","1")));