__NUXT_JSONP__("/svet/mesto/zouerate", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w,x){return {data:[{place:[{id:k,id_state:l,name:m,population:27312,area:41.2,altitude:406,slug:n,information_chatgpt:a,coordinates:[{latitude:22.7359,longitude:-12.4833}],biggest:"yes",type_place:b,id_image_cover:a,id_image_hero:a,affiliate:[{name:o,value:f}],alerts:a,zoom:[{google:10,booking:11}],parking:a,information_author:a,importance:c,seo_tags:a,created_at:"2023-12-27T16:36:26.534285+00:00",updated_at:"2024-03-04T19:48:29.85915+00:00",name_original:a,setting_top:f,setting_status_public:h}],imagePlace:[],placeState:[{tld:".mr",name:"Mauritánie",area:1030700,population:4661149,slug:p,id_continent:g,information_chatgpt:"\u003Cp\u003EMauritánie je africká země na západním pobřeží Sahary, která hraničí s Marokem, Západní Saharou, Alžírskem, Mali a Senegalem. Země je bohatá na nerostné suroviny, jako je železná ruda, měď, zlato a diamanty.\u003C\u002Fp\u003E\n\u003Cp\u003EHlavním městem Mauritánie je Nouakchott, které je také největším městem v zemi. Město se nachází přímo u pobřeží Atlantského oceánu a nabízí mnoho zajímavých míst, jako jsou trhy, muzea a mešity.\u003C\u002Fp\u003E\n\u003Cp\u003EMauritánie je známá pro své rozmanité kmeny a kulturu. V zemi žijí lidé ze Saharské pouště, arabské a africké kmeny a také Berbeři. Země má také rozmanitou kuchyni, která odráží tuto kulturní různorodost.\u003C\u002Fp\u003E\n\u003Cp\u003EMauritánie nabízí také mnoho zajímavých turistických atrakcí, jako jsou historické památky, jako například starověké město Chinguetti, které je zapsáno na seznamu světového dědictví UNESCO. Země má také krásné pláže a pobřeží, kde si turisté mohou užít slunce a moře.\u003C\u002Fp\u003E\n\u003Cp\u003EZemě má také mnoho chráněných oblastí a přírodních rezervací, jako je například Národní park Banc d'Arguin, který je domovem tisíců ptáků, jako jsou pelikáni a plameňáci. V zemi také žijí zebry, krokodýli a další divoká zvířata.\u003C\u002Fp\u003E\n\u003Cp\u003ENavzdory své kráse a bohatství se Mauritánie potýká s mnoha problémy, jako jsou nedostatek pitné vody, chudoba a vysoká míra negramotnosti. Nicméně, země má velký potenciál pro rozvoj a turismus může hrát klíčovou roli v hospodářském růstu země.\u003C\u002Fp\u003E",money_prices:[{name:"Taxi (cena za km)",value:q,date_update:d},{name:"Hlavní jídlo",value:300,date_update:d},{name:"Chleba",value:75,date_update:d},{name:"Mléko",value:80,date_update:d},{name:"Voda (500ml)",value:q,date_update:d},{name:"Espresso",value:i,date_update:d},{name:"Zmrzlina (jeden kopeček)",value:i,date_update:d},{name:"Pivo",value:200,date_update:d},{name:"Krabička cigaret",value:700,date_update:d},{name:"Bic Mac",value:a,date_update:d},{name:"Coca Cola (500ml)",value:i,date_update:d}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:"17",date_update:e},{name:"Policie",value:"117",date_update:e},{name:"Hasiči",value:"118",date_update:e},{name:"Zdravotnická záchranná služba",value:"101",date_update:e}],phone_prefix:"+222",coordinates:[{latitude:"20.9043",longitude:"-17.0428"}],people_religion:[{name:"Islám",value:"100%"}],people_nationality:[{name:"Mauri",value:r},{name:"Haúzové",value:r},{name:"Sonerové",value:"20%"},{name:"Wolofové",value:s},{name:"Peulové",value:s},{name:"Ostatní",value:"10%"}],visitors_entry:[{name:"Občanský průkaz",value:"Pro vstup do Mauritánie není dostatečný pouze občanský průkaz. Je nutné mít platný cestovní pas.",date_update:e},{name:"Cestovní pas",value:"Pro vstup do Mauritánie je nutné mít platný cestovní pas.",date_update:e},{name:"Vízum",value:"Pro vstup do Mauritánie je nutné mít platné vízum, které lze získat na mauritánském velvyslanectví nebo konzulátu v zemi pobytu. Občané Evropské unie mohou získat vízum po příletu na mauritánském letišti, pokud splní určité podmínky, například mít platný cestovní pas, zpáteční letenku a dostatečné finanční prostředky pro pobyt v zemi.",date_update:e},{name:"Očkování",value:"Při vstupu do Mauritánie nejsou žádná očkování povinná, ale jsou doporučena očkování proti hepatitidě A a B, žluté zimnici a meningokokové nemoci. Před cestou do země je vhodné konzultovat se s odborníkem na cestovní medicínu.",date_update:e},{name:"Cestovní pojištění",value:"Pro vstup do Mauritánie není nutné mít cestovní nebo zdravotní pojištění. Nicméně, při cestování do této země se doporučuje mít platné cestovní pojištění, které pokryje případné zdravotní problémy a nehody.",date_update:e},{name:"Bezpečnostní situace",value:"V Mauritánii existuje vysoké riziko teroristických útoků a únosů ze strany islamistických extremistů. Vláda země často zavádí omezení pohybu a zvýšená bezpečnostní opatření. Cestovatelé by měli být opatrní a informovat se o aktuální situaci před cestou. Doporučuje se cestovat pouze za denního světla, vyhnout se oblastem na hranicích s Mali a Alžírskem, a být velmi opatrní v okolí města Nouakchott.",date_update:e},{name:"Další informace",value:"Při cestování do Mauritánie se doporučuje dodržovat místní zvyklosti a kulturu, respektovat náboženská a společenská pravidla. Je vhodné mít u sebe dostatečné množství hotovosti, protože v zemi nejsou přijímány platební karty. Také je třeba si uvědomit, že v některých oblastech může být problematické zajištění potravin a vody. Před cestou je vhodné se informovat o aktuální situaci v zemi, případně se poradit s odborníkem na cestovní medicínu ohledně preventivních opatření a očkování.",date_update:e}],type_place:t,id_image_cover:j,id_image_hero:j,id_city_main:u,affiliate:[{name:o,value:f}],mpz:"MR",alerts:a,zoom:[{google:4,booking:g}],ids_neighboring_countries:[{id:62},{id:66},{id:145},{id:205}],organization:a,apps:a,links:a,information_author:a,seo_tags:a,language_phrases:a,wall_sockets:a,id:l,facts_place:a,created_at:"2023-12-27T16:33:10.517062+00:00",updated_at:"2024-03-04T19:48:12.84066+00:00",name_original:a,currency:[{code:"MRO",name:"mauritánská ouguiya"}],setting_top:f,setting_status_public:h}],imageState:[{id:j,name:p,source:"\u002Fworld\u002Fstates\u002F",type:t,author:"pixabay.com",created_at:"2023-05-06T09:27:42.033044+00:00",updated_at:"2023-12-27T20:42:18.520959+00:00"}],placeContinent:[{id:g,slug:"afrika",name:"Afrika",area:30370000,population:1340000000,population_density:44.12,number_states:54,information_chatgpt:"\u003Cp\u003EAfrika je největší kontinent na Zemi a rozkládá se na ploše přesahující 30 milionů kilometrů čtverečních. Nachází se zde více než 50 zemí a žije zde více než 1,3 miliardy lidí. \u003C\u002Fp\u003E\n\u003Cp\u003EJižní Afrika, Egypt, Maroko a Keňa patří mezi nejoblíbenější turistické cíle v Africe. V Jihoafrické republice se můžete podívat na slavné Krugerův národní park a sledovat zde život zvířat v jejich přirozeném prostředí. Egypt je zase proslulý svými pyramidami, které se nacházejí v Gíze. V Maroku můžete navštívit tradiční trhy, zatímco v Keni se můžete setkat s Masajským kmenem a podívat se na úžasnou přírodu.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má bohatou historii a kulturu. První lidé, kteří se usadili v Africe, byli loveckým a sběračským národem. Poté se v této oblasti objevily první civilizace, jako je Kúšská říše, Egyptská říše nebo Království Aksum. Afrika byla také kolonizována evropskými mocnostmi a toto období mělo značný dopad na dnešní politickou situaci na kontinentu.\u003C\u002Fp\u003E\n\u003Cp\u003EJe důležité zmínit, že Afrika má velké množství jazyků a kultur. Zahrnuje mnoho různých národů a etnických skupin, a to má vliv na různorodost obyvatelstva a kultury. Nejrozšířenějším jazykem je arabština, která se mluví především v severní Africe a v některých částech západní Afriky. Dalšími důležitými jazyky jsou svahilština, francouzština, portugalština, angličtina a mnoho dalších.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má také nádhernou přírodu. Nachází se zde nejvyšší hora světa, Kilimandžáro, která se nachází na území Tanzanie a Keňy. Kromě toho se v Africe nachází také největší pouště na světě, Sahara, a největší prales, Kongský prales. Na tomto kontinentě můžete vidět také mnoho zvířat, jako jsou sloni, lvi, gepardi, zebry a mnoho dalších druhů. V Africe se také nachází několik nejvýznamnějších řek světa, jako je Nil, Kongo, Niger a Zambezi.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je bohužel také známá pro své problémy. Mnoho zemí se potýká s chudobou, nedostatkem potravin a vody, nemocemi a válečnými konflikty. Mnoho afrických zemí závisí na zemědělství a rybolovu, což však často není dostatečně výnosné, aby zabezpečilo potřeby obyvatelstva. Kromě toho jsou některé země v Africe trvale postižené extrémními přírodními katastrofami, jako jsou sucha, povodně a bouře.\u003C\u002Fp\u003E\n\u003Cp\u003ENa druhou stranu se v Africe také nachází mnoho lidí, kteří se snaží změnit situaci a zlepšit životy svých spoluobčanů. Vznikají neziskové organizace, které se snaží pomáhat při řešení těchto problémů a zlepšovat kvalitu života v mnoha afrických zemích. Zároveň se v posledních letech objevují nové možnosti pro ekonomický růst a rozvoj, jako jsou technologické start-upy a zahraniční investice.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika je fascinující kontinent, který nabízí mnoho zážitků a zároveň v sobě ukrývá mnoho výzev. Pokud se rozhodnete navštívit Afriku, můžete se těšit na krásnou přírodu, bohatou kulturu a nezapomenutelné zážitky. Je však také důležité být obezřetný a připravený na místní podmínky, protože některé oblasti mohou být nebezpečné kvůli konfliktům nebo zločinnosti.\u003C\u002Fp\u003E",coordinates:[{latitude:1.650801,longitude:17.371642}],type_place:"continent",id_image_cover:g,id_image_hero:g,zoom:[{google:2,booking:3}],information_author:[{text:"\u003Cp\u003E\u003Cb\u003EAfrika\u003C\u002Fb\u003E, druhý největší a druhý nejlidnatější kontinent na světě, láká cestovatele svou neuvěřitelnou rozmanitostí kultur, přírodními krásami a bohatou historií. Při cestování touto obrovskou plochou se otevírá mozaika různorodých zážitků, které zahrnují odlesky moderního městského života, divokou přírodu až po historická místa.\u003C\u002Fp\u003E\u003Cp\u003EJižní Afrika, domov několika národních parků a rezervací, je rájem pro milovníky safari. \u003Cb\u003EKrugerův národní park (Kruger National Park)\u003C\u002Fb\u003E nabízí úžasné setkání s \"Velkým pětkou\" - slony, nosorožci, buvoly, lvy a levharty. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fcape-town\"\u003EKapské Město (Cape Town)\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s ikonickou horou \u003Cb\u003EMaclear's Beacon\u003C\u002Fb\u003E, poskytuje nezapomenutelný výhled na město a okolní krajinu.\u003C\u002Fp\u003E\u003Cp\u003EVýchodní Afrika je známá svými ohromujícími sopkami. \u003Cb\u003EKilimandžáro\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E, nejvyšší hora Afriky, přitahuje dobrodruhy a horolezce z celého světa. Návštěva \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003EZanzibaru\u003C\u002Fa\u003E\u003C\u002Fb\u003E s jeho bílými plážemi a historickým centrem \u003Cb\u003EStone Town\u003C\u002Fb\u003E přidává do cestovatelského mixu kouzlo exotiky.\u003C\u002Fp\u003E\u003Cp\u003EStřední Afrika, s obrovskou rovníkovou pralesní plochou, skrývá některé z nejzajímavějších biodiverzit na světě. \u003Cb\u003EKonžský deštný prales\u003C\u002Fb\u003E je po amazonském druhý největší na světě, je domovem vzácných druhů zvířat a rostlin, a expedice sem mohou odhalit tajemství dosud neobjevená.\u003C\u002Fp\u003E\u003Cp\u003ESeverní Afrika je poznamenána starověkými civilizacemi a písečnými pouštěmi. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fegypt\"\u003EEgypt\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pyramidami a chrámy na březích \u003Cb\u003ENilu\u003C\u002Fb\u003E je jednou z nejnavštěvovanějších destinací v této části kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmaroko\"\u003EMaroko\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s mistrovským spojením tradice a moderny v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fmarrakes\"\u003EMarrákeši\u003C\u002Fa\u003E\u003C\u002Fb\u003E, je fascinujícím mixem barev, vůní a architektury.\u003C\u002Fp\u003E\u003Cp\u003EZápadní Afrika, bohatá na kulturní dědictví, nabízí autentické zážitky. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fghana\"\u003EGhana\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pevnostmi, které připomínají období otroctví, a kultovními festivaly představuje hluboký pohled do minulosti. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fsenegal\"\u003ESenegal\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s historickým ostrovem \u003Cb\u003EGorée\u003C\u002Fb\u003E, který sehral roli s obchodem s otroky, kde najdete koloniální budovy a muzeum \u003Cb\u003EHouse of Slaves (Maison des esclaves)\u003C\u002Fb\u003E.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je rovněž známá svými divokými zvířaty. Kromě safari v jižní části kontinentu můžete zažít jedinečnou příležitost sledovat migraci tisíců zeber a gazel v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fkena\"\u003EKeni\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E. Návštěva gorilí v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Frwanda\"\u003ERwandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fuganda\"\u003EUgandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E je neopakovatelným zážitkem pro ty, kteří touží po setkání s těmito ohroženými tvory.\u003C\u002Fp\u003E\u003Cp\u003EPřírodní skvosty nejsou omezeny pouze na pevninskou část kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fseychely\"\u003ESeychely\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMauricius\u003C\u002Fa\u003E\u003C\u002Fb\u003E, dvě ostrůvky v \u003Cb\u003EIndickém oceánu\u003C\u002Fb\u003E, lákají svými bílými plážemi, průzračnými vodami a jedinečnou faunou a florou.\u003C\u002Fp\u003E\u003Cp\u003EAfrika rovněž hýří kulturou a historií. Starověké památky jako \u003Cb\u003EGreat Zimbabwe National Monument\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fzimbabwe\"\u003EZimbabwe\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003ETimbuktu (Tombouctou)\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMali\u003C\u002Fa\u003E\u003C\u002Fb\u003E jsou svědectvím o bohaté historii a rozvoji obchodu a kultury na africkém kontinentu. Každý region má své vlastní tradiční tance, hudbu a umění, které odrážejí místní identitu a historii.\u003C\u002Fp\u003E\u003Cp\u003ECestování Afrikou je také o interakci s místními lidmi. Přívětivost a pohostinnost afrických komunit jsou nezapomenutelnými součástmi cestování. Místní trhy nabízejí pestrou škálu barev, vůní a chutí, zatímco setkání s kmeny a etnickými skupinami přináší pochopení různorodosti kultur a tradic.\u003C\u002Fp\u003E\u003Cp\u003EJe však důležité si být vědomý sociálních, ekonomických a environmentálních výzev, kterým mnohé africké země čelí. Cestovatelé by měli být respektující vůči místním zvyklostem a životnímu prostředí a přispívat k udržitelnému turismu, který podporuje místní ekonomiky a ochranu přírodních zdrojů.\u003C\u002Fp\u003E\u003Cp\u003ECelkově lze říci, že cestování po Africe je dobrodružstvím naplněným kontrasty a překvapeními. Od rozlehlých savan po exotické pláže, od starověkých ruin po moderní metropole. Afrika nabízí cestovatelům nekonečné možnosti objevování a poznávání. Je to kontinent, který zanechává nezapomenutelný dojem na každém, kdo se odhodlá k jeho prozkoumání.\u003C\u002Fp\u003E",date_create:v,date_update:v,author_create:w,author_update:w}],seo_tags:[],created_at:"2023-12-27T16:35:37.460505+00:00",updated_at:"2024-03-04T19:48:06.634661+00:00",name_original:"Africa",setting_top:f,setting_status_public:h}],placesCities:[{id:1486,id_image_cover:a,importance:c,slug:"aleg",type_place:b,name:"Aleg"},{id:1484,id_image_cover:a,importance:c,slug:"atar",type_place:b,name:"Atar"},{id:1485,id_image_cover:a,importance:c,slug:"kaedi",type_place:b,name:"Kaédi"},{id:1480,id_image_cover:a,importance:c,slug:"kiffa",type_place:b,name:"Kiffa"},{id:1487,id_image_cover:a,importance:c,slug:"nema",type_place:b,name:"Nema"},{id:1479,id_image_cover:a,importance:c,slug:"nouadhibou",type_place:b,name:"Nouadhibou"},{id:u,id_image_cover:a,importance:c,slug:"nouakchott",type_place:b,name:"Nouakchott"},{id:1481,id_image_cover:a,importance:c,slug:"rosso",type_place:b,name:"Rosso"},{id:1482,id_image_cover:a,importance:c,slug:"tidjikja",type_place:b,name:"Tidjikja"},{id:k,id_image_cover:a,importance:c,slug:n,type_place:b,name:m}],imagesCities:a}],fetch:{"OrganismsoPlaceTeaserSpotsComponent:0":{spots:[],images:a},"OrganismsoSidebarEventComponent:0":{_error:{type:"system",errno:x,code:x,erroredSysCall:"read",message:"request to https:\u002F\u002Fapi.frytolnacestach.cz\u002Fapi\u002Fevents-id-city\u002F1483 failed, reason: read ENETDOWN",statusCode:500}}},mutations:[]}}(null,"city","biggest","04-2021","05-2023",true,5,1,100,415,1483,151,"Zouerate","zouerate","booking","mauritanie",50,"30%","5%","state",1478,"2024-03-01","1","ENETDOWN")));