__NUXT_JSONP__("/svet/stat/tunisko/lide", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t){return {data:[{tabsLoad:{tabWhatToSee:c,tabPrice:c,tabPeople:c,tabTrip:c,tabContact:c,tabHotel:c,tabNeighboring:g,tabVideos:h,tabArticles:h,tabWallSockets:g,tabChains:e,tabFauna:e,tabFlora:h,tabFoods:g,tabBrands:h},place:[{tld:".tn",name:"Tunisko",area:163610,population:11818619,slug:n,id_continent:f,information_chatgpt:"\u003Cp\u003ETunisko je země nacházející se v severní Africe a sousedící s Alžírskem, Libyí a Středozemním mořem. Tato země má bohatou historii, která sahá až do antického období, kdy bylo Tunisko římskou provincií. Dnes je Tunisko oblíbenou turistickou destinací, především díky svým krásným plážím, historickým památkám a kulturním tradicím.\u003C\u002Fp\u003E\n\u003Cp\u003EMezi nejvýznamnější turistické atrakce v Tunisku patří zřejmě starobylé město Karthágo, které je zapsáno na seznamu světového dědictví UNESCO. Dalšími zajímavými místy jsou například národní parky Ichkeul a Boukornine nebo město Sidi Bou Said, které se pyšní svými bílými domky s modrými okny a dveřmi.\u003C\u002Fp\u003E\n\u003Cp\u003EKromě turistických atrakcí nabízí Tunisko i bohatou kuchyni. Tradiční tuniská jídla zahrnují tajine, harissu a slané palačinky zvané brik. Tunisko je také známé svou vynikající olivovou olejí, která se používá v mnoha místních pokrmech.\u003C\u002Fp\u003E\n\u003Cp\u003ETunisko je zároveň zemí s mnoha kulturními tradicemi, které jsou pro turisty velmi zajímavé. Například každý rok se v hlavním městě Tunis koná Mezinárodní festival orientálního tance, který je plný hudby, tance a barev. Věřící turisté si mohou prohlédnout některé z mnoha historických mešit, které jsou roztroušeny po celé zemi.\u003C\u002Fp\u003E\n\u003Cp\u003ENa druhé straně však Tunisko trpí některými problémy, jako jsou například sociální a hospodářské výzvy a politická nestabilita. Turisté by se proto měli řídit doporučeními svých zemí ohledně bezpečnostních opatření a zvážit předem plány na případné nouzové situace.\u003C\u002Fp\u003E",money_prices:[{name:"Taxi (cena za km)",value:i,date_update:a},{name:"Hlavní jídlo",value:10,date_update:a},{name:"Chleba",value:o,date_update:a},{name:"Mléko",value:i,date_update:a},{name:"Voda (500ml)",value:o,date_update:a},{name:"Espresso",value:i,date_update:a},{name:"Zmrzlina (jeden kopeček)",value:e,date_update:a},{name:"Pivo",value:p,date_update:a},{name:"Krabička cigaret",value:4,date_update:a},{name:"Bic Mac",value:b,date_update:a},{name:"Coca Cola (500ml)",value:e,date_update:a}],phone_numbers_emergency:[{name:"Mezinárodní tísňová linka",value:q,date_update:a},{name:"Policie",value:q,date_update:a},{name:"Hasiči",value:"198",date_update:a},{name:"Zdravotnická záchranná služba",value:"190",date_update:a},{name:"Turistická policie",value:"71 960 999",date_update:a}],phone_prefix:"+216",coordinates:[{latitude:"33.8869",longitude:"9.5375"}],people_religion:[{name:"islám",value:"99%",date_update:j},{name:"jiné náboženství",value:k,date_update:j},{name:"nevěřící",value:"0%",date_update:j}],people_nationality:[{name:"Arabové",value:"98%",date_update:l},{name:"Berbeři",value:k,date_update:l},{name:"jiné národnosti",value:k,date_update:l}],visitors_entry:[{name:"Občanský průkaz",value:"Občanský průkaz nestačí pro vstup do Tuniska. Pro vstup je potřeba mít platný cestovní pas.",date_update:d},{name:"Cestovní pas",value:"Pro vstup do Tuniska je nezbytné mít platný cestovní pas. Občanský průkaz nestačí.",date_update:d},{name:"Vízum",value:"Občané Evropské unie a Šengenského prostoru nevyžadují pro pobyt do 90 dnů vízum pro vstup do Tuniska. Vízum je však nutné pro delší pobyt nebo pro účely práce nebo studia. Informace o vízových podmínkách je možné získat na konzulárním oddělení velvyslanectví Tuniska v ČR.",date_update:d},{name:"Očkování",value:"Pro vstup do Tuniska není vyžadováno žádné povinné očkování. Doporučuje se však očkování proti běžným chorobám, jako jsou hepatitida A a B, žloutenka, tetanus a dávivý kašel. Před cestou do Tuniska je vhodné poradit se s odborníkem na cestovní medicínu.",date_update:d},{name:"Cestovní pojištění",value:"Přestože cestovní pojištění není pro vstup do Tuniska povinné, je však doporučeno mít ho uzavřené, aby se zajistila ochrana před nečekanými zdravotními výdaji a komplikacemi během pobytu v zemi.",date_update:d},{name:"Bezpečností situace",value:"V Tunisku se vyskytují bezpečnostní rizika spojená s terorismem a kriminalitou. Je důležité být ostražitý a věnovat pozornost okolí. Obzvláště se doporučuje být opatrný v blízkosti hranic s Libyí a Alžírskem. Pro více informací o bezpečnosti v Tunisku se doporučuje sledovat aktuální zpravodajství a informace na webu Ministerstva zahraničních věcí ČR.",date_update:d},{name:"Další informace",value:"Při návštěvě Tuniska se doporučuje respektovat místní zvyklosti a tradice. V náboženských a historických místech je třeba dodržovat určitá pravidla oblečení a chování. Při cestování do vnitrozemí je vhodné být připraven na obtížné podmínky a omezenou infrastrukturu. V případě potřeby lze kontaktovat české velvyslanectví v Tunisku, které poskytuje konzulární a další služby pro české občany.",date_update:d}],type_place:r,id_image_cover:m,id_image_hero:m,id_city_main:2203,affiliate:[{name:"booking",value:c}],mpz:"TN",alerts:b,zoom:[{google:f,booking:6}],ids_neighboring_countries:[{id:62},{id:136}],organization:b,apps:b,links:b,information_author:b,seo_tags:b,language_phrases:b,wall_sockets:b,id:223,facts_place:b,created_at:"2023-12-27T16:33:10.517062+00:00",updated_at:"2024-03-04T19:48:12.84066+00:00",name_original:b,currency:[{code:"TND",name:"Tuniský dinár"}],setting_top:c,setting_status_public:e}],imagePlace:[{id:m,name:n,source:"\u002Fworld\u002Fstates\u002F",type:r,author:"pixabay.com",created_at:"2023-05-03T22:13:53.42428+00:00",updated_at:"2023-12-27T20:42:18.520959+00:00"}],placeContinent:[{id:f,slug:"afrika",name:"Afrika",area:30370000,population:1340000000,population_density:44.12,number_states:54,information_chatgpt:"\u003Cp\u003EAfrika je největší kontinent na Zemi a rozkládá se na ploše přesahující 30 milionů kilometrů čtverečních. Nachází se zde více než 50 zemí a žije zde více než 1,3 miliardy lidí. \u003C\u002Fp\u003E\n\u003Cp\u003EJižní Afrika, Egypt, Maroko a Keňa patří mezi nejoblíbenější turistické cíle v Africe. V Jihoafrické republice se můžete podívat na slavné Krugerův národní park a sledovat zde život zvířat v jejich přirozeném prostředí. Egypt je zase proslulý svými pyramidami, které se nacházejí v Gíze. V Maroku můžete navštívit tradiční trhy, zatímco v Keni se můžete setkat s Masajským kmenem a podívat se na úžasnou přírodu.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má bohatou historii a kulturu. První lidé, kteří se usadili v Africe, byli loveckým a sběračským národem. Poté se v této oblasti objevily první civilizace, jako je Kúšská říše, Egyptská říše nebo Království Aksum. Afrika byla také kolonizována evropskými mocnostmi a toto období mělo značný dopad na dnešní politickou situaci na kontinentu.\u003C\u002Fp\u003E\n\u003Cp\u003EJe důležité zmínit, že Afrika má velké množství jazyků a kultur. Zahrnuje mnoho různých národů a etnických skupin, a to má vliv na různorodost obyvatelstva a kultury. Nejrozšířenějším jazykem je arabština, která se mluví především v severní Africe a v některých částech západní Afriky. Dalšími důležitými jazyky jsou svahilština, francouzština, portugalština, angličtina a mnoho dalších.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika má také nádhernou přírodu. Nachází se zde nejvyšší hora světa, Kilimandžáro, která se nachází na území Tanzanie a Keňy. Kromě toho se v Africe nachází také největší pouště na světě, Sahara, a největší prales, Kongský prales. Na tomto kontinentě můžete vidět také mnoho zvířat, jako jsou sloni, lvi, gepardi, zebry a mnoho dalších druhů. V Africe se také nachází několik nejvýznamnějších řek světa, jako je Nil, Kongo, Niger a Zambezi.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je bohužel také známá pro své problémy. Mnoho zemí se potýká s chudobou, nedostatkem potravin a vody, nemocemi a válečnými konflikty. Mnoho afrických zemí závisí na zemědělství a rybolovu, což však často není dostatečně výnosné, aby zabezpečilo potřeby obyvatelstva. Kromě toho jsou některé země v Africe trvale postižené extrémními přírodními katastrofami, jako jsou sucha, povodně a bouře.\u003C\u002Fp\u003E\n\u003Cp\u003ENa druhou stranu se v Africe také nachází mnoho lidí, kteří se snaží změnit situaci a zlepšit životy svých spoluobčanů. Vznikají neziskové organizace, které se snaží pomáhat při řešení těchto problémů a zlepšovat kvalitu života v mnoha afrických zemích. Zároveň se v posledních letech objevují nové možnosti pro ekonomický růst a rozvoj, jako jsou technologické start-upy a zahraniční investice.\u003C\u002Fp\u003E\n\u003Cp\u003EAfrika je fascinující kontinent, který nabízí mnoho zážitků a zároveň v sobě ukrývá mnoho výzev. Pokud se rozhodnete navštívit Afriku, můžete se těšit na krásnou přírodu, bohatou kulturu a nezapomenutelné zážitky. Je však také důležité být obezřetný a připravený na místní podmínky, protože některé oblasti mohou být nebezpečné kvůli konfliktům nebo zločinnosti.\u003C\u002Fp\u003E",coordinates:[{latitude:1.650801,longitude:17.371642}],type_place:"continent",id_image_cover:f,id_image_hero:f,zoom:[{google:g,booking:p}],information_author:[{text:"\u003Cp\u003E\u003Cb\u003EAfrika\u003C\u002Fb\u003E, druhý největší a druhý nejlidnatější kontinent na světě, láká cestovatele svou neuvěřitelnou rozmanitostí kultur, přírodními krásami a bohatou historií. Při cestování touto obrovskou plochou se otevírá mozaika různorodých zážitků, které zahrnují odlesky moderního městského života, divokou přírodu až po historická místa.\u003C\u002Fp\u003E\u003Cp\u003EJižní Afrika, domov několika národních parků a rezervací, je rájem pro milovníky safari. \u003Cb\u003EKrugerův národní park (Kruger National Park)\u003C\u002Fb\u003E nabízí úžasné setkání s \"Velkým pětkou\" - slony, nosorožci, buvoly, lvy a levharty. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fcape-town\"\u003EKapské Město (Cape Town)\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s ikonickou horou \u003Cb\u003EMaclear's Beacon\u003C\u002Fb\u003E, poskytuje nezapomenutelný výhled na město a okolní krajinu.\u003C\u002Fp\u003E\u003Cp\u003EVýchodní Afrika je známá svými ohromujícími sopkami. \u003Cb\u003EKilimandžáro\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E, nejvyšší hora Afriky, přitahuje dobrodruhy a horolezce z celého světa. Návštěva \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003EZanzibaru\u003C\u002Fa\u003E\u003C\u002Fb\u003E s jeho bílými plážemi a historickým centrem \u003Cb\u003EStone Town\u003C\u002Fb\u003E přidává do cestovatelského mixu kouzlo exotiky.\u003C\u002Fp\u003E\u003Cp\u003EStřední Afrika, s obrovskou rovníkovou pralesní plochou, skrývá některé z nejzajímavějších biodiverzit na světě. \u003Cb\u003EKonžský deštný prales\u003C\u002Fb\u003E je po amazonském druhý největší na světě, je domovem vzácných druhů zvířat a rostlin, a expedice sem mohou odhalit tajemství dosud neobjevená.\u003C\u002Fp\u003E\u003Cp\u003ESeverní Afrika je poznamenána starověkými civilizacemi a písečnými pouštěmi. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fegypt\"\u003EEgypt\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pyramidami a chrámy na březích \u003Cb\u003ENilu\u003C\u002Fb\u003E je jednou z nejnavštěvovanějších destinací v této části kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmaroko\"\u003EMaroko\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s mistrovským spojením tradice a moderny v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fmesto\u002Fmarrakes\"\u003EMarrákeši\u003C\u002Fa\u003E\u003C\u002Fb\u003E, je fascinujícím mixem barev, vůní a architektury.\u003C\u002Fp\u003E\u003Cp\u003EZápadní Afrika, bohatá na kulturní dědictví, nabízí autentické zážitky. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fghana\"\u003EGhana\u003C\u002Fa\u003E\u003C\u002Fb\u003E s pevnostmi, které připomínají období otroctví, a kultovními festivaly představuje hluboký pohled do minulosti. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fsenegal\"\u003ESenegal\u003C\u002Fa\u003E\u003C\u002Fb\u003E, s historickým ostrovem \u003Cb\u003EGorée\u003C\u002Fb\u003E, který sehral roli s obchodem s otroky, kde najdete koloniální budovy a muzeum \u003Cb\u003EHouse of Slaves (Maison des esclaves)\u003C\u002Fb\u003E.\u003C\u002Fp\u003E\u003Cp\u003EAfrika je rovněž známá svými divokými zvířaty. Kromě safari v jižní části kontinentu můžete zažít jedinečnou příležitost sledovat migraci tisíců zeber a gazel v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fkena\"\u003EKeni\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Ftanzanie\"\u003ETanzanii\u003C\u002Fa\u003E\u003C\u002Fb\u003E. Návštěva gorilí v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Frwanda\"\u003ERwandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fuganda\"\u003EUgandě\u003C\u002Fa\u003E\u003C\u002Fb\u003E je neopakovatelným zážitkem pro ty, kteří touží po setkání s těmito ohroženými tvory.\u003C\u002Fp\u003E\u003Cp\u003EPřírodní skvosty nejsou omezeny pouze na pevninskou část kontinentu. \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fseychely\"\u003ESeychely\u003C\u002Fa\u003E\u003C\u002Fb\u003E a \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMauricius\u003C\u002Fa\u003E\u003C\u002Fb\u003E, dvě ostrůvky v \u003Cb\u003EIndickém oceánu\u003C\u002Fb\u003E, lákají svými bílými plážemi, průzračnými vodami a jedinečnou faunou a florou.\u003C\u002Fp\u003E\u003Cp\u003EAfrika rovněž hýří kulturou a historií. Starověké památky jako \u003Cb\u003EGreat Zimbabwe National Monument\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fzimbabwe\"\u003EZimbabwe\u003C\u002Fa\u003E\u003C\u002Fb\u003E nebo \u003Cb\u003ETimbuktu (Tombouctou)\u003C\u002Fb\u003E v \u003Cb\u003E\u003Ca href=\"https:\u002F\u002Fwww.frytolnacestach.cz\u002Fsvet\u002Fstat\u002Fmauricius\"\u003EMali\u003C\u002Fa\u003E\u003C\u002Fb\u003E jsou svědectvím o bohaté historii a rozvoji obchodu a kultury na africkém kontinentu. Každý region má své vlastní tradiční tance, hudbu a umění, které odrážejí místní identitu a historii.\u003C\u002Fp\u003E\u003Cp\u003ECestování Afrikou je také o interakci s místními lidmi. Přívětivost a pohostinnost afrických komunit jsou nezapomenutelnými součástmi cestování. Místní trhy nabízejí pestrou škálu barev, vůní a chutí, zatímco setkání s kmeny a etnickými skupinami přináší pochopení různorodosti kultur a tradic.\u003C\u002Fp\u003E\u003Cp\u003EJe však důležité si být vědomý sociálních, ekonomických a environmentálních výzev, kterým mnohé africké země čelí. Cestovatelé by měli být respektující vůči místním zvyklostem a životnímu prostředí a přispívat k udržitelnému turismu, který podporuje místní ekonomiky a ochranu přírodních zdrojů.\u003C\u002Fp\u003E\u003Cp\u003ECelkově lze říci, že cestování po Africe je dobrodružstvím naplněným kontrasty a překvapeními. Od rozlehlých savan po exotické pláže, od starověkých ruin po moderní metropole. Afrika nabízí cestovatelům nekonečné možnosti objevování a poznávání. Je to kontinent, který zanechává nezapomenutelný dojem na každém, kdo se odhodlá k jeho prozkoumání.\u003C\u002Fp\u003E",date_create:s,date_update:s,author_create:t,author_update:t}],seo_tags:[],created_at:"2023-12-27T16:35:37.460505+00:00",updated_at:"2024-03-04T19:48:06.634661+00:00",name_original:"Africa",setting_top:c,setting_status_public:e}]}],fetch:{},mutations:[]}}("05-2023",null,true,"09-2021",1,5,2,0,1.5,"01-2021","1%","01-2020",376,"tunisko",.4,3,"197","state","2024-03-01","1")));